Create Table zlWordBasic(
	字词 varchar2(100) not null,
	输入码 varchar2(12) not null,
	是否字 number(1) default 0,
	输入法 number(3) default 2)
	PCTFREE 5
	PCTUSED 90
	STORAGE (INITIAL 512 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
ALTER TABLE zlWordBasic ADD CONSTRAINT 
    zlWordBasic_PK PRIMARY KEY (字词,输入码,输入法)
    USING INDEX PCTFREE 5
    STORAGE(INITIAL 256 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
Create Table zlWordCodes(
	字词 varchar2(100),
	输入码 varchar2(12),
	输入法 number(3) default 2)
	PCTFREE 5
	PCTUSED 90
	STORAGE (INITIAL 512 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
Create Table zlWordTable(
	系统 number(5),
	表名 varchar2(20),
	字段 varchar2(20))
	PCTFREE 5
	PCTUSED 90
	STORAGE (INITIAL 512 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
ALTER TABLE zlWordTable ADD CONSTRAINT 
    zlWordTable_PK PRIMARY KEY (系统,表名,字段)
    USING INDEX PCTFREE 5
    STORAGE(INITIAL 256 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
Create Table zlWordPolyphone(
	系统 number(5),
	表名 varchar2(50),
	字词 varchar2(10),
	读音 varchar2(12))
	PCTFREE 5
	PCTUSED 90
	STORAGE (INITIAL 512 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/
ALTER TABLE zlWordPolyphone ADD CONSTRAINT 
    zlWordPolyphone_PK PRIMARY KEY (系统,表名,字词)
    USING INDEX PCTFREE 5
    STORAGE(INITIAL 256 NEXT 128 PCTINCREASE 0 MAXEXTENTS UNLIMITED)
/

CREATE INDEX zlWordBasic_IX_字词 ON zlWordBasic(字词) PCTFREE 10
/
CREATE INDEX zlWordBasic_IX_输入法 ON zlWordBasic(输入法) PCTFREE 10
/
CREATE INDEX zlWordBasic_IX_是否字 ON zlWordBasic(是否字) PCTFREE 10
/
CREATE INDEX zlWordBasic_IX_输入码 ON zlWordBasic(输入码) PCTFREE 10
/
CREATE INDEX zlWordTable_IX_系统 ON zlWordTable(系统) PCTFREE 10
/
CREATE INDEX zlWordTable_IX_表名 ON zlWordTable(表名) PCTFREE 10
/
CREATE OR REPLACE PROCEDURE ZL_zlWordCodes_INSERT(
	字词_IN IN zlWordCodes.字词%type,
	输入码_IN IN zlWordCodes.输入码%type,
	输入法_IN IN zlWordCodes.输入法%type)
IS
BEGIN
	INSERT INTO zlWordCodes(字词,输入码,输入法) values (字词_IN,输入码_IN,输入法_IN);
END ZL_zlWordCodes_INSERT;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordTable_INSERT(
	系统_IN IN zlWordTable.系统%type,
	表名_IN IN zlWordTable.表名%type,
	字段_IN IN zlWordTable.字段%type)
IS
BEGIN
	INSERT INTO zlWordTable(系统,表名,字段) values (系统_IN,表名_IN,字段_IN);
END ZL_zlWordTable_INSERT;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordTable_DELETE(
	系统_IN IN zlWordTable.系统%type)
IS
BEGIN
	DELETE FROM zlWordTable WHERE 系统=系统_IN;
END ZL_zlWordTable_DELETE;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordCodes_DELETE
IS
BEGIN
	DELETE FROM zlWordCodes;
END ZL_zlWordCodes_DELETE;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordBasic_INSERT(
	字词_IN IN zlWordBasic.字词%type,
	输入码_IN IN zlWordBasic.输入码%type,
	输入法_IN IN zlWordBasic.输入法%type,
	是否字_IN IN zlWordBasic.是否字%type)
IS
BEGIN
	INSERT INTO zlWordBasic(字词,输入码,输入法,是否字) values (字词_IN,输入码_IN,输入法_IN,是否字_IN);
END ZL_zlWordBasic_INSERT;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordBasic_UPDATE(
	字词_IN IN zlWordBasic.字词%type,
	输入码_IN IN zlWordBasic.输入码%type,
	输入法_IN IN zlWordBasic.输入法%type,
	是否字_IN IN zlWordBasic.是否字%type,
	原字词_IN IN zlWordBasic.字词%type,
	原输入码_IN IN zlWordBasic.输入码%type)
IS
BEGIN
	UPDATE zlwordbasic set 字词=字词_IN,输入码=输入码_IN 
	where 字词=原字词_IN and 输入码=原输入码_IN and 输入法=输入法_IN and 是否字=是否字_IN;
END ZL_zlWordBasic_UPDATE;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordPolyphone_INSERT(
	系统_IN IN zlWordPolyphone.系统%type,
	表名_IN IN zlWordPolyphone.表名%type,
	字词_IN IN zlWordPolyphone.字词%type,
	读音_IN IN zlWordPolyphone.读音%type)
IS
BEGIN
	INSERT INTO zlWordPolyphone(系统,表名,字词,读音) values (系统_IN,表名_IN,字词_IN,读音_IN);
END ZL_zlWordPolyphone_INSERT;
/

CREATE OR REPLACE PROCEDURE ZL_zlWordPolyphone_DELETE(
	系统_IN IN zlWordPolyphone.系统%type,
	表名_IN IN zlWordPolyphone.表名%type)
IS
BEGIN
	DELETE FROM zlWordPolyphone WHERE 系统=系统_IN AND 表名=表名_IN;
END ZL_zlWordPolyphone_DELETE;
/

