------------------------------------------------------
--服务器管理工具创建脚本
--说明：是创建服务器管理工具所必须的结构和数据的脚本；
--    为了能够在SQL中直接运行调试，并能将函数的创建统一完成，
--    包、包体、Type、过程、存储过程、函数以"\n/"做结束符，其余的以;为结束符,这是该脚本文件组织的必须规则

------------------------------------------------------------------------------------------------------------
--[[1.数据结构]]
------------------------------------------------------------------------------------------------------------
--系统数据表:安装清单、程序清单、程序功能、程序权限、角色权限

Create Table ZLERRTRANSLATE(
       类别         Number(5),
       错误号       Number(18),
       是否可重试   Number(1),
       提示信息     Varchar2(250)
);
Alter Table ZLERRTRANSLATE Add Constraint ZLERRTRANSLATE_PK Primary Key(类别,错误号) Using Index;
CREATE TABLE zlSystems(
    编号 NUMBER(5),
    名称 VARCHAR2(20),
    共享号 NUMBER(5),
    所有者 VARCHAR2(20),
    安装日期 DATE,
    正常安装 NUMBER(3),
    版本号 VARCHAR2(20))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlSystems ADD CONSTRAINT zlSystems_PK PRIMARY KEY (编号) USING INDEX PCTFREE 5;
ALTER TABLE zlSystems ADD CONSTRAINT zlSystems_FK_共享号 FOREIGN KEY (共享号) REFERENCES zlSystems(编号);

create table ZLSysList(
系统 number(5),
名称 varchar2(100));
alter table ZLSysList add constraint ZLSysList_PK primary key (系统) using index;

create table ZLSysCompati(
系统 number(5),
版本号 varchar2(20),
兼容系统 number(5),
兼容版本号 varchar2(20));
alter table ZLSysCompati add constraint ZLSysCompati_PK primary key (系统,版本号,兼容系统,兼容版本号) using index ;
ALTER TABLE ZLSysCompati ADD CONSTRAINT ZLSysCompati_FK_系统 FOREIGN KEY (系统) REFERENCES ZLSysList(系统) ON DELETE CASCADE;
ALTER TABLE ZLSysCompati ADD CONSTRAINT ZLSysCompati_FK_兼容系统 FOREIGN KEY (兼容系统) REFERENCES ZLSysList(系统) ON DELETE CASCADE;

CREATE TABLE zlSysFiles(
    系统 NUMBER(5),
    操作 NUMBER(1),
    文件名 VARCHAR2(100),
    日期 DATE,
    操作人 VARCHAR2(20))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlSysFiles ADD CONSTRAINT zlSysFiles_UQ_操作 Unique (系统,操作) USING INDEX PCTFREE 5;
ALTER TABLE zlSysFiles ADD CONSTRAINT zlSysFiles_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

CREATE TABLE zlUpgrade(
    系统 NUMBER(5),
    原始版本 VARCHAR2(20),
    目标版本 VARCHAR2(20),
    升迁时间 DATE,
    升迁结果 NUMBER(1),
    结果版本 VARCHAR2(20),
    中止语句 VARCHAR2(200),
    提前执行 number(1))
    PCTFREE 5;
ALTER TABLE zlUpgrade ADD CONSTRAINT  zlUpgrade_UQ_升迁时间 Unique (系统,升迁时间)   USING INDEX PCTFREE 5;
ALTER TABLE zlUpgrade ADD CONSTRAINT  zlUpgrade_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

CREATE TABLE zlUpgradeLog(
    系统 NUMBER(5),
    目标版本 VARCHAR2(20),
    类型 NUMBER(1),
    内容 VARCHAR2(200),
    备注 VARCHAR2(200),
    时间 DATE)
    PCTFREE 5;
ALTER TABLE zlUpgradeLog ADD CONSTRAINT  zlUpgradeLog_UQ_时间 Unique (系统,时间) USING INDEX PCTFREE 5;
ALTER TABLE zlUpgradeLog ADD CONSTRAINT  zlUpgradeLog_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

Create Table zlUpgradeConfig(
	项目 varchar2(50),
	内容 varchar2(4000)
	)PCTFREE 5;
Alter Table zlUpgradeConfig Add Constraint zlUpgradeConfig_PK Primary Key (项目) USING INDEX PCTFREE 5;

Create Table ZLTriggers(
	名称 varChar2(100),
	所有者 varChar2(100)
	)PCTFREE 5;
Alter Table ZLTriggers Add Constraint ZLTriggers_UQ_名称 Unique (名称,所有者) USING INDEX PCTFREE 5;
Alter Table ZLTriggers Modify 名称  constraint ZLTriggers_NN_名称   not  null;


Create Table zlComponent(
    部件 varchar2(50),
    名称 varchar2(30),
    主版本 number(3),
    次版本 number(3),
    附版本 number(5),
    系统 number(5),
    注册产品名称  Varchar2(100),
    注册产品简名 Varchar2(20),
    注册产品版本 Varchar2(20))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlComponent ADD CONSTRAINT zlComponent_PK PRIMARY KEY (部件,系统) USING INDEX PCTFREE 5;
ALTER TABLE zlComponent ADD CONSTRAINT zlComponent_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

CREATE TABLE zlPrograms(
    序号 NUMBER(18),
    标题 VARCHAR2(30),
    说明 VARCHAR2(250),
    系统 NUMBER(5),
    部件 VARCHAR2(50),
    资源ID VARCHAR2(36))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlPrograms ADD CONSTRAINT zlPrograms_UQ_序号 UNIQUE (系统,序号)  USING INDEX PCTFREE 5;
Alter Table zlPrograms Modify 序号  constraint zlPrograms_NN_序号   not  null;
ALTER TABLE zlPrograms ADD CONSTRAINT zlPrograms_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

CREATE TABLE zlProgFuncs(
    系统 NUMBER(5),
    序号 NUMBER(18),
    功能 VARCHAR2(30),
    排列 NUMBER(3),
    说明 VARCHAR2(250),
    缺省值 NUMBER(1) Default 1)
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlProgFuncs ADD CONSTRAINT zlProgFuncs_UQ_功能 UNIQUE (系统,序号,功能) USING INDEX PCTFREE 5;
Alter Table zlProgFuncs Modify 序号  constraint zlProgFuncs_NN_序号   not  null;
ALTER TABLE zlProgFuncs ADD CONSTRAINT zlProgFuncs_FK_序号 FOREIGN KEY (系统,序号) REFERENCES zlPrograms(系统,序号) ON DELETE CASCADE;
CREATE INDEX zlProgFuncs_IX_序号   ON zlProgFuncs(系统,序号) PCTFREE 5 Compress 2;

CREATE TABLE zlProgPrivs(
    系统 NUMBER(5),
    序号 NUMBER(18),
    功能 VARCHAR2(30),
    对象 VARCHAR2(50),
    所有者 VARCHAR2(20),
    权限 VARCHAR2(10))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlProgPrivs ADD CONSTRAINT zlProgPrivs_UQ_权限 UNIQUE (系统,序号,功能,对象,所有者,权限) USING INDEX PCTFREE 5;
Alter Table zlProgPrivs Modify 序号  constraint zlProgPrivs_NN_序号   not  null;
ALTER TABLE zlProgPrivs ADD CONSTRAINT zlProgPrivs_FK_序号 FOREIGN KEY (系统,序号,功能) REFERENCES zlProgFuncs(系统,序号,功能) ON DELETE CASCADE;
CREATE INDEX zlProgPrivs_IX_序号 ON zlProgPrivs(系统,序号)  PCTFREE 5 Compress 2;

Create table zlProgRelas
(
  系统     NUMBER(5),
  序号     NUMBER(18),
  功能     VARCHAR2(30),
  组号     NUMBER(5),
  关系     NUMBER(3),
  主项     NUMBER(1),
  主项关系 NUMBER(1))
  PCTFREE 5
  Cache Storage(Buffer_Pool Keep);
Alter Table zlProgRelas Add Constraint zlProgRelas_PK Primary Key (系统, 序号, 功能, 组号) using index  PCTFREE 5;
Alter Table zlProgRelas Add Constraint zlProgRelas_FK_序号 Foreign Key (系统, 序号, 功能) references ZLPROGFUNCS (系统, 序号, 功能) On Delete Cascade;
Alter Table zlProgRelas Add Constraint zlProgRelas_CK_主项 Check (主项 IN(0,1));
Alter Table zlProgRelas Add Constraint zlProgRelas_CK_主项关系 Check (主项关系 IN(0,1));

create table ZLModuleRelas
(
系统  Number(5),
模块  Number(18),
功能  Varchar2(30),
相关系统  Number(5),
相关模块  Number(18),
相关类型  Number(1),
相关功能  Varchar2(30),
缺省值    Number(1)
);
alter table ZLModuleRelas Modify 系统  constraint ZLModuleRelas_NN_系统   not  null;
alter table ZLModuleRelas Modify 模块  constraint ZLModuleRelas_NN_模块   not  null;
alter table ZLModuleRelas Modify 相关模块  constraint ZLModuleRelas_NN_相关模块   not  null;
alter table ZLModuleRelas add constraint ZLModuleRelas_UQ_相关模块 Unique(系统,模块,功能,相关系统,相关模块,相关功能) using index;
alter table ZLModuleRelas add constraint ZLModuleRelas_FK_模块 foreign key(系统,模块) references  zlprograms(系统,序号) on delete cascade;


CREATE TABLE zlRoleGrant(
    系统 NUMBER(5),
    序号 NUMBER(18),
    角色 VARCHAR2(30),
    功能 VARCHAR2(30))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlRoleGrant ADD CONSTRAINT zlRoleGrant_PK UNIQUE (系统,序号,功能,角色) USING INDEX PCTFREE 5;
ALTER TABLE zlRoleGrant ADD CONSTRAINT zlRoleGrant_FK_序号 FOREIGN KEY (系统,序号,功能) REFERENCES zlProgFuncs(系统,序号,功能) ON DELETE CASCADE;
CREATE INDEX zlRoleGrant_IX_序号 ON zlRoleGrant(系统,序号) PCTFREE 5 Compress 2;
CREATE INDEX zlRoleGrant_IX_角色 ON zlRoleGrant(角色) PCTFREE 5 Compress 1 ;


CREATE TABLE zlRoleGroups(
    组名 VARCHAR2(30),
    角色 VARCHAR2(30))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlRoleGroups ADD CONSTRAINT zlRoleGroups_UQ_组名 UNIQUE (组名,角色) USING INDEX PCTFREE 5;
Alter Table zlRoleGroups Modify 组名 Constraint zlRoleGroups_NN_组名 Not Null;

Create Table Zlroles(
       名称   Varchar2(50),
       系统   Number(5));
ALTER TABLE Zlroles ADD CONSTRAINT ZLRoles_PK PRIMARY KEY (名称) USING INDEX;

CREATE Sequence zlMenus_ID Start With 1;

CREATE TABLE zlMenus(
    组别 VARCHAR2(30),
    ID   NUMBER(18),
    上级ID   NUMBER(18),
    标题 VARCHAR2(30),
    快键 VARCHAR2(2),
    说明 VARCHAR2(250),
    系统 NUMBER(5),
    模块 NUMBER(18),
    短标题 VARCHAR2(30),
    图标 NUMBER(4))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlMenus ADD CONSTRAINT zlMenus_PK PRIMARY KEY (组别,ID)  USING INDEX PCTFREE 5;
ALTER TABLE zlMenus ADD CONSTRAINT zlMenus_UQ_组别 Unique (组别,上级ID,标题,系统) USING INDEX PCTFREE 5;
ALTER TABLE zlMenus ADD CONSTRAINT zlMenus_FK_模块 FOREIGN KEY (系统,模块) REFERENCES zlPrograms(系统,序号) ON DELETE CASCADE;
ALTER TABLE zlMenus ADD CONSTRAINT zlMenus_FK_上级 FOREIGN KEY (组别,上级ID) REFERENCES zlMenus(组别,ID) ON DELETE CASCADE;

Create Table zlUserRoles(
    用户 varchar2(30),
    角色 varchar2(30),
    管理 number(1))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlUserRoles ADD CONSTRAINT zlUserRoles_PK UNIQUE (用户,角色)  USING INDEX PCTFREE 5;

Create Table zlMgrGrant(
    用户名 varchar2(30),
    功能 varchar2(4000))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlMgrGrant ADD CONSTRAINT zlMgrGrant_PK PRIMARY KEY (用户名) USING INDEX PCTFREE 5;

--自定义过程管理脚本
Create Sequence zlProcedure_ID start with 1;

Create Table zlProcedure(
    ID NUMBER(5),
    类型 NUMBER(5),
    名称 VARCHAR2(50),
    状态 NUMBER(5),
    说明 Varchar2(2000),
    所有者 VARCHAR2(20),
    修改人员 Varchar2(20),
    修改时间 DATE,
    上次修改人员 VARCHAR2(20),
    上次修改时间 Date,
    升级前版本 Varchar2(50),
    升级后版本 Varchar2(50),
    系统编号 Number(5))
    PCTFREE 5 Cache Storage (Buffer_Pool Keep);
ALTER TABLE zlProcedure ADD CONSTRAINT zlProcedure_PK PRIMARY KEY (ID) USING INDEX PCTFREE 5;
ALTER TABLE Zlprocedure ADD CONSTRAINT Zlprocedure_UQ_名称 UNIQUE(名称) USING INDEX PCTFREE 5;

CREATE TABLE zlProcedureText(
    过程id NUMBER(5),
    性质 VARCHAR2(50),
    序号 NUMBER(5),
    内容 VARCHAR2(4000))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlProcedureText ADD CONSTRAINT zlProcedureText_FK_过程id FOREIGN KEY (过程id) REFERENCES zlProcedure(ID)  ON DELETE CASCADE;

--自动提醒功能脚本
Create Table zlNotices(
    序号 NUMBER(5),
    系统 NUMBER(5),
    提醒条件 VARCHAR2(4000),
    提醒内容 VARCHAR2(250),
    提醒报表 VARCHAR2(50),
    提醒声音 NUMBER(5),
    提醒窗口 NUMBER(1),
    提醒顺序 VARCHAR2(200) DEFAULT '',
    检查周期 NUMBER(5),
    提醒周期 NUMBER(5),
    开始时间 DATE,
    终止时间 DATE)
    PCTFREE 5;
Alter Table zlNotices ADD CONSTRAINT zlNotices_PK PRIMARY KEY (序号) USING INDEX PCTFREE 5;

Create Table zlNoticeUsr(
    提醒序号 NUMBER(5),
    提醒对象 NUMBER(1) DEFAULT 0,
    对象名称 VARCHAR2(50))
    PCTFREE 5 ;

Create Table zlNoticeRec(
    提醒序号 NUMBER(5),
    用户名 VARCHAR2(30),
    检查时间 DATE,
    检查结果 NUMBER(1) DEFAULT 0,
    提醒标志 NUMBER(1) DEFAULT 0,
    提醒时间 DATE,
    已读标志 NUMBER(1) DEFAULT 0,
    提醒内容 VARCHAR2(1000))
    PCTFREE 5;
Alter Table zlNoticeUsr ADD CONSTRAINT zlNoticeUsr_PK PRIMARY KEY (提醒序号,提醒对象,对象名称) USING INDEX PCTFREE 5;
Alter Table zlNoticeUsr ADD CONSTRAINT zlNoticeUsr_FK_提醒序号 FOREIGN KEY(提醒序号) REFERENCES zlNotices(序号) ON DELETE CASCADE;
Alter Table zlNoticeRec ADD CONSTRAINT zlNoticeRec_PK PRIMARY KEY (提醒序号,用户名) USING INDEX PCTFREE 5;
Alter Table zlNoticeRec ADD CONSTRAINT zlNoticeRec_FK_提醒序号 FOREIGN KEY(提醒序号) REFERENCES zlNotices(序号) ON DELETE CASCADE;

--服务器管理工具清单
CREATE TABLE zlSvrTools(
    编号 VARCHAR2(6),
    上级 VARCHAR2(6),
    标题 VARCHAR2(20),
    快键 VARCHAR2(2),
    说明 VARCHAR2(250),
    次序 NUMBER(3))
    PCTFREE 5;
ALTER TABLE zlSvrTools ADD CONSTRAINT zlSvrTools_PK PRIMARY KEY (编号)  USING INDEX PCTFREE 5;
ALTER TABLE zlSvrTools ADD CONSTRAINT zlSvrTools_UQ_标题 Unique (上级,标题) USING INDEX PCTFREE 5;
ALTER TABLE zlSvrTools ADD CONSTRAINT zlSvrTools_FK_上级 FOREIGN KEY (上级) REFERENCES zlSvrTools(编号) ON DELETE CASCADE;


--使用许可与注册管理
CREATE TABLE zlRegInfo(
    项目 VARCHAR2(20),
    行号 NUMBER(3),
    内容 VARCHAR2(4000),
    站点 Varchar2(1))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
ALTER TABLE zlRegInfo ADD CONSTRAINT zlRegInfo_UQ_项目 UNIQUE (项目,行号,站点) USING INDEX PCTFREE 5;

CREATE TABLE zlUnitInfoItem(
    编码 VARCHAR2(3),
    名称 VARCHAR2(20),
    是否图片 NUMBER(1))
    PCTFREE 5;
Alter Table zlUnitInfoItem Add Constraint zlUnitInfoItem_PK Primary Key (编码) USING INDEX PCTFREE 5;
Alter Table zlUnitInfoItem Add Constraint zlUnitInfoItem_UQ_名称 Unique (名称) USING INDEX PCTFREE 5;

CREATE TABLE zlUnitInfoImage(
    项目 VARCHAR2(20),
    图片 Blob,
    站点 Varchar2(1))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep);
Alter Table zlUnitInfoImage Add Constraint zlUnitInfoImage_UQ_项目 UNIQUE (项目,站点) USING INDEX PCTFREE 5;
Alter Table zlUnitInfoImage Modify 项目 constraint zlUnitInfoImage_NN_项目 not null;
ALTER TABLE zlUnitInfoImage ADD CONSTRAINT  zlUnitInfoImage_FK_项目 FOREIGN KEY (项目) REFERENCES zlUnitInfoItem(名称) ON DELETE CASCADE;

Create Table zlRegFunc(
  系统 Number(5),
  序号 Number(18),
  功能 Varchar2(30))
  PCTFREE 5
  Cache Storage(Buffer_Pool Keep);
Alter Table zlRegFunc  Add Constraint zlRegFunc_PK PRIMARY KEY(系统, 序号, 功能) USING INDEX PCTFREE 5;

Create Global Temporary Table zlRegFile(
  项目 VARCHAR2(20),
  系统 Number(5),
  序号 Number(18),
  功能 Varchar2(1000))
  On Commit Preserve Rows;

Create Global Temporary Table zlRegAudit(
  项目 VARCHAR2(20),
  内容 Varchar2(1000))
  On Commit Preserve Rows;

--字典管理中心
CREATE TABLE zlBaseCode(
    系统 NUMBER(5),
    表名 VARCHAR2(20),
    固定 NUMBER(1) default 0,
    说明 VARCHAR2(200),
    分类 VARCHAR2(16))
    PCTFREE 5;
ALTER TABLE zlBaseCode ADD CONSTRAINT zlBaseCode_PK PRIMARY KEY (系统,表名) USING INDEX PCTFREE 5;
ALTER TABLE zlBaseCode ADD CONSTRAINT zlBaseCode_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

--系统参数与选项
CREATE TABLE zlOptions(
    参数号 NUMBER(3),
    参数名 VARCHAR2(20),
    参数值 VARCHAR2(500),
    缺省值 VARCHAR2(100),
    参数说明 VARCHAR2(100))
    PCTFREE 5
    Cache Storage(Buffer_Pool Keep) ;
ALTER TABLE zlOptions ADD CONSTRAINT zlOptions_PK PRIMARY KEY (参数号) USING INDEX PCTFREE 5;

--数据转移管理中心
Create Table zlDataMove(
    系统 NUMBER(5),
    组号 NUMBER(3),
    组名 VARCHAR2(20),
    说明 VARCHAR2(50),
    状态 NUMBER(1),
    停用作业号 Varchar2(4000),
    日期字段 VARCHAR2(20),
    转出描述 NUMBER(5),
    上次日期 DATE,
    重建索引范围 NUMBER(1),
    重建索引间隔 NUMBER(3),
    本次最终日期 date)
    PCTFREE 5
;
ALTER TABLE zlDataMove ADD CONSTRAINT zlDataMove_PK PRIMARY KEY (系统,组号) USING INDEX PCTFREE 5;
ALTER TABLE zlDataMove ADD CONSTRAINT zlDataMove_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

CREATE table zlDataMovelog(
    系统 number(5),
    批次 number(8),
    序列 number(8),
    截止时间 date,
    待转出 number(1),
    当前进度 varchar2(200),
    标记开始时间 date,
    标记结束时间 date,
    转出开始时间 date,
    转出结束时间 date,
    重建结束时间 date);
ALTER TABLE zlDataMovelog ADD CONSTRAINT zlDataMovelog_PK PRIMARY KEY (系统,批次) USING INDEX PCTFREE 5;
ALTER TABLE zlDataMovelog ADD CONSTRAINT zlDataMovelog_FK_系统 FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

Create Table zlBakTables(
    系统 Number(5),
    表名 Varchar2(30),
    组号 Number(2),
    序号 Number(3),
    直接转出 Number(1),
    停用触发器 number(1));
Alter Table zlBakTables    Add Constraint zlBakTables_PK Primary Key (系统,表名) USING INDEX PCTFREE 5;
Alter Table zlBakTables Add Constraint zlBakTables_FK_系统 Foreign Key (系统) References zlSystems(编号) On Delete Cascade;

Create Table zlBakTableIndex(
	系统 NUMBER(5) ,
	表名 Varchar2(30),
	索引名 VARCHAR2(30));
Alter Table zlBakTableIndex Add Constraint zlBakTableIndex_PK Primary Key (系统,表名,索引名) USING INDEX PCTFREE 5;
Alter Table zlBakTableIndex Add Constraint zlBakTableIndex_FK_系统 Foreign Key (系统,表名) References zlBakTables(系统,表名) On Delete Cascade;

Create Table zlBakSpaces(
    系统 Number(5),
    编号 Number(18),
    名称 Varchar2(30),
    所有者 Varchar2(30),
    DB连接 Varchar2(128),
    当前 Number(1),
    只读 Number(1))
    PCTFREE 5;
Alter Table zlBakSpaces Add Constraint zlBakSpaces_PK Primary Key (系统,编号) USING INDEX PCTFREE 5;
Alter Table zlBakSpaces    Add Constraint zlBakSpaces_UQ_名称 Unique (系统,名称) USING INDEX PCTFREE 5;
Alter Table zlBakSpaces Add Constraint zlBakSpaces_FK_系统 Foreign Key (系统) References zlSystems(编号) On Delete Cascade;

create table Zlbigtables(
    系统 Number(5),
    表名 Varchar2(30));
Alter table Zlbigtables add constraint Zlbigtables_PK primary key (系统, 表名) USING INDEX PCTFREE 5;

Create Table zlTables(
    系统    Number(5),
    表名    Varchar2(30),
    表空间  Varchar2(30),
    分类    Varchar2(3),
    是否高并发写 Number(1)
);
Alter Table zlTables Add Constraint zlTables_PK Primary Key (表名,系统) USING INDEX PCTFREE 5;

--自动作业管理中心
CREATE TABLE zlAutoJobs(
    系统 NUMBER(5),
    类型 NUMBER(1),
    序号 NUMBER(3),
    名称 VARCHAR2(20),
    说明 VARCHAR2(200),
    内容 VARCHAR2(200),
    参数 VARCHAR2(200),
    执行时间 DATE,
    间隔时间 NUMBER(5),
    作业号 NUMBER(18),
    系统升级停用 Number(1),
    时间单位 Varchar2(5))
    PCTFREE 5;
ALTER TABLE zlAutoJobs ADD CONSTRAINT zlAutoJobs_PK UNIQUE(系统,类型,序号)  USING INDEX PCTFREE 5;
ALTER TABLE zlAutoJobs ADD CONSTRAINT zlAutoJobs_FK_系统    FOREIGN KEY (系统) REFERENCES zlSystems(编号) ON DELETE CASCADE;

--使用日志与错误日志中心
CREATE TABLE zlDiaryLog(
    会话号 NUMBER(18),
    用户名 VARCHAR2(30),
    工作站 VARCHAR2(30),
    部件名 VARCHAR2(30),
    窗体名 VARCHAR2(40),
    工作内容 VARCHAR2(50),
    进入时间 DATE,
    退出原因 NUMBER(1),
    退出时间 DATE)
    PCTFREE 5 Initrans 20;
Alter Table zldiarylog Add Constraint zldiarylog_PK Primary Key (进入时间,会话号,窗体名) Using Index Initrans 20;

Create Table zlErrorLog(
    会话号 NUMBER(18),
    用户名 VARCHAR2(30),
    工作站 VARCHAR2(30),
    时间 DATE,
    类型 NUMBER(1),
