	Name		VARCHAR2(100) not null,		--日志分类名称
	Description	VARCHAR2(500),			--日志介绍
	Builtin		Number(1),			--数据来源。1-系统固定，0-自定义添加
	Begin_Time	date,				--开始跟踪时间
	End_time	date,				--结束跟踪时间
	Log_Keep_Days	number(5),			--日志保留天数
	Log_Mode	number(1),			--0-不记录。1-本地记录。2-数据库记录,3-本地和数据库都记录
	Log_Level	number(1)
);
alter table zllogCategory add constraint zllogCategory_PK primary key(ID) using index;
alter table zllogCategory add constraint zllogCategory_UQ_Name Unique(Name) using index;
create sequence zllogCategory_ID start with 1;

--控制日志的表，没有该配置时，由zllogCategory生效
--User_name与IP,Station同时为空为其他缺省处理。没有该数据时，只有限定条件的客户端用户IP生成日志，其他的客户端不生成。
Create Table zllogSet(
	ID		number(18),			--日志设置ID
	Category_ID	number(18),			--日志分类ID
	User_name	varchar2(100),			--限定的用户，为空不限制
	Station		varchar2(100),			--日记记录的工作站
	IP		varchar2(100),			--限定的IP  ，为空不限制
	Component_Names	varchar2(4000),			--限定日志发生的部件
	Module_Names	varchar2(4000),			--限定日志发生的模块,多个以分号分隔。为空不限制
	Function_Names	varchar2(4000),			--限定日志发生的功能,多个以分号分隔。为空不限制
	Call_Names	varchar2(4000)			--限定日志发生的API或存储过程名称,多个以分号分隔。为空不限制
);
alter table zllogSet add constraint zllogSet_PK primary key(ID) using index;
alter table zllogSet add constraint zllogSet_UQ_Station Unique(Station,User_name,IP) using index;
alter table zllogSet add constraint zllogSet_FK_Category_ID Foreign Key(Category_ID) references zllogCategory(ID) on delete cascade;
create sequence zllogSet_ID start with 1;

--日志记录表。
create Table zllogInfo (
	ID		number(18),			--日志目录ID
	Log_Level	number(1),			--记录的日志级别
	Server		varchar2(100),			--日志产生的实例,IP:Port/SID
	User_name	varchar2(100),			--日志记录的用户名称
	Session_id	number(18),			--日志产生的会话
	IP		varchar2(100),			--日记记录的IP
	Station		varchar2(100),			--日记记录的工作站
	Process_ID	number(18),			--日志的记录进程
	Process_name	varchar2(100),			--日记的记录进程名
	Category_Name	varchar2(100),			--日记的分类名称
	Component_Name	varchar2(100),			--发生的部件
	Module_Name	varchar2(100),			--发生的模块
	Function_Name	varchar2(100),			--发生的日志功能
	Call_Name	varchar2(100),			--记录的API或存储过程名称
	Stage		number(1),			--执行阶段:0-开始执行阶段。1-执行完毕阶段
	Create_Time	Date,
	Log_info	varchar2(4000),
	Log_info_Ex	CLOB				--超长日志存储在这里
) Initrans 20;
alter table zllogInfo add constraint zllogInfo_PK primary key(ID) using index;
create index zllogInfo_IX_Create_Time on zllogInfo(Create_Time);
create sequence zllogInfo_ID start with 1;

--通用讨论
Create Table zlChatNotify(
    ID Number(18),
    Receiver Varchar2(36),
    Url Varchar2(500),
    Sys_Code Varchar2(20),
    Main_Code Varchar2(20),
    Main_ID Varchar2(36),
    Subject Varchar2(50),
    Sender Varchar2(36),
    Send_Time Date,
    State Number(1)  --0-未接收，1-已接收
) Initrans 20;
ALTER TABLE zlChatNotify ADD CONSTRAINT zlChatNotify_PK PRIMARY KEY (ID) USING INDEX PCTFREE 5;
Create INDEX zlChatNotify_IX_Send_Time ON zlChatNotify(Send_Time) PCTFREE 5;
