--说明： 该组函数，作为对Oracle系统的补充，建立在ZLSOFT中，授予公共运行权限，建立公共同义词，所有用户可直接使用。

Create Or Replace Function Zl_Strsplit
(
  Str_In   In Varchar2, --需要分隔的字符串
  Split_In In Varchar2, --分隔符
  Group_In In Number := 1 --返回第几组字符，从1开始
)
--功能：将字符串按分割符进行分组，返回指定分组数的字符
 Return Varchar2 As
  Strtmp   Varchar2(4000);
  Intbegin Number(5);
  Intend   Number(5);

  v_Error Varchar2(255);
  Err_Custom Exception;
Begin
  If Group_In <= 0 Then
    v_Error := '下标值越界，请检查组数参数！';
    Raise Err_Custom;
  Else
    Intend := Instr(Str_In || Split_In, Split_In, 1, Group_In);
    If Intend > 0 Then
      If Group_In = 1 Then
        Strtmp := Substr(Str_In, 1, Intend - 1);
      Else
        Intbegin := Instr(Str_In, Split_In, 1, Group_In - 1);
        Strtmp   := Substr(Str_In, Intbegin + Lengthb(Split_In), Intend - Intbegin - Lengthb(Split_In));
      End If;
    Else
      v_Error := '下标值越界，请检查分隔符参数！';
      Raise Err_Custom;
    End If;
  End If;

  Return Strtmp;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20999, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End;
/
Create Or Replace Function Zl_Checkobject
(
  n_Type        In Number, --1=表,2=字段,3=约束,4=索引 ,5=序列
  v_Object_Name In Varchar2,
  v_Table_Name  In Varchar2 := Null --仅当n_Type=2时才需要传入 
) Return Number Authid Current_User As
  --功能：以执行者的身份检查指定表的指定对象是否存在 
  --返回值：>0表示存在，0表示不存在 
  n_Count Number(5);
Begin
  If n_Type = 1 Then
    If v_Table_Name Is Null Then
      Select Count(1) Into n_Count From User_Tables Where Table_Name = Upper(v_Object_Name);
    Else
      Select Count(1) Into n_Count From User_Tables Where Table_Name = Upper(v_Table_Name);
    End If;
  Elsif n_Type = 2 Then
    Select Count(1)
    Into n_Count
    From User_Tab_Columns
    Where Table_Name = Upper(v_Table_Name) And Column_Name = Upper(v_Object_Name);
  
  Elsif n_Type = 3 Then
    Select Count(1) Into n_Count From User_Constraints Where Constraint_Name = Upper(v_Object_Name);
  
  Elsif n_Type = 4 Then
    Select Count(1) Into n_Count From User_Indexes Where Index_Name = Upper(v_Object_Name);
  
  Elsif n_Type = 5 Then
    Select Count(1) Into n_Count From User_Sequences Where Sequence_Name = Upper(v_Object_Name);
  End If;

  Return n_Count;
End Zl_Checkobject;
/
