Create Or Replace Procedure Zllogcategory_Edit
(
  Mode_In          In Number,
  Id_In            In Zllogcategory.Id%Type := Null,
  Name_In          In Zllogcategory.Name%Type := Null,
  Description_In   In Zllogcategory.Description%Type := Null,
  Begin_Time_In    In Zllogcategory.Begin_Time%Type := Null,
  End_Time_In      In Zllogcategory.End_Time%Type := Null,
  Log_Keep_Days_In In Zllogcategory.Log_Keep_Days%Type := Null,
  Log_Mode_In      In Zllogcategory.Log_Mode%Type := Null,
  Log_Level_In     In Zllogcategory.Log_Level%Type := Null
) Is
  vBuiltin Zllogcategory.Builtin%Type;
Begin
  If Mode_In = 0 Then
    Insert Into Zllogcategory
      (ID, Name, Description, Builtin, Begin_Time, End_Time, Log_Keep_Days, Log_Mode, Log_Level)
    Values
      (Zllogcategory_Id.Nextval, Name_In, Description_In, 0, Begin_Time_In, Begin_Time_In, Log_Keep_Days_In,
       Log_Mode_In, Log_Level_In);
