Create Or Replace Function zl_GetSysParameter
(
  参数_In   Zlparameters.参数名%Type,
  模块_In   Zlparameters.模块%Type := Null,
  系统_In   Zlparameters.系统%Type := 1,
  部门id_In Zldeptparas.部门id%Type := 0
  --功能：获取当前系统中指定参数的参数值
  ----本函数主要供其他过程调用，因zlParameters是公共表，程序中使用公共部件的公共函数
  ----调用时注意,如果参数值为空或没有该参数,则返回空
  --参数：
  ----参数_In：必须传入非Null值，以字符形式传入的参数号或参数名,注意参数名不能为数字。
  ----系统_IN：非标准版系统需要传入系统号，注意是示扩展的系统号，如1，而不是100。无系统传入Null
) Return Varchar2 As
  v_系统 Zlparameters.系统%Type;
  v_私有 Zlparameters.私有%Type;
  v_本机 Zlparameters.本机%Type;
  v_部门 Zlparameters.部门%Type;

  v_参数id Zluserparas.参数id%Type;
  v_机器名 Zluserparas.机器名%Type;
  v_参数值 Zlparameters.参数值%Type;
Begin
  --确定系统,可能没有系统(如私有全局)
  If 系统_In Is Not Null Then
    Select Min(编号) Into v_系统 From zlSystems Where Trunc(编号 / 100) = 系统_In;
  End If;

  --读取参数信息
  Begin
    If Zl_To_Number(参数_In) <> 0 Then
      Select ID, Nvl(参数值, 缺省值), 私有, 本机, 部门
      Into v_参数id, v_参数值, v_私有, v_本机, v_部门
      From zlParameters
      Where 参数号 = Zl_To_Number(参数_In) And Nvl(模块, 0) = Nvl(模块_In, 0) And Nvl(系统, 0) = Nvl(v_系统, 0);
    Else
      Select ID, Nvl(参数值, 缺省值), 私有, 本机, 部门
      Into v_参数id, v_参数值, v_私有, v_本机, v_部门
      From zlParameters
      Where 参数名 = 参数_In And Nvl(模块, 0) = Nvl(模块_In, 0) And Nvl(系统, 0) = Nvl(v_系统, 0);
    End If;

  Exception
    When Others Then
      Return Null;
  End;
  If Nvl(v_部门, 0) = 0 Then
    --读取非部门参数值
    If Nvl(v_私有, 0) = 1 Or Nvl(v_本机, 0) = 1 Then
      If Nvl(v_本机, 0) = 1 Then
        Select Sys_Context('USERENV', 'TERMINAL') Into v_机器名 From Dual;
      End If;
      Begin
        Select Nvl(参数值, v_参数值)
        Into v_参数值
        From zlUserParas
        Where 参数id = v_参数id And (用户名 = User Or Nvl(v_私有, 0) = 0) And (机器名 = Nvl(v_机器名, '空空') Or Nvl(v_本机, 0) = 0);
      Exception
        When Others Then
          Return v_参数值;
      End;
    End If;
  Else
    Begin
      Select Nvl(参数值, v_参数值) Into v_参数值 From Zldeptparas Where 参数id = v_参数id And 部门id = 部门id_In;
    Exception
      When Others Then
        Return v_参数值;
    End;
  End If;
  Return v_参数值;
End zl_GetSysParameter;
/

CREATE OR REPLACE Function Zl_Rptinparams_Check(
  Content_In Varchar2
) Return Number 
Is
  n_Count Number;
Begin
  --1.该函数仅用于报表的无类型参数录入关键字检查，避免安全评估检查为SQL注入
  --2.其他需要限制的关键字请技术人员扩充
  --3.返回1表示检查通过，0表示检查不通过
  
  Select Count(1) Into n_Count From Dual 
  Where Upper(Content_In) like '%SELECT %FROM %';  
  
  If n_Count <= 0 Then
    Return 1;
  Else
    Return 0;    
  End If;
  
Exception
  When Others Then
    Return 0;
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Rptinparams_Check;
/

CREATE OR REPLACE Procedure Zl_Zlclientsession_Save
(
  工作站_In     In Zlclientsession.工作站%Type,
  会话号_In     In Zlclientsession.会话号%Type := Null,
  消息端口_In   In Zlclientsession.消息端口%Type := Null,
  Ip_In         In Zlclientsession.Ip%Type := Null,
  用户名_In     In Zlclientsession.用户名%Type := Null,
  姓名_In       In Zlclientsession.人员姓名%Type := Null,
  当前部门id_In In Zlclientsession.当前部门id%Type := Null,
  当前位置_In   In Zlclientsession.当前位置%Type := Null,
  状态_In       In Zlclientsession.状态%Type := Null, --1-登录，0-退出导航台,Null-改变位置 
  部门性质_In   In Zlclientsession.部门性质%Type := Null,
  人员性质_In   In Zlclientsession.人员性质%Type := Null
) Is
  v_会话号   Zlclientsession.会话号%Type;
  v_消息端口 Zlclientsession.消息端口%Type;
  I          Number;
  n_Portmin  Number;
  n_Portmax  Number;
Begin
  --功能:保存客户端的会话、端口信息 

  --如果没有传入消息端口，则说明是登录或退出时调用方法 
  --登录时，获取最小的可用端口 
  --退出时，应该根据会话号来取端口号 
  If Nvl(会话号_In, 0) = 0 Then
    Select Userenv('sessionID') Into v_会话号 From Dual;
  Else
    v_会话号 := 会话号_In;
  End If;
  If Nvl(消息端口_In, 0) = 0 Then
    If 状态_In = 1 Then
      --读取端口设置的最大\最小值 
      Select Decode(参数值, '0', 20001, Nvl(Substr(参数值, 0, Instr(参数值, '-') - 1), 20001)),
             Decode(参数值, '0', 20200, Nvl(Substr(参数值, Instr(参数值, '-') + 1), 20200))
      Into n_Portmin, n_Portmax
      From zlOptions
      Where 参数号 = 9;
    
      --循环每一个端口,查看是否被占用 
      I := n_Portmin;
      While Nvl(v_消息端口, 0) = 0 And I < n_Portmax Loop
        Select Decode(Nvl(Max(状态), 0), 0, I, 0)
        Into v_消息端口
        From Zlclientsession
        Where 工作站 = 工作站_In And 消息端口 = I;
        I := I + 1;
      End Loop;
    
      --如果循环后所有端口都被占用,一般是由于异常关闭导致,就不再保存 
      If v_消息端口 = 0 Then
        Return;
      End If;
    Else
      Select Nvl(Max(消息端口), 0) Into v_消息端口 From Zlclientsession Where 会话号 = v_会话号;
    End If;
  Else
    v_消息端口 := 消息端口_In;
  End If;

  Update Zlclientsession
  Set Ip = Nvl(Ip_In, Ip), 会话号 = Nvl(v_会话号, 会话号), 用户名 = Upper(Nvl(用户名_In, 用户名)), 人员姓名 = Nvl(姓名_In, 人员姓名),
      当前部门id = Nvl(当前部门id_In, 当前部门id), 当前位置 = Nvl(当前位置_In, 当前位置), 状态 = Nvl(状态_In, 状态),
      部门性质 = Nvl(部门性质_In, 部门性质), 人员性质 = Nvl(人员性质_In, 人员性质)
  Where 工作站 = 工作站_In And 消息端口 = v_消息端口;

  If Sql%RowCount = 0 Then
    Insert Into Zlclientsession
      (工作站, 消息端口, Ip, 会话号, 用户名, 人员姓名, 当前部门id, 当前位置, 状态, 部门性质, 人员性质)
    Values
      (工作站_In, v_消息端口, Ip_In, v_会话号, Upper(用户名_In), 姓名_In, 当前部门id_In, 当前位置_In, 状态_In, 部门性质_In, 人员性质_In);
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlclientsession_Save;
/

