Create Or Replace Procedure Zl_Grantspecialtorole
(
  Rolesname_In  Varchar2,
  Sysowners_In  Varchar2 := Null,
  Grantbase_In  Number := 0,
  Createrole_In Number := 0
) Authid Current_User As
  --Rolesname_In,可以同时对多个角色授权，角色以逗号分割 
  --Sysowners_In，多个系统所有者以逗号分割，当传递空值时，取所有的存在“部门表”的系统所有者 
  --Grantbase_In，是否授予基础字典表（ZLBaseCode）管理权限,当角色存在 “系统=NULL And 序号=11” 模块的权限时，该变量应该为1 
  --CreateRole_IN,是否创建角色时调用 
  Arr_Owner t_Strlist;
  v_Stowner Varchar2(100); --标准版所有者 
  v_Object  Varchar2(4000); --对象 
  Procedure Execute_Sql
  (
    v_Sql        In Varchar2,
    Errresume_In Number := 1
  ) Is
  Begin
    Execute Immediate v_Sql;
  Exception
    When Others Then
      If Nvl(Errresume_In, 0) = 1 Then
        Null;
      Else
        zl_ErrorCenter(SQLCode, SQLErrM);
      End If;
  End;
Begin
  If Rolesname_In Is Null Then
    Return;
  End If;
  Select Nvl(Max(Upper(所有者)), 'NONEDATA') Into v_Stowner From Zltools.Zlsystems Where 编号 = 100;
  If Sysowners_In Is Null Then
    Select a.Owner
    Bulk Collect
    Into Arr_Owner
    From All_Objects A
    Where a.Owner In (Select Distinct b.所有者 From Zltools.Zlsystems B) And a.Object_Name = '部门表' And
          a.Object_Type = 'TABLE';
  Else
    Select Trim(a.Column_Value) Bulk Collect Into Arr_Owner From Table(f_Str2list(Upper(Sysowners_In))) A;
  End If;
  For I In 1 .. Arr_Owner.Count Loop
    If Nvl(Createrole_In, 0) = 1 Then
      For R In (Select Column_Value
                From Table(f_Str2list('人员表,部门表,部门人员,上机人员表,人员性质说明,人员性质分类'))) Loop
        Execute_Sql('grant select on ' || Arr_Owner(I) || '.' || r.Column_Value || ' to ' || Rolesname_In);
      End Loop;
    
      If Arr_Owner(I) = v_Stowner Then
        For R In (Select Column_Value
                  From Table(f_Str2list('业务消息类型,业务消息清单,业务消息提醒部门,业务消息提醒人员,业务消息状态,三方服务配置目录,三方接口配置'))) Loop
          Execute_Sql('grant select on ' || Arr_Owner(I) || '.' || r.Column_Value || ' to ' || Rolesname_In);
        End Loop;
      
        For R In (Select Column_Value
                  From Table(f_Str2list('Zlpub_业务消息清单_insert,Zl_业务消息清单_insert,Zl_业务消息清单_read,Zl_人员表_停用自己,ZL_三方接口配置_UPDATE'))) Loop
          Execute_Sql('grant execute on ' || Arr_Owner(I) || '.' || r.Column_Value || ' to ' || Rolesname_In);
        End Loop;
      End If;
      Execute_Sql('grant execute on ' || Arr_Owner(I) || '.Zlpassword_Update to ' || Rolesname_In);
    End If;
    If Nvl(Grantbase_In, 0) = 1 Then
      Execute_Sql('grant execute on ' || Arr_Owner(I) || '.zl_字典管理_execute to ' || Rolesname_In);
    End If;
  End Loop;
  If Nvl(Createrole_In, 0) = 1 Then
    --服务包的授权
    For R In (Select a.Owner, a.Object_Name, a.Object_Type
              From All_Objects A
              Where a.Object_Name Like 'ZL\_%SVR\_%' Escape '\'  And
                    a.Object_Type In ('PROCEDURE', 'FUNCTION', 'PACKAGE', 'TYPE') And
                    a.Owner In (Select Distinct b.所有者 From Zltools.Zlsystems B)) Loop
      Execute_Sql('grant execute on ' || r.Owner || '.' || r.Object_Name || ' to ' || Rolesname_In);
    End Loop;
    --ZLTOOLS授予ZLHIS角色的部分对象权限 
    v_Object := 'B_PUBLIC,ZL_DEPTPARAMETERS_DELETE,ZL_PARAMETERS_CHANGE_VALUE,ZL_PARAMETERS_UPDATE_BATCH,ZL_RPTRUNHISTORY_UPDATE,' ||
                'ZL_RPTRUN_UPDATE,ZL_ZLAUDITLOG_INSERT,ZL_ZLCLIENTS_SET,ZL_ZLCLIENTS_UPDATEPROCESS,ZL_ZLCLIENTUPDATELOG_INSERT,ZL_ZLClientVertify_INSERT,ZL_ZLDIARYLOG_INSERT,' ||
                'ZL_ZLMESSAGES_NEW,ZL_ZLMSGSTATE_ADDADDRESSEE,ZL_ZLMSGSTATE_EDIT,ZL_ZLNOTICEREC_EDIT,ZL_ZLUSERPARAS_CLEAR,ZL_ZLCLIENTSESSION_SAVE';
    For R In (Select Column_Value From Table(f_Str2list(v_Object))) Loop
      Execute_Sql('grant execute on ZLTOOLS.' || r.Column_Value || ' to ' || Rolesname_In);
    End Loop;
  
    v_Object := 'ZLMESSAGES_ID';
    For R In (Select Column_Value From Table(f_Str2list(v_Object))) Loop
      Execute_Sql('grant Select on ZLTOOLS.' || r.Column_Value || ' to ' || Rolesname_In);
    End Loop;
  
    v_Object := 'ZL_ZLINSUREBASE_DELETE,ZL_ZLINSUREBASE_INSERT,ZL_ZLINSUREBASE_UPDATE,ZL_ZLINSURECOMPONENTS_DELETE,ZL_ZLINSURECOMPONENTS_INSERT,' ||
                'ZL_ZLINSURECOMPONENTS_START,ZL_ZLINSURECOMPONENTS_UPDATE,ZL_ZLINSUREFUNCS_DELETE,ZL_ZLINSUREFUNCS_INSERT,ZL_ZLINSUREMODULS_DELETE,' ||
                'ZL_ZLINSUREMODULS_INSERT,ZL_ZLINSUREOPERATION_DELETE,ZL_ZLINSUREOPERATION_INSERT,ZL_ZLINSUREPRIVS_DELETE,ZL_ZLINSUREPRIVS_INSERT';
    For R In (Select Column_Value From Table(f_Str2list(v_Object))) Loop
      Execute_Sql('grant execute on ZLTOOLS.' || r.Column_Value || ' to ' || Rolesname_In);
    End Loop;
    --断网重连后更新Session
    Execute_Sql('grant update                on ZLTOOLS.Zlclientsession to ' || Rolesname_In);
    --10.34.90+,10.35.30+已经修改为存储过程，不需要该权限 
    --Execute_Sql('grant delete                on ZLTOOLS.zluserparas to ' || Rolesname_In); 
    --10.33+(65203)已经修改为存储过程，不需要该权限，自动升级需要 
    Execute_Sql('grant update                on ZLTOOLS.zlclients to ' || Rolesname_In);
    Execute_Sql('grant Insert,delete         on ZLTOOLS.Zlclientupdatelog to ' || Rolesname_In);
  
    --10.34.120+,10.35.70+已经修改为存储过程，不需要该权限，自动升级需要 
    Execute_Sql('grant insert,update         on ZLTOOLS.zlDiaryLog to ' || Rolesname_In);
    Execute_Sql('grant insert                on ZLTOOLS.zlErrorLog to ' || Rolesname_In);
  
    --10.34.110+,10.35.60+(97672)已经修改为存储过程，不需要该权限 
