Create Or Replace Procedure Zl_GrantspecialtoOwner Authid Current_User As
--功能：创建系统所有者的基本权限，该过程必须以sys调用 
  Arr_Owner t_Strlist;
  v_Sql          Varchar2(1000);
  Procedure Execute_Sql
  (
    v_Sql        In Varchar2
  ) Is
  Begin
    Execute Immediate v_Sql;
  Exception
    When Others Then
         Null;
  End;
Begin
  --获取所有的系统所有者 
  Select d.Username
  Bulk Collect
  Into Arr_Owner
  From (Select Distinct Upper(b.所有者) 所有者 From Zltools.Zlsystems b) c, All_Users d
  Where c.所有者 = d.Username;
  For i In 1 .. Arr_Owner.Count Loop
    --所有者基本权限
    v_Sql:='Grant Connect,Resource,UNLIMITED TABLESPACE,Create Table,Create Sequence,Create Role,
                Create User,Drop User,Alter User,Create Public Synonym,Drop Public Synonym,Alter Session,Create Session,
                Create Synonym,Create View,Create Database Link,Create Cluster,Create Materialized View, Alter Any Materialized View,
                Drop Any Materialized View,CREATE TRIGGER to ' || Arr_Owner(i) || '  With Admin Option';
    Execute_Sql(v_Sql);
    For Rssql In (Select 'Grant ' || a.Privilege || ' On Sys.' || a.Table_Name || ' To ' || Arr_Owner(i) || ' ' ||
                          Decode(a.Grantable, 'YES', 'With GRANT Option', Null) 修正sql
                      From (Select 'DBA_ROLES' Table_Name, 'SELECT' Privilege, '' Grantable From Dual Union All
                     Select 'DBA_ROLE_PRIVS', 'SELECT', '' From Dual Union All
                     Select 'GV_$SESSION', 'SELECT', 'YES' From Dual Union All
                     Select 'V_$SESSION', 'SELECT', 'YES' From Dual Union All
                     Select 'V_$PARAMETER', 'SELECT', 'YES' From Dual Union All
                     Select 'V_$PROCESS', 'SELECT', 'YES' From Dual Union All
                     Select 'dbms_crypto', 'EXECUTE', '' From Dual) a) Loop
      Execute_Sql(Rssql.修正sql);      
    End Loop; 
  End Loop;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_GrantspecialtoOwner;
/
Create Or Replace Procedure  Zl_Grantspecialtopub
(
  Sysowner_In Varchar2 := Null,
  Force_In    Number := 0
) As
  --功能：创建ZLTOOLS的公共同义词以及并将ZLTOOLS对象授予PUBLIC用户、应用系统所有者，该过程必须以ZLTOOLS调用
  --Sysowner_In:应用系统创建时，新的系统所有者还没有ZLTOOLS对象权限，因此不能通过ZLSYSTEM查询出
  --FORCE_In,是否强制重新处理
  Arr_Owner t_Strlist;
  Procedure Execute_Sql
  (
    v_Sql        In Varchar2,
    Errresume_In Number := 1
  ) Is
  Begin
    Execute Immediate v_Sql;
  Exception
    When Others Then
      If Nvl(Errresume_In, 0) = 1 Then
        Null;
      Else
        zl_ErrorCenter(SQLCode, SQLErrM);
      End If;
  End;
Begin
  --获取所有的系统所有者
  If Sysowner_In Is Null Then
    Select d.Username
    Bulk Collect
    Into Arr_Owner
    From (Select Distinct Upper(b.所有者) 所有者 From Zltools.Zlsystems B) C, All_Users D
    Where c.所有者 = d.Username;
  Else
    Select d.Username
    Bulk Collect
    Into Arr_Owner
    From (Select Distinct b.所有者
           From (Select Upper(所有者) 所有者 From Zltools.Zlsystems Union All Select Upper(Sysowner_In) 所有者 From Dual) B) C,
         All_Users D
    Where c.所有者 = d.Username;
  End If;
  If Nvl(Force_In, 0) = 0 Then
    --公共同义词创建
    For Rs In (Select Object_Name
               From ((Select Object_Name
                       From User_Objects
                       Where Object_Type In ('FUNCTION', 'PROCEDURE', 'TYPE', 'PACKAGE', 'SEQUENCE', 'TABLE', 'VIEW') And
                             Instr(Object_Name, 'BIN$') <= 0) Minus
                      (Select Synonym_Name From All_Synonyms Where Owner = 'PUBLIC' And Table_Owner = 'ZLTOOLS'))) Loop
      Execute_Sql('Create Or Replace Public Synonym ' || Rs.Object_Name || ' For ZLTOOLS.' || Rs.Object_Name);
    End Loop;
    --授予PUBLIC权限
    For Rs In (Select Object_Name, Privilege
               From (Select a.Object_Name, Decode(a.Object_Type, 'TABLE', 'SELECT', 'VIEW', 'SELECT', 'EXECUTE') Privilege
                      From User_Objects A
                      Where Instr(a.Object_Name, 'BIN$') <= 0 And
                            (a.Object_Type In ('TABLE', 'TYPE', 'FUNCTION', 'VIEW') Or
                             a.Object_Type = 'PROCEDURE' And
                             a.Object_Name In
                             ('ZLLOGINFO_INSERT','ZL_CREATESYNONYMS', 'ZL_ERRORCENTER', 'ZL_PARAMETERS_UPDATE', 'ZL_ZLDIARYLOG_INSERT',
                              'ZL_ZLDIARYLOG_UPDATE', 'ZL_ZLERRORLOG_INSERT','ZL_ZLCLIENTS_UPDATEPROCESS','ZL_ZLCLIENTUPDATELOG_INSERT','ZL_ZLClientVertify_INSERT'))
                      Minus (Select Table_Name, Privilege
                            From User_Tab_Privs
                            Where Grantee = 'PUBLIC' And Grantor = 'ZLTOOLS' And Instr(Table_Name, 'BIN$') <= 0))) Loop
      Execute_Sql('Grant ' || Rs.Privilege || ' On ZLtools.' || Rs.Object_Name || ' To  Public');
    End Loop;
    --授予系统所有者的权限
    For I In 1 .. Arr_Owner.Count Loop
      --以前简化判断系统所有者的权限是通过EXECUTE,INSERT,但是序列不存在这两个权限。因此调整为通过修改为ALter,序列、表都有ALTER,视图存在特殊权限MERGE VIEW
      For Rs In (Select Object_Name
                 From (Select a.Object_Name
                        From User_Objects A
                        Where a.Object_Type In ('FUNCTION', 'PROCEDURE', 'TYPE', 'PACKAGE', 'SEQUENCE', 'TABLE', 'VIEW') And
                              Instr(a.Object_Name, 'BIN$') <= 0
                        Minus
                        Select Table_Name
                        From User_Tab_Privs A
                        Where Grantee = Arr_Owner(I) And Grantor = 'ZLTOOLS' And Grantable = 'YES' And
                              Instr(Table_Name, 'BIN$') <= 0 And Privilege In ('EXECUTE', 'ALTER', 'MERGE VIEW'))) Loop
        Execute_Sql('Grant All On ZLtools.' || Rs.Object_Name || ' To  ' || Arr_Owner(I) || '  With Grant Option');
      End Loop;
    End Loop;
  Else
    --公共同义词创建
    For Rs In (Select Object_Name
               From ((Select Object_Name
                       From User_Objects
                       Where Object_Type In ('FUNCTION', 'PROCEDURE', 'TYPE', 'PACKAGE', 'SEQUENCE', 'TABLE', 'VIEW') And
                             Instr(Object_Name, 'BIN$') <= 0))) Loop
      Execute_Sql('Create Or Replace Public Synonym ' || Rs.Object_Name || ' For ZLTOOLS.' || Rs.Object_Name);
    End Loop;
    --授予PUBLIC权限
    For Rs In (Select Object_Name, Privilege
               From (Select a.Object_Name, Decode(a.Object_Type, 'TABLE', 'SELECT', 'VIEW', 'SELECT', 'EXECUTE') Privilege
                      From User_Objects A
                      Where Instr(a.Object_Name, 'BIN$') <= 0 And
                            (a.Object_Type In ('TABLE', 'TYPE', 'FUNCTION', 'VIEW') Or
                             a.Object_Type = 'PROCEDURE' And
                             a.Object_Name In
                             ('ZLLOGINFO_INSERT','ZL_CREATESYNONYMS', 'ZL_ERRORCENTER', 'ZL_PARAMETERS_UPDATE', 'ZL_ZLDIARYLOG_INSERT',
                              'ZL_ZLDIARYLOG_UPDATE', 'ZL_ZLERRORLOG_INSERT','ZL_ZLCLIENTS_UPDATEPROCESS','ZL_ZLCLIENTUPDATELOG_INSERT','ZL_ZLClientVertify_INSERT')))) Loop
      Execute_Sql('Grant ' || Rs.Privilege || ' On ZLtools.' || Rs.Object_Name || ' To  Public');
    End Loop;
    --授予系统所有者的权限
    For Rs In (Select a.Object_Name
               From User_Objects A
               Where Instr(a.Object_Name, 'BIN$') <= 0 And
                     a.Object_Type In ('FUNCTION', 'PROCEDURE', 'TYPE', 'PACKAGE', 'SEQUENCE', 'TABLE', 'VIEW')) Loop
      For I In 1 .. Arr_Owner.Count Loop
        Execute_Sql('Grant All On ZLtools.' || Rs.Object_Name || ' To  ' || Arr_Owner(I) || '  With Grant Option');
      End Loop;
    End Loop;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Grantspecialtopub;
/
