create or replace Function zlUppMoney(numMoney in Number)
    RETURN VARCHAR2
    IS strMoney VARCHAR2(60);
    strSign varchar2(4);
    numValue number(22);
    strNum Varchar2(2);
    strBit Varchar2(2);
    numBit number(2);
    numMod Number(1);
    strUppNum VARCHAR2(20):='零壹贰叁肆伍陆柒捌玖';
    strUppUnit VARCHAR2(34):='分角圆拾佰仟万拾佰仟亿拾佰仟万拾佰';
BEGIN
    if numMoney<0 then
        strSign:='[负]';
    else
        strSign:='';
    end if;
    numValue:=abs(numMoney)*100;
    strMoney:='';
    numBit:=0;
    LOOP
        numMod:=Mod(numValue,10);
        strNum:=substr(strUppNum,numMod+1,1);
        strBit:=substr(strUppUnit,numBit+1,1);
        strMoney:=strNum||strBit||strMoney;
        numValue:=FLOOR(numValue/10);
        numBit:=numBit+1;
        exit when numValue<1 or numBit>16;
    END LOOP;
    strMoney:=replace(strMoney,'零角','零');
    strMoney:=replace(strMoney,'零仟','零');
    strMoney:=replace(strMoney,'零佰','零');
    strMoney:=replace(strMoney,'零拾','零');
    strMoney:=replace(strMoney,'零零','零');
    strMoney:=replace(strMoney,'零零','零');
    strMoney:=replace(strMoney,'零分','整');
    strMoney:=replace(strMoney,'零圆','圆');
    strMoney:=replace(strMoney,'零万','万');
    strMoney:=replace(strMoney,'零亿','亿');
    strMoney:=replace(strMoney,'亿万','亿');
    strMoney:=strSign||strMoney;
    RETURN(strMoney);
END;
/

create or replace Function zlUppNumber(numMoney in Number,numSpace in Number)
    RETURN VARCHAR2
    IS strMoney VARCHAR2(200);
    strSign varchar2(4);
    numValue number(22);
    strNum Varchar2(20);
    numLen number(2);
    numBit number(2);
    numMod Number(1);
    strUppNum VARCHAR2(20):='零壹贰叁肆伍陆柒捌玖';
BEGIN
    if numSpace>10 then
        numLen:=12;
    else
        numLen:=numSpace+2;
    end if;
    if numMoney<0 then
        strSign:='[负]';
    else
        strSign:='';
    end if;
    numValue:=abs(numMoney)*100;
    strMoney:='';
    numBit:=0;
    LOOP
        numMod:=Mod(numValue,10);
        strNum:=lpad(substr(strUppNum,numMod+1,1),numLen);
        strMoney:=strNum||strMoney;
        numValue:=FLOOR(numValue/10);
        numBit:=numBit+1;
        exit when numValue<1 or numBit>16;
    END LOOP;
    strMoney:=strSign||strMoney;
    RETURN(strMoney);
END;
/

CREATE OR REPLACE Function Zl_To_Number
(
  Input_In    In Varchar2,
  Enhanced_In In Number := 0
) Return Number Is
  n_Index  Number;
  v_Number Varchar2(1000);
  n_Output Number;
Begin
  If Nvl(Enhanced_In, 0) = 0 Then
    n_Output := To_Number(Input_In);
  Else
    n_Index := 0;

    Begin
      n_Output := To_Number(Input_In);
    Exception
      When Others Then
        n_Index := 1;
    End;

    If n_Index = 1 Then
      For n_Index In 1 .. Length(Input_In) Loop
        if Enhanced_In=1 then
        --取出字符串中所有数字
            If Instr('0123456789.-', Substr(Input_In, n_Index, 1)) > 0 Then
              v_Number := v_Number || Substr(Input_In, n_Index, 1);
            End If;
        else
        --VAL函数
            If Instr('0123456789.-+', Substr(Input_In, n_Index, 1)) > 0 Then
               if instr('-+',Substr(Input_In, n_Index, 1))>0 and n_Index>1 then
                  return to_number(v_Number);
               else
                  v_Number := v_Number || Substr(Input_In, n_Index, 1);
               end if ;
            else
              return to_number(nvl(v_Number,0));
            End If;
        end if ;
      End Loop;

      n_Output := To_Number(v_Number);
    End If;
  End If;

  Return n_Output;
Exception
  When Others Then
    Return 0;
End Zl_To_Number;
/
Create Or Replace Procedure Zlmanuser_Edit
(
  Type_In   Number,
  Owner_In  Varchar2,
  用户名_In Varchar2,
  人员id_In Number := Null
  --Type_IN:0-删除用户对应的上机人员，1-新增用户对应的上机人员
  --Owner_IN:上机人员表的所有者
  --用户名_IN:上机人员表.用户名
  --人员id:上机人员表.人员id
) Authid Current_User As
  v_Pkgname_Lis Varchar2(100);
  v_Pkgname_His Varchar2(100);
  n_Type        Number(1) := 0;
  n_Return      Number(1) := 0;
  n_Count       Number(1) := 0;
  Arr_Manid     t_Numlist;
  Procedure Executesql(Sql_In Varchar2) Is
  Begin
    Execute Immediate Sql_In;
    n_Return := 1;
  Exception
    When Others Then
      n_Return := 0;
  End;
  Procedure Sendmessage As
  Begin
    Select Count(1) Into n_Count From zlSystems A Where a.编号 = 100 And a.所有者 = Upper(Owner_In);
    If n_Count = 1 Then
      If Type_In = 0 Then
        v_Pkgname_His := Owner_In || '.' || 'b_Message.ZLTOOLS_USERS_001';
      Else
        v_Pkgname_His := Owner_In || '.' || 'b_Message.ZLTOOLS_USERS_002';
      End If;
    End If;
    Select Count(1) Into n_Count From zlSystems A Where a.编号 = 2500 And a.所有者 = Upper(Owner_In);
    If n_Count = 1 Then
      If Type_In = 0 Then
        v_Pkgname_Lis := Owner_In || '.' || 'b_Message_LIS.ZLTOOLS_USERS_001';
      Else
        v_Pkgname_Lis := Owner_In || '.' || 'b_Message_LIS.ZLTOOLS_USERS_002';
      End If;
    End If;
  
    If Not v_Pkgname_Lis Is Null Or Not v_Pkgname_His Is Null Then
      If Type_In = 0 Then
        Execute Immediate 'Select 人员ID From ' || Owner_In || '.上机人员表 Where 用户名=' || Chr(39) || 用户名_In || Chr(39) Bulk
                          Collect
          Into Arr_Manid;
      Else
        Execute Immediate 'Select ' || 人员id_In || ' From Dual' Bulk Collect
          Into Arr_Manid;
      End If;
      For I In 1 .. Arr_Manid.Count Loop
        If n_Type = 0 Then
          If Not v_Pkgname_His Is Null Then
            Executesql('Begin ' || v_Pkgname_His || '(' || Chr(39) || 用户名_In || Chr(39) || ',' || Arr_Manid(I) ||
                       '); End;');
          
            If n_Return = 1 Then
              n_Type := 1;
            End If;
            --可能HIS没有对应接口
            If n_Type = 0 Then
              Executesql('Begin ' || v_Pkgname_Lis || '(' || Chr(39) || 用户名_In || Chr(39) || ',' || Arr_Manid(I) ||
                         '); End;');
            
              If n_Return = 1 Then
                n_Type := 2;
              End If;
            End If;
          Else
            Executesql('Begin ' || v_Pkgname_Lis || '(' || Chr(39) || 用户名_In || Chr(39) || ',' || Arr_Manid(I) ||
                       '); End;');
          
            If n_Return = 1 Then
              n_Type := 2;
            End If;
          End If;
          If n_Type = 0 Then
            --不发送消息,没有对应接口
            n_Type := -1;
          End If;
        Elsif n_Type = 1 Then
          Executesql('Begin ' || v_Pkgname_His || '(' || Chr(39) || 用户名_In || Chr(39) || ',' || Arr_Manid(I) ||
                     '); End;');
        Elsif n_Type = 2 Then
          Executesql('Begin ' || v_Pkgname_Lis || '(' || Chr(39) || 用户名_In || Chr(39) || ',' || Arr_Manid(I) ||
                     '); End;');
        End If;
      End Loop;
    End If;
  Exception
    --可能所有者或者没有权限的用户查询出错
    When Others Then
      Null;
  End;
Begin

  If Type_In = 0 Then
    Sendmessage;
    Execute Immediate 'Delete From ' || Owner_In || '.上机人员表 Where 用户名=' || Chr(39) || 用户名_In || Chr(39);
  Elsif Type_In = 1 Then
    Execute Immediate 'Insert Into ' || Owner_In || '.上机人员表(用户名,人员id) Values (' || Chr(39) || 用户名_In || Chr(39) || ',' ||
                      人员id_In || ')';
    Sendmessage;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End;
/
Create Or Replace Procedure Zl_Zluserroles_Add
(
  User_In In Zluserroles.用户%Type := Null,
  Role_In In Zluserroles.角色%Type := Null,
  管理_In In Zluserroles.管理%Type := 0
) Is
  --当用户角色均为空时，记录所有用户角色数据 
Begin
  If Not User_In Is Null And Not Role_In Is Null Then
    Insert Into zlUserRoles (用户, 角色, 管理) Values (User_In, Role_In, 管理_In);
    --用户角色都不传时，清空所有数据，并重新生成 
  Elsif User_In Is Null And Role_In Is Null Then
    Delete Zltools.Zluserroles;
    Insert Into Zltools.Zluserroles
      (用户, 角色, 管理)
      Select Grantee, Granted_Role, Decode(Admin_Option, 'YES', 1, 0)
      From Dba_Role_Privs
      Where Granted_Role Like 'ZL_%'
      Order By Grantee;
    Insert Into Zlroles
      (名称, 系统)
      Select 名称, 系统
      From (Select a.Role 名称, Null 系统 From Dba_Roles A Where a.Role Like 'ZL%' Minus Select 名称, Null From Zlroles);
    Delete Zlroles
    Where 名称 In (Select 名称 From Zlroles Minus Select a.Role From Dba_Roles A Where a.Role Like 'ZL%');
    --用户传入时，清空该用户，并重新生成数据 
  Elsif Not User_In Is Null Then
    Delete Zltools.Zluserroles Where 用户 = User_In;
    Insert Into Zltools.Zluserroles
      (用户, 角色, 管理)
      Select Grantee, Granted_Role, Decode(Admin_Option, 'YES', 1, 0)
      From Dba_Role_Privs
      Where Granted_Role Like 'ZL_%' And Grantee = User_In;
  End If;
