--功能：获取传入参数的拼音码
--参数说明：
--    v_InStr：传入字符串，可能是汉字、字母、数字的混合。
--    n_Mode：0-返回汉字的简码，1-返回汉字的全拼。可选参数，缺省为0。
--    n_Initial：当n_Mode=1时使用，0-全部返回大写，1-全拼的首字母大写。可选参数，缺省为0。
--    v_Split：当n_Mode=1时使用，返回的汉字全拼之间的分隔符。可选参数，缺省为Null。
--    n_Multi：0-不处理多音汉字(以码表中第一个为准)，1-处理多音汉字。可选参数，缺省为1。
--    n_OutNum：处理v_InStr前面指定个数的字符，可选参数，缺省为10；当=0或Null时，表示处理所有字符。
--返回：返回使用","号分隔的多个拼音码。
--示例：
--zlPinYinCode('重庆曾超')='ZQCC,CQCC,ZQZC,CQZC'
--zlPinYinCode('ABC中联123')='ABCZL123'
--zlPinYinCode('ABC中联123',1)='ABCZHONGLIAN123'
--zlPinYinCode('ABC中联123',1,1,' ')='ABC Zhong Lian 123'

--VB：
--汉字->Unicode：HEX(AscW("研"))=&H7814
--Unicode->汉字：ChrW(&H7814)="研"
--
--Oracle取汉字Unicode码：
--Select AsciiStr('研') From Dual;
--ASCIISTR('研')
----------------
--\7814
--
--Oracle将Unicode码转换为汉字：
--SELECT UniStr('\7814') FROM dual;
--UNISTR('\7814')
-----------------
--研
--
--Oracle16/10进制互换：
--Select To_Number('7814','XXXX') From Dual;
--TO_NUMBER('7814','XXXX')
--------------------------
--30740
--
--Select To_Char(30740,'XXXX') From Dual;
--TO_CHAR(30740,'XXXX')
-----------------------
--7814
Create Or Replace Function Zlpinyincode
(
  v_Instr   In Varchar2,
  n_Mode    In Number := 0,
  n_Initial In Number := 0,
  v_Split   In Varchar2 := Null,
  n_Mutil   In Number := 1,
  n_Outnum  In Number := 10
) Return Varchar2 As
  n_Pos Number(2);
  n_Js  Number(2);
  n_In  Number(2);
-- n_Out     Number(2);
-- v_Mutil   Varchar2(200);
  v_Ascii  Varchar2(2000);
  v_Symbol Varchar2(5);
  v_Spell  Zlpinyin.拼音%Type;
  b_Symbol Number(1);
-- v_Spell_m Zlpinyin.拼音%Type;
  v_Text   Varchar2(2000);
  v_Return Varchar2(2000);

--定义用以处理多音节的可变数组
  Type Ma_Type Is Table Of Varchar2(200);
  Mutilascii Ma_Type := Ma_Type();

--去掉重复的发音内容
  Function Getdistascii(v_Process Varchar2) Return Varchar2 Is
    n_Max  Number(2);
    n_In   Number(2);
    v_Pro  Varchar2(2000);
    v_Sa   Varchar2(1000);
    v_Dist Varchar2(1000);
  Begin
    v_Pro := v_Process;
    v_Sa  := Null;
    While True Loop
  --多音字
      Select Length(v_Process) - Length(Replace(v_Process, ',', '')) + 1 Into n_Max From Dual; --取逗号出现次数
      If n_Max Is Null Then
        n_Max := 1;
      End If;
      v_Dist := Null;
      For n_In In 1 .. n_Max Loop
        If n_In = 1 And n_Max = 1 Then
          v_Sa := v_Process;
        Elsif n_In = 1 And n_Max > 1 Then
          v_Sa := Substr(v_Process, 1, Instr(v_Process, ',', 1, n_In) - 1);
        Elsif n_In < n_Max Then
          v_Sa := Substr(v_Process,
                         Instr(v_Process, ',', 1, n_In - 1) + 1,
                         Instr(v_Process, ',', 1, n_In) - Instr(v_Process, ',', 1, n_In - 1) - 1);
        Elsif n_In = n_Max Then
          v_Sa := Substr(v_Process, Instr(v_Process, ',', 1, n_In - 1) + 1);
        End If;
        If Instr(',' || v_Dist || ',', ',' || v_Sa || ',') = 0 Then
          If v_Dist Is Null Then
            v_Dist := v_Sa;
          Else
            v_Dist := v_Dist || ',' || v_Sa;
          End If;
        End If;
      End Loop;

      If v_Dist Is Null Then
        v_Dist := v_Process;
      End If;
      Return(v_Dist);
    End Loop;
  End;

--递归函数，用来处理多单字
  Function Getmutilascii
  (
    v_Process Varchar2,
    n_Pos     Number
  ) Return Varchar2 Is
    n_Max    Number(2);
    n_Lp     Number(2);
    n_In     Number(2);
    v_Pro    Varchar2(2000);
    v_Ma     Varchar2(2000);
    v_Sa     Varchar2(1000);
    v_Sa_Pro Varchar2(1000);
  Begin
    n_Lp  := n_Pos;
    v_Pro := v_Process;
    v_Sa  := Null;
    v_Ma  := Null;
    While True Loop
  --多音字
      Select Length(Mutilascii(n_Lp)) - Length(Replace(Mutilascii(n_Lp), ',', '')) + 1 Into n_Max From Dual; --取逗号出现次数
      While v_Pro Is Not Null Loop
    --依次取出源串
        If Instr(v_Pro, ',') > 0 Then
          v_Sa_Pro := Substr(v_Pro, 1, Instr(v_Pro, ',') - 1);
          v_Pro    := Substr(v_Pro, Instr(v_Pro, ',') + 1);
        Else
          v_Sa_Pro := v_Pro;
          v_Pro    := Null;
        End If;
    --拼接上当前多音字1,2,3,4
        If n_Max Is Null Then
          n_Max := 1;
        End If;
        For n_In In 1 .. n_Max Loop
          If n_In = 1 And n_Max = 1 Then
            v_Sa := Mutilascii(n_Lp);
          Elsif n_In = 1 And n_Max > 1 Then
            v_Sa := Substr(Mutilascii(n_Lp), 1, Instr(Mutilascii(n_Lp), ',', 1, n_In) - 1);
          Elsif n_In < n_Max Then
            v_Sa := Substr(Mutilascii(n_Lp),
                           Instr(Mutilascii(n_Lp), ',', 1, n_In - 1) + 1,
                           Instr(Mutilascii(n_Lp), ',', 1, n_In) - Instr(Mutilascii(n_Lp), ',', 1, n_In - 1) - 1);
          Elsif n_In = n_Max Then
            v_Sa := Substr(Mutilascii(n_Lp), Instr(Mutilascii(n_Lp), ',', 1, n_In - 1) + 1);
          End If;
          If n_Mode = 1 And (Length(v_Sa) > 1 Or Length(Mutilascii(n_Lp + 1)) > 1) Then
            v_Sa := v_Sa || v_Split || v_Sa_Pro;
          Else
            v_Sa := v_Sa || v_Sa_Pro;
          End If;
          If v_Ma Is Not Null Then
            v_Ma := v_Ma || ',' || v_Sa;
          Else
            v_Ma := v_Sa;
          End If;
        End Loop;
      End Loop;

      If v_Ma Is Null Then
        v_Ma := Mutilascii(n_Lp);
      End If;
      Return(v_Ma);
    End Loop;
  End;
Begin
  If v_Instr Is Null Then
    Return '';
  End If;

  v_Text := v_Instr;
  n_Js   := n_Outnum;
  If n_Js = 0 Or n_Js > Length(v_Text) Then
    n_Js := Length(v_Text);
  End If;
  Mutilascii.Extend(n_Js);

--循环将简码赋值到数组中;
  n_Js := 1;
  While v_Text Is Not Null Loop
    b_Symbol := 0;
    v_Symbol := Substr(v_Text, n_Js, 1);
    Select Asciistr(v_Symbol) Into v_Ascii From Dual;
    If v_Ascii <> v_Symbol And Substr(v_Ascii, 1, 1) = '\' Then
  --不同则说明是汉字
      v_Ascii := To_Number(Substr(v_Ascii, 2), 'XXXX');
      Begin
        Select 拼音 Into v_Spell From Zlpinyin Where 汉字 = v_Ascii;
        b_Symbol := 1;
      Exception
        When Others Then
          v_Spell := v_Symbol;
      End;
      If b_Symbol = 1 Then
    --去掉所有声调(数字)
        v_Spell := Replace(v_Spell, '0', '');
        v_Spell := Replace(v_Spell, '1', '');
        v_Spell := Replace(v_Spell, '2', '');
        v_Spell := Replace(v_Spell, '3', '');
        v_Spell := Replace(v_Spell, '4', '');
        v_Spell := Replace(v_Spell, '5', '');
        v_Spell := Getdistascii(v_Spell);
    --按入参规则处理
        v_Ascii := Null;
        If n_Mode = 0 Then
      --取首字母
          While True Loop
            If v_Ascii Is Null Then
              v_Ascii := Substr(v_Spell, 1, 1);
            Else
              v_Ascii := v_Ascii || ',' || Substr(v_Spell, 1, 1);
            End If;
            n_In := Instr(v_Spell, ',', 1);
            If n_In > 0 Then
              v_Spell := Substr(v_Spell, n_In + 1);
            Else
              Exit;
            End If;
          End Loop;
          v_Ascii := Upper(v_Ascii);
        Else
          v_Ascii := Lower(v_Spell);
          If n_Initial = 0 Then
        --全部大写
            v_Ascii := Upper(v_Ascii);
          Else
        --首字母大写
            n_Pos   := 2;
            v_Ascii := Upper(Substr(v_Ascii, 1, 1)) || Substr(v_Ascii, 2);
            While n_Pos <= Length(v_Ascii) Loop
              If Substr(v_Ascii, n_Pos - 1, 1) = ',' Then
                v_Ascii := Substr(v_Ascii, 1, n_Pos - 1) || Upper(Substr(v_Ascii, n_Pos, 1)) ||
                           Substr(v_Ascii, n_Pos + 1);
              End If;
              n_Pos := n_Pos + 1;
            End Loop;
          End If;
        End If;
        Mutilascii(n_Js) := v_Ascii;
      Else
        Mutilascii(n_Js) := v_Symbol;
      End If;
    Else
      Mutilascii(n_Js) := v_Ascii;
    End If;
    n_Js := n_Js + 1;
    If n_Js > Mutilascii.Count Then
      Exit;
    End If;
  End Loop;

  n_In := Mutilascii.Count;
  While n_In > 0 Loop
    v_Return := Getmutilascii(v_Return, n_In);
    n_In     := n_In - 1;
  End Loop;

  If n_Mutil = 0 Then
    Return Substr(v_Return || ',', 1, Instr(v_Return || ',', ',', 1) - 1);
  Else
    Return v_Return;
  End If;
Exception
  When Others Then
    Return '';
End Zlpinyincode;
/

Create Or Replace Function zlJsonStr
(
  Str_In  In Varchar2,
  Type_In Number := 0
) Return Varchar2 Is
  --功能：处理要组合成Json串的字符串中的特殊符号
  --参数：
  --     Type_In=数据类型:0-字符,1-数值
  v_Temp Varchar2(32767);
Begin
  If Str_In Is Null Then
    If Nvl(Type_In, 0) = 1 Then
      Return '0';
    Else
      Return Null;
    End If;
  Elsif Nvl(Type_In, 0) = 1 Then
    If Instr(Str_In, '.') = 0 Then
      Return Str_In;
    Else
      Return To_Char(Str_In, 'FM9999999990.0999999999');
    End If;
  Else
    --注意顺序
    v_Temp := Str_In;
    v_Temp := Replace(v_Temp, '\', '\\');
    v_Temp := Replace(v_Temp, '"', '\"');
    v_Temp := Replace(v_Temp, Chr(13), '\r');
    v_Temp := Replace(v_Temp, Chr(10), '\n');
    v_Temp := Replace(v_Temp, Chr(9), '\t');
    Return v_Temp;
  End If;
End zlJsonStr;
/

CREATE OR REPLACE Procedure zlJsonPutValue
(
  Json_InOut  In Out Varchar2,
  Node_In     In Varchar2,
  Val_In      In Varchar2,
  Type_In     In Number := 0,
  Local_In    In Number := 0
) As
--功能：用于多次调用生成Json结点对字符串组合
--参数：
--     PreJson_In：调用过程中定义的用于处理Json字符串的Varchar2变量
--     Node_In：Json结点名
--     Val_In：Json结点值。如果是字符串，格式要外部自已转换，函数内会自动处理特殊字符转换和小数点前的补充0。
--     Type_In：Json结点数据类型:0-字符,1-数值
--     Local_In：当前结点是JsonList中某个数组元素的第1个结点时，传入1，最后一点结点时，传入2，其他情况不传。
--返回：最终生成类似字符串
--       "结点名1":"结点值1","结点名2":"结点值2",...
--       ｛"结点名1":"结点值1","结点名2":"结点值2",...｝,｛...｝,...
  v_Split Varchar2(1);
Begin
  If Local_In = 1 Then
    If Substr(Json_InOut, Length(Json_InOut), 1) = '}' Then
      Json_InOut := Json_InOut || ',{';
    Else
      Json_InOut := Json_InOut || '{';
    End If;
  End If;
  If Not (Json_InOut Is Null Or Local_In = 1) Then
    v_Split := ',';
  End If;
  If Not Val_In Is Null Then
    If Type_In = 1 Then
        Json_InOut := Json_InOut || v_Split || '"' || Node_In || '":' || zlJsonStr(Val_In, Type_In);
    Else
        Json_InOut := Json_InOut || v_Split || '"' || Node_In || '":"' || zlJsonStr(Val_In, Type_In) || '"';
    End IF;
  Else
    Json_InOut := Json_InOut || v_Split || '"' || Node_In || '":null';
  End If;
  If Local_In = 2 Then
    Json_InOut := Json_InOut || '}';
  End If;
End;
/

CREATE OR REPLACE Function zlJsonOut
(
  Message_In Varchar2,
  Code_In    Number := 0 --0-失败,1-成功
) Return Varchar2 Is
  --------------------------------------------------------------------------------------------------------------------
  --功能：获取应答消息
