Create Or Replace Procedure Zl_Zlclients_Update
(
  工作站_In   In Varchar2,
  更新字段_In In Number,
  更新值_In   In Number
) Is
  --工作站_In 工作站字符串，以逗号相连接
  --更新字段_In 0：升级标志 1：是否预升级 2：收集标志 3：功能验证状态
  --更新值_In 0：不勾选 1：勾选
Begin
  If 更新字段_In = 0 Then
    Update Zlclients Set 升级标志 = 更新值_In Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    If 更新值_In = 1 Then
      Update Zlclients
      Set 升级情况 = 0, 修复状态 = 0, 收集标志 = Null, 收集状态 = Null
      Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    Else
      Update Zlclients Set 功能验证标志 = Null Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    End If;
  Elsif 更新字段_In = 1 Then
    Update Zlclients
    Set 是否预升级 = 更新值_In
    Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    If 更新值_In = 1 Then
      Update Zlclients
      Set 预升完成 = 0, 收集标志 = Null, 收集状态 = Null
      Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    End If;
  Elsif 更新字段_In = 2 Then
    Update Zlclients
    Set 收集标志 = Decode(Nvl(升级标志, 0), 1, Null, Decode(Nvl(是否预升级, 0), 1, Null, Decode(更新值_In, 0, Null, 1)))
    Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    If 更新值_In = 1 Then
      Update Zlclients
      Set 收集状态 = Decode(收集标志, 1, Null, 收集状态)
      Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
      Delete Zlclientupdatelog Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In))) And 类型 = 1;
    End If;
  Elsif 更新字段_In = 3 Then
    --功能验证状态，null表示不验证(不勾选)，勾选时也需清除上次验证信息
    Update Zlclients
    Set 功能验证标志 = Decode(更新值_In, 1, 1, Null)
    Where 工作站 In (Select Column_Value From Table(f_Str2list(工作站_In)));
    Delete From ZLClientVertify Where 客户端 In (Select Column_Value From Table(f_Str2list(工作站_In)));
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Zlclients_Update;
/
Create Or Replace Procedure Zlfiles_Autoupdate
(
  名称_In         In Zlfiles.名称%Type,
  标准md5_In      In Zlfiles.标准md5%Type,
  版本号_In       In Zlfiles.版本号%Type,
  修改日期_In     In Zlfiles.修改日期%Type,
  加入日期_In     In Zlfiles.加入日期%Type,
  文件类型_In     In Zlfiles.文件类型%Type,
  安装路径_In     In Zlfiles.安装路径%Type,
  业务部件_In     In Zlfiles.业务部件%Type,
  所属系统_In     In Zlfiles.所属系统%Type,
  文件说明_In     In Zlfiles.文件说明%Type,
  自动注册_In     In Zlfiles.自动注册%Type,
  强制覆盖_In     In Zlfiles.强制覆盖%Type,
  附加安装路径_In In Zlfiles.附加安装路径%Type:=Null
) Is
  n_Count Number(3);
Begin
  n_Count := 0;
  --部件更新
  For Rs In (Select Rowid From Zlfiles A Where Upper(a.名称) = Upper(名称_In)) Loop
    n_Count := n_Count + 1;
    Update Zlfiles
    Set 名称 = 名称_In, 标准md5 = 标准md5_In, 版本号 = 版本号_In, 修改日期 = 修改日期_In, 加入日期 = 加入日期_In, 文件类型 = 文件类型_In, 安装路径 = 安装路径_In,
        业务部件 = 业务部件_In, 所属系统 = 所属系统_In, 文件说明 = 文件说明_In, 自动注册 = 自动注册_In, 强制覆盖 = 强制覆盖_In, 附加安装路径 = 附加安装路径_In
    Where Rowid = Rs.Rowid;
  End Loop;
  --新增部件
  If n_Count = 0 Then
    Insert Into Zlfiles
      (名称, 标准md5, 版本号, 修改日期, 加入日期, 文件类型, 安装路径, 业务部件, 所属系统, 文件说明, 自动注册, 强制覆盖, 附加安装路径)
    Values
      (名称_In, 标准md5_In, 版本号_In, 修改日期_In, 加入日期_In, 文件类型_In, 安装路径_In, 业务部件_In, 所属系统_In, 文件说明_In, 自动注册_In, 强制覆盖_In,
       附加安装路径_In);
  End If;
  n_Count := 0;
  --部件更新
  For Rs In (Select Rowid From zlFilesUpgrade A Where Upper(a.文件名) = Upper(名称_In)) Loop
    n_Count := n_Count + 1;
    Update zlFilesUpgrade
    Set 文件名 = 名称_In, 文件类型 = 文件类型_In, 安装路径 = 安装路径_In, 业务部件 = 业务部件_In, 所属系统 = 所属系统_In, 文件说明 = 文件说明_In, 自动注册 = 自动注册_In,
        强制覆盖 = 强制覆盖_In, 附加安装路径 = 附加安装路径_In
    Where Rowid = Rs.Rowid;
  End Loop;
  --新增部件
  If n_Count = 0 Then
    Insert Into zlFilesUpgrade
      (序号, 文件名, 文件类型, 安装路径, 业务部件, 所属系统, 文件说明, 自动注册, 强制覆盖, 附加安装路径)
      Select 序号, 名称_In, 文件类型_In, 安装路径_In, 业务部件_In, 所属系统_In, 文件说明_In, 自动注册_In, 强制覆盖_In, 附加安装路径_In
      From Dual A, (Select Nvl(Max(To_Number(序号)), 0) + 1 序号 From zlFilesUpgrade) B;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zlfiles_Autoupdate;
/

Create Or Replace Procedure Zl_Createsynonyms(系统_In In Zlprogprivs.系统%Type) Authid Current_User As
  v_Sql    Varchar2(2000);
  v_所有者 Varchar2(100);
  n_Cnt    Number(5);

--非当前所有者的对象的私有同义词与当前所有者的对象相同则删除
  Cursor c_Delsyn9(v_所有者 Varchar2) Is
    Select Synonym_Name 对象
    From User_Synonyms A
    Where Table_Owner != v_所有者 And Exists
     (Select 1
           From All_Objects B
           Where a.Synonym_Name = b.Object_Name And b.Owner = v_所有者 And
                 b.Object_Type In ('TABLE', 'VIEW', 'SEQUENCE', 'PROCEDURE', 'FUNCTION','PACKAGE'));

  Cursor c_Delsyn10(v_所有者 Varchar2) Is
    Select a.Synonym_Name 对象
    From User_Synonyms A, All_Objects B
    Where a.Table_Owner != v_所有者 And a.Synonym_Name = b.Object_Name And b.Owner = v_所有者 And
          b.Object_Type In ('TABLE', 'VIEW', 'SEQUENCE', 'PROCEDURE', 'FUNCTION','PACKAGE');

--对用户有权访问的对象,属于当前系统所有者的,如果没有公共或私有同义词,则创建私有同义词
--不仅限于当前模块所访问的对象,因为存在虚拟模块和模块中调用其它模块
  Cursor c_Newsyn(v_所有者 Varchar2) Is
    Select Object_Name 对象, Owner 所有者
    From All_Objects A
    Where Owner = v_所有者 And Instr(Object_Name, 'BIN$') = 0 And
          Object_Type In ('TABLE', 'VIEW', 'SEQUENCE', 'PROCEDURE', 'FUNCTION','PACKAGE')
    Minus
    Select Synonym_Name, Table_Owner
    From All_Synonyms C
    Where Table_Owner = v_所有者 And (Owner = User Or Owner = 'PUBLIC');

Begin
  Select Count(Distinct 所有者) Into n_Cnt From Zlsystems;
  If n_Cnt > 1 Then
    Select Upper(所有者) Into v_所有者 From Zlsystems Where 编号 = 系统_In;
--角色授权已限制所有者不能访问其它系统,所以,系统所有者不用创建私有同义词
    If v_所有者 != User Then
      Begin
        Select Substr(Banner, 6, 2) Into n_Cnt From V$version Where Substr(Banner, 1, 4) = 'CORE';
      Exception
        When Others Then
          n_Cnt := 9;
      End;
      If Nvl(n_Cnt, 10) > 9 Then
        For c_Syn In c_Delsyn10(v_所有者) Loop
          v_Sql := 'Drop Synonym ' || c_Syn.对象;
          Execute Immediate v_Sql;
        End Loop;
      Else
        For c_Syn In c_Delsyn9(v_所有者) Loop
          v_Sql := 'Drop Synonym ' || c_Syn.对象;
          Execute Immediate v_Sql;
        End Loop;
      End If;

      For c_Syn In c_Newsyn(v_所有者) Loop
        v_Sql := 'Create Synonym ' || c_Syn.对象 || ' For ' || c_Syn.所有者 || '.' || c_Syn.对象;
        Execute Immediate v_Sql;
      End Loop;
    End If;
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Createsynonyms;
/

Create Or Replace Procedure Zl_Createpubsynonyms Authid Current_User As
  --功能：修正应用系统的公共同义词
  n_Do Number(5);

  Procedure Execute_Sql(v_Sql In Varchar2) Is
  Begin
    Execute Immediate v_Sql;
  Exception
    When Others Then
      Null;
  End;
Begin
  Select Count(*) Into n_Do From zlSystems Where 所有者 = User;

  If n_Do > 0 Then
    --1.对当前用户的对象补建公共同义词（一般是升级后新增的对象）
    --如果其它系统有同名对象的公共同义词,则不创建,当用户进入模块时会创建私有同义词
    For c_Syn In (Select 'Create or Replace Public Synonym ' || Synonym_Name || ' for ' || Synonym_Name Sql_Text
                  From (Select Object_Name As Synonym_Name
                         From User_Objects A
                         Where Instr(Object_Name, 'BIN$') = 0 And
                               Object_Type In ('TABLE', 'VIEW', 'SEQUENCE', 'PROCEDURE', 'FUNCTION', 'PACKAGE', 'TYPE')
                         Minus
                         Select Synonym_Name
                         From All_Synonyms
                         Where (Table_Owner In (Select Distinct 所有者 From zlSystems) Or Table_Owner = 'ZLTOOLS') And
                               Owner = 'PUBLIC')) Loop
      Execute_Sql(c_Syn.Sql_Text);
    End Loop;
  
    --2.删除“对象已不存在”的公共同义词；
    For c_Syn In (Select 'Drop Public SYNONYM ' || Synonym_Name Sql_Text
                  From All_Synonyms A
                  Where Owner = 'PUBLIC' And (Table_Owner = User Or Table_Owner = 'ZLTOOLS') And Not Exists
                   (Select 1
                         From All_Objects B
                         Where a.Table_Owner = b.Owner And a.Table_Name = b.Object_Name And Object_Type != 'SYNONYM')) Loop
      Execute_Sql(c_Syn.Sql_Text);
    End Loop;
  
    --3.重新创建失效的公共同义词
    For c_Syn In (Select 'Create or Replace Public Synonym ' || Synonym_Name || ' for ' || Synonym_Name Sql_Text
                  From All_Synonyms A
                  Where Table_Owner = User And Owner = 'PUBLIC' And
                        Synonym_Name In
                        (Select Object_Name
                         From All_Objects
                         Where Object_Type = 'SYNONYM' And Owner = 'PUBLIC' And Status = 'INVALID') And Exists
                   (Select 1
                         From All_Objects B
                         Where a.Table_Name = b.Object_Name And a.Table_Owner = b.Owner And b.Status = 'VALID' And
                               Object_Type != 'SYNONYM')) Loop
      Execute_Sql(c_Syn.Sql_Text);
    End Loop;
  Else
    Raise_Application_Error(-20101, '[ZLSOFT]当前用户不是应用系统所有者，请以所有者用户登录执行[ZLSOFT]');
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Createpubsynonyms;
/

Create Or Replace Procedure Zl_Parameters_Change
(
  参数id_In   Zlparameters.Id%Type,
  私有_In     Zlparameters.私有%Type,
  本机_In     Zlparameters.本机%Type,
  授权_In     Zlparameters.授权%Type,
  变动人_In   Zlparachangedlog.变动人%Type,
  变动原因_In Zlparachangedlog.变动原因%Type,
  部门_In     Zlparameters.部门%Type := 0
) Is
  v_Temp     Varchar2(200);
  n_模块     Zlparameters.模块%Type;
  n_私有     Zlparameters.私有%Type;
  n_本机     Zlparameters.本机%Type;
  n_部门     Zlparameters.部门%Type;
  n_授权     Zlparameters.授权%Type;
  n_序号     Zlparachangedlog.序号%Type;
  v_变动说明 Zlparachangedlog.变动说明%Type;
  v_变动内容 Zlparachangedlog.变动内容%Type;

  Function Gettype
  (
    模块_In Zlparameters.私有%Type,
    私有_In Zlparameters.私有%Type,
    本机_In Zlparameters.本机%Type,
    部门_In Zlparameters.部门%Type
  ) Return Varchar2 Is
  Begin
    If Nvl(部门_In, 0) = 1 Then
      Return '部门参数';
    End If;
    If Nvl(模块_In, 0) = 0 Then
      --不存模块,证明只有两种类型:公共全局和私有全局
      If Nvl(私有_In, 0) = 0 Then
        Return '公共全局';
      End If;
      Return '私有全局';
    End If;

    --对模块的处理
    If 本机_In = 0 Then
      --不是本机的情况,只有两种类型:公共模块和私有模块
      If Nvl(私有_In, 0) = 0 Then
        Return '公共模块';
      End If;
      Return '私有模块';
    End If;
    --对本机的模块进行处理也有两种情况:
    If Nvl(私有_In, 0) = 0 Then
      Return '本机公共模块';
    End If;
    Return '本机私有模块';
  Exception
    When Others Then
      Return Null;
  End Gettype;
Begin

  Select Nvl(模块, 0), Nvl(私有, 0), Nvl(本机, 0), Nvl(授权, 0), Nvl(部门, 0)
  Into n_模块, n_私有, n_本机, n_授权, n_部门
  From Zlparameters
  Where Id = 参数id_In;
  Select Nvl(Max(序号), 0) + 1 Into n_序号 From Zlparachangedlog Where 参数id = 参数id_In;
  --插入数据
  --说明变动说明:比如:私有模块变为公用模块。
  -- 变动内容:说明变动字段的变化情况:比如:私有:1-->0,本机:1-->0
  v_变动说明 := Null;
  v_变动内容 := Null;
  --类型发生了改变
  If n_私有 <> Nvl(私有_In, 0) Or n_本机 <> Nvl(本机_In, 0) Or n_部门 <> Nvl(部门_In, 0) Then
    v_Temp     := '从' || Gettype(n_模块, n_私有, n_本机, n_部门);
    v_Temp     := v_Temp || '变为' || Gettype(n_模块, Nvl(私有_In, 0), Nvl(本机_In, 0), Nvl(部门_In, 0));
    v_变动说明 := v_Temp;
    v_Temp     := '';
    If n_部门 <> Nvl(部门_In, 0) Then
      v_Temp := v_Temp || ',部门:' || n_部门 || '-->' || Nvl(部门_In, 0);
    End If;
    If n_私有 <> Nvl(私有_In, 0) Then
      v_Temp := v_Temp || ',私有:' || n_私有 || '-->' || Nvl(私有_In, 0);
    End If;
    If n_私有 <> Nvl(私有_In, 0) Then
      v_Temp := v_Temp || ',本机:' || n_本机 || '-->' || Nvl(本机_In, 0);
    End If;
    v_变动内容 := Substr(v_Temp, 2);
  End If;
  --检查授权发生改变没有
  If n_授权 <> Nvl(授权_In, 0) Then
    If Not v_变动说明 Is Null Then
      v_变动说明 := v_变动说明 || ',';
    End If;
    If n_授权 = 0 Then
      v_Temp := '不需要授权';
    Else
      v_Temp := '需要授权';
    End If;
    v_变动说明 := Nvl(v_变动说明, '') || '从' || v_Temp || '改为';
    If 授权_In = 0 Then
      v_Temp := '不需要授权';
    Else
      v_Temp := '需要授权';
    End If;
    v_变动说明 := Nvl(v_变动说明, '') || v_Temp;

    If Not v_变动内容 Is Null Then
      v_变动内容 := v_变动内容 || ',';
    End If;
    v_变动内容 := Nvl(v_变动内容, '') || '授权:' || n_授权 || '-->' || Nvl(授权_In, 0);
  End If;

  Insert Into Zlparachangedlog
    (参数id, 序号, 变动说明, 变动内容, 变动人, 变动时间, 变动原因)
  Values
    (参数id_In, n_序号, v_变动说明, v_变动内容, 变动人_In, Sysdate, 变动原因_In);

  Update Zlparameters
  Set 私有 = Nvl(私有_In, 0), 本机 = Nvl(本机_In, 0), 授权 = Nvl(授权_In, 0), 部门 = Nvl(部门_In, 0)
  Where Id = 参数id_In;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Parameters_Change;
/
Create Or Replace Procedure Zlparameters_Delall_Details
(
  参数列表_In Varchar2,
  n_部门      Number := 0
  --n_部门:1，部门类型的参数。0-非部门类型的参数
  --参数列表_In 系统1^模块1^参数名1#系统2......,
) Is
  t_参数id t_Numlist;
Begin
  Select a.Id Bulk Collect
  Into t_参数id
  From Zlparameters a,
       (Select Zl_To_Number(C1) 系统, Zl_To_Number(Substr(C2, 1, Instr(C2, '^') - 1)) 模块,
                Substr(C2, Instr(C2, '^') + 1) 参数名
         From Table(f_Str2list2(参数列表_In, '#', '^'))) b
  Where Nvl(a.系统, 0) = Nvl(b.系统, 0) And Nvl(a.模块, 0) = Nvl(b.模块, 0) And a.参数名 = b.参数名;
  If n_部门 = 0 Then
    Forall i In 1 .. t_参数id.Count
      Delete Zluserparas Where 参数id = t_参数id(i);
  Else
    Forall i In 1 .. t_参数id.Count
      Delete Zldeptparas Where 参数id = t_参数id(i);
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zlparameters_Delall_Details;
/
CREATE OR REPLACE Procedure Zlparameters_Add_Details
(
  参数id_In Zlparameters.Id%Type,
  用户名_In Varchar2,
  机器名_In Varchar2,
  参数值_In Varchar2
  --用户名_In 以逗号分割，用户1,用户2,
  --机器名_In 以逗号分割，机器1,机器2,
) Is
  n_部门 Number(1);
Begin
  Select Nvl(部门, 0) Into n_部门 From Zlparameters Where Id = 参数id_In;
  If n_部门 = 0 Then
    Insert Into Zluserparas
      (参数id, 用户名, 机器名, 参数值)
      Select 参数id, 用户名, 机器名, 参数值
      From (Select 参数id_In 参数id, a.用户名, b.机器名, 参数值_In 参数值
             From (Select Distinct Column_Value 用户名 From Table(f_Str2list(Nvl(用户名_In, ',')))) a,
                  (Select Distinct Column_Value 机器名 From Table(f_Str2list(Nvl(机器名_In, ',')))) b) c
      Where Not Exists
       (Select 1
             From Zluserparas
             Where 参数id = c.参数id And Nvl(用户名, '空空') = Nvl(c.用户名, '空空') And Nvl(机器名, '空空') = Nvl(c.机器名, '空空'));
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zlparameters_Add_Details;
/
Create Or Replace Procedure Zlparameters_Update_Details
(
  参数id_In   Zlparameters.Id%Type,
  参数列表_In Varchar2
  --参数列表_In 用户名1^机器名1^参数值1#用户名2^机器名2^参数值2......,
  --           部门类型参数：部门ID1,,参数值1#部门ID2,,参数值2
) Is
  n_部门   Number(1);
  t_部门id t_Numlist;
  t_用户名 t_Strlist;
  t_机器名 t_Strlist;
  t_参数值 t_Strlist;
Begin
  Select Nvl(部门, 0) Into n_部门 From zlParameters Where ID = 参数id_In;
  If n_部门 = 0 Then
    Select C1, Substr(C2, 1, Instr(C2, '^') - 1), Substr(C2, Instr(C2, '^') + 1) Bulk Collect
    Into t_用户名, t_机器名, t_参数值
    From Table(f_Str2list2(参数列表_In, '#', '^'));

    Forall I In 1 .. t_参数值.Count
      Update zlUserParas
      Set 参数值 = t_参数值(I)
      Where 参数id = 参数id_In And Nvl(用户名, '空空') = Nvl(t_用户名(I), '空空') And Nvl(机器名, '空空') = Nvl(t_机器名(I), '空空');
  Else
    Select To_Number(C1), Substr(C2, 1, Instr(C2, '^') - 1), Substr(C2, Instr(C2, '^') + 1) Bulk Collect
    Into t_部门id, t_机器名, t_参数值
    From Table(f_Str2list2(参数列表_In, '#', '^'));

    Forall I In 1 .. t_参数值.Count
      Update Zldeptparas Set 参数值 = t_参数值(I) Where 参数id = 参数id_In And 部门id = t_部门id(I);
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zlparameters_Update_Details;
/
Create Or Replace Procedure Zlparameters_Del_Details
(
  参数id_In   Zlparameters.Id%Type,
  参数列表_In Varchar2
  --参数列表_In 用户名1^机器名1#用户名2^机器名2......,
  --           部门类型参数：部门ID1#部门ID2
) Is
  n_部门   Number(1);
  t_部门id t_Numlist;
  t_用户名 t_Strlist;
  t_机器名 t_Strlist;
Begin
  Select Nvl(部门, 0) Into n_部门 From Zlparameters Where Id = 参数id_In;
  If n_部门 = 0 Then
    Select C1, C2 Bulk Collect Into t_用户名, t_机器名 From Table(f_Str2list2(参数列表_In, '#', '^'));

    Forall i In 1 .. t_用户名.Count
      Delete Zluserparas
      Where 参数id = 参数id_In And Nvl(用户名, '空空') = Nvl(t_用户名(i), '空空') And Nvl(机器名, '空空') = Nvl(t_机器名(i), '空空');
  Else
    Select To_Number(Column_Value) Bulk Collect Into t_部门id From Table(f_Str2list(参数列表_In, '#'));

    Forall i In 1 .. t_部门id.Count
      Delete Zldeptparas Where 参数id = 参数id_In And 部门id = t_部门id(i);
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zlparameters_Del_Details;
/
Create Or Replace Procedure Zlparameters_Imp_Details
(
  系统_In     Zlparameters.系统%Type,
  模块_In     Zlparameters.模块%Type,
  参数_In     Zlparameters.参数名%Type,
  参数列表_In Varchar2
  --参数列表_In 用户名1^机器名1^参数值1#用户名2^机器名2^参数值2......,
  --           部门类型参数：部门ID1,,参数值1#部门ID2,,参数值2
  --参数列表为空时删除所有详细参数
) Is
  n_参数id Zlparameters.Id%Type;
  n_部门   Number(1);
  n_私有   Number(1);
  n_本机   Number(1);
  t_部门id t_Numlist;
  t_用户名 t_Strlist;
  t_机器名 t_Strlist;
  t_参数值 t_Strlist;
Begin
  --获取参数ID与部门性质
  If Zl_To_Number(参数_In) <> 0 Then
    Select Nvl(部门, 0), Nvl(私有, 0), Nvl(本机, 0), ID
    Into n_部门, n_私有, n_本机, n_参数id
    From zlParameters
    Where 参数号 = Zl_To_Number(参数_In) And Nvl(模块, 0) = Nvl(模块_In, 0) And Nvl(系统, 0) = Nvl(系统_In, 0);
  Else
    Select Nvl(部门, 0), Nvl(私有, 0), Nvl(本机, 0), ID
    Into n_部门, n_私有, n_本机, n_参数id
    From zlParameters
    Where 参数名 = 参数_In And Nvl(模块, 0) = Nvl(模块_In, 0) And Nvl(系统, 0) = Nvl(系统_In, 0);
  End If;
  If n_参数id Is Not Null Then
    If n_部门 = 0 Then
      If 参数列表_In Is Null Then
        Delete zlUserParas Where 参数id = n_参数id;
        --私有或本机参数，才插入
      Elsif n_私有 = 1 Or n_本机 = 1 Then
        Select C1, Substr(C2, 1, Instr(C2, '^') - 1), Substr(C2, Instr(C2, '^') + 1) Bulk Collect
        Into t_用户名, t_机器名, t_参数值
        From Table(f_Str2list2(参数列表_In, '#', '^'));
        --采用全部删除，再插入
        Forall I In 1 .. t_参数值.Count
          Insert Into zlUserParas
            (参数id, 用户名, 机器名, 参数值)
          Values
            (n_参数id, t_用户名(I), t_机器名(I), t_参数值(I));
      End If;
    Else
      If 参数列表_In Is Null Then
        Delete Zldeptparas Where 参数id = n_参数id;
      Else
        Select To_Number(C1), Substr(C2, 1, Instr(C2, '^') - 1), Substr(C2, Instr(C2, '^') + 1) Bulk Collect
        Into t_部门id, t_机器名, t_参数值
        From Table(f_Str2list2(参数列表_In, '#', '^'));
        --采用全部删除，再插入
        Forall I In 1 .. t_参数值.Count
          Insert Into Zldeptparas (参数id, 部门id, 参数值) Values (n_参数id, t_部门id(I), t_参数值(I));
      End If;
    End If;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zlparameters_Imp_Details;
/
Create Or Replace Procedure Zl_Parameters_Change_Value
(
  参数id_In     Zlparachangedlog.参数id%Type,
  变动内容_In   Zlparachangedlog.变动内容%Type, --原值-->新值
  变动原因_In   Zlparachangedlog.变动原因%Type,
  操作员姓名_In Zlparachangedlog.变动人%Type,
  变动时间_In   Zlparachangedlog.变动时间%Type
) Is
  n_Max序号 Zlparachangedlog.序号%Type;
Begin
  Select Nvl(Max(序号), 1)+1 Into n_Max序号 From Zlparachangedlog Where 参数id = 参数id_In;

  Insert Into Zlparachangedlog
    (参数id, 序号, 变动说明, 变动内容, 变动人, 变动时间, 变动原因)
  Values
    (参数id_In, n_Max序号, '值变动', 变动内容_In, 操作员姓名_In, 变动时间_In, 变动原因_In);

