CREATE OR REPLACE Procedure Zl_Parameters_Update_Batch
(
  系统编号_In   Zlsystems.编号%Type,
  参数列表_In   Varchar2, --模块号1^参数号1^参数值1#模块号2^参数号2^参数值2......
  操作员姓名_In Zlparachangedlog.变动人%Type
) Is
  t_模块   t_Numlist;
  t_参数号 t_Numlist;
  t_参数值 t_Strlist;
Begin
  Select To_Number(C1), To_Number(Substr(C2, 1, Instr(C2, '^') - 1)), Substr(C2, Instr(C2, '^') + 1) Bulk Collect
  Into t_模块, t_参数号, t_参数值
  From Table(f_Str2list2(参数列表_In, '#', '^'));

  For Rs In (Select /*+ rule*/
              a.Id, a.参数值 || '-->' || Substr(C2, Instr(C2, '^') + 1) As 变动内容, Sysdate As 变动时间
             From zlParameters A, Table(f_Str2list2(参数列表_In, '#', '^')) B
             Where a.系统 = 系统编号_In And Nvl(a.模块, 0) = To_Number(b.C1) And
                   a.参数号 = To_Number(Substr(b.C2, 1, Instr(b.C2, '^') - 1)) And a.警告说明 Is Null) Loop
    --有警告说明的关键参数，单独在界面提供变动登记
    Zl_Parameters_Change_Value(Rs.Id, Rs.变动内容, '', 操作员姓名_In, Rs.变动时间);
  End Loop;

  Forall I In 1 .. t_参数号.Count
    Update zlParameters
    Set 参数值 = t_参数值(I)
    Where 系统 = 系统编号_In And Nvl(模块, 0) = t_模块(I) And 参数号 = t_参数号(I);

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Parameters_Update_Batch;
/

Create Or Replace Procedure Zl_Zlinterface_Edit
(
  Mode_In      Number,
  Appno_In     Zlinterface.Appno%Type,
  Appname_In   Zlinterface.Appname%Type := Null,
  Key_In       Zlinterface.Key%Type := Null,
  Note_In      Zlinterface.Note%Type := Null,
  Starttime_In Zlinterface.Starttime%Type := Null,
  Stoptime_In  Zlinterface.Stoptime%Type := Null,
  State_In     Zlinterface.State%Type := 0
) As
  --Mode_In=0-新增或修改，Appno_In传0或NULL时为新增，1-删除，2-改变状态
Begin
  If Nvl(Mode_In, 0) = 0 Then
    If Nvl(Appno_In, 0) = 0 Then
      Insert Into Zlinterface
        (Appno, Appname, Key, Note, Starttime, Stoptime, State)
        Select Nvl(Max(Appno), 0) + 1, Appname_In, Key_In, Note_In, Starttime_In, Stoptime_In, 0 From Zlinterface;
    Else
      Update Zlinterface
      Set Appname = Appname_In, Key = Key_In, Note = Note_In, Starttime = Starttime_In, Stoptime = Stoptime_In
      Where Appno = Appno_In;
    End If;
  Elsif Nvl(Mode_In, 0) = 1 Then
    Delete Zlinterface Where Appno = Appno_In;
  Elsif Nvl(Mode_In, 0) = 2 Then
    Update Zlinterface Set State = State_In Where Appno = Appno_In;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlinterface_Edit;
/
