Create Or Replace Procedure Zl_Zlclients_Delete
(
  v_工作站_In Zlclients.工作站%Type := Null,
  v_Ip_In     Zlclients.Ip%Type := Null
) Is
Begin
  If Not (v_工作站_In Is Null And v_Ip_In Is Null) Then
    If v_Ip_In Is Null Then
      Delete Zlclients Where 工作站 = v_工作站_In;
    Elsif v_工作站_In Is Null Then
      Delete Zlclients Where Ip = v_Ip_In;
    Else
      Delete Zlclients Where Ip = v_Ip_In And 工作站 = v_工作站_In;
    End If;
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Zlclients_Delete;
/

