Create Or Replace Procedure Zl_Zlclients_Control
(
  n_Mode_In       Number,
  v_工作站_In     Zlclients.工作站%Type := Null,
  v_Ip_In         Zlclients.Ip%Type := Null,
  n_升级标志_In   Zlclients.升级标志%Type := Null,
  n_升级服务器_In Zlclients.升级服务器%Type := Null,
  d_预升时点_In   Zlclients.预升时点%Type := Null,
  n_预升完成_In   Zlclients.预升完成%Type := Null,
  n_Ftp服务器_In  Zlclients.Ftp服务器%Type := Null,
  n_收集标志_In   Zlclients.收集标志%Type := Null,
  n_禁止使用_In   Zlclients.禁止使用%Type := Null,
  v_说明_In       Zlclients.说明%Type := Null
  --对客户端进行控制
  --N_Mode_In：0-禁用或启用客户端(IP做为主要条件）,1-预升级设置,2 -升级信息保存(IP做为主要条件）
  --3-取消预升级标志,4-将所有站点设置为升级,5-部件搜集（设置搜集标志）,6-重置升级状态
) Is
  v_Timeset  Varchar2(300);
  v_Err      Varchar2(500);
  n_收集标志 Zlclients.收集标志%Type;
  Err_Custom Exception;
Begin
  --0-禁用或启用客户端(IP做为主要条件）
  If n_Mode_In = 0 Then
    If v_工作站_In Is Not Null Then
      Update zlClients Set 禁止使用 = n_禁止使用_In Where Ip = v_Ip_In;
    End If;
    --1-预升级设置,不需要传其他参数
  Elsif n_Mode_In = 1 Then
    Select Max(内容) Into v_Timeset From zlRegInfo Where 项目 = '客户端预升级时间点';
    If v_Timeset Is Not Null Then
      For r_Ip In (Select To_Date(Today || ' ' || Date_d, 'yyyy-mm-dd HH24:mi:ss') 预升时点, 工作站, Ip
                   From (Select 工作站, Ip, Rownum Rn_c From zlClients) A,
                        (Select To_Char(Sysdate, 'yyyy-mm-dd') Today, Column_Value Date_d, Rownum Rn_d, Count(1) Over() Sn
                          From Table(f_Str2list(v_Timeset, ','))) B
                   Where Mod(a.Rn_c, Sn) + 1 = Rn_d) Loop
      
        Update zlClients Set 预升时点 = r_Ip.预升时点 Where 工作站 = r_Ip.工作站 And Ip = r_Ip.Ip;
      End Loop;
    Else
      Update zlClients Set 预升时点 = Null;
    End If;
    --2 -升级信息保存(IP做为主要条件）
  Elsif n_Mode_In = 2 Then
    If n_Ftp服务器_In Is Null Then
      Update zlClients
      Set 升级标志 = n_升级标志_In, 升级服务器 = n_升级服务器_In, 预升时点 = d_预升时点_In, 预升完成 = n_预升完成_In,
          收集标志 = Decode(n_升级标志_In, 1, 0, n_收集标志_In)
      Where Ip = v_Ip_In
      Returning Max(收集标志) Into n_收集标志;
    Else
      Update zlClients
      Set 升级标志 = n_升级标志_In, Ftp服务器 = n_Ftp服务器_In, 预升时点 = d_预升时点_In, 预升完成 = n_预升完成_In,
          收集标志 = Decode(n_升级标志_In, 1, 0, n_收集标志_In)
      Where Ip = v_Ip_In
      Returning Max(收集标志) Into n_收集标志;
    End If;
    If Nvl(n_收集标志, 0) = 1 Then
      Delete Zlclientupdatelog A Where a.工作站 In (Select 工作站 From zlClients Where Ip = v_Ip_In) And 类型 = 1;
    End If;
    --3-取消预升级标志
  Elsif n_Mode_In = 3 Then
    Update zlClients Set 预升完成 = n_预升完成_In;
    --4-将所有站点设置为升级
  Elsif n_Mode_In = 4 Then
    Update zlClients Set 升级标志 = n_升级标志_In, 收集标志 = Decode(n_升级标志_In, 1, 0, 收集标志);
    --5-部件搜集（设置搜集标志）
  Elsif n_Mode_In = 5 Then
    If v_工作站_In Is Null Then
      Update zlClients Set 收集标志 = n_收集标志_In;
      If Nvl(n_收集标志_In, 0) = 1 Then
        Delete Zlclientupdatelog A Where a.类型 = 1;
      End If;
    Else
      Update zlClients Set 收集标志 = n_收集标志_In Where 工作站 = v_工作站_In;
      If Nvl(n_收集标志_In, 0) = 1 Then
        Delete Zlclientupdatelog A Where a.工作站 = v_工作站_In And 类型 = 1;
      End If;
    End If;
  Elsif n_Mode_In = 6 Then
    Update zlClients Set 升级情况 = 0 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 7 Then
    --7未升级
    Update zlClients Set 升级情况 = 1 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 8 Then
    --8已升级
    Update zlClients Set 升级情况 = 2 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 9 Then
    --9修改说明
    Update zlClients Set 说明 = v_说明_In Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 10 Then
    --10修改说明和收集标志
    Update zlClients Set 说明 = v_说明_In, 收集标志 = 0 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 11 Then
    --11修改说明和升级标志
    Update zlClients Set 说明 = v_说明_In, 升级标志 = 0 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 12 Then
    --12更改站点的预升级出错状态
    Update zlClients Set 说明 = v_说明_In, 预升完成 = 0 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 13 Then
    --13更改站点的预升级完成状态
    Update zlClients Set 预升完成 = 1 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 14 Then
    --14定时升级
    Update zlClients Set 预升时点 = Null, 预升完成 = Null Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 15 Then
    --15升级成功
    Update zlClients Set 升级情况 = 1 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 16 Then
    --16升级失败
    Update zlClients Set 升级情况 = 2 Where 工作站 = v_工作站_In;
  Elsif n_Mode_In = 17 Then
    --17用途
    Update zlClients Set 用途 = v_说明_In Where 工作站 = v_工作站_In;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlclients_Control;
/

Create Or Replace Procedure Zl_Zlclients_Setpretime
(
  n_Mode_In  Number,
  n_Cover_In Number := 1,
  v_工作站_In   Zlclients.工作站%Type := Null,
  d_预升时点_In Zlclients.预升时点%Type := Null
) Is
  v_Timeset Varchar2(300);
  v_Err     Varchar2(500);
  Err_Custom Exception;
  --n_Cover_In 0-不覆盖 1-覆盖
Begin
  --0-单独对客户端预升级时间设置
  If n_Mode_In = 0 Then
    If v_工作站_In Is Not Null Then
      Update zlClients Set 预升时点 = d_预升时点_In Where 工作站 = v_工作站_In;
    End If;
  --1-预升级时间自动设置n_Cover_In决定覆盖与否
  Elsif n_Mode_In = 1 Then
    Select Max(内容) Into v_Timeset From zlRegInfo Where 项目 = '客户端预升级时间点';
    If v_Timeset Is Not Null Then
      For r_Ip In (Select To_Date(Today || ' ' || Date_d, 'yyyy-mm-dd HH24:mi:ss') 预升时点, 工作站, Ip
                   From (Select 工作站, Ip, Rownum Rn_c From zlClients) A,
                        (Select To_Char(Sysdate, 'yyyy-mm-dd') Today, Column_Value Date_d, Rownum Rn_d, Count(1) Over() Sn
                          From Table(f_Str2list(v_Timeset, ','))) B
                   Where Mod(a.Rn_c, Sn) + 1 = Rn_d) Loop
        If n_Cover_In = 1 Then
          Update zlClients Set 预升时点 = r_Ip.预升时点 Where 工作站 = r_Ip.工作站 And Ip = r_Ip.Ip;
        Elsif n_Cover_In = 0 Then
          Update zlClients
          Set 预升时点 = r_Ip.预升时点
          Where 工作站 = r_Ip.工作站 And Ip = r_Ip.Ip And 预升时点 Is Null;
        End If;
      End Loop;
    Else
      Update zlClients Set 预升时点 = Null;
    End If;
  Elsif n_Mode_In = 3 Then
    Update zlClients Set 预升完成 = 0;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlclients_Setpretime;
/

Create Or Replace Procedure Zl_Zlconnections_Edit
(
  操作_In       In Number, --0-新增,1-修改,2-删除
  编号_In       In Zlconnections.编号%Type,
  名称_In       In Zlconnections.名称%Type := Null,
  用户名_In     In Zlconnections.用户名%Type := Null,
  密码_In       In Zlconnections.密码%Type := Null,
  Ip_In         In Zlconnections.Ip%Type := Null,
  端口_In       In Zlconnections.端口%Type := Null,
  实例名_In     In Zlconnections.实例名%Type := Null,
  说明_In       In Zlconnections.说明%Type := Null,
  数据库类型_In In Zlconnections.数据库类型%Type := Null,
  提供者类型_In In Zlconnections.提供者类型%Type := Null,
  驱动版本_In   In Zlconnections.驱动版本%Type := Null,
  连接字符串_In In Zlconnections.连接字符串%Type := Null
) Is
  n_编号    Zlconnections.编号%Type;
  n_Count   Number(1);
  v_Err_Msg Varchar2(255);
  Err_Item Exception;
Begin
  If 操作_In = 0 Then
    Select Count(1) Into n_Count From Zlconnections Where 名称 = 名称_In;
    If n_Count = 1 Then
      v_Err_Msg := '该连接名称已存在！';
      Raise Err_Item;
    End If;
    Select Nvl(Max(编号), 0) Into n_编号 From Zlconnections;
    Insert Into Zlconnections
      (编号, 名称, 数据库类型, 提供者类型, 驱动版本, 连接字符串, 用户名, 密码, Ip, 端口, 实例名, 说明)
    Values
      (n_编号 + 1, 名称_In, 数据库类型_In, 提供者类型_In, 驱动版本_In, 连接字符串_In, 用户名_In, 密码_In, Ip_In, 端口_In, 实例名_In, 说明_In);
  Elsif 操作_In = 1 Then
    Update Zlconnections
    Set 用户名 = 用户名_In, 名称 = 名称_In, 密码 = 密码_In, Ip = Ip_In, 端口 = 端口_In, 实例名 = 实例名_In, 说明 = 说明_In, 数据库类型 = 数据库类型_In,
        提供者类型 = 提供者类型_In, 驱动版本 = 驱动版本_In, 连接字符串 = 连接字符串_In
    Where 编号 = 编号_In;
  Else
    Delete Zlconnections Where 编号 = 编号_In;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Zlconnections_Edit;
/

CREATE OR REPLACE Procedure Zl_Zlmsgstate_Edit
(
  操作_In     Number, --0-新增,1-修改,2-删除
  消息id_In   Zlmsgstate.消息id%Type,
  类型_In     Zlmsgstate.类型%Type := Null,
  用户_In     Zlmsgstate.用户%Type := Null,
  身份_In     Zlmsgstate.身份%Type := Null,
  删除_In     Zlmsgstate.删除%Type := Null,
  状态_In     Zlmsgstate.状态%Type := Null,
  保存天数_In Number := Null
) Is
  n_总数 Number;
  n_数量 Number;
Begin
  If 操作_In = 0 Then
    Insert Into Zlmsgstate
      (消息id, 类型, 用户, 身份, 删除, 状态)
    Values
      (消息id_In, 类型_In, 用户_In, 身份_In, 删除_In, 状态_In);
  Elsif 操作_In = 1 Then
    If 状态_In Is Not Null Then
      If 身份_In Is Null Then
        Update Zlmsgstate Set 状态 = 状态_In Where 消息id = 消息id_In And 类型 = 类型_In And 用户 = 用户_In;
      Else
        Update Zlmsgstate
        Set 状态 = 状态_In, 身份 = 身份_In
        Where 消息id = 消息id_In And 类型 = 类型_In And 用户 = 用户_In;
      End If;
    End If;

    If 删除_In Is Not Null Then
      If 类型_In Is Not Null Then
        Update Zlmsgstate Set 删除 = 删除_In Where 消息id = 消息id_In And 类型 = 类型_In And 用户 = 用户_In;
      Else
        Update Zlmsgstate Set 删除 = 删除_In Where 消息id = 消息id_In And 用户 = 用户_In;
      End If;
      Select Count(*), Sum(Decode(删除, 2, 1, 0)) Into n_总数, n_数量 From Zlmsgstate Where 消息id = 消息id_In;
      If n_总数 = n_数量 Then
        Delete From Zlmessages Where Id = 消息id_In;
      End If;
    End If;
  Elsif 操作_In = 2 Then
    Delete From Zlmessages Where 时间 < Sysdate - 保存天数_In;
  End If;
Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_Zlmsgstate_Edit;
/

