Create Or Replace Procedure Zl_Zlrunlimittime_Update
(
  操作_In     In Number, --0:新增或修改，2:删除
  Id_In       In Zlrunlimittime.Id%Type,
  方案_In     In Zlrunlimittime.方案%Type := Null,
  星期_In     In Zlrunlimittime.星期%Type := Null,
  开始时间_In In Zlrunlimittime.开始时间%Type := Null,
  结束时间_In In Zlrunlimittime.结束时间%Type := Null,
  记录序号_In In Zltools.Zlrunlimitscope.记录序号%Type := Null,
  限制天数_In In Zltools.Zlrunlimitscope.限制天数%Type := Null
) As
  d_开始时间 Zlrunlimittime.开始时间%Type := Null;
  d_结束时间 Zlrunlimittime.结束时间%Type := Null;
  n_Id       Zlrunlimittime.Id%Type;
  n_Min_Id   Zlrunlimittime.Id%Type;
  n_Max_Id   Zlrunlimittime.Id%Type;
  n_限制天数 Zlrunlimitscope.限制天数%Type := Null;
  n_Count    Number(5);
Begin
  If 操作_In = 2 Then
    Delete Zlrunlimittime Where ID = Id_In;
  Else
    --步骤一：调整开始时间与结束时间，开始时间在传入的开始时间5分钟之内，结束时间在传入的结束时间5分钟之内，则忽略舍弃
    --程序内限制时段不能小于5分钟，因此做这样的处理
    Select Nvl(Min(开始时间), 开始时间_In)
    Into d_开始时间
    From Zlrunlimittime
    Where 开始时间 Between (开始时间_In - 1 / 24 / 60 * 5 - 1 / 24 / 60 / 60) And (开始时间_In - 1 / 24 / 60 / 60) And
          结束时间 >= 开始时间_In And 方案 = 方案_In And 星期 = 星期_In;
    Select Nvl(Max(结束时间), 结束时间_In)
    Into d_结束时间
    From Zlrunlimittime
    Where 结束时间 Between (结束时间_In + 1 / 24 / 60 / 60) And (结束时间_In + 1 / 24 / 60 * 5 + 1 / 24 / 60 / 60) And
          开始时间 <= 结束时间_In And 方案 = 方案_In And 星期 = 星期_In;
    --步骤二、插入或更新当前时间段
    If Nvl(Id_In, 0) = 0 Then
      Select Zlrunlimittime_Id.Nextval Into n_Id From Dual;
      Insert Into Zlrunlimittime
        (ID, 方案, 星期, 开始时间, 结束时间)
      Values
        (n_Id, 方案_In, 星期_In, d_开始时间, d_结束时间);
    Else
      n_Id := Id_In;
      Update Zlrunlimittime Set 开始时间 = d_开始时间, 结束时间 = d_结束时间 Where ID = Id_In;
    End If;
    --步骤三、先处理在当前时间范围内的时间段
    --0、分解交叉时间段,将处于范围内的时间分界出来，ID为负数
    Select Min(ID)
    Into n_Min_Id
    From Zlrunlimittime B
    Where 开始时间 < d_开始时间 And 结束时间 >= d_开始时间 - 1 / 24 / 60 / 60 And b.方案 = 方案_In And b.星期 = 星期_In;
    If n_Min_Id Is Not Null Then
      Update Zlrunlimittime Set 结束时间 = d_开始时间 - 1 / 24 / 60 / 60 Where ID = n_Min_Id;
    End If;
  
    Select Min(ID)
    Into n_Max_Id
    From Zltools.Zlrunlimittime
    Where 开始时间 <= d_结束时间 And 结束时间 > d_结束时间 And 方案 = 方案_In And 星期 = 星期_In;
    If n_Max_Id Is Not Null Then
      Update Zlrunlimittime Set 开始时间 = d_结束时间 + 1 / 24 / 60 / 60 Where ID = n_Max_Id;
    End If;
  
    n_限制天数 := Nvl(限制天数_In, 0);
    If Nvl(记录序号_In, 0) = 0 Then
      n_限制天数 := 0;
    End If;
    --2.处理当前功能的方案限制
    If Nvl(记录序号_In, 0) <> 0 Then
      Update Zltools.Zlrunlimitscope A Set 限制天数 = n_限制天数 Where Tid = n_Id And 记录序号 = Nvl(记录序号_In, 0);
      If Sql%RowCount = 0 Then
        Insert Into Zltools.Zlrunlimitscope
          (Tid, 记录序号, 限制天数)
          Select n_Id, 序号, n_限制天数 From Zlrunlimitset Where 序号 = Nvl(记录序号_In, 0);
      End If;
    End If;
    --3、新增限制
    --4、删除范围之内的时段，级联删除方案限制
    Delete Zlrunlimittime
    Where 开始时间 Between d_开始时间 And d_结束时间 And 结束时间 Between d_开始时间 And d_结束时间 And 方案 = 方案_In And 星期 = 星期_In And
          ID <> n_Id;
    --步骤四、处理分割出来的时间段
    --当不存在不同的限制天数时，自动合并分割的时间段
    --不用再次处理限制天数问题，该天数在前面已经新增了进去
    If n_Min_Id Is Not Null Then
      Select Count(1)
      Into n_Count
      From (Select 限制天数 From Zltools.Zlrunlimitscope A Where a.Tid In (n_Id, n_Min_Id) Group By 限制天数);
      If n_Count <= 1 Then
        Update Zlrunlimittime A
        Set a.开始时间 =
             (Select b.开始时间 From Zlrunlimittime B Where b.Id = n_Min_Id)
        Where a.Id = Id_In;
        Delete Zlrunlimittime A Where a.Id = n_Min_Id;
      End If;
    End If;
    If n_Max_Id Is Not Null Then
      Select Count(1)
      Into n_Count
      From (Select 限制天数 From Zltools.Zlrunlimitscope A Where a.Tid In (n_Id, n_Max_Id) Group By 限制天数);
      If n_Count <= 1 Then
        Update Zlrunlimittime A
        Set a.结束时间 =
             (Select b.结束时间 From Zlrunlimittime B Where b.Id = n_Max_Id)
        Where a.Id = Id_In;
        Delete Zlrunlimittime A Where a.Id = n_Max_Id;
      End If;
    End If;
    --删除等于缺省值的限制
    Delete Zlrunlimitscope A
    Where a.Tid = Id_In And a.限制天数 = (Select Nvl(b.限制天数, 0) From Zlrunlimitset B Where b.序号 = a.记录序号);
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlrunlimittime_Update;
/

Create Or Replace Procedure Zl_Zlrunlimit_Update
(
  操作_In     In Number, --0:新增，1:修改,2:删除
  序号_In     In Zlrunlimit.序号%Type,
  名称_In     In Zlrunlimit.名称%Type := Null,
  是否启用_In In Zlrunlimit.是否启用%Type := Null,
  描述_In     In Zlrunlimit.描述%Type := Null
) As
  n_序号 Zlrunlimit.序号%Type;
Begin
  If 操作_In = 0 Then
    --新增
    Select Max(序号) + 1 Into n_序号 From Zlrunlimit;
    Insert Into Zlrunlimit (序号, 名称, 是否启用, 描述) Values (n_序号, 名称_In, 是否启用_In, 描述_In);
    Insert Into Zlrunlimittime
      (ID, 方案, 星期, 开始时间, 结束时间)
      Select Zlrunlimittime_Id.Nextval, n_序号, 星期, 开始时间, 结束时间 From Zlrunlimittime Where 方案 = 1;
  Elsif 操作_In = 1 Then
    --修改
    If 是否启用_In Is Null Then
      --修改方案信息功能
      Update Zlrunlimit Set 名称 = 名称_In, 描述 = 描述_In Where 序号 = 序号_In;
    Else
      --启停方案功能
      Update Zlrunlimit Set 是否启用 = 是否启用_In Where 序号 = 序号_In;
    End If;
  Else
    --删除
    Delete Zlrunlimit Where 序号 = 序号_In;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlrunlimit_Update;
/

--用户登录触发器
Create Or Replace Trigger zl_Trigger_LoginLimit
        After logon On Database
Declare
  --IP_1:ip的前三位, ip_2:IP地址的后一位
  v_Ip     Varchar2(25);
  v_Ip_1   Varchar2(20);
  v_Ip_2   Varchar2(5);
  v_User   Varchar2(40);
  v_Date   Date;
  v_Module Varchar2(100);
  n_Count  Number(5);
Begin

  --如果没有限制规则,就不执行操作,防止多余耗时
  Select Count(1)
  Into n_Count
  From (Select 1
         From Zlapppermission
         Where Rownum = 1
         Union All
         Select 1 From Zlloginlimit Where Rownum = 1);

  If n_Count <> 0 Then
    Select Sys_Context('userenv', 'ip_address'), User, Sysdate, Module
    Into v_Ip, v_User, v_Date, v_Module
    From V$session
    Where Audsid = Userenv('sessionid') And Rownum = 1;
    v_Ip_1 := Substr(v_Ip, 1, Instr(v_Ip, '.', 1, 3) - 1);
    v_Ip_2 := Substr(v_Ip, Instr(v_Ip, '.', 1, 3) + 1);

    --检查登录规则
    Select Count(1)
    Into n_Count
    From Zlloginlimit
    Where 状态 = 1 And
          ((用户名 = User And Substr(开始ip, 1, Instr(开始ip, '.', 1, 3) - 1) = v_Ip_1 And
          v_Ip_2 Between Substr(开始ip, Instr(开始ip, '.', 1, 3) + 1) And Substr(结束ip, Instr(结束ip, '.', 1, 3) + 1) And
          v_Date Between 开始时间 And 结束时间) Or (用户名 = User And 开始ip Is Null And 开始时间 Is Null) Or
          (Substr(开始ip, 1, Instr(开始ip, '.', 1, 3) - 1) = v_Ip_1 And
          v_Ip_2 Between Substr(开始ip, Instr(开始ip, '.', 1, 3) + 1) And Substr(结束ip, Instr(结束ip, '.', 1, 3) + 1) And
          用户名 Is Null And 开始时间 Is Null) Or (v_Date Between 开始时间 And 结束时间 And 用户名 Is Null And 开始ip Is Null) Or
          (用户名 = User And Substr(开始ip, 1, Instr(开始ip, '.', 1, 3) - 1) = v_Ip_1 And
          v_Ip_2 Between Substr(开始ip, Instr(开始ip, '.', 1, 3) + 1) And Substr(结束ip, Instr(结束ip, '.', 1, 3) + 1) And
          开始时间 Is Null) Or (用户名 = User And v_Date Between 开始时间 And 结束时间 And 开始ip Is Null) Or
          (Substr(开始ip, 1, Instr(开始ip, '.', 1, 3) - 1) = v_Ip_1 And
          v_Ip_2 Between Substr(开始ip, Instr(开始ip, '.', 1, 3) + 1) And Substr(结束ip, Instr(结束ip, '.', 1, 3) + 1) And
          v_Date Between 开始时间 And 结束时间 And 用户名 Is Null));

    If n_Count > 0 Then
      Raise_Application_Error(-20001, '当前用户被禁止登录数据库，请联系管理员。');
    End If;

    --检查应用授权
    Select Count(1) Into n_Count From Zlapppermission Where 应用程序 = v_Module And 状态 = 1;

    If n_Count > 0 Then
      Select Count(1)
      Into n_Count
      From Zlapppermission
      Where 状态 = 1 And
            ((应用程序 = v_Module And 用户名 = v_User And 开始ip Is Null) Or
            (应用程序 = v_Module And 用户名 = v_User And Substr(开始ip, 1, Instr(开始ip, '.', 1, 3) - 1) = v_Ip_1 And
            v_Ip_2 Between Substr(开始ip, Instr(开始ip, '.', 1, 3) + 1) And Substr(结束ip, Instr(结束ip, '.', 1, 3) + 1)));
      If n_Count = 0 Then
        Raise_Application_Error(-20002, '当前用户不能使用该应用登录数据库，请联系管理员。');
      End If;
    End If;
  End If;
End Zl_Trigger_Loginlimit;
/

CREATE OR REPLACE Procedure Zl_Zlobsoletedatadeal_Update
(
  名称_In         In Zlobsoletedatadeal.名称%Type,
  是否定期处理_In In Zlobsoletedatadeal.是否定期处理%Type := Null,
  保留天数_In     In Zlobsoletedatadeal.保留天数%Type := Null,
  操作人员_In     In Zlobsoletedatadeal.操作人员%Type := Null,
  操作时间_In     In Zlobsoletedatadeal.操作时间%Type := Null
) Authid Current_User Is
  n_Jobnum Number := 0;
  v_What   Varchar2(1000);
Begin

  If Not 是否定期处理_In Is Null Then
    If 是否定期处理_In = 1 Then
      --开启定期处理，需要将需要执行的存储过程添加到自动作业中
      Select 数据处理过程, Nvl(作业号, 0) Into v_What, n_Jobnum From Zlobsoletedatadeal Where 名称 = 名称_In;
      If n_Jobnum = 0 Then
        Dbms_Job.Submit(n_Jobnum, v_What || ';', Trunc(Sysdate) + 1 + 1 / 24, 'sysdate + 1');
        Update Zlobsoletedatadeal
        Set 操作人员 = 操作人员_In, 操作时间 = 操作时间_In, 保留天数 = 保留天数_In, 是否定期处理 = 是否定期处理_In, 作业号 = n_Jobnum
        Where 名称 = 名称_In;
      End If;
    Else
      --关闭定期处理，将对应存储过程从自动作业中删除
      Select Nvl(作业号, 0) Into n_Jobnum From Zlobsoletedatadeal Where 名称 = 名称_In;
      If n_Jobnum <> 0 Then
        Dbms_Job.Remove(n_Jobnum);
        Update Zlobsoletedatadeal
        Set 操作人员 = 操作人员_In, 操作时间 = 操作时间_In, 保留天数 = 保留天数_In, 是否定期处理 = 是否定期处理_In, 作业号 = Null
        Where 名称 = 名称_In;
      End If;
    End If;
  Else
    Update Zlobsoletedatadeal
    Set 操作人员 = 操作人员_In, 操作时间 = 操作时间_In, 保留天数 = 保留天数_In
    Where 名称 = 名称_In;
  End If;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Zlobsoletedatadeal_Update;
/

