  --功能：存储BLOb图片
  Procedure Zllobappend
  (
    Tab_In In Number,
    Key_In In Varchar2,
    Txt_In In Varchar2,
    Cls_In In Number := 0
    --参数说明：
    --Tab_In：包含LOB的数据表
    --        0-zlUnitInfoImage
    --Key_In：数据记录的关键字
    --Txt_In：16进制的文件片段或文字片段
    --Cls_In：是否清除原来的内容，第一片段传递时为1，以后为0
  ) Is
    l_Blob Blob;
  Begin
    If Tab_In = 0 Then
      If Txt_In Is Null And Cls_In = 1 Then
        Delete Zlunitinfoimage
        Where 项目 = Substr(Key_In, 1, Instr(Key_In, ',') - 1) And
              Nvl(站点, '空空') = Nvl(Substr(Key_In, Instr(Key_In, ',') + 1), '空空');
      Else
        If Cls_In = 1 Then
          Update Zlunitinfoimage
          Set 图片 = Empty_Blob()
          Where 项目 = Substr(Key_In, 1, Instr(Key_In, ',') - 1) And
                Nvl(站点, '空空') = Nvl(Substr(Key_In, Instr(Key_In, ',') + 1), '空空');
          If Sql%Rowcount = 0 Then
            Insert Into Zlunitinfoimage
              (项目, 图片, 站点)
            Values
              (Substr(Key_In, 1, Instr(Key_In, ',') - 1), Empty_Blob(), Substr(Key_In, Instr(Key_In, ',') + 1));
          End If;
        End If;
        Select 图片
        Into l_Blob
        From Zlunitinfoimage
        Where 项目 = Substr(Key_In, 1, Instr(Key_In, ',') - 1) And
              Nvl(站点, '空空') = Nvl(Substr(Key_In, Instr(Key_In, ',') + 1), '空空')
        For Update;
      End If;
    End If;
    If Tab_In = 0 And Txt_In Is Null And Cls_In = 1 Then
      Null;
    Else
      Dbms_Lob.Writeappend(l_Blob, Length(Hextoraw(Txt_In)) / 2, Hextoraw(Txt_In));
    End If;
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End Zllobappend;
  --功能：读取BLOb图片
  Function Zllobread
  (
    Tab_In In Number,
    Key_In In Varchar2,
    Pos_In In Number --参数说明：
    --Tab_In：包含LOB的数据表
    --        0-zlUnitInfoImage
    --Key_In：数据记录的关键字
    --Pos_In：从0开始不断读取，直到返回为空
  ) Return Varchar2 Is
    l_Blob   Blob;
    v_Buffer Varchar2(32767);
    n_Amount Number := 2000;
    n_Offset Number := 1;
  Begin
    If Tab_In = 0 Then
      Select 图片
      Into l_Blob
      From Zlunitinfoimage
      Where 项目 = Substr(Key_In, 1, Instr(Key_In, ',') - 1) And
            Nvl(站点, '空空') = Nvl(Substr(Key_In, Instr(Key_In, ',') + 1), '空空');
    End If;
    n_Offset := n_Offset + Pos_In * n_Amount;
    If l_Blob Is Null Then
      v_Buffer := Null;
    Else
      Dbms_Lob.Read(l_Blob, n_Amount, n_Offset, v_Buffer);
    End If;
    Return v_Buffer;
  Exception
    When No_Data_Found Then
      Return Null;
  End Zllobread;

  Procedure Zlreginfoupdate
  (
    项目_In In Zlreginfo.项目%Type,
    行号_In In Zlreginfo.行号%Type,
    内容_In In Zlreginfo.内容%Type,
    站点_In In Zlreginfo.站点%Type
  ) Is
    --项目_In：项目名称
    --行号_In:为空或者为1时先删除该项目，然后插入
    --内容_In:为空则不插入
  Begin
    If Nvl(行号_In, 0) < 2 Then
      Delete Zlreginfo Where 项目 = 项目_In And Nvl(站点, '空空') = Nvl(站点_In, '空空');
    End If;
    If Not 内容_In Is Null Then
      Insert Into Zlreginfo (项目, 行号, 内容, 站点) Values (项目_In, 行号_In, 内容_In, 站点_In);
    End If;
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End Zlreginfoupdate;
  Procedure Zlunitinfoitemchange
  (
    Type_n  In Number,
    编码_In In Zlunitinfoitem.编码%Type,
    名称_In In Zlunitinfoitem.名称%Type := Null,
    图片_In In Zlunitinfoitem.是否图片%Type := Null
  ) Is
    --Type_n:0-新增，1-修改,2-删除
    --编码_In：项目编码
    --名称_In:项目名称
    --图片_In:项目是否是图片类型
    v_名称 Zlunitinfoitem.名称%Type;
    n_图片 Zlunitinfoitem.是否图片%Type;
  Begin
    If Type_n = 0 Then
      Insert Into Zlunitinfoitem (编码, 名称, 是否图片) Values (编码_In, 名称_In, 图片_In);
    Elsif Type_n = 1 Then
      Select Nvl(名称, 0), Nvl(是否图片, 0) Into v_名称, n_图片 From Zlunitinfoitem Where 编码 = 编码_In;
      --存在该项目
      If Not n_图片 Is Null Then
        --类型变更，删除所有数据
        If n_图片 <> Nvl(图片_In, 0) Then
          If n_图片 = 0 Then
            Delete Zlreginfo Where 项目 = v_名称;
          Else
            Delete Zlunitinfoimage Where 项目 = v_名称;
          End If;
          --名称变更
        Elsif v_名称 <> Nvl(名称_In, '空空') Then
          If n_图片 = 0 Then
            Update Zlreginfo Set 项目 = 名称_In Where 项目 = v_名称;
          Else
            Update Zlunitinfoimage Set 项目 = 名称_In Where 项目 = v_名称;
          End If;
        End If;
        Update Zlunitinfoitem Set 名称 = 名称_In, 是否图片 = 图片_In Where 编码 = 编码_In;
      End If;
    Else
      Select Nvl(名称, 0), Nvl(是否图片, 0) Into v_名称, n_图片 From Zlunitinfoitem Where 编码 = 编码_In;
      --存在该项目
      If Not n_图片 Is Null Then
        If n_图片 = 0 Then
          Delete Zlreginfo Where 项目 = v_名称;
        Else
          Delete Zlunitinfoimage Where 项目 = v_名称;
        End If;
        Delete Zlunitinfoitem Where 编码 = 编码_In;
      End If;
    End If;
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End Zlunitinfoitemchange;
End b_Public;
/

Create Or Replace Package Body b_Runmana Is

  --功能：取参数信息
  --frmParameters
  Procedure Get_Parameters
  (
    Cursor_Out Out t_Refcur,
    系统_In    In Number := 0
  ) Is
  Begin
    If Nvl(系统_In, 0) = 0 Then
      Open Cursor_Out For
        Select a.Id, Nvl(a.系统, 0) 系统, Nvl(a.模块, 0) 模块, Nvl(a.私有, 0) 私有, a.参数号, a.参数名, a.参数值, a.缺省值, Nvl(a.性质, 0) 性质,
               a.影响控制说明, a.参数值含义, a.关联说明, a.适用说明, a.警告说明, Nvl(a.本机, 0) 本机, Nvl(a.授权, 0) 授权, Nvl(a.固定, 0) 固定,
               Nvl(a.部门, 0) 部门, b.标题 As 模块名称, zlSpellCode(b.标题) As 模块简码
        From zlParameters A, zlPrograms B
        Where Nvl(a.系统, 0) = 0 And Nvl(a.系统, 0) = b.系统(+) And Nvl(a.模块, 0) = b.序号(+);
    Else
      Open Cursor_Out For
        Select a.Id, Nvl(a.系统, 0) 系统, Nvl(a.模块, 0) 模块, Nvl(a.私有, 0) 私有, a.参数号, a.参数名, a.参数值, a.缺省值, Nvl(a.性质, 0) 性质,
               a.影响控制说明, a.参数值含义, a.关联说明, a.适用说明, a.警告说明, Nvl(a.本机, 0) 本机, Nvl(a.授权, 0) 授权, Nvl(a.固定, 0) 固定,
               Nvl(a.部门, 0) 部门, b.标题 As 模块名称, zlSpellCode(b.标题) As 模块简码
        From zlParameters A, zlPrograms B,
             --处理权限部分，只有授权的才能显示
             (Select Distinct f.序号
               From zlProgFuncs F, zlRegFunc R
               Where Trunc(f.系统 / 100) = r.系统(+) And f.序号 = r.序号(+) And f.功能 = r.功能(+) And
                     (r.功能 Is Not Null Or r.功能 Is Null And (f.序号 Between 10000 And 19999)) And f.系统 = 系统_In And
                     1 = (Select 1 From Zlregaudit A Where a.项目 = '授权证章')
