  (
    Cur_Out   Out t_Refcur,
    方案号_In In Zlclientparaset.方案号%Type,
    类型_In   In Number := 0
  ) Is
  Begin
    If 类型_In = 0 Then
      Open Cur_Out For
        Select Distinct a.工作站 || Decode(m.工作站, Null, ' ', '[' || m.Ip || ']') As 工作站, a.用户名, a.恢复标志,
                        '[' || b.方案号 || ']' || b.方案名称 As 方案名称
