        Where a.方案号 = b.方案号 And a.工作站 = m.工作站(+) And a.方案号 = 方案号_In;
    End If;
  
    If 类型_In = 1 Then
      Open Cur_Out For
        Select Distinct Upper(工作站) 工作站, Min(恢复标志) 恢复标志
        From Zlclientparaset A
        Where a.方案号 = 方案号_In
