Create Or Replace Package Body Zlbase64 Is
  Function Clob2blob
  (
    Input_In   In Clob,
    Charset_In Number := 0
  ) Return Blob Is
    n_Maxlen   Number(18);
    n_Sizeb    Number(10) := 6000; --6000字符读取，因为32000/4=8000,Utf8最多四个字节
    n_Offset   Number(18) := 1;
    Lr_Buffer  Raw(24000);
    Output_Out Blob;
  Begin
    Dbms_Lob.Createtemporary(Output_Out, True);
    n_Maxlen := Dbms_Lob.Getlength(Input_In);
    If n_Maxlen > 0 Then
      While True Loop
        If Charset_In = 0 Then
          Lr_Buffer := Utl_Raw.Cast_To_Raw(Dbms_Lob.Substr(Input_In, n_Sizeb, n_Offset));
        Else
          Lr_Buffer := Utl_Raw.Convert(Utl_Raw.Cast_To_Raw(Dbms_Lob.Substr(Input_In, n_Sizeb, n_Offset)),
                                       'AMERICAN_AMERICA.AL32UTF8', 'AMERICAN_AMERICA.ZHS16GBK');
        End If;
        If Utl_Raw.Length(Lr_Buffer) > 0 Then
          Dbms_Lob.Writeappend(Output_Out, Utl_Raw.Length(Lr_Buffer), Lr_Buffer);
        End If;
        n_Offset := n_Offset + n_Sizeb;
        Exit When n_Offset > n_Maxlen;
      End Loop;
    End If;
    Return Output_Out;
  End Clob2blob;

  Function Blob2clob
  (
    Input_In   In Blob,
    Charset_In Number := 0
  ) Return Clob Is
    n_Maxlen   Number(18);
    n_Sizeb    Number(10) := 24000;
    n_Offset   Number(18) := 1;
    v_Buffer   Varchar2(24000);
    Output_Out Clob;
  Begin
    Dbms_Lob.Createtemporary(Output_Out, True);
    n_Maxlen := Dbms_Lob.Getlength(Input_In);
  
    If n_Maxlen > 0 Then
      While True Loop
        If Charset_In = 0 Then
          v_Buffer := Utl_Raw.Cast_To_Varchar2(Dbms_Lob.Substr(Input_In, n_Sizeb, n_Offset));
        Else
          v_Buffer := Convert(Utl_Raw.Cast_To_Varchar2(Dbms_Lob.Substr(Input_In, n_Sizeb, n_Offset)), 'ZHS16GBK',
                              'AL32UTF8');
        End If;
        Dbms_Lob.Writeappend(Output_Out, Length(v_Buffer), v_Buffer);
        n_Offset := n_Offset + n_Sizeb;
        Exit When n_Offset > n_Maxlen;
      End Loop;
    End If;
    Return Output_Out;
  End Blob2clob;

  Function Encode
  (
    Input_In   In Varchar2,
    Charset_In Number := 0
  ) Return Varchar2 Is
  Begin
    If Input_In Is Null Then
      Return Null;
    End If;
  
    If Charset_In = 0 Then
      Return Utl_Raw.Cast_To_Varchar2(Utl_Encode.Base64_Encode(Utl_Raw.Cast_To_Raw(Input_In)));
    Else
      Return Utl_Raw.Cast_To_Varchar2(Utl_Encode.Base64_Encode(Utl_Raw.Convert(Utl_Raw.Cast_To_Raw(Input_In),
                                                                               'AMERICAN_AMERICA.AL32UTF8',
                                                                               'AMERICAN_AMERICA.ZHS16GBK')));
    End If;
  End Encode;

  Function Encode(Input_In In Blob) Return Clob Is
    n_Sizeb    Number(10) := 21000; --3的倍数,超过该值会导致无法读取
    Lr_Buffer  Raw(21000);
    n_Offset   Number(10) := 1;
    Output_Out Clob;
  Begin
    Dbms_Lob.Createtemporary(Output_Out, True);
  
    If Dbms_Lob.Getlength(Input_In) > 0 Then
      Loop
        Begin
          Dbms_Lob.Read(Input_In, n_Sizeb, n_Offset, Lr_Buffer);
        Exception
          When No_Data_Found Then
            Exit;
        End;
        n_Offset := n_Offset + n_Sizeb;
        Dbms_Lob.Append(Output_Out, To_Clob(Utl_Raw.Cast_To_Varchar2(Utl_Encode.Base64_Encode(Lr_Buffer))));
      End Loop;
    End If;
    Return Output_Out;
  End Encode;

  Function Encode
  (
    Input_In   In Clob,
    Charset_In Number := 0
  ) Return Clob Is
    Lb_Tmp     Blob;
    Output_Out Clob;
  Begin
    Dbms_Lob.Createtemporary(Lb_Tmp, False);
    Lb_Tmp     := Clob2blob(Input_In, Charset_In);
    Output_Out := Encode(Lb_Tmp);
    Dbms_Lob.Freetemporary(Lb_Tmp);
    Return Output_Out;
  End;

  Function Decode
  (
    Input_In   In Varchar2,
    Charset_In Number := 0
  ) Return Varchar2 Is
  Begin  
    If Input_In Is Null Then
      Return Null;
    End If;
  
    If Charset_In = 0 Then
    
      Return Utl_Raw.Cast_To_Varchar2(Utl_Encode.Base64_Decode(Utl_Raw.Cast_To_Raw(Input_In)));
    Else
      Return Utl_Raw.Cast_To_Varchar2(Utl_Raw.Convert(Utl_Encode.Base64_Decode(Utl_Raw.Cast_To_Raw(Input_In)),
                                                      'AMERICAN_AMERICA.ZHS16GBK', 'AMERICAN_AMERICA.AL32UTF8'));
    End If;
  End Decode;

  Function Decode(Input_In In Clob) Return Blob Is
    n_Maxlen   Number(18);
    n_Sizeb    Number(10) := 21000; --4的倍数
    Lr_Buffer  Raw(21000);
    v_Buffer   Varchar2(21000);
    n_Offset   Number(10) := 1;
    Output_Out Blob;
  Begin
    Dbms_Lob.Createtemporary(Output_Out, True);
    n_Maxlen := Dbms_Lob.Getlength(Input_In);  
    If n_Maxlen > 0 Then    
      While True Loop
        --用Dbms_Lob.Substr读取会导致错误
        Dbms_Lob.Read(Input_In, n_Sizeb, n_Offset, v_Buffer);
        Lr_Buffer := Utl_Encode.Base64_Decode(Utl_Raw.Cast_To_Raw(v_Buffer));
        If Utl_Raw.Length(Lr_Buffer) > 0 Then
          Dbms_Lob.Writeappend(Output_Out, Utl_Raw.Length(Lr_Buffer), Lr_Buffer);
        End If;
        n_Offset := n_Offset + n_Sizeb;
        Exit When n_Offset > n_Maxlen;
      End Loop;
    End If;
    Return Output_Out;
  End Decode;

  Function Decode
  (
    Input_In   In Clob,
    Charset_In Number
  ) Return Clob Is
    Lb_Tmp     Blob;
    Output_Out Clob;
  Begin
    Dbms_Lob.Createtemporary(Output_Out, True);
    Dbms_Lob.Createtemporary(Lb_Tmp, False);
    Lb_Tmp     := Decode(Input_In);
    Output_Out := Blob2clob(Lb_Tmp, Charset_In);
    Dbms_Lob.Freetemporary(Lb_Tmp);
    Return Output_Out;
  End Decode;
End zlBase64;
/

