Insert Into zlParameters
  (ID, 系统, 模块, 私有, 本机, 授权, 固定, 部门, 性质, 参数号, 参数名, 参数值, 缺省值, 影响控制说明, 参数值含义, 关联说明, 适用说明, 警告说明)
  Select Zlparameters_Id.Nextval, &n_System, 1261, 1, 1, 0, 0, 0, 0, 42, '启用语音提示', Null, '1',
         '住院医生站消息列表中的消息是否启用语音提醒。', '0－不启用，1－启用。', Null, '只有能在消息列表中显示出来的消息才会被播报。', Null
