--95740:胡俊勇,2016-05-04,医嘱申请单修改         
Insert Into Zlparameters
  (Id, 系统, 模块, 私有, 本机, 授权, 固定, 部门, 性质, 参数号, 参数名, 参数值, 缺省值, 影响控制说明, 参数值含义, 关联说明, 适用说明, 警告说明)
  Select Zlparameters_Id.Nextval, &n_System, -null, -null, -null, -null, -null, 0, 0, 260, '启用申请单后必须使用申请单下达医嘱', '11', '11',
         '在医嘱编辑界面开医嘱时必须使用申请单。', '0-未启用;1-启用', '用两位表示第1位表示门诊，第2位表示住院。与(申请单启用环节)参数配合使用。',null, Null
  From Dual;

--95740:胡俊勇,2016-05-04,医嘱申请单修改   
Declare
  v_Old  Varchar2(100);
  v_New  Varchar2(100);
  v_Temp Varchar2(100);
Begin
  Select Max(参数值) Into v_Old From zlParameters Where 参数名 = '申请单启用环节';
  If Instr(v_Old, '|') = 0 And v_Old Is Not Null Then
    v_Temp := Substr(v_Old, 1, 1);
    If v_Temp = '1' Then
      v_New := '11';
    Else
      v_New := '00';
    End If;
    v_Temp := Substr(v_Old, 2, 1);
    If v_Temp = '1' Then
      v_New := v_New || '|11';
    Else
      v_New := v_New || '|00';
    End If;
    v_Temp := Substr(v_Old, 3, 1);
    If v_Temp = '1' Then
      v_New := v_New || '|11';
    Else
      v_New := v_New || '|00';
    End If;
    v_Temp := Substr(v_Old, 4, 1);
    If v_Temp = '1' Then
      v_New := v_New || '|11';
    Else
      v_New := v_New || '|00';
    End If;
    v_Temp := Substr(v_Old, 5, 1);
    If v_Temp = '1' Then
      v_New := v_New || '|1';
    Else
      v_New := v_New || '|0';
    End If;
  Elsif Length(v_Old) = 13 Then
    v_New := v_Old;
  Elsif Length(v_Old) = 8 Then
    v_New := v_Old || '|11|1';
  Else
    v_New := Null;
  End If;
  Update zlParameters
  Set 参数值 = v_New, 缺省值 = '11|11|11|11|1', 影响控制说明 = '控制申请单启用环节，如果启用对应的申请单，则通过申请单方式下达医嘱。',
      参数值含义 = '依次是：检查|检验|输血|手术|会诊；门诊住院分开控制，参数格式－(11|11|11|11|1)竖线分割两位一个组合，第1位表示门诊，第2位表示住院，其中会诊申请单仅住院才有。',
      适用说明 = '因为申请单中包含一些明细信息，以前的医嘱下达方式不能填写，为医院的管理要求，可控制某些申请必须通过申请单方式下达此参数和(启用申请单后必须使用申请单下达医嘱)参数配合使用；'
  Where 参数名 = '申请单启用环节';
End;
/

