    Open Val For
      Select a.设备号, a.ip地址, a.ftp用户名, a.ftp密码 From 影像设备目录 a, 影像流程参数 b
      Where a.设备号 = b.参数值 And b.参数名 = '存储设备号' And b.科室id=科室ID_In;
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End p_GetFtpinfoByDeptId;
  
  --功能：根据医嘱ID获取FTP信息
