        Select 1
        Into n_Exists
        From 临床出诊序号控制
        Where 工作站名称 = v_机器名 And 操作员姓名 = v_操作员姓名 And 挂号状态 = 5 And (序号 = n_号序 Or 备注 = n_号序) And 记录id = n_记录id And
