      Exception
        When Others Then
          n_Exists := 0;
      End;
      If n_Exists = 1 Then
        Update 临床出诊序号控制
        Set 挂号状态 = 0
        Where 工作站名称 = v_机器名 And 操作员姓名 = v_操作员姓名 And 挂号状态 = 5 And (序号 = n_号序 Or 备注 = n_号序) And 记录id = n_记录id;
