    If n_操作类型 = 1 Then
      --锁号
      If n_号序 Is Null Then
        Select 是否序号控制, 是否分时段 Into n_序号控制, n_分时段 From 临床出诊记录 Where ID = n_记录id;
        Begin
          Select 1
          Into n_启用合作单位
          From 临床出诊挂号控制记录
          Where 记录id = n_记录id And 名称 = v_合作单位 And 类型 = 1 And 性质 = 1 And Rownum < 2;
        Exception
          When Others Then
            n_启用合作单位 := 0;
        End;
        If n_序号控制 = 1 Then
          If n_分时段 = 1 Then
            Begin
              Select 1, 挂号状态, 操作员姓名, 工作站名称
              Into n_存在, n_状态, v_验证姓名, v_验证机器名
              From 临床出诊序号控制
              Where 记录id = n_记录id And To_Char(开始时间, 'hh24:mi') = To_Char(d_日期, 'hh24:mi') And Nvl(挂号状态, 0) <> 0 And
                    Rownum < 2;
            Exception
              When Others Then
                n_存在 := 0;
            End;
          Else
            n_存在 := 0;
          End If;
          If n_存在 = 1 Then
            If n_状态 = 5 And v_验证姓名 = v_操作员姓名 And v_机器名 = v_验证机器名 Then
              Null;
            Else
              --传入时间的序号已经被使用
              v_Temp := '传入时间' || d_日期 || '的序号已被使用';
              Raise Err_Item;
            End If;
          Else
            If n_分时段 = 1 Then
              Begin
                Select 序号
                Into n_序号
                From 临床出诊序号控制
                Where 记录id = n_记录id And To_Char(开始时间, 'hh24:mi') = To_Char(d_日期, 'hh24:mi') And Nvl(挂号状态, 0) = 0 And
                      Rownum < 2;
              Exception
                When Others Then
                  Select Max(序号) + 1 Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id;
              End;
              Update 临床出诊序号控制
              Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
              Where 记录id = n_记录id And 序号 = n_序号;
              If Sql%RowCount = 0 Then
                Insert Into 临床出诊序号控制
                  (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称)
                  Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                         To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5, Sysdate,
                         v_合作单位, 1, v_操作员姓名, v_机器名
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And Rownum < 2;
              End If;
              v_Temp := '<HX>' || n_序号 || '</HX>';
              Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
            Else
              If v_合作单位 Is Null Or n_启用合作单位 = 0 Then
                --非合作单位
                Select Min(序号) Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id And Nvl(挂号状态, 0) = 0;
                If n_序号 = 0 Then
                  Select Max(序号) + 1 Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id;
                End If;
                Update 临床出诊序号控制
                Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
                Where 记录id = n_记录id And 序号 = n_序号;
                If Sql%RowCount = 0 Then
                  Insert Into 临床出诊序号控制
                    (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称)
                    Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                           To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5,
                           Sysdate, v_合作单位, 1, v_操作员姓名, v_机器名
                    From 临床出诊序号控制
                    Where 记录id = n_记录id And Rownum < 2;
                End If;
                v_Temp := '<HX>' || n_序号 || '</HX>';
                Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
              Else
                --合作单位
                Begin
                  Select 控制方式
                  Into n_合约模式
                  From 临床出诊挂号控制记录
                  Where 记录id = n_记录id And 类型 = 1 And 性质 = 1 And 名称 = v_合作单位 And Rownum < 2;
                Exception
                  When Others Then
                    n_合约模式 := 4;
                End;
                If n_合约模式 = 0 Then
                  v_Temp := '本号别禁止该合作单位预约!';
                  Raise Err_Item;
                End If;
                If n_合约模式 = 1 Or n_合约模式 = 2 Or n_合约模式 = 4 Then
                  Select Min(序号) Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id And Nvl(挂号状态, 0) = 0;
                  If n_序号 = 0 Then
                    Select Max(序号) + 1 Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id;
                  End If;
                End If;
                If n_合约模式 = 3 Then
                  Select Min(a.序号)
                  Into n_序号
                  From 临床出诊序号控制 A, 临床出诊挂号控制记录 B
                  Where a.记录id = n_记录id And a.记录id = b.记录id And b.类型 = 1 And b.性质 = 1 And b.名称 = v_合作单位 And a.序号 = b.序号 And
                        Nvl(a.挂号状态, 0) = 0;
                  If n_序号 = 0 Then
                    v_Temp := '本号别合作单位可预约序号已经全部使用完!';
                    Raise Err_Item;
                  End If;
                End If;
                Update 临床出诊序号控制
                Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
                Where 记录id = n_记录id And 序号 = n_序号;
                If Sql%RowCount = 0 Then
                  Insert Into 临床出诊序号控制
                    (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称)
                    Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                           To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5,
                           Sysdate, v_合作单位, 1, v_操作员姓名, v_机器名
                    From 临床出诊序号控制
                    Where 记录id = n_记录id And Rownum < 2;
                End If;
                v_Temp := '<HX>' || n_序号 || '</HX>';
                Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
              End If;
            End If;
          End If;
        Else
          --非序号控制
          If n_分时段 = 1 Then
            If v_合作单位 Is Null Or n_启用合作单位 = 0 Then
              Begin
                Select 序号, 数量
                Into n_号序, n_数量
                From 临床出诊序号控制
                Where 记录id = n_记录id And 预约顺序号 Is Null And 开始时间 = d_日期;
                Select Count(1)
                Into n_Exists
                From 临床出诊序号控制
                Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) <> 0;
                If n_Exists >= n_数量 Then
                  v_Temp := '本号别可用序号已经全部使用完!';
                  Raise Err_Item;
                Else
                  Select Min(预约顺序号)
                  Into n_顺序号
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) = 0;
                  If n_顺序号 = 0 Then
                    Select Max(预约顺序号) + 1
                    Into n_顺序号
                    From 临床出诊序号控制
                    Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序;
                  End If;
                  Update 临床出诊序号控制
                  Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
                  Where 记录id = n_记录id And 序号 = n_序号 And 预约顺序号 = n_顺序号;
                  If Sql%RowCount = 0 Then
                    Insert Into 临床出诊序号控制
                      (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称, 预约顺序号)
                      Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                             To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5,
                             Sysdate, v_合作单位, 1, v_操作员姓名, v_机器名, n_顺序号
                      From 临床出诊序号控制
                      Where 记录id = n_记录id And Rownum < 2;
                  End If;
                  v_Temp := '<HX>' || n_号序 || '</HX>';
                  Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
                End If;
              Exception
                When Others Then
                  Null;
              End;
            Else
              --合作单位
              Begin
                Select 控制方式
                Into n_合约模式
                From 临床出诊挂号控制记录
                Where 记录id = n_记录id And 类型 = 1 And 性质 = 1 And 名称 = v_合作单位 And Rownum < 2;
              Exception
                When Others Then
                  n_合约模式 := 4;
              End;
              If n_合约模式 = 0 Then
                v_Temp := '本号别禁止该合作单位预约!';
                Raise Err_Item;
              End If;
              If n_合约模式 = 1 Or n_合约模式 = 2 Or n_合约模式 = 4 Then
                Begin
                  Select 序号, 数量
                  Into n_号序, n_数量
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And 预约顺序号 Is Null And 开始时间 = d_日期;
                  Select Count(1)
                  Into n_Exists
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) <> 0;
                  If n_Exists >= n_数量 Then
                    v_Temp := '本号别可用序号已经全部使用完!';
                    Raise Err_Item;
                  Else
                    Select Min(预约顺序号)
                    Into n_顺序号
                    From 临床出诊序号控制
                    Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) = 0;
                    If n_顺序号 = 0 Then
                      Select Max(预约顺序号) + 1
                      Into n_顺序号
                      From 临床出诊序号控制
                      Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序;
                    End If;
                    Update 临床出诊序号控制
                    Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
                    Where 记录id = n_记录id And 序号 = n_序号 And 预约顺序号 = n_顺序号;
                    If Sql%RowCount = 0 Then
                      Insert Into 临床出诊序号控制
                        (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称, 预约顺序号)
                        Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                               To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5,
                               Sysdate, v_合作单位, 1, v_操作员姓名, v_机器名, n_顺序号
                        From 临床出诊序号控制
                        Where 记录id = n_记录id And Rownum < 2;
                    End If;
                    v_Temp := '<HX>' || n_号序 || '</HX>';
                    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
                  End If;
                Exception
                  When Others Then
                    Null;
                End;
              End If;
              If n_合约模式 = 3 Then
                Begin
                  Select 序号
                  Into n_号序
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And 预约顺序号 Is Null And 开始时间 = d_日期;
                  Select 数量
                  Into n_数量
                  From 临床出诊挂号控制记录
                  Where 记录id = n_记录id And 类型 = 1 And 性质 = 1 And 名称 = v_合作单位 And 序号 = n_号序;
                  Select Count(1)
                  Into n_Exists
                  From 临床出诊序号控制
                  Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) <> 0;
                  If n_Exists >= n_数量 Then
                    v_Temp := '本号别可用序号已经全部使用完!';
                    Raise Err_Item;
                  Else
                    Select Min(预约顺序号)
                    Into n_顺序号
                    From 临床出诊序号控制
                    Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序 And Nvl(挂号状态, 0) = 0;
                    If n_顺序号 = 0 Then
                      Select Max(预约顺序号) + 1
                      Into n_顺序号
                      From 临床出诊序号控制
                      Where 记录id = n_记录id And 预约顺序号 Is Not Null And 序号 = n_号序;
                    End If;
                    Update 临床出诊序号控制
                    Set 挂号状态 = 5, 锁号时间 = Sysdate, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名
                    Where 记录id = n_记录id And 序号 = n_序号 And 预约顺序号 = n_顺序号;
                    If Sql%RowCount = 0 Then
                      Insert Into 临床出诊序号控制
                        (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称, 预约顺序号)
                        Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                               To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5,
                               Sysdate, v_合作单位, 1, v_操作员姓名, v_机器名, n_顺序号
                        From 临床出诊序号控制
                        Where 记录id = n_记录id And Rownum < 2;
                    End If;
                    v_Temp := '<HX>' || n_号序 || '</HX>';
                    Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
                  End If;
                Exception
                  When Others Then
                    Null;
                End;
              End If;
            End If;
          End If;
        End If;
      Else
        n_序号 := n_号序;
        Update 临床出诊序号控制
        Set 挂号状态 = 5, 操作员姓名 = v_操作员姓名, 工作站名称 = v_机器名, 锁号时间 = Sysdate
        Where 记录id = n_记录id And 序号 = n_序号;
        If Sql%RowCount = 0 Then
          Begin
            Insert Into 临床出诊序号控制
              (记录id, 序号, 开始时间, 终止时间, 数量, 是否预约, 挂号状态, 锁号时间, 名称, 类型, 操作员姓名, 工作站名称)
              Select 记录id, n_序号, To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')),
                     To_Date(To_Char(开始时间, 'yyyy-mm-dd') || ' ' || To_Char(d_日期, 'hh24:mi:ss')), 1, 是否预约, 5, Sysdate,
                     v_合作单位, 1, v_操作员姓名, v_机器名
              From 临床出诊序号控制
              Where 记录id = n_记录id And Rownum < 2;
          Exception
            When Others Then
              v_Temp := '传入的锁号序号已被使用!';
              Raise Err_Item;
          End;
        End If;
      End If;
    End If;
  End If;
  Xml_Out := x_Templet;
Exception
  When Err_Item Then
    v_Temp := '[ZLSOFT]' || v_Temp || '[ZLSOFT]';
    Raise_Application_Error(-20101, v_Temp);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Third_Lockno;
/

Create Or Replace Procedure Zl_Third_Getdeptlist
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is
  --------------------------------------------------------------------------------------------------
  --功能:获取可挂号科室

  --入参:Xml_In:
  --<IN>
  --  <CXTS>14</CXTS>        //查询天数
  --  <HZDW>支付宝</HZDW>    //合作单位
  --  <ZD></ZD>              //站点
  --</IN>

  --出参:Xml_Out
  --<OUTPUT>
  -- <KSLIST>
  --  <KS>
  --    <ID>科室ID</ID>       //科室ID
  --    <MC>科室名称</MC>     //科室名称
  --    <ZDYYTS>最大可预约天数</ZDYYTS>     //最大可预约天数
  --  </KS>
  --  <KS>
  --    ...
  --  </KS>
  -- </KSLIST>
  --</OUTPUT>
  --------------------------------------------------------------------------------------------------
  v_Temp      Varchar(5000); --临时XML
  x_Templet   Xmltype; --模板XML
  v_Para      Varchar2(4000);
  n_查询天数  Number(5);
  n_预约天数  Number(5);
  n_Add_Lists Number(3);
  v_合作单位  合作单位安排控制.合作单位%Type;
  n_站点      部门表.站点%Type;
  v_Err_Msg   Varchar2(200);
  d_启用时间  Date;
  Err_Item Exception;
  n_挂号模式 Number(3);
Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');
  Select Extractvalue(Value(A), 'IN/CXTS'), Extractvalue(Value(A), 'IN/HZDW'), Extractvalue(Value(A), 'IN/ZD')
  Into n_查询天数, v_合作单位, n_站点
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;
  v_Para     := zl_GetSysParameter('挂号排班模式');
  n_预约天数 := zl_GetSysParameter(66);
  n_挂号模式 := To_Number(Substr(v_Para, 1, 1));
  If n_挂号模式 = 1 Then
    Begin
      d_启用时间 := To_Date(Substr(v_Para, 3), 'yyyy-mm-dd hh24:mi:ss');
    Exception
      When Others Then
        d_启用时间 := Null;
    End;
  End If;
  If n_挂号模式 = 0 Then
    If n_查询天数 Is Null Then
      If v_合作单位 Is Null Then
        For r_Dept In (Select Distinct a.科室id, b.名称, Max(Nvl(a.预约天数, n_预约天数)) As 预约天数
                       From 挂号安排 A, 部门表 B
                       Where a.停用日期 Is Null And a.科室id = b.Id And (b.站点 = Nvl(n_站点, 0) Or b.站点 Is Null)
                       Group By a.科室id, b.名称) Loop
          If Nvl(n_Add_Lists, 0) = 0 Then
            --增加DJList节点
            Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype('<KSLIST></KSLIST>')) Into x_Templet From Dual;
            n_Add_Lists := 1;
          End If;
          v_Temp := '<KS>' || '<ID>' || r_Dept.科室id || '</ID>' || '<MC>' || r_Dept.名称 || '</MC>' || '<ZDYYTS>' ||
                    r_Dept.预约天数 || '</ZDYYTS>' || '</KS>';
          Select Appendchildxml(x_Templet, '/OUTPUT/KSLIST', Xmltype(v_Temp)) Into x_Templet From Dual;
        End Loop;
      Else
        For r_Dept In (Select 科室id, 名称, Max(预约天数) As 预约天数
                       From (Select b.科室id, d.名称, Max(a.预约天数) As 预约天数
                              From (Select a.Id, Nvl(a.预约天数, n_预约天数) As 预约天数
                                     From 挂号安排 A
                                     Where a.停用日期 Is Null And Not Exists
