--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_未审药品记录_Update
(
  状态_In     In Number,
  单据_In     In 未审药品记录.单据%Type,
  No_In       In 未审药品记录.No%Type,
  库房id_In   In 未审药品记录.库房id%Type,
  填制日期_In In 未审药品记录.填制日期%Type := Null
) Is
  --状态_in 0-新增 1-删除
  n_库房id 药品收发记录.库房ID%type;
Begin
  If 状态_In = 0 Then
    Begin
      Select 库房id
      Into n_库房id
      From 未审药品记录
      Where 单据 = 单据_In And NO = No_In And 库房id = 库房id_In And Rownum < 2;
    Exception
      When Others Then
        n_库房id := 0;
    End;
    if n_库房id=0 then
        Insert Into 未审药品记录 (单据, NO, 库房id, 填制日期) Values (单据_In, No_In, 库房id_In, 填制日期_In);
    end if;
  Else
    Delete From 未审药品记录 Where 单据 = 单据_In And NO = No_In And 库房id = 库房id_In;
  End If;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品申领_Insert
(
  No_In           In 药品收发记录.No%Type,
  序号_In         In 药品收发记录.序号%Type,
  库房id_In       In 药品收发记录.库房id%Type,
  对方部门id_In   In 药品收发记录.对方部门id%Type,
  药品id_In       In 药品收发记录.药品id%Type,
  批次_In         In 药品收发记录.批次%Type,
  填写数量_In     In 药品收发记录.填写数量%Type,
  实际数量_In     In 药品收发记录.实际数量%Type,
  成本价_In       In 药品收发记录.成本价%Type,
  成本金额_In     In 药品收发记录.成本金额%Type,
  零售价_In       In 药品收发记录.零售价%Type,
  零售金额_In     In 药品收发记录.零售金额%Type,
  差价_In         In 药品收发记录.差价%Type,
  填制人_In       In 药品收发记录.填制人%Type,
  产地_In         In 药品收发记录.产地%Type := Null,
  批号_In         In 药品收发记录.批号%Type := Null,
  效期_In         In 药品收发记录.效期%Type := Null,
  摘要_In         In 药品收发记录.摘要%Type := Null,
  填制日期_In     In 药品收发记录.填制日期%Type := Null,
  上次供应商id_In In 药品收发记录.供药单位id%Type := Null,
  批准文号_In     In 药品收发记录.批准文号%Type := Null,
  申领方式_In     In 药品收发记录.单量%Type := 0,
  结束时间_In     In 药品收发记录.频次%Type := Null
) Is
  v_Lngid        药品收发记录.Id%Type; --收发ID 
  n_出库收发id   药品收发记录.Id%Type; --出库库房收发id 
  v_入的类别id   药品收发记录.入出类别id%Type; --入出类别ID 
  v_出的类别id   药品收发记录.入出类别id%Type; --入出类别ID 
  d_上次生产日期 药品库存.上次生产日期%Type;
Begin
  --首先找出入和出的类别ID 
  Select b.Id 
  Into v_入的类别id 
  From 药品单据性质 A, 药品入出类别 B 
  Where a.类别id = b.Id And a.单据 = 6 And b.系数 = 1 And Rownum < 2; 
  
  Select b.Id 
  Into v_出的类别id 
  From 药品单据性质 A, 药品入出类别 B 
  Where a.类别id = b.Id And a.单据 = 6 And b.系数 = -1 And Rownum < 2; 

  Select 药品收发记录_Id.Nextval Into v_Lngid From Dual;

  Begin
    Select 上次生产日期
    Into d_上次生产日期
    From 药品库存
    Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And Nvl(批次, 0) = Nvl(批次_In, 0);
  Exception
    When Others Then
      d_上次生产日期 := Null;
  End;

  Select 药品收发记录_Id.Nextval Into n_出库收发id From Dual;
  --插入类别为出的那一笔 
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要,
     填制人, 填制日期, 发药方式, 供药单位id, 批准文号, 生产日期, 单量, 频次)
  Values
    (n_出库收发id, 1, 6, No_In, 序号_In, 库房id_In, 对方部门id_In, v_出的类别id, -1, 药品id_In, 批次_In, 产地_In, 批号_In, 效期_In, 填写数量_In,
     实际数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 1, 上次供应商id_In, 批准文号_In, d_上次生产日期, 申领方式_In,
     结束时间_In);

  --插入未审药品记录
  Zl_未审药品记录_Update(0, 6, No_In, 库房id_In, 填制日期_In);

  --处理库存
  Zl_药品库存_Update(n_出库收发id, 0);

  --插入类别为入的那一笔 
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要,
     填制人, 填制日期, 发药方式, 供药单位id, 批准文号, 生产日期, 单量, 频次)
  Values
    (v_Lngid, 1, 6, No_In, 序号_In + 1, 对方部门id_In, 库房id_In, v_入的类别id, 1, 药品id_In, 批次_In, 产地_In, 批号_In, 效期_In, 填写数量_In,
     实际数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 1, 上次供应商id_In, 批准文号_In, d_上次生产日期, 申领方式_In,
     结束时间_In);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品申领_Insert;
/

--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品申领_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;
Begin
  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 6 And 记录状态 = 1 And 入出系数 = -1 And 审核人 Is Null;
  Exception
    When Others Then
      n_库房id := 0;
  End;

  --删除未审药品记录
  If n_库房id <> 0 Then
    Zl_未审药品记录_Update(1, 6, No_In, n_库房id);
  End If;

  For r_Detail In (Select Id
                   From 药品收发记录
                   Where 单据 = 6 And 记录状态 = 1 And 审核人 Is Null And NO = No_In
                   Order By 药品id) Loop
    --处理库存
    Zl_药品库存_Update(r_Detail.Id, 1);
  End Loop;

  Delete 药品收发记录 Where NO = No_In And 单据 = 6 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品申领_Delete;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品移库_Insert
(
  No_In           In 药品收发记录.No%Type,
  序号_In         In 药品收发记录.序号%Type,
  库房id_In       In 药品收发记录.库房id%Type,
  对方部门id_In   In 药品收发记录.对方部门id%Type,
  药品id_In       In 药品收发记录.药品id%Type,
  批次_In         In 药品收发记录.批次%Type,
  填写数量_In     In 药品收发记录.填写数量%Type,
  实际数量_In     In 药品收发记录.实际数量%Type,
  成本价_In       In 药品收发记录.成本价%Type,
  成本金额_In     In 药品收发记录.成本金额%Type,
  零售价_In       In 药品收发记录.零售价%Type,
  零售金额_In     In 药品收发记录.零售金额%Type,
  差价_In         In 药品收发记录.差价%Type,
  填制人_In       In 药品收发记录.填制人%Type,
  产地_In         In 药品收发记录.产地%Type := Null,
  批号_In         In 药品收发记录.批号%Type := Null,
  效期_In         In 药品收发记录.效期%Type := Null,
  摘要_In         In 药品收发记录.摘要%Type := Null,
  填制日期_In     In 药品收发记录.填制日期%Type := Null,
  上次供应商id_In In 药品收发记录.供药单位id%Type := Null,
  批准文号_In     In 药品收发记录.批准文号%Type := Null,
  申领方式_In     In 药品收发记录.单量%Type := Null,
  结束时间_In     In 药品收发记录.频次%Type := Null
) Is
  v_Lngid        药品收发记录.Id%Type; --收发ID 
  n_出库收发id   药品收发记录.Id%Type; --出库库房收发id 
  v_入的类别id   药品收发记录.入出类别id%Type; --入出类别ID 
  v_出的类别id   药品收发记录.入出类别id%Type; --入出类别ID 
  v_批次         药品收发记录.批次%Type := Null; --主要针对入库中实行药库分批的药品 
  v_是否分批     Integer; --判断入库是否药库分批   1:分批；0：不分批 
  v_药库分批     Integer; --判断入库是否药库分批   1:分批；0：不分批 
  v_药房分批     Integer; --判断入库是否药库分批   1:分批；0：不分批 
  d_上次生产日期 药品库存.上次生产日期%Type;
Begin
  Select b.Id
  Into v_入的类别id
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 6 And b.系数 = 1 And Rownum < 2;
  
  Select b.Id
  Into v_出的类别id
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 6 And b.系数 = -1 And Rownum < 2;

  Begin
    Select 上次生产日期
    Into d_上次生产日期
    From 药品库存
    Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And Nvl(批次, 0) = Nvl(批次_In, 0);
  Exception
    When Others Then
      d_上次生产日期 := Null;
  End;
  Select 药品收发记录_Id.Nextval Into n_出库收发id From Dual;
  
  --产生出的那笔
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要,
     填制人, 填制日期, 供药单位id, 批准文号, 生产日期, 单量, 频次)
  Values
    (n_出库收发id, 1, 6, No_In, 序号_In, 库房id_In, 对方部门id_In, v_出的类别id, -1, 药品id_In, 批次_In, 产地_In, 批号_In, 效期_In, 填写数量_In,
     实际数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 上次供应商id_In, 批准文号_In, d_上次生产日期, 申领方式_In,
     结束时间_In);
     
  --插入未审药品记录
  zl_未审药品记录_update(0,6,No_In,库房id_In,填制日期_In);
  
  --处理库存
  Zl_药品库存_Update(n_出库收发id, 0);
  
  Select Nvl(药库分批, 0), Nvl(药房分批, 0) Into v_药库分批, v_药房分批 From 药品规格 Where 药品id = 药品id_In;

  v_是否分批 := 0;
  If v_药房分批 = 0 Then
    If v_药库分批 = 1 Then
      Begin
        Select Distinct 0
        Into v_是否分批
        From 部门性质说明
        Where ((工作性质 Like '%药房') Or (工作性质 Like '制剂室')) And 部门id = 对方部门id_In;
      Exception
        When Others Then
          v_是否分批 := 1;
      End;
    End If;
  Else
    v_是否分批 := 1;
  End If;

  Select 药品收发记录_Id.Nextval Into v_Lngid From Dual;

  If v_是否分批 = 1 And Nvl(批次_In, 0) = 0 Then
    --入库分批且出库不分批 
    v_批次 := Zl_Fun_Getbatchnum(药品id_In, 产地_In, 批号_In, 成本价_In, 零售价_In, v_Lngid);
  Elsif v_是否分批 = 0 Then
    --入库不分批 
    v_批次 := 0;
  Elsif Nvl(批次_In, 0) <> 0 Then
    --入库分批且出库也分批 
    v_批次 := 批次_In;
  End If;
  
  --产生入的那笔
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要,
     填制人, 填制日期, 供药单位id, 批准文号, 生产日期, 单量, 频次)
  Values
    (v_Lngid, 1, 6, No_In, 序号_In + 1, 对方部门id_In, 库房id_In, v_入的类别id, 1, 药品id_In, v_批次, 产地_In, 批号_In, 效期_In, 填写数量_In,
     实际数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 上次供应商id_In, 批准文号_In, d_上次生产日期, 申领方式_In,
     结束时间_In);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品移库_Insert;
/

--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品移库_Delete
(
  No_In       In 药品收发记录.No%Type,
  记录状态_In In 药品收发记录.记录状态%Type := 1
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 6 Order By 药品id;

  Cursor c_申请冲销记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 6 And 记录状态 = 记录状态_In Order By 药品id;
Begin
  If 记录状态_In = 1 Then
    --处理未审核移库单据
    --通过循环，恢复原来的可用数量
    For v_药品收发记录 In c_药品收发记录 Loop
      Zl_药品库存_Update(v_药品收发记录.Id, 1);
    End Loop;
  Else
    --处理移库申请冲销单据
    --通过循环，恢复原来的可用数量
    For v_申请冲销记录 In c_申请冲销记录 Loop
      Zl_药品库存_Update(v_申请冲销记录.Id, 1);
    End Loop;
  End If;

  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 6 And 记录状态 = 记录状态_In And 入出系数 = -1;
  Exception
    When Others Then
      n_库房id := 0;
  End;

  --删除未审药品记录
  Zl_未审药品记录_Update(1, 6, No_In, n_库房id);

  --删除未审核单据 
  Delete From 药品收发记录 Where NO = No_In And 单据 = 6 And 记录状态 = 记录状态_In And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品移库_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_药品移库_Verify
(
  序号_In         In 药品收发记录.序号%Type,
  库房id_In       In 药品收发记录.库房id%Type,
  对方部门id_In   In 药品收发记录.对方部门id%Type,
  药品id_In       In 药品收发记录.药品id%Type,
  产地_In         In 药品收发记录.产地%Type,
  出批次_In       In 药品收发记录.批次%Type,
  实际数量_In     In 药品收发记录.实际数量%Type,
  成本价_In       In 药品收发记录.成本价%Type,
  成本金额_In     In 药品收发记录.成本金额%Type,
  零售金额_In     In 药品收发记录.零售金额%Type,
  差价_In         In 药品收发记录.差价%Type,
  No_In           In 药品收发记录.No%Type,
  审核人_In       In 药品收发记录.审核人%Type,
  批号_In         In 药品收发记录.批号%Type := Null,
  效期_In         In 药品收发记录.效期%Type := Null,
  审核日期_In     In 药品收发记录.审核日期%Type := Null,
  上次供应商id_In In 药品收发记录.供药单位id%Type := Null,
  批准文号_In     In 药品收发记录.批准文号%Type := Null,
  零售价_In       In 药品收发记录.零售价%Type := Null
) Is
  Err_Isverified Exception;
  Err_Isnonumber Exception;
  Err_Isbatch Exception;
  Err_Isprice Exception;
  v_Druginf  Varchar2(50); --原不分批现在分批的药品信息 
  v_实际数量 药品库存.实际数量%Type;
  v_编码     收费项目目录.编码%Type;
  Intdigit   Number;
  v_上次扣率 药品库存.上次扣率%Type;
  Cursor c_药品收发记录 Is
    Select ID, 入出系数
    From 药品收发记录
    Where NO = No_In And 单据 = 6 And 药品id = 药品id_In And 记录状态 = 1 And 序号 In (序号_In, 序号_In + 1) And 审核日期 Is Not Null;
Begin
  --获取金额小数位数 
  Select Nvl(精度, 2) Into Intdigit From 药品卫材精度 Where 性质 = 0 And 类别 = 1 And 内容 = 4 And 单位 = 5;

  --主要针对原不分批现在分批的药品，不能对其审核 
  --仅检查入类别 
  Begin
    Select Distinct '(' || i.编码 || ')' || Nvl(n.名称, i.名称) As 药品信息
    Into v_Druginf
    From 药品收发记录 A, 药品规格 B, 收费项目目录 I, 收费项目别名 N
    Where a.药品id = b.药品id And a.药品id = i.Id And a.药品id = n.收费细目id(+) And n.性质(+) = 3 And a.No = No_In And a.单据 = 6 And
          a.记录状态 = 1 And Nvl(a.批次, 0) = 0 And a.药品id + 0 = 药品id_In And a.序号 = 序号_In + 1 And
          ((Nvl(b.药库分批, 0) = 1 And
          a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室'))) Or Nvl(b.药房分批, 0) = 1) And
          Rownum = 1;
  Exception
    When Others Then
      v_Druginf := '';
  End;

  If v_Druginf Is Not Null Then
    Raise Err_Isbatch;
  End If;

  Begin
    Select Nvl(实际数量, 0), Nvl(上次扣率, 100)
    Into v_实际数量, v_上次扣率
    From 药品库存
    Where 药品id = 药品id_In And Nvl(批次, 0) = 出批次_In And 库房id = 库房id_In And 性质 = 1 And Rownum = 1;
  Exception
    When Others Then
      v_实际数量 := 0;
      v_上次扣率 := 100;
  End;

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = 审核日期_In, 实际数量 = 实际数量_In, 成本价 = 成本价_In, 成本金额 = 成本金额_In, 零售价 = 零售价_In, 零售金额 = 零售金额_In,
      差价 = 差价_In, 扣率 = v_上次扣率
  Where NO = No_In And 单据 = 6 And 药品id = 药品id_In And 记录状态 = 1 And 序号 In (序号_In, 序号_In + 1) And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --更新入出库库存
  For v_药品收发记录 In c_药品收发记录 Loop
    If v_药品收发记录.入出系数 = 1 Then
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 0);
    Else
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
    End If;
  End Loop;
 
 --删除未审药品记录
  zl_未审药品记录_update(1,6,No_In,库房id_In);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Err_Isnonumber Then
    Select 编码 Into v_编码 From 收费项目目录 Where ID = 药品id_In;
    Raise_Application_Error(-20101,
                            '[ZLSOFT]编码为' || v_编码 || ',批号为' || 批号_In || '的药库分批药品' || Chr(10) || Chr(13) ||
                             '可用库存数量不够！[ZLSOFT]');
  When Err_Isbatch Then
    Raise_Application_Error(-20102, '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的药品[' || v_Druginf || ']，不能审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品移库_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品移库_Strike
(
  行次_In       In Integer,
  原记录状态_In In 药品收发记录.记录状态%Type,
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  冲销数量_In   In 药品收发记录.实际数量%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  冲销方式_In   In Integer := 0 --0－正常冲销方式；1－产生冲销申请单据；2－审核已产生的冲销申请单据
) Is
  Err_Isstriked Exception;
  Err_Isoutstock Exception;
  Err_Isnonum Exception;
  Err_Isbatch Exception;
  v_Druginf      Varchar2(50); --原不分批现在分批的药品信息
  v_库房id       药品收发记录.库房id%Type;
  v_批次         药品收发记录.批次%Type;
  v_成本价       药品收发记录.成本价%Type;
  v_成本金额     药品收发记录.成本金额%Type;
  v_零售价       药品收发记录.零售价%Type;
  v_零售金额     药品收发记录.零售金额%Type;
  v_差价         药品收发记录.差价%Type;
  v_剩余数量     药品收发记录.实际数量%Type;
  v_剩余成本金额 药品收发记录.成本金额%Type;
  v_剩余零售金额 药品收发记录.零售金额%Type;
  v_收发id       药品收发记录.Id%Type;
  v_批准文号     药品收发记录.批准文号%Type;

  v_药库分批 Integer;
  v_药房分批 Integer;
  Intdigit   Number;

  Cursor c_药品收发记录 Is
    Select a.Id, a.序号, a.库房id, a.对方部门id, a.入出类别id, a.入出系数, a.药品id, a.批次, a.产地, a.批号, a.效期, a.配药人, a.配药日期, a.摘要, a.供药单位id,
           a.批准文号, a.生产日期, a.成本价, a.零售价, Nvl(b.是否变价, 0) As 时价, a.扣率, a.单量, a.频次
    From 药品收发记录 A, 收费项目目录 B
    Where a.药品id = b.Id And a.No = No_In And a.单据 = 6 And (a.序号 >= 序号_In And a.序号 <= 序号_In + 1) And
          (a.记录状态 = 1 Or Mod(a.记录状态, 3) = 0)
    Order By a.药品id;

  Cursor c_冲销申请记录 Is
    Select a.Id, a.序号, a.库房id, a.对方部门id, a.入出类别id, a.入出系数, a.药品id, a.批次, a.产地, a.批号, a.效期, a.配药人, a.配药日期, a.摘要, a.供药单位id,
           a.批准文号, a.生产日期, a.成本价, a.实际数量, a.零售金额, a.差价, a.零售价, Nvl(b.是否变价, 0) As 时价, a.扣率, a.单量, a.频次
    From 药品收发记录 A, 收费项目目录 B
    Where a.药品id = b.Id And a.No = No_In And a.单据 = 6 And (a.序号 >= 序号_In And a.序号 <= 序号_In + 1) And
          (a.记录状态 = 原记录状态_In And Mod(a.记录状态, 3) = 2) And a.审核日期 Is Null
    Order By a.药品id;
Begin
  --获取金额小数位数
  Select Nvl(精度, 2) Into Intdigit From 药品卫材精度 Where 性质 = 0 And 类别 = 1 And 内容 = 4 And 单位 = 5;

  If 冲销方式_In = 1 Then
    --产生冲销申请单据，不填写审核人、审核日期，不更新库存记录
    If 行次_In = 1 Then
      Update 药品收发记录
      Set 记录状态 = Decode(原记录状态_In, 1, 3, 原记录状态_In + 3)
      Where NO = No_In And 单据 = 6 And 记录状态 = 原记录状态_In;
      If Sql%RowCount = 0 Then
        Raise Err_Isstriked;
      End If;
    End If;
  
    --主要针对原不分批现在分批的药品，不能对其冲销
    Begin
      Select Distinct '(' || i.编码 || ')' || Nvl(n.名称, i.名称) As 药品信息
      Into v_Druginf
      From 药品收发记录 A, 药品规格 B, 收费项目目录 I, 收费项目别名 N
      Where a.药品id = b.药品id And a.药品id = i.Id And a.药品id = n.收费细目id(+) And n.性质(+) = 3 And a.No = No_In And a.单据 = 6 And
            a.药品id + 0 = 药品id_In And Mod(a.记录状态, 3) = 0 And Nvl(a.批次, 0) = 0 And a.序号 = 序号_In And
            ((Nvl(b.药库分批, 0) = 1 And
            a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室'))) Or
            Nvl(b.药房分批, 0) = 1) And Rownum = 1;
    Exception
      When Others Then
        v_Druginf := '';
    End;
  
    If v_Druginf Is Not Null Then
      Raise Err_Isbatch;
    End If;
  
    Select Sum(a.实际数量) As 剩余数量, Sum(a.成本金额) As 剩余成本金额, Sum(a.零售金额) As 剩余零售金额, a.成本价, a.零售价, a.对方部门id, Nvl(a.批次, 0),
           b.药库分批, b.药房分批, a.批准文号
    Into v_剩余数量, v_剩余成本金额, v_剩余零售金额, v_成本价, v_零售价, v_库房id, v_批次, v_药库分批, v_药房分批, v_批准文号
    From 药品收发记录 A, 药品规格 B
    Where a.No = No_In And a.药品id = b.药品id And a.单据 = 6 And a.药品id = 药品id_In And a.序号 = 序号_In
    Group By a.成本价, a.零售价, a.对方部门id, Nvl(a.批次, 0), b.药库分批, b.药房分批, a.批准文号;
  
    --V_分批:(原分批,现分批为批次;否则为零)原不分批,现分批,本过程不考虑
    --因为对于对方库房，相当于出库，而对于当前库房，相当于入库，所以当前库房不予检查，仅检查退库那条记录
    Select Nvl(a.批次, 0)
    Into v_批次
    From 药品收发记录 A
    Where a.No = No_In And a.单据 = 6 And a.药品id = 药品id_In And a.序号 = 序号_In + 1 And Mod(a.记录状态, 3) = 0;
  
    --冲销数量大于剩余数量，不允许
    If v_剩余数量 < 冲销数量_In Then
      Raise Err_Isnonum;
    End If;
  
    v_成本金额 := Round(冲销数量_In / v_剩余数量 * v_剩余成本金额, Intdigit);
    v_零售金额 := Round(冲销数量_In / v_剩余数量 * v_剩余零售金额, Intdigit);
    v_差价     := v_零售金额 - v_成本金额;
  
    For v_药品收发记录 In c_药品收发记录 Loop
    
      Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价,
         摘要, 填制人, 填制日期, 审核人, 审核日期, 配药人, 配药日期, 供药单位id, 批准文号, 生产日期, 扣率, 单量, 频次)
      Values
        (v_收发id, Decode(原记录状态_In, 1, 2, 原记录状态_In + 2), 6, No_In, v_药品收发记录.序号, v_药品收发记录.库房id, v_药品收发记录.对方部门id,
         v_药品收发记录.入出类别id, v_药品收发记录.入出系数, 药品id_In, v_药品收发记录.批次, v_药品收发记录.产地, v_药品收发记录.批号, v_药品收发记录.效期, -冲销数量_In, -冲销数量_In,
         v_成本价, -v_成本金额, v_零售价, -v_零售金额, -v_差价, 摘要_In, 填制人_In, 填制日期_In, Null, Null, v_药品收发记录.配药人, v_药品收发记录.配药日期,
         v_药品收发记录.供药单位id, v_药品收发记录.批准文号, v_药品收发记录.生产日期, v_药品收发记录.扣率, v_药品收发记录.单量, v_药品收发记录.频次);
    
      --处理库存，原入的那笔相当于出库
      If v_药品收发记录.入出系数 = 1 Then
        Zl_药品库存_Update(v_收发id, 0, 1);
      End If;
    
      If v_药品收发记录.入出系数 = -1 Then
        v_库房id := v_药品收发记录.库房id;
      End If;
    End Loop;
    --插入未审药品记录
    Zl_未审药品记录_Update(0, 6, No_In, v_库房id, 填制日期_In);
  
  Elsif 冲销方式_In = 2 Then
    --审核已产生的冲销申请单据，填写审核人、审核日期，更新库存记录
    For v_药品收发记录 In c_冲销申请记录 Loop
      --填写审核人、审核日期
      Update 药品收发记录
      Set 审核人 = 填制人_In, 审核日期 = 填制日期_In
      Where NO = No_In And 单据 = 6 And ID = v_药品收发记录.Id;
    
      --更改药品库存表的相应数据，注意这时传入的数量等是负数
      --参数为1表示申请冲销时下可用数量，仅对原移入库房，下了可用数量就不用再更新可用数量了
      If v_药品收发记录.入出系数 = 1 Then
        Zl_药品库存_Update(v_药品收发记录.Id, 3, 1);
      Else
        Zl_药品库存_Update(v_药品收发记录.Id, 3, 0);
      End If;
    
      If v_药品收发记录.入出系数 = -1 Then
        v_库房id := v_药品收发记录.库房id;
      
        --删除未审药品记录
        Zl_未审药品记录_Update(1, 6, No_In, v_库房id);
      End If;
    
      --处理调价后冲销
      Zl_药品收发记录_调价修正(v_药品收发记录.Id);
    End Loop;
  Else
    --正常冲销方式，产生冲销记录，填写审核人、审核日期，更新库存记录
    If 行次_In = 1 Then
      Update 药品收发记录
      Set 记录状态 = Decode(原记录状态_In, 1, 3, 原记录状态_In + 3)
      Where NO = No_In And 单据 = 6 And 记录状态 = 原记录状态_In;
      If Sql%RowCount = 0 Then
        Raise Err_Isstriked;
      End If;
    End If;
  
    --主要针对原不分批现在分批的药品，不能对其冲销
    Begin
      Select Distinct '(' || i.编码 || ')' || Nvl(n.名称, i.名称) As 药品信息
      Into v_Druginf
      From 药品收发记录 A, 药品规格 B, 收费项目目录 I, 收费项目别名 N
      Where a.药品id = b.药品id And a.药品id = i.Id And a.药品id = n.收费细目id(+) And n.性质(+) = 3 And a.No = No_In And a.单据 = 6 And
            a.药品id + 0 = 药品id_In And Mod(a.记录状态, 3) = 0 And Nvl(a.批次, 0) = 0 And a.序号 = 序号_In And
            ((Nvl(b.药库分批, 0) = 1 And
            a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室'))) Or
            Nvl(b.药房分批, 0) = 1) And Rownum = 1;
    Exception
      When Others Then
        v_Druginf := '';
    End;
  
    If v_Druginf Is Not Null Then
      Raise Err_Isbatch;
    End If;
  
    Select Sum(a.实际数量) As 剩余数量, Sum(a.成本金额) As 剩余成本金额, Sum(a.零售金额) As 剩余零售金额, a.成本价, a.零售价, a.对方部门id, Nvl(a.批次, 0),
           b.药库分批, b.药房分批, a.批准文号
    Into v_剩余数量, v_剩余成本金额, v_剩余零售金额, v_成本价, v_零售价, v_库房id, v_批次, v_药库分批, v_药房分批, v_批准文号
    From 药品收发记录 A, 药品规格 B
    Where a.No = No_In And a.药品id = b.药品id And a.单据 = 6 And a.药品id = 药品id_In And a.序号 = 序号_In
    Group By a.成本价, a.零售价, a.对方部门id, Nvl(a.批次, 0), b.药库分批, b.药房分批, a.批准文号;
  
    --V_分批:(原分批,现分批为批次;否则为零)原不分批,现分批,本过程不考虑
    --因为对于对方库房，相当于出库，而对于当前库房，相当于入库，所以当前库房不予检查，仅检查退库那条记录
    Select Nvl(a.批次, 0)
    Into v_批次
    From 药品收发记录 A
    Where a.No = No_In And a.单据 = 6 And a.药品id = 药品id_In And a.序号 = 序号_In + 1 And Mod(a.记录状态, 3) = 0;
  
    --冲销数量大于剩余数量，不允许
    If v_剩余数量 < 冲销数量_In Then
      Raise Err_Isnonum;
    End If;
  
    v_成本金额 := Round(冲销数量_In / v_剩余数量 * v_剩余成本金额, Intdigit);
    v_零售金额 := Round(冲销数量_In / v_剩余数量 * v_剩余零售金额, Intdigit);
    v_差价     := v_零售金额 - v_成本金额;
  
    For v_药品收发记录 In c_药品收发记录 Loop
      Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价,
         摘要, 填制人, 填制日期, 审核人, 审核日期, 配药人, 配药日期, 供药单位id, 批准文号, 生产日期, 扣率, 单量, 频次)
      Values
        (v_收发id, Decode(原记录状态_In, 1, 2, 原记录状态_In + 2), 6, No_In, v_药品收发记录.序号, v_药品收发记录.库房id, v_药品收发记录.对方部门id,
         v_药品收发记录.入出类别id, v_药品收发记录.入出系数, 药品id_In, v_药品收发记录.批次, v_药品收发记录.产地, v_药品收发记录.批号, v_药品收发记录.效期, -冲销数量_In, -冲销数量_In,
         v_成本价, -v_成本金额, v_零售价, -v_零售金额, -v_差价, 摘要_In, 填制人_In, 填制日期_In, 填制人_In, 填制日期_In, v_药品收发记录.配药人, v_药品收发记录.配药日期,
         v_药品收发记录.供药单位id, v_药品收发记录.批准文号, v_药品收发记录.生产日期, v_药品收发记录.扣率, v_药品收发记录.单量, v_药品收发记录.频次);
    
      --更改药品库存表的相应数据
      If v_药品收发记录.入出系数 = 1 Then
        Zl_药品库存_Update(v_收发id, 3, 1);
      Else
        Zl_药品库存_Update(v_收发id, 3, 0);
      End If;
    
      --处理调价后冲销
      Zl_药品收发记录_调价修正(v_收发id);
    End Loop;
  End If;
Exception
  When Err_Isstriked Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人冲销！[ZLSOFT]');
  When Err_Isbatch Then
    Raise_Application_Error(-20102, '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的药品[' || v_Druginf || ']，不能冲销！[ZLSOFT]');
  When Err_Isnonum Then
    Raise_Application_Error(-20103, '[ZLSOFT]该单据中第' || Ceil(序号_In / 2) || '行的药品冲销的数量大于了剩余的数据，不能冲销！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品移库_Strike;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品外购_Insert
(
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  对方部门id_In In 药品收发记录.对方部门id%Type,
  供药单位id_In In 药品收发记录.供药单位id%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  实际数量_In   In 药品收发记录.实际数量%Type := Null,
  成本价_In     In 药品收发记录.成本价%Type := Null,
  成本金额_In   In 药品收发记录.成本金额%Type := Null,
  扣率_In       In 药品收发记录.扣率%Type := Null,
  零售价_In     In 药品收发记录.零售价%Type := Null,
  零售金额_In   In 药品收发记录.零售金额%Type := Null,
  差价_In       In 药品收发记录.差价%Type := Null,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  填制人_In     In 药品收发记录.填制人%Type := Null,
  发票号_In     In 应付记录.发票号%Type := Null,
  发票日期_In   In 应付记录.发票日期%Type := Null,
  发票金额_In   In 应付记录.发票金额%Type := Null,
  填制日期_In   In 药品收发记录.填制日期%Type := Null,
  外观_In       In 药品收发记录.外观%Type := Null,
  产品合格证_In In 药品收发记录.产品合格证%Type := Null,
  核查人_In     In 药品收发记录.配药人%Type := Null,
  核查日期_In   In 药品收发记录.配药日期%Type := Null,
  批次_In       In 药品收发记录.批次%Type := 0,
  退货_In       In Number := 1,
  生产日期_In   In 药品收发记录.生产日期%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null,
  随货单号_In   In 应付记录.随货单号%Type := Null,
  金额差_In     In 药品收发记录.零售金额%Type := Null,
  加成率_In     In 药品收发记录.频次%Type := Null,
  发票代码_In   In 应付记录.发票代码%Type := Null,
  计划id_In     In 药品收发记录.计划id%Type := Null,
  财务审核_In   In Number := 0
) Is
  v_No         应付记录.No%Type; --应付记录的NO
  v_商品名     收费项目目录.名称%Type; --通用名称
  v_规格       收费项目目录.规格%Type;
  v_产地       收费项目目录.规格%Type;
  v_单位       收费项目目录.计算单位%Type;
  v_Lngid      药品收发记录.Id%Type; --收发ID
  v_应付id     应付记录.Id%Type; --应付记录的ID
  v_入出类别id 药品收发记录.入出类别id%Type; --入出类别ID
  v_入出系数   药品收发记录.入出系数%Type; --入出系数
  v_批次       药品收发记录.批次%Type := Null; --批次
  v_药库分批   Integer; --是否药库分批    1:分批；0：不分批
  v_药房分批   Integer; --是否药房分批       1:分批；0：不分批
  v_指导批价   药品规格.指导批发价%Type;
  v_时价分批   Number(1);

  Err_Msg Varchar2(255);
  Err_Noenough Exception;
Begin

  If Not 批准文号_In Is Null And Not 产地_In Is Null Then
    Update 药品生产商对照 Set 批准文号 = 批准文号_In Where 药品id = 药品id_In And 厂家名称 = 产地_In;
  End If;
  If Sql%RowCount = 0 And Not 产地_In Is Null And Not 批准文号_In Is Null Then
    Insert Into 药品生产商对照 (药品id, 厂家名称, 批准文号) Values (药品id_In, 产地_In, 批准文号_In);
  End If;

  --取该药品的商品名
  v_产地 := '';
  Select 名称, 规格, 计算单位, Nvl(是否变价, 0)
  Into v_商品名, v_规格, v_单位, v_时价分批
  From 收费项目目录
  Where ID = 药品id_In;
  If v_规格 Is Not Null Then
    If Instr(v_规格, '|') <> 0 Then
      v_产地 := Substr(v_规格, Instr(v_规格, '|'));
      v_规格 := Substr(v_规格, Instr(v_规格, '|') - 1);
    End If;
  End If;

  Select 药品收发记录_Id.Nextval Into v_Lngid From Dual;
  Select Nvl(药库分批, 0), Nvl(药房分批, 0), Nvl(指导批发价, 0)
  Into v_药库分批, v_药房分批, v_指导批价
  From 药品规格
  Where 药品id = 药品id_In;

  --财务审核_in=0表示普通入库，财务审核_in=1表示是财务审核产生新单据，如果是财务审核模式不需要重新产生批次
  If 财务审核_In = 0 Then
    If v_药房分批 = 0 Then
      If v_药库分批 = 1 Then
        Begin
          Select Distinct 0
          Into v_药库分批
          From 部门性质说明
          Where ((工作性质 Like '%药房') Or (工作性质 Like '制剂室')) And 部门id = 库房id_In;
        Exception
          When Others Then
            v_药库分批 := 1;
        End;
      
        If v_药库分批 = 1 Then
          v_批次 := Zl_Fun_Getbatchnum(药品id_In, 产地_In, 批号_In, 成本价_In, 零售价_In, v_Lngid);
        End If;
      End If;
    Else
      v_批次 := Zl_Fun_Getbatchnum(药品id_In, 产地_In, 批号_In, 成本价_In, 零售价_In, v_Lngid);
    End If;
  Else
    v_批次 := 批次_In;
  End If;

  If v_时价分批 = 1 And v_批次 > 0 Then
    v_时价分批 := 1;
  Else
    v_时价分批 := 0;
  End If;

  Select b.Id, b.系数
  Into v_入出类别id, v_入出系数
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 1 And Rownum < 2;

  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 供药单位id, 入出类别id, 对方部门id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 扣率, 零售价,
     零售金额, 差价, 摘要, 填制人, 填制日期, 配药人, 配药日期, 发药方式, 单量, 外观, 产品合格证, 生产日期, 批准文号, 用法, 频次, 计划id)
  Values
    (v_Lngid, 1, 1, No_In, 序号_In, 库房id_In, 供药单位id_In, v_入出类别id, 对方部门id_In, v_入出系数, 药品id_In,
     Decode(退货_In, -1, 批次_In, v_批次), 产地_In, 批号_In, 效期_In, 退货_In * 实际数量_In, 退货_In * 实际数量_In, 成本价_In, 退货_In * 成本金额_In,
     扣率_In, 零售价_In, 退货_In * 零售金额_In, 退货_In * 差价_In, 摘要_In, 填制人_In, 填制日期_In, 核查人_In, 核查日期_In, Decode(退货_In, -1, 1, 0),
     v_指导批价, 外观_In, 产品合格证_In, 生产日期_In, 批准文号_In, Decode(退货_In, -1, Null, Decode(v_时价分批, 1, 金额差_In, Null)), 加成率_In,
     计划id_In);

  If 退货_In = -1 Then
    --插入未审药品记录
    Zl_未审药品记录_Update(0, 1, No_In, 库房id_In, 填制日期_In);
  End If;

  --调用库存更新过程
  Zl_药品库存_Update(v_Lngid, 0);

  If 发票号_In Is Not Null Or 随货单号_In Is Not Null Then
    --如果是第一笔明细,则产生应付记录的NO
    Begin
      Select NO
      Into v_No
      From 应付记录
      Where 系统标识 = 1 And 记录性质 = 0 And 记录状态 = 1 And 入库单据号 = No_In And Rownum < 2;
    Exception
      When Others Then
        v_No := Nextno(67);
    End;
    Select 应付记录_Id.Nextval Into v_应付id From Dual;
    Insert Into 应付记录
      (ID, 记录性质, 记录状态, 单位id, NO, 系统标识, 收发id, 入库单据号, 单据金额, 发票号, 发票日期, 发票金额, 品名, 规格, 产地, 批号, 计量单位, 数量, 采购价, 采购金额, 填制人,
       填制日期, 审核人, 审核日期, 摘要, 项目id, 序号, 随货单号, 库房id, 发票修改时间, 发票代码)
    Values
      (v_应付id, 0, 1, 供药单位id_In, v_No, 1, v_Lngid, No_In, 退货_In * 零售金额_In, 发票号_In, 发票日期_In,
       退货_In * Decode(Nvl(发票金额_In, 0), 0, 成本金额_In, 发票金额_In), v_商品名, v_规格, v_产地, 批号_In, v_单位, 退货_In * 实际数量_In, 成本价_In,
       退货_In * 成本金额_In, 填制人_In, 填制日期_In, Null, Null, 摘要_In, 药品id_In, 序号_In, 随货单号_In, 库房id_In, Sysdate, 发票代码_In);
  End If;
Exception
  When Err_Noenough Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品外购_Insert;
/

--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品外购_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 1 Order By 序号;
Begin

  --通过循环，恢复原来的可用数量
  For v_药品收发记录 In c_药品收发记录 Loop
    --调用库存更新过程
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  End Loop;

  Begin
    Select Distinct 库房id Into n_库房id From 药品收发记录 Where NO = No_In And 单据 = 1 And Nvl(发药方式, 0) = 1;
  Exception
    When Others Then
      n_库房id := 0;
  End;
  --删除未审药品记录
  If n_库房id > 0 Then
    Zl_未审药品记录_Update(1, 1, No_In, n_库房id);
  End If;

  Delete 应付记录 Where 系统标识 = 1 And 收发id In (Select ID From 药品收发记录 Where NO = No_In And 单据 = 1);

  --对应应付记录的删除通过级联删除
  Delete --删除本身
  From 药品收发记录
  Where NO = No_In And 单据 = 1 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品外购_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_药品外购_Verify
(
  Newno_In    In 药品收发记录.No%Type := Null,
  Oldno_In    In 药品收发记录.No%Type := Null,
  审核人_In   In 药品收发记录.审核人%Type := Null,
  审核日期_In In 药品收发记录.审核日期%Type := Sysdate
) Is
  Err_Isverified Exception;
  Err_Isbatch Exception;
  v_Druginf        Varchar2(50); --原不分批现在分批的药品信息
  v_供药单位id     药品收发记录.供药单位id%Type;
  v_发票金额       应付记录.发票金额%Type;
  v_可用数量       药品库存.可用数量%Type;
  v_时价分批       Number(1);
  n_原成本价       药品收发记录.成本价%Type;
  v_Newno          药品收发记录.No%Type;
  n_New序号        Number;
  n_收发id         药品收发记录.Id%Type;
  n_调整额         药品收发记录.零售金额%Type;
  n_入出类别id     药品收发记录.入出类别id%Type;
  n_售价入出类别id 药品收发记录.入出类别id%Type;
  n_入出系数       药品收发记录.入出系数%Type;
  n_平均成本价     药品库存.平均成本价%Type;
  n_冲销成本价     药品收发记录.成本价%Type;
  n_冲销售价       药品收发记录.零售价%Type;
  v_Billno         药品收发记录.No%Type;

  Cursor c_药品收发记录 Is
    Select a.Id, a.零售价, a.实际数量, a.零售金额, a.差价, a.库房id, a.药品id, a.批次, a.供药单位id, a.成本价, a.批号, a.效期, a.产地, a.入出类别id, a.生产日期,
           a.批准文号, Nvl(b.是否变价, 0) As 时价, Nvl(a.发药方式, 0) As 退库, a.灭菌效期, a.扣率, Nvl(a.计划id, 0) As 计划id,
           Nvl(a.费用id, 0) As 费用id, a.序号
    From 药品收发记录 A, 收费项目目录 B
    Where a.药品id = b.Id And a.No = Newno_In And a.单据 = 1 And a.记录状态 = 1
    Order By a.药品id;
Begin

  n_New序号 := 1;

  Select b.Id, b.系数
  Into n_入出类别id, n_入出系数
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 5 And Rownum < 2;

  Select 类别id Into n_售价入出类别id From 药品单据性质 Where 单据 = 13 And Rownum < 2;

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = 审核日期_In
  Where NO = Newno_In And 单据 = 1 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --主要针对原不分批现在分批的药品，不能对其审核
  Begin
    Select Distinct '(' || i.编码 || ')' || Nvl(n.名称, i.名称) As 药品信息
    Into v_Druginf
    From 药品收发记录 A, 药品规格 B, 收费项目目录 I, 收费项目别名 N
    Where a.药品id = b.药品id And a.药品id = i.Id And a.药品id = n.收费细目id(+) And n.性质(+) = 3 And a.No = Newno_In And a.单据 = 1 And
          a.记录状态 = 1 And Nvl(a.批次, 0) = 0 And
          ((Nvl(b.药库分批, 0) = 1 And
          a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室'))) Or Nvl(b.药房分批, 0) = 1) And
          Rownum = 1;
  Exception
    When Others Then
      v_Druginf := '';
  End;

  If v_Druginf Is Not Null Then
    Raise Err_Isbatch;
  End If;

  --原分批现不分批的药品,在审核时，要处理他
  Update 药品收发记录
  Set 批次 = 0
  Where ID In
        (Select ID
         From 药品收发记录 A, 药品规格 B
         Where b.药品id = a.药品id And a.No = Newno_In And a.单据 = 1 And a.记录状态 = 1 And Nvl(a.批次, 0) > 0 And
               (Nvl(b.药库分批, 0) = 0 Or
               (Nvl(b.药房分批, 0) = 0 And
               a.库房id In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室')))));

  For v_药品收发记录 In c_药品收发记录 Loop
    --处理采购计划表中的执行数量，多次导入采用累加执行数量
    If v_药品收发记录.计划id > 0 Then
      Update 药品计划内容
      Set 执行数量 = Nvl(执行数量, 0) + v_药品收发记录.实际数量
      Where 计划id = v_药品收发记录.计划id And 药品id = v_药品收发记录.药品id;
    End If;
  
    --如果是分批药品退库，取原来的成本价
    If v_药品收发记录.退库 = 1 Then
      Begin
        Select 平均成本价
        Into n_原成本价
        From 药品库存
        Where 性质 = 1 And 库房id = v_药品收发记录.库房id And 药品id = v_药品收发记录.药品id And Nvl(批次, 0) = Nvl(v_药品收发记录.批次, 0);
      Exception
        When Others Then
          n_原成本价 := 0;
      End;
      --调用库存更新记录更新库存表
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
      
      --删除未审药品记录
      zl_未审药品记录_update(1,1,Newno_In,v_药品收发记录.库房id);
    Else
      --调用库存更新记录更新库存表
      If Oldno_In Is Null Then
        Zl_药品库存_Update(v_药品收发记录.Id, 2, 0);
      Else
        --财务审核单据为了保持库存成本价不变，所以用出库方式
        Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
      End If;
    End If;
  
    If v_药品收发记录.时价 = 1 Then
      Update 药品规格 Set 上次售价 = v_药品收发记录.零售价 Where 药品id = v_药品收发记录.药品id;
    End If;
  
    If v_药品收发记录.退库 = 0 Then
      --更新该药品的成本价
      Update 药品规格
      Set 成本价 = v_药品收发记录.成本价, 上次供应商id = v_药品收发记录.供药单位id, 上次批号 = v_药品收发记录.批号, 上次生产日期 = v_药品收发记录.生产日期, 上次产地 = v_药品收发记录.产地,
          上次批准文号 = v_药品收发记录.批准文号
      Where 药品id = v_药品收发记录.药品id;
    End If;
  
    --如果是分批药品退库，则检查成本价是否变动，如果变动，则产生差价调整记录并修正库存差价
    If Oldno_In Is Null Then
      If v_药品收发记录.退库 = 1 Then
        If n_原成本价 <> 0 And n_原成本价 <> v_药品收发记录.成本价 Then
          If v_Newno Is Null Then
            v_Newno := Nextno(25, v_药品收发记录.库房id);
          End If;
        
          --产生库存差价调整单
          Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        
          n_调整额 := (v_药品收发记录.零售金额 - v_药品收发记录.差价) - Round(n_原成本价 * v_药品收发记录.实际数量, 2);
        
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期,
             审核人, 审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率, 灭菌效期, 费用id)
          Values
            (n_收发id, 1, 5, v_Newno, n_New序号, v_药品收发记录.库房id, n_入出类别id, v_药品收发记录.供药单位id, n_入出系数, v_药品收发记录.药品id,
             v_药品收发记录.批次, v_药品收发记录.产地, v_药品收发记录.批号, v_药品收发记录.效期, v_药品收发记录.实际数量, v_药品收发记录.零售金额, v_药品收发记录.差价, n_调整额,
             '外购退库差价误差自动修正', Nvl(审核人_In, Zl_Username), 审核日期_In, Nvl(审核人_In, Zl_Username), 审核日期_In, v_药品收发记录.生产日期,
             v_药品收发记录.批准文号, v_药品收发记录.成本价, 0, n_原成本价, v_药品收发记录.灭菌效期, v_药品收发记录.Id);
        
          n_New序号 := n_New序号 + 1;
          --退库相当于是出库业务
          Zl_药品库存_Update(n_收发id, 2, 1);
        End If;
      End If;
    Else
      --成本价修正
      Select distinct 成本价, 零售价
      Into n_冲销成本价, n_冲销售价
      From 药品收发记录
      Where NO = Oldno_In And 药品id = v_药品收发记录.药品id And Nvl(批次, 0) = Nvl(v_药品收发记录.批次, 0) And 序号 = v_药品收发记录.序号 And 单据 = 1 And
            Mod(记录状态, 3) = 2;
      If n_冲销成本价 <> v_药品收发记录.成本价 Then
        --产生库存差价调整单
        v_Newno := Nextno(25, v_药品收发记录.库房id);
        Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
      
        n_调整额 := Round((v_药品收发记录.成本价 - n_冲销成本价) * v_药品收发记录.实际数量, 2);
      
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期,
           审核人, 审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率, 灭菌效期, 费用id)
        Values
          (n_收发id, 1, 5, v_Newno, n_New序号, v_药品收发记录.库房id, n_入出类别id, v_药品收发记录.供药单位id, n_入出系数, v_药品收发记录.药品id, v_药品收发记录.批次,
           v_药品收发记录.产地, v_药品收发记录.批号, v_药品收发记录.效期, v_药品收发记录.实际数量, v_药品收发记录.零售金额, v_药品收发记录.差价, n_调整额, '财务审核价格变动修正',
           Nvl(审核人_In, Zl_Username), 审核日期_In, Nvl(审核人_In, Zl_Username), 审核日期_In, v_药品收发记录.生产日期, v_药品收发记录.批准文号,
           v_药品收发记录.成本价, 0, n_冲销成本价, v_药品收发记录.灭菌效期, v_药品收发记录.Id);
      
        n_New序号 := n_New序号 + 1;
      
        --更新库存
        Zl_药品库存_Update(n_收发id, 2, 0);
      End If;
    
      --更新售价
      If n_冲销售价 <> v_药品收发记录.零售价 Then
        Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        Select Nextno(147) Into v_Billno From Dual;
      
        n_调整额 := Round((n_冲销售价 - v_药品收发记录.零售价) * v_药品收发记录.实际数量, 2);
      
        --产生调价修正记录
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 扣率, 零售金额, 差价, 摘要, 填制人,
           填制日期, 库房id, 入出系数, 审核人, 审核日期, 费用id)
        Values
          (n_收发id, 1, 13, v_Billno, n_New序号, n_售价入出类别id, v_药品收发记录.药品id, v_药品收发记录.批次, v_药品收发记录.批号, v_药品收发记录.效期,
           v_药品收发记录.产地, 1, v_药品收发记录.实际数量, 0, n_冲销售价, 0, v_药品收发记录.零售价, 0, n_调整额, n_调整额, '财务审核价格变动修正',
           Nvl(审核人_In, Zl_Username), 审核日期_In, v_药品收发记录.库房id, 1, Nvl(审核人_In, Zl_Username), 审核日期_In, v_药品收发记录.Id);
      
        n_New序号 := n_New序号 + 1;
        --更新药品库存
        Zl_药品库存_Update(n_收发id, 2, 0);
      End If;
    End If;
  End Loop;

  --对应付余额表进行处理
  --此处用一个块，主要是解决没有对应发票号的记录
  Begin
    Update 应付记录
    Set 审核人 = 审核人_In, 审核日期 = 审核日期_In
    Where 入库单据号 = Newno_In And 系统标识 = 1 And 记录性质 = 0 And 记录状态 = 1;
  
    Select b.单位id, Sum(发票金额)
    Into v_供药单位id, v_发票金额
    From 药品收发记录 A, 应付记录 B
    Where a.Id = b.收发id And a.No = Newno_In And a.单据 = 1 And b.系统标识 = 1
    Group By b.单位id;
  
    If Nvl(v_供药单位id, 0) <> 0 Then
      Update 应付余额 Set 金额 = Nvl(金额, 0) + Nvl(v_发票金额, 0) Where 单位id = v_供药单位id And 性质 = 1;
    
      If Sql%NotFound Then
        Insert Into 应付余额 (单位id, 性质, 金额) Values (v_供药单位id, 1, v_发票金额);
      End If;
    End If;
  Exception
    When No_Data_Found Then
      Null;
  End;
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Err_Isbatch Then
    Raise_Application_Error(-20102, '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的药品[' || v_Druginf || ']，不能审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品外购_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品领用_Insert
(
  入出类别id_In In 药品收发记录.入出类别id%Type,
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  对方部门id_In In 药品收发记录.对方部门id%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  批次_In       In 药品收发记录.批次%Type,
  填写数量_In   In 药品收发记录.填写数量%Type,
  成本价_In     In 药品收发记录.成本价%Type,
  成本金额_In   In 药品收发记录.成本金额%Type,
  零售价_In     In 药品收发记录.零售价%Type,
  零售金额_In   In 药品收发记录.零售金额%Type,
  差价_In       In 药品收发记录.差价%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  领用人_In     In 药品收发记录.领用人%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null,
  发药方式_In   In 药品收发记录.发药方式%Type := 0
) Is
  v_入出系数 药品收发记录.入出系数%Type; --收发ID 
  Err_Isnonumber Exception;
  v_编码         收费项目目录.编码%Type;
  n_上次供应商id 药品库存.上次供应商id%Type;
  d_上次生产日期 药品库存.上次生产日期%Type;
  n_收发id       药品收发记录.Id%Type;
  v_按月留存领用 Varchar2(4000);
Begin
  v_入出系数 := -1;

  Begin
    Select 上次供应商id, 上次生产日期
    Into n_上次供应商id, d_上次生产日期
    From 药品库存
    Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And Nvl(批次, 0) = Nvl(批次_In, 0);
  Exception
    When Others Then
      n_上次供应商id := Null;
      d_上次生产日期 := Null;
  End;

  Select 药品收发记录_Id.Nextval Into n_收发id From Dual;

  --插入类别为出的那一笔 
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要,
     填制人, 填制日期, 领用人, 批准文号, 发药方式, 供药单位id, 生产日期)
  Values
    (n_收发id, 1, 7, No_In, 序号_In, 库房id_In, 对方部门id_In, 入出类别id_In, v_入出系数, 药品id_In, 批次_In, 产地_In, 批号_In, 效期_In, 填写数量_In,
     填写数量_In, 成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 领用人_In, 批准文号_In, 发药方式_In, n_上次供应商id,
     d_上次生产日期);

  --插入未审药品记录
  Zl_未审药品记录_Update(0, 7, No_In, 库房id_In, 填制日期_In);

  --更新库存数据
  Zl_药品库存_Update(n_收发id, 0);

  If 发药方式_In = 1 Then
    Select zl_GetSysParameter('按月留存领用', 1305) Into v_按月留存领用 From Dual;
  
    --按月向科室留存领用
    Update 药品留存
    Set 可用数量 = Nvl(可用数量, 0) - 填写数量_In
    Where 期间 = To_Char(Sysdate, Decode(v_按月留存领用, '1', 'yyyymm', 'yyyy')) And 科室id = 对方部门id_In And 库房id = 库房id_In And
          药品id = 药品id_In;
  End If;

Exception
  When Err_Isnonumber Then
    Select 编码 Into v_编码 From 收费项目目录 Where ID = 药品id_In;
    Raise_Application_Error(-20101,
                            '[ZLSOFT]编码为' || v_编码 || ',批号为' || 批号_In || '的分批核算药品' || Chr(10) || Chr(13) ||
                             '可用库存数量不够！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品领用_Insert;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品领用_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID, 填写数量, 库房id, 批次, 药品id, 批号, 效期, 产地, 批准文号, 对方部门id, 发药方式
    From 药品收发记录
    Where NO = No_In And 单据 = 7
    Order By 药品id;
  v_按月留存领用 Varchar2(4000);
Begin

  Select zl_GetSysParameter('按月留存领用', 1305) Into v_按月留存领用 From Dual;
  --通过循环，恢复原来的可用数量
  For v_药品收发记录 In c_药品收发记录 Loop
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  
    If v_药品收发记录.发药方式 = 1 Then
      Update 药品留存
      Set 可用数量 = Nvl(可用数量, 0) + v_药品收发记录.填写数量
      Where 期间 = To_Char(Sysdate, Decode(v_按月留存领用, '1', 'yyyymm', 'yyyy')) And 科室id = v_药品收发记录.对方部门id And
            库房id = v_药品收发记录.库房id And 药品id = v_药品收发记录.药品id;
    End If;
  End Loop;

  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 7 And 记录状态 = 1 And 审核人 Is Null;
  Exception
    When Others Then
      n_库房id := 0;
  End;
  Delete From 药品收发记录 Where NO = No_In And 单据 = 7 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
  --插入未审药品记录
  Zl_未审药品记录_Update(1, 7, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品领用_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_药品领用_Verify
(
  序号_In       In 药品收发记录.序号%Type,
  No_In         In 药品收发记录.No%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  对方部门id_In In 药品收发记录.对方部门id%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  产地_In       In 药品收发记录.产地%Type,
  批次_In       In 药品收发记录.批次%Type,
  填写数量_In   In 药品收发记录.填写数量%Type,
  实际数量_In   In 药品收发记录.实际数量%Type,
  成本价_In     In 药品收发记录.成本价%Type,
  成本金额_In   In 药品收发记录.成本金额%Type,
  零售金额_In   In 药品收发记录.零售金额%Type,
  差价_In       In 药品收发记录.差价%Type,
  审核人_In     In 药品收发记录.审核人%Type,
  审核日期_In   In 药品收发记录.审核日期%Type,
  批号_In       In 药品收发记录.批号%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null,
  发药方式_In   In 药品收发记录.发药方式%Type := 0
) Is
  Err_Isverified Exception;
  Err_Isnonumber Exception;
  v_实际数量     药品库存.实际数量%Type;
  v_编码         收费项目目录.编码%Type;
  Intdigit       Number;
  v_下可用数量   药品库存.可用数量%Type;
  v_下可用库存   Zlparameters.参数值%Type;
  n_上次供应商id 药品库存.上次供应商id%Type;
  d_上次生产日期 药品库存.上次生产日期%Type;
  v_上次扣率     药品库存.上次扣率%Type;
  n_平均成本价   药品库存.平均成本价%Type;
  n_收发id       药品收发记录.Id%Type;
  v_按月留存领用 Varchar2(4000);
Begin
  --获取金额小数位数 
  Select Nvl(精度, 2) Into Intdigit From 药品卫材精度 Where 性质 = 0 And 类别 = 1 And 内容 = 4 And 单位 = 5;

  Select zl_GetSysParameter('按月留存领用', 1305) Into v_按月留存领用 From Dual;

  --由于领用处理允许在审核时改变实际数量， 
  --所以首先对实际数量和其他相应的字段进行更新。 
  Begin
    Select Nvl(实际数量, 0), Nvl(上次扣率, 100)
    Into v_实际数量, v_上次扣率
    From 药品库存
    Where 药品id = 药品id_In And Nvl(批次, 0) = 批次_In And 库房id = 库房id_In And 性质 = 1 And Rownum = 1;
  Exception
    When Others Then
      v_实际数量 := 0;
      v_上次扣率 := 100;
  End;

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = 审核日期_In, 实际数量 = 实际数量_In, 成本价 = 成本价_In, 成本金额 = 成本金额_In, 零售金额 = 零售金额_In, 差价 = 差价_In,
      扣率 = v_上次扣率
  Where NO = No_In And 单据 = 7 And 药品id = 药品id_In And 序号 = 序号_In And 记录状态 = 1 And 审核人 Is Null
  Returning ID Into n_收发id;

  --更改药品库存的相应数据 
  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --插入未审药品记录
  Zl_未审药品记录_Update(1, 7, No_In, 库房id_In);

  If 批次_In > 0 Then
    If v_实际数量 + 填写数量_In - 实际数量_In < 0 Then
      Raise Err_Isnonumber;
    End If;
  End If;

  --如果填单时已下可用库存，则根据填写数量和实际数量算出审核时下可用库存；如果填单时没有下可以库存，则审核时根据实际数量下可用库存 
  Zl_药品库存_Update(n_收发id, 2, 1);

  --科室药品留存处理 
  If 发药方式_In = 1 Then
    --按月留存领用 
    Update 药品留存
    Set 可用数量 = Nvl(可用数量, 0) - 实际数量_In, 实际数量 = Nvl(实际数量, 0) - 实际数量_In, 实际金额 = Nvl(实际金额, 0) - 零售金额_In
    Where 期间 = To_Char(Sysdate, Decode(v_按月留存领用, '1', 'yyyymm', 'yyyy')) And 科室id = 对方部门id_In And 库房id = 库房id_In And
          药品id = 药品id_In;
    --将金额和数量等于0的记录删除掉 
    Delete From 药品留存 Where Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0;
  End If;

Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Err_Isnonumber Then
    Select 编码 Into v_编码 From 收费项目目录 Where ID = 药品id_In;
    Raise_Application_Error(-20101,
                            '[ZLSOFT]编码为' || v_编码 || ',批号为' || 批号_In || '的分批核算药品' || Chr(10) || Chr(13) ||
                             '可用库存数量不够！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品领用_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品其他出库_Insert
(
  入出类别id_In In 药品收发记录.入出类别id%Type,
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  批次_In       In 药品收发记录.批次%Type,
  填写数量_In   In 药品收发记录.填写数量%Type,
  成本价_In     In 药品收发记录.成本价%Type,
  成本金额_In   In 药品收发记录.成本金额%Type,
  零售价_In     In 药品收发记录.零售价%Type,
  零售金额_In   In 药品收发记录.零售金额%Type,
  差价_In       In 药品收发记录.差价%Type,
  外调价_In     In 药品收发记录.单量%Type,
  外调单位_In   In 药品收发记录.发药窗口%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null,
  增值税率_In   In Number := Null
) Is
  v_入出系数     药品收发记录.入出系数%Type; --收发ID 
  n_上次供应商id 药品库存.上次供应商id%Type;
  d_上次生产日期 药品库存.上次生产日期%Type;
  n_收发id       药品收发记录.Id%Type;
Begin
  v_入出系数 := -1;

  Begin
    Select 上次供应商id, 上次生产日期
    Into n_上次供应商id, d_上次生产日期
    From 药品库存
    Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And Nvl(批次, 0) = Nvl(批次_In, 0);
  Exception
    When Others Then
      n_上次供应商id := Null;
      d_上次生产日期 := Null;
  End;

  Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要, 填制人,
     填制日期, 单量, 发药窗口, 批准文号, 供药单位id, 生产日期, 频次)
  Values
    (n_收发id, 1, 11, No_In, 序号_In, 库房id_In, 入出类别id_In, v_入出系数, 药品id_In, 批次_In, 产地_In, 批号_In, 效期_In, 填写数量_In, 填写数量_In,
     成本价_In, 成本金额_In, 零售价_In, 零售金额_In, 差价_In, 摘要_In, 填制人_In, 填制日期_In, 外调价_In, 外调单位_In, 批准文号_In, n_上次供应商id, d_上次生产日期,
     增值税率_In);

  --更新库存数据
  Zl_药品库存_Update(n_收发id, 0);

  --插入未审药品记录
  Zl_未审药品记录_Update(0, 11, No_In, 库房id_In, 填制日期_In);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品其他出库_Insert;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品其他出库_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 11 Order By 药品id;
Begin
  For v_药品收发记录 In c_药品收发记录 Loop
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  End Loop;

  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 11 And 记录状态 = 1 And 审核人 Is Null And 入出系数 = -1;
  Exception
    When Others Then
      n_库房id := 0;
  End;
  Delete From 药品收发记录 Where NO = No_In And 单据 = 11 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --插入未审药品记录
  Zl_未审药品记录_Update(1, 11, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品其他出库_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_药品其他出库_Verify
(
  序号_In     In 药品收发记录.序号%Type,
  No_In       In 药品收发记录.No%Type,
  库房id_In   In 药品收发记录.库房id%Type,
  药品id_In   In 药品收发记录.药品id%Type,
  批次_In     In 药品收发记录.批次%Type,
  实际数量_In In 药品收发记录.实际数量%Type,
  成本价_In   In 药品收发记录.成本价%Type,
  成本金额_In In 药品收发记录.成本金额%Type,
  零售金额_In In 药品收发记录.零售金额%Type,
  差价_In     In 药品收发记录.差价%Type,
  审核人_In   In 药品收发记录.审核人%Type,
  审核日期_In In 药品收发记录.审核日期%Type
) Is
  Err_Isverified Exception;
  Err_Isbatch Exception;
  v_Druginf  Varchar2(50); --原不分批现在分批的药品信息
  Intdigit   Number;
  v_上次扣率 药品库存.上次扣率%Type;
  n_收发id   药品收发记录.Id%Type;
Begin
  --获取金额小数位数
  Select Nvl(精度, 2) Into Intdigit From 药品卫材精度 Where 性质 = 0 And 类别 = 1 And 内容 = 4 And 单位 = 5;

  --主要针对原不分批现在分批的药品，不能对其审核
  Begin
    Select Distinct '(' || i.编码 || ')' || Nvl(n.名称, i.名称) As 药品信息
    Into v_Druginf
    From 药品收发记录 A, 药品规格 B, 收费项目目录 I, 收费项目别名 N
    Where a.药品id = b.药品id And a.药品id = i.Id And a.药品id = n.收费细目id(+) And n.性质(+) = 3 And a.No = No_In And a.单据 = 11 And
          a.记录状态 = 1 And Nvl(a.批次, 0) = 0 And a.药品id + 0 = 药品id_In And
          ((Nvl(b.药库分批, 0) = 1 And
          a.库房id Not In (Select 部门id From 部门性质说明 Where (工作性质 Like '%药房') Or (工作性质 Like '制剂室'))) Or Nvl(b.药房分批, 0) = 1) And
          Rownum = 1;
  Exception
    When Others Then
      v_Druginf := '';
  End;

  If v_Druginf Is Not Null Then
    Raise Err_Isbatch;
  End If;

  Begin
    Select Nvl(上次扣率, 100)
    Into v_上次扣率
    From 药品库存
    Where 药品id = 药品id_In And Nvl(批次, 0) = 批次_In And 库房id = 库房id_In And 性质 = 1 And Rownum = 1;
  Exception
    When Others Then
      v_上次扣率 := 100;
  End;

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = 审核日期_In, 成本价 = 成本价_In, 成本金额 = 成本金额_In, 差价 = 差价_In, 扣率 = v_上次扣率
  Where NO = No_In And 单据 = 11 And 药品id = 药品id_In And 序号 = 序号_In And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --删除未审药品记录
  Zl_未审药品记录_Update(1, 11, No_In, 库房id_In);

  Select ID
  Into n_收发id
  From 药品收发记录
  Where NO = No_In And 单据 = 11 And 药品id = 药品id_In And 序号 = 序号_In And 记录状态 = 1 And 审核人 Is Not Null;

  --更新库存 其他出库是审核出库业务
  Zl_药品库存_Update(n_收发id, 2, 1);

Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Err_Isbatch Then
    Raise_Application_Error(-20102, '[ZLSOFT]该单据中包含有一条原来不分批，现在分批的药品[' || v_Druginf || ']，不能审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品其他出库_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品盘点_Insert
(
  No_In         In 药品收发记录.No%Type,
  序号_In       In 药品收发记录.序号%Type,
  库房id_In     In 药品收发记录.库房id%Type,
  批次_In       In 药品收发记录.批次%Type,
  入出类别id_In In 药品收发记录.入出类别id%Type,
  入出系数_In   In 药品收发记录.入出系数%Type,
  药品id_In     In 药品收发记录.药品id%Type,
  帐面数量_In   In 药品收发记录.填写数量%Type,
  实盘数量_In   In 药品收发记录.扣率%Type,
  数量差_In     In 药品收发记录.实际数量%Type,
  售价_In       In 药品收发记录.零售价%Type,
  金额差_In     In 药品收发记录.零售金额%Type,
  差价差_In     In 药品收发记录.差价%Type,
  填制人_In     In 药品收发记录.填制人%Type,
  填制日期_In   In 药品收发记录.填制日期%Type,
  摘要_In       In 药品收发记录.摘要%Type := Null,
  产地_In       In 药品收发记录.产地%Type := Null,
  批号_In       In 药品收发记录.批号%Type := Null,
  效期_In       In 药品收发记录.效期%Type := Null,
  盘点时间_In   In 药品收发记录.频次%Type := Null,
  库存金额_In   In 药品收发记录.成本价%Type := Null,
  库存差价_In   In 药品收发记录.成本金额%Type := Null,
  成本价_In     In 药品收发记录.单量%Type := Null,
  批准文号_In   In 药品收发记录.批准文号%Type := Null,
  新批次_In     In Number := 0,
  库房货位_In   In 药品收发记录.库房货位%Type := Null
) Is
  v_Lngid        Number(18);
  n_上次供应商id 药品库存.上次供应商id%Type;
  d_上次生产日期 药品库存.上次生产日期%Type;
  n_批次         药品收发记录.批次%Type;
  n_药库分批     Integer; --是否药库分批    1:分批;0：不分批 
  n_药房分批     Integer; --是否药库分批    1:分批;0：不分批 
Begin
  --如果批次_IN为-1,则表示新产生一个批次药品 
  Select 药品收发记录_Id.Nextval Into v_Lngid From Dual;

  Begin
    Select 上次供应商id, 上次生产日期
    Into n_上次供应商id, d_上次生产日期
    From 药品库存
    Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And Nvl(批次, 0) = Nvl(批次_In, 0);
  Exception
    When Others Then
      n_上次供应商id := Null;
      d_上次生产日期 := Null;
  End;
  If 批次_In = -1 Then
    --判断分批属性，根据分批属性生成对应批次 
    Select Nvl(药库分批, 0), Nvl(药房分批, 0) Into n_药库分批, n_药房分批 From 药品规格 Where 药品id = 药品id_In;
    If n_药房分批 = 0 Then
      If n_药库分批 = 1 Then
        Begin
          Select Distinct 0
          Into n_药库分批
          From 部门性质说明
          Where ((工作性质 Like '%药房') Or (工作性质 Like '制剂室')) And 部门id = 库房id_In;
        Exception
          When Others Then
            n_药库分批 := 1;
        End;
      
        If n_药库分批 = 1 Then
          n_批次 := Zl_Fun_Getbatchnum(药品id_In, 产地_In, 批号_In, 成本价_In, 售价_In, v_Lngid);
        Else
          n_批次 := 0;
        End If;
      End If;
    Else
      n_批次 := Zl_Fun_Getbatchnum(药品id_In, 产地_In, 批号_In, 成本价_In, 售价_In, v_Lngid);
    End If;
  Else
    n_批次 := 批次_In;
  End If;

  Insert Into 药品收发记录
    (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 扣率, 实际数量, 零售价, 零售金额, 差价, 摘要, 填制人, 填制日期, 频次,
     成本价, 成本金额, 单量, 批准文号, 供药单位id, 生产日期, 发药方式, 库房货位)
  Values
    (v_Lngid, 1, 12, No_In, 序号_In, 库房id_In, 入出类别id_In, 入出系数_In, 药品id_In, n_批次, 产地_In, 批号_In, 效期_In, 帐面数量_In, 实盘数量_In,
     数量差_In, 售价_In, 金额差_In, 差价差_In, 摘要_In, 填制人_In, 填制日期_In, 盘点时间_In, 库存金额_In, 库存差价_In, 成本价_In, 批准文号_In, n_上次供应商id,
     d_上次生产日期, 新批次_In, 库房货位_In);

  If 入出系数_In = -1 Then
    --入出系数=-1的需要下库存
    Zl_药品库存_Update(v_Lngid, 0);
  
    --插入未审药品记录
    Zl_未审药品记录_Update(0, 12, No_In, 库房id_In, 填制日期_In);
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品盘点_Insert;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品盘点_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;
  
  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 12 Order By 药品id;
Begin
  --通过循环，恢复出库类别原来的可用数量，
  --实际数量保存的是数量差
  For v_药品收发记录 In c_药品收发记录 Loop
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  End Loop;

  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 12 And 记录状态 = 1 And 审核人 Is Null And 入出系数 = -1;
  Exception
    When Others Then
      n_库房id := 0;
  End;

  Delete From 药品收发记录 Where NO = No_In And 单据 = 12 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
  --插入未审药品记录
  Zl_未审药品记录_Update(1, 12, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品盘点_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_药品盘点_Verify
(
  No_In     In 药品收发记录.No%Type := Null,
  审核人_In In 药品收发记录.审核人%Type := Null
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID, 实际数量, 零售金额, 差价, 库房id, 药品id, 批次, 批号, 效期, 产地, 入出类别id, 入出系数, 批准文号, 供药单位id, 生产日期, 单量
    From 药品收发记录
    Where NO = No_In And 单据 = 12 And 记录状态 = 1
    Order By 药品id;
Begin
  Update 药品收发记录
  Set 审核人 = 审核人_In, 审核日期 = Sysdate
  Where NO = No_In And 单据 = 12 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  For v_药品收发记录 In c_药品收发记录 Loop
    --更改药品库存表的相应数据
    If v_药品收发记录.入出系数 = 1 Then
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 0);
    Else
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
      n_库房id := v_药品收发记录.库房id;
    End If;
  End Loop;
  --删除未审药品记录
  Zl_未审药品记录_Update(1, 12, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品盘点_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品自制原料出库_Insert
(
  No_In         In 药品收发记录.No%Type,
  对方部门id_In In 药品收发记录.对方部门id%Type,
  耗损数量_In   In Varchar2 := Null
) As
  --入参：耗损数量_In格式：自制药品id，原料药品id，耗损数量；自制药品id，原料药品id，耗损数量....
  v_出的类别id 药品收发记录.入出类别id%Type; --入出类别ID
  v_Maxserial  药品收发记录.序号%Type;
  v_Digit      Number(2);
  n_收发id     药品收发记录.Id%Type;

  v_Strinput   Varchar2(4000);
  v_起始字符串 Varchar2(4000);
  n_耗损数量   Number;
  n_位置       Number;
  v_Find       Varchar2(400);

  n_原料数量     药品收发记录.实际数量%Type;
  n_原料成本金额 药品收发记录.成本金额%Type;
  n_原料售价金额 药品收发记录.零售金额%Type;
  n_原料差价     药品收发记录.差价%Type;
  d_填制日期     药品收发记录.填制日期%Type;
  --数量算法解释：药品目录管理中自制入库采用的是剂量单位保存，因此要将数量换算成剂量单位才参与计算
  --收发记录表中，数量是售价单位，那么要换算成剂量单位因此需要乘以剂量系数
  --分子/分母乘以换算后的剂量数量，就能算出需要好多剂量系数的原料药，而我们系统中保存到收发记录的价格都是用售价单位参与换算的，因此需要除以原料药的剂量系数得到售价单位数量


  Cursor c_组成药品 Is
    Select Rownum + v_Maxserial 序号, 原料药品id, 上次产地, 数量, 摘要, 填制人, 填制日期, 药品id, 药品序号, 对方部门id, 库房id, 成本价, 成本金额, 售价, 售价金额, 差价,
           上次供应商id, 上次采购价, 上次批号, 效期, 上次生产日期, 批准文号
    From (Select a.原料药品id, c.上次产地, (e.实际数量 * (a.分子 / a.分母) / b.剂量系数) As 数量, e.摘要, e.填制人, e.填制日期, e.药品id, e.序号 As 药品序号,
                  e.对方部门id, e.库房id, Decode(Nvl(c.平均成本价, 0), 0, b.成本价, c.平均成本价) As 成本价,
                  c.平均成本价 * e.实际数量 * (a.分子 / a.分母) / b.剂量系数 As 成本金额, d.现价 As 售价,
                  (e.实际数量 * (a.分子 / a.分母) / b.剂量系数 * d.现价) As 售价金额,
                  e.实际数量 * (a.分子 / a.分母) / b.剂量系数 * (d.现价 - c.平均成本价) As 差价, c.上次供应商id, c.上次采购价, c.上次批号, c.效期, c.上次生产日期,
                  c.批准文号
           From 自制药品构成 A,
                (Select b.药品id, b.剂量系数, b.成本价
                  From 收费项目目录 A, 药品规格 B
                  Where a.Id = b.药品id And Nvl(是否变价, 0) = 0) B,
                (Select 库房id, 药品id, 实际金额, 实际差价, 上次采购价, 上次产地, 上次供应商id, 上次批号, 效期, 上次生产日期, 批准文号, 平均成本价
                  From 药品库存
                  Where 性质 = 1 And 库房id = 对方部门id_In) C,
                (Select 收费细目id, 现价
                  From 收费价目
                  Where ((Sysdate Between 执行日期 And 终止日期) Or (Sysdate >= 执行日期 And 终止日期 Is Null))) D,
                (Select e.实际数量 * f.剂量系数 As 实际数量, e.摘要, e.填制人, e.填制日期, e.药品id, e.序号, e.对方部门id, e.库房id
                  From 药品收发记录 E, 药品规格 F
                  Where e.药品id = f.药品id And e.No = No_In And e.单据 = 2 And e.入出系数 = 1) E
           Where a.原料药品id = b.药品id And b.药品id = d.收费细目id And b.药品id = c.药品id(+) And a.自制药品id = e.药品id
           Union
           Select a.原料药品id, c.上次产地, (e.实际数量 * (a.分子 / a.分母) / b.剂量系数) As 数量, e.摘要, e.填制人, e.填制日期, e.药品id, e.序号 As 药品序号,
                  e.对方部门id, e.库房id, Decode(Nvl(c.平均成本价, 0), 0, b.成本价, c.平均成本价) As 成本价,
                  c.平均成本价 * e.实际数量 * (a.分子 / a.分母) / b.剂量系数 As 成本金额, c.现价 As 售价,
                  (e.实际数量 * (a.分子 / a.分母) / b.剂量系数 * c.现价) As 售价金额, e.实际数量 * (a.分子 / a.分母) / b.剂量系数 * c.平均成本价 As 差价,
                  c.上次供应商id, c.上次采购价, c.上次批号, c.效期, c.上次生产日期, c.批准文号
           From 自制药品构成 A,
                (Select b.药品id, b.剂量系数, b.成本价
                  From 收费项目目录 A, 药品规格 B
                  Where a.Id = b.药品id And Nvl(是否变价, 0) = 1) B,
                (Select 库房id, 药品id, 实际金额, 实际差价, 上次采购价, 上次产地, 实际金额 / Nvl(实际数量, 1) As 现价, 上次供应商id, 上次批号, 效期, 上次生产日期, 批准文号,
                         平均成本价
                  From 药品库存
                  Where 性质 = 1 And 库房id = 对方部门id_In And 实际数量 > 0) C,
                (Select e.实际数量 * f.剂量系数 As 实际数量, e.摘要, e.填制人, e.填制日期, e.药品id, e.序号, e.对方部门id, e.库房id
                  From 药品收发记录 E, 药品规格 F
                  Where e.药品id = f.药品id And e.No = No_In And e.单据 = 2 And e.入出系数 = 1) E
           Where a.原料药品id = b.药品id And b.药品id = c.药品id And a.自制药品id = e.药品id
           Order By 原料药品id);
Begin
  Select Nvl(精度, 2) Into v_Digit From 药品卫材精度 Where 性质 = 0 And 类别 = 1 And 内容 = 4 And 单位 = 5;

  Select b.Id
  Into v_出的类别id
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 2 And b.系数 = -1 And Rownum < 2;

  Select Max(序号) Into v_Maxserial From 药品收发记录 Where NO = No_In And 单据 = 2 And 入出系数 = 1;

  If 耗损数量_In Is Not Null Then
    v_Strinput := 耗损数量_In || ';';
  End If;
  For v_组成药品 In c_组成药品 Loop
    Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
  
    n_耗损数量 := 0;
    If 耗损数量_In Is Not Null Then
      v_Find       := v_组成药品.药品id || ',' || v_组成药品.原料药品id || ',';
      n_位置       := Instr(v_Strinput, v_Find);
      v_起始字符串 := Substr(v_Strinput, n_位置 + Length(v_Find));
      n_耗损数量   := Substr(v_起始字符串, 1, Instr(v_起始字符串, ';') - 1);
    
      n_原料数量     := v_组成药品.数量 + n_耗损数量;
      n_原料成本金额 := Round(n_原料数量 * v_组成药品.成本价, v_Digit);
      n_原料售价金额 := Round(n_原料数量 * v_组成药品.售价, v_Digit);
      n_原料差价     := Round(n_原料售价金额 - n_原料成本金额, v_Digit);
    Else
      n_原料数量     := v_组成药品.数量;
      n_原料成本金额 := Round(v_组成药品.成本金额, v_Digit);
      n_原料售价金额 := Round(v_组成药品.售价金额, v_Digit);
      n_原料差价     := Round(v_组成药品.差价, v_Digit);
    End If;
  
    Insert Into 药品收发记录
      (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 产地, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要, 填制人, 填制日期,
       费用id, 扣率, 供药单位id, 批号, 效期, 生产日期, 批准文号, 单量)
    Values
      (n_收发id, 1, 2, No_In, v_组成药品.序号, v_组成药品.对方部门id, v_组成药品.库房id, v_出的类别id, -1, v_组成药品.原料药品id, v_组成药品.上次产地, n_原料数量,
       n_原料数量, v_组成药品.成本价, n_原料成本金额, v_组成药品.售价, n_原料售价金额, n_原料差价, v_组成药品.摘要, v_组成药品.填制人, v_组成药品.填制日期, v_组成药品.药品id,
       v_组成药品.药品序号, v_组成药品.上次供应商id, v_组成药品.上次批号, v_组成药品.效期, v_组成药品.上次生产日期, v_组成药品.批准文号, n_耗损数量);
  
    d_填制日期 := v_组成药品.填制日期;
    
    --调用库存更新过程
    Zl_药品库存_Update(n_收发id, 0);
  End Loop;

  --插入未审药品记录
  Zl_未审药品记录_Update(0, 2, No_In, 对方部门id_In, d_填制日期);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品自制原料出库_Insert;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_自制入库_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 2 Order By 药品id;
Begin
  --通过循环，恢复所有构成原料药原来的可用数量
  For v_药品收发记录 In c_药品收发记录 Loop
    --更新库存
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  End Loop;

  Begin
    Select Distinct 库房id
    Into n_库房id
    From 药品收发记录
    Where NO = No_In And 单据 = 2 And 记录状态 = 1 And 入出系数 = -1 And 审核人 Is Null;
  Exception
    When Others Then
      n_库房id := 0;
  End;

  Delete --删除本身及相应的构成原料药 
  From 药品收发记录
  Where NO = No_In And 单据 = 2 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
  --删除未审药品记录
  Zl_未审药品记录_Update(1, 2, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_自制入库_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_自制入库_Verify
(
  No_In     In 药品收发记录.No%Type := Null,
  审核人_In In 药品收发记录.审核人%Type := Null
) Is
  Err_Isverified Exception;
  v_差价率     Number;
  v_出库差价   药品库存.实际差价%Type;
  v_成本价     药品收发记录.成本价%Type;
  v_成本金额   药品收发记录.成本金额%Type;
  v_成本价方式 Zlparameters.参数值%Type;
  Intdigit     Number;
  n_库房id     药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID, 实际数量, Nvl(零售价, 0) As 零售价, 零售金额, 差价, 库房id, 药品id, 批次, 成本价, 批号, 效期, 产地, 入出类别id, 入出系数, 对方部门id, 供药单位id, 生产日期,
           批准文号
    From 药品收发记录
    Where NO = No_In And 单据 = 2 And 记录状态 = 1
    Order By 药品id;
Begin
  Select Zl_To_Number(Nvl(zl_GetSysParameter(9), '2')) Into Intdigit From Dual;

  Select Nvl(zl_GetSysParameter('药品自制入库成本价计算方式', 1301), 0) Into v_成本价方式 From Dual;

  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = Sysdate
  Where NO = No_In And 单据 = 2 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  --数量算法解释：药品目录管理中自制入库采用的是剂量单位保存，因此要将数量换算成剂量单位才参与计算
  --收发记录表中，数量是售价单位，那么要换算成剂量单位因此需要乘以剂量系数
  --分子/分母乘以换算后的剂量数量，就能算出需要好多剂量系数的原料药，而我们系统中保存到收发记录的价格都是用售价单位参与换算的，因此需要除以原料药的剂量系数得到售价单位数量

  For v_药品收发记录 In c_药品收发记录 Loop
    --更改药品库存表的相应数据
    v_成本价   := v_药品收发记录.成本价;
    v_成本金额 := Round(v_药品收发记录.成本价 * v_药品收发记录.实际数量, Intdigit);
    v_出库差价 := Round(v_药品收发记录.零售金额 - v_成本金额, Intdigit);
  
    Update 药品收发记录 Set 成本价 = v_成本价, 成本金额 = v_成本金额, 差价 = v_出库差价 Where ID = v_药品收发记录.Id;
  
    --调用库存更新过程
    If v_药品收发记录.入出系数 = 1 Then
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 0);
    Else
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
      n_库房id := v_药品收发记录.库房id;
    End If;
  
    If v_药品收发记录.入出系数 = 1 Then
      --只有入业务才处理
      --更新该药品的成本价
      Update 药品规格 Set 成本价 = v_成本价 Where 药品id = v_药品收发记录.药品id;
    End If;
  End Loop;
  --删除未审药品记录
  Zl_未审药品记录_Update(1, 2, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_自制入库_Verify;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_药品协定对照出库_Insert
(
  No_In         In 药品收发记录.No%Type,
  出库类别id_In In 药品收发记录.入出类别id%Type,
  库房id_In     In 药品收发记录.库房id%Type
) As
  v_Maxserial 药品收发记录.序号%Type;
  n_收发id    药品收发记录.Id%Type;
  d_填制日期  药品收发记录.填制日期%Type;

  Cursor c_组成药品 Is
    Select (Rownum + v_Maxserial) As 序号, 协定药品id, 上次产地, 上次供应商id, 上次批号, 效期, 上次生产日期, 批准文号, 数量, 摘要, 填制人, 填制日期, 药品id, 药品序号,
           对方部门id, 库房id, 成本价, 成本金额, 售价, 售价金额, 差价
    From (Select a.协定药品id, c.上次产地, c.上次供应商id, c.上次批号, c.效期, c.上次生产日期, c.批准文号, (e.实际数量 * (a.分子 / a.分母)) As 数量, e.摘要, e.填制人,
                  e.填制日期, e.药品id, e.序号 As 药品序号, e.对方部门id, e.库房id, Decode(Nvl(c.平均成本价, 0), 0, b.成本价, c.平均成本价) As 成本价,
                  (c.平均成本价 * e.实际数量 * (a.分子 / a.分母)) As 成本金额, d.现价 As 售价, (e.实际数量 * (a.分子 / a.分母) * d.现价) As 售价金额,
                  e.实际数量 * (a.分子 / a.分母) * (d.现价 - c.平均成本价) As 差价
           From 协定药品对照 A, (Select b.* From 收费项目目录 A, 药品规格 B Where a.Id = b.药品id And Nvl(是否变价, 0) = 0) B,
                (Select 库房id, 药品id, 实际金额, 实际差价, 上次采购价, 上次产地, 上次供应商id, 上次批号, 效期, 上次生产日期, 批准文号, 平均成本价
                  From 药品库存
                  Where 性质 = 1 And 库房id = 库房id_In) C,
                (Select 收费细目id, 现价
                  From 收费价目
                  Where ((Sysdate Between 执行日期 And 终止日期) Or (Sysdate >= 执行日期 And 终止日期 Is Null))) D,
                (Select * From 药品收发记录 Where NO = No_In And 单据 = 3 And 入出系数 = 1) E
           Where a.协定药品id = b.药品id And b.药品id = d.收费细目id And b.药品id = c.药品id(+) And a.药品id = e.药品id
           Union All
           Select a.协定药品id, c.上次产地, c.上次供应商id, c.上次批号, c.效期, c.上次生产日期, c.批准文号, (e.实际数量 * (a.分子 / a.分母)) As 数量, e.摘要, e.填制人,
                  e.填制日期, e.药品id, e.序号 As 药品序号, e.对方部门id, e.库房id, Decode(Nvl(c.平均成本价, 0), 0, b.成本价, c.平均成本价) As 成本价,
                  (c.平均成本价 * e.实际数量 * (a.分子 / a.分母)) As 成本金额, c.现价 As 售价, (e.实际数量 * (a.分子 / a.分母) * c.现价) As 售价金额,
                  e.实际数量 * (a.分子 / a.分母) * (c.现价 - c.平均成本价) As 差价
           From 协定药品对照 A, (Select b.* From 收费项目目录 A, 药品规格 B Where a.Id = b.药品id And Nvl(是否变价, 0) = 1) B,
                (Select 库房id, 药品id, 实际金额, 实际差价, 上次采购价, 上次产地, 上次供应商id, 上次批号, 效期, 上次生产日期, 批准文号, 实际金额 / 实际数量 As 现价, 平均成本价
                  From 药品库存
                  Where 性质 = 1 And 库房id = 库房id_In And 实际数量 > 0) C,
                (Select * From 药品收发记录 Where NO = No_In And 单据 = 3 And 入出系数 = 1) E
           Where a.协定药品id = b.药品id And b.药品id = c.药品id And a.药品id = e.药品id)
    Order By 协定药品id;
Begin
  Select Max(序号) Into v_Maxserial From 药品收发记录 Where NO = No_In And 单据 = 3 And 入出系数 = 1;
  For v_组成药品 In c_组成药品 Loop
    Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
  
    Insert Into 药品收发记录
      (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 产地, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 零售金额, 差价, 摘要, 填制人, 填制日期,
       费用id, 扣率, 供药单位id, 批号, 效期, 生产日期, 批准文号)
    Values
      (n_收发id, 1, 3, No_In, v_组成药品.序号, v_组成药品.对方部门id, v_组成药品.库房id, 出库类别id_In, -1, v_组成药品.协定药品id, v_组成药品.上次产地, v_组成药品.数量,
       v_组成药品.数量, v_组成药品.成本价, v_组成药品.成本金额, v_组成药品.售价, v_组成药品.售价金额, v_组成药品.差价, v_组成药品.摘要, v_组成药品.填制人, v_组成药品.填制日期,
       v_组成药品.药品id, v_组成药品.药品序号, v_组成药品.上次供应商id, v_组成药品.上次批号, v_组成药品.效期, v_组成药品.上次生产日期, v_组成药品.批准文号);
  
    d_填制日期 := v_组成药品.填制日期;
  
    --调用库存更新过程
    Zl_药品库存_Update(n_收发id, 0);
  End Loop;

  --插入未审药品记录
  Zl_未审药品记录_Update(0, 3, No_In, 库房id_In, d_填制日期);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品协定对照出库_Insert;
/

--96519:马政,2016-05-27,可用数量处理
--96519:李业庆,2016-07-27,可用数量处理
Create Or Replace Procedure Zl_协定入库_Delete(
  No_In In 药品收发记录.No%Type
) Is
  Err_Isverified Exception;
  n_库房id 药品收发记录.库房id%Type;

  Cursor c_药品收发记录 Is
    Select ID From 药品收发记录 Where NO = No_In And 单据 = 3 Order By 药品id;
Begin
  --通过循环，恢复所有构成协定药原来的可用数量
  For v_药品收发记录 In c_药品收发记录 Loop
    Zl_药品库存_Update(v_药品收发记录.Id, 1);
  End Loop;

  Begin
    Select Distinct 库房id Into n_库房id From 药品收发记录 Where NO = No_In And 单据 = 3 And 入出系数 = -1;
  Exception
    When Others Then
      n_库房id := 0;
  End;

  Delete --删除本身及相应的构成协定药 
  From 药品收发记录
  Where NO = No_In And 单据 = 3 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;
  --删除未审药品记录
  Zl_未审药品记录_Update(1, 3, No_In, n_库房id);
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人删除或已被人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_协定入库_Delete;
/

--96519:马政,2016-05-27,可用数量处理
Create Or Replace Procedure Zl_协定入库_Verify
(
  No_In     In 药品收发记录.No%Type := Null,
  审核人_In In 药品收发记录.审核人%Type := Null
) Is
  Err_Isverified Exception;
  v_出库差价 药品库存.实际差价%Type;
  v_成本价   药品收发记录.成本价%Type;
  v_成本金额 药品收发记录.成本金额%Type;

  Cursor c_药品收发记录 Is
    Select ID, 填写数量, 零售价, 零售金额, 差价, 库房id, 药品id, 批次, 成本价, 批号, 产地, 入出类别id, 入出系数, 对方部门id, 供药单位id, 生产日期, 批准文号, 效期
    From 药品收发记录
    Where NO = No_In And 单据 = 3 And 记录状态 = 1;
Begin
  Update 药品收发记录
  Set 审核人 = Nvl(审核人_In, 审核人), 审核日期 = Sysdate
  Where NO = No_In And 单据 = 3 And 记录状态 = 1 And 审核人 Is Null;

  If Sql%RowCount = 0 Then
    Raise Err_Isverified;
  End If;

  For v_药品收发记录 In c_药品收发记录 Loop
    --更改药品库存表的相应数据
    If v_药品收发记录.入出系数 = -1 Then
      v_成本价   := Zl_Fun_Getoutcost(v_药品收发记录.药品id, Nvl(v_药品收发记录.批次, 0), v_药品收发记录.库房id);
      v_成本金额 := v_成本价 * v_药品收发记录.填写数量;
      v_出库差价 := v_药品收发记录.零售金额 - v_成本金额;
    Else
      Begin
        Select Sum(成本价)
        Into v_成本价
        From (Select c.平均成本价 * (a.分子 / a.分母) As 成本价
               From 协定药品对照 A,
                    (Select b.药品id, b.成本价
                      From 收费项目目录 A, 药品规格 B
                      Where a.Id = b.药品id And Nvl(是否变价, 0) = 0) B,
                    (Select 库房id, 药品id, 实际金额, 实际差价, 上次采购价, 平均成本价
                      From 药品库存
                      Where 性质 = 1 And 库房id = v_药品收发记录.对方部门id) C,
                    (Select 收费细目id, 现价
                      From 收费价目
                      Where ((Sysdate Between 执行日期 And 终止日期) Or (Sysdate >= 执行日期 And 终止日期 Is Null))) D
               Where a.协定药品id = b.药品id And b.药品id = d.收费细目id And b.药品id = c.药品id(+) And a.药品id = v_药品收发记录.药品id
               Union All
               Select c.平均成本价 * (a.分子 / a.分母) As 成本价
               From 协定药品对照 A,
                    (Select b.药品id, b.成本价
                      From 收费项目目录 A, 药品规格 B
                      Where a.Id = b.药品id And Nvl(是否变价, 0) = 1) B,
                    (Select 库房id, 药品id, 实际金额, 实际差价, 实际金额 / 实际数量 As 现价, 上次采购价, 平均成本价
                      From 药品库存
                      Where 性质 = 1 And 库房id = v_药品收发记录.对方部门id And 实际数量 > 0) C
               Where a.协定药品id = b.药品id And b.药品id = c.药品id And a.药品id = v_药品收发记录.药品id);
      Exception
        When Others Then
          v_成本价 := 0;
      End;
    
      v_成本金额 := v_成本价 * v_药品收发记录.填写数量;
      v_出库差价 := v_药品收发记录.零售金额 - v_成本金额;
    End If;
  
    Update 药品收发记录 Set 成本价 = v_成本价, 成本金额 = v_成本金额, 差价 = v_出库差价 Where ID = v_药品收发记录.Id;
  
    --调用库存更新过程
    If v_药品收发记录.入出系数 = 1 Then
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 0);
    Else
      Zl_药品库存_Update(v_药品收发记录.Id, 2, 1);
      --删除未审药品记录
      Zl_未审药品记录_Update(1, 3, No_In, v_药品收发记录.库房id);
    End If;
  
    If v_药品收发记录.入出系数 = 1 Then
      --只有入业务才处理
      --更新该药品的成本价
      Update 药品规格 Set 成本价 = v_成本价 Where 药品id = v_药品收发记录.药品id;
    End If;
  
  End Loop;
Exception
  When Err_Isverified Then
    Raise_Application_Error(-20101, '[ZLSOFT]该单据已经被他人审核！[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_协定入库_Verify;
/

--97401:刘尔旋,2016-06-14,结帐使用门诊票据作废问题
--92159:刘尔旋,2016-08-11,结帐作废红票
--96645:刘尔旋,2016-05-23,重打票据结帐作废
Create Or Replace Procedure Zl_病人结帐记录_Cancel
(
  No_In         病人结帐记录.No%Type,
  冲销id_In     病人结帐记录.Id%Type,
  操作员编号_In 病人结帐记录.操作员编号%Type,
  操作员姓名_In 病人结帐记录.操作员姓名%Type,
  作废时间_In   病人结帐记录.收费时间%Type := Null,
  票据号_In     病人结帐记录.实际票号%Type := Null,
  领用id_In     票据领用记录.Id%Type := Null,
  票种_In       票据使用明细.票种%Type := Null
) As
  Err_Item Exception;
  v_Err_Msg Varchar2(255);

  --该游标用于预交记录相关信息

  --该游标用于处理费用相关汇总表
  Cursor c_Money(v_Id 病人预交记录.结帐id%Type) Is
    Select NO, 开单部门id, 病人科室id, 执行部门id, 病人病区id, 病人id, 主页id, 收入项目id, 门诊标志, 结帐金额
    From 住院费用记录
    Where 结帐id = v_Id
    Union All
    Select NO, 开单部门id, 病人科室id, 执行部门id, 0 As 病人病区id, 病人id, 0 As 主页id, 收入项目id, 门诊标志, 结帐金额
    From 门诊费用记录
    Where 结帐id = v_Id;

  r_Moneyrow c_Money%RowType;

  --该游标包含病人的相关信息
  Cursor c_Pati(n_病人id 病人信息.病人id%Type) Is
    Select a.姓名, a.性别, a.年龄, a.住院号, a.门诊号, b.主页id, b.出院病床, b.当前病区id, b.出院科室id, Nvl(b.费别, a.费别) As 费别, a.险类, c.编码 As 付款方式
    From 病人信息 A, 病案主页 B, 医疗付款方式 C
    Where a.病人id = n_病人id And a.病人id = b.病人id(+) And Nvl(a.主页id, 0) = b.主页id(+) And a.医疗付款方式 = c.名称(+);
  r_Pati c_Pati%RowType;

  --过程变量
  v_实际票号 病人预交记录.实际票号%Type;
  n_预交id   病人预交记录.Id%Type;
  n_病人id   病人信息.病人id%Type;

  n_原id    病人结帐记录.Id%Type;
  n_结帐id  病人结帐记录.Id%Type;
  v_打印ids Varchar2(5000);
  v_打印id  票据打印内容.Id%Type;

  n_来源     Number; --1-门诊;2-住院;3-门诊和住院
  n_返回值   病人余额.预交余额%Type;
  n_组id     财务缴款分组.Id%Type;
  n_预交类别 Number;
  d_Date     Date;

Begin
  n_组id := Zl_Get组id(操作员姓名_In);
  Begin
    Select ID, 病人id, 实际票号 Into n_原id, n_病人id, v_实际票号 From 病人结帐记录 Where 记录状态 = 1 And NO = No_In;
    --打印的内容
    Begin
      Select ID
      Into v_打印ids
      From (Select b.Id
             From 票据使用明细 A, 票据打印内容 B
             Where a.打印id = b.Id And a.性质 = 1 And a.原因 In (1, 3) And b.数据性质 = 3 And b.No = No_In
             Order By a.使用时间 Desc)
      Where Rownum < 2;
    Exception
      When Others Then
        Null;
    End;
  Exception
    When Others Then
      Begin
        v_Err_Msg := '没有发现要作废的结帐单据,可能已经作废！';
        Raise Err_Item;
      End;
  End;

  If 票据号_In Is Not Null Then
    Select 票据打印内容_Id.Nextval Into v_打印id From Dual;
  
    --发出票据
    Insert Into 票据打印内容 (ID, 数据性质, NO) Values (v_打印id, 3, No_In);
  
    Insert Into 票据使用明细
      (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
    Values
      (票据使用明细_Id.Nextval, 票种_In, 票据号_In, 1, 6, 领用id_In, v_打印id, 作废时间_In, 操作员姓名_In);
  
    --状态改动
    Update 票据领用记录
    Set 当前号码 = 票据号_In, 剩余数量 = Decode(Sign(剩余数量 - 1), -1, 0, 剩余数量 - 1), 使用时间 = Sysdate
    Where ID = Nvl(领用id_In, 0);
  End If;

  Open c_Pati(n_病人id);
  Fetch c_Pati
    Into r_Pati; --体检系统调用此过程,团体结帐时没有病人信息
  d_Date := 作废时间_In;
  If d_Date Is Null Then
    Select Sysdate Into d_Date From Dual;
  End If;
  n_结帐id := 冲销id_In;
  If Nvl(n_结帐id, 0) = 0 Then
    Select 病人结帐记录_Id.Nextval Into n_结帐id From Dual;
  End If;

  --病人结帐记录
  Insert Into 病人结帐记录
    (ID, NO, 实际票号, 记录状态, 中途结帐, 病人id, 操作员编号, 操作员姓名, 开始日期, 结束日期, 收费时间, 备注, 原因, 缴款组id, 结帐类型, 结算状态, 主页id, 住院次数, 结帐金额)
    Select n_结帐id, NO, 实际票号, 2, 中途结帐, 病人id, 操作员编号_In, 操作员姓名_In, 开始日期, 结束日期, d_Date, 备注, 原因, n_组id, 结帐类型, 1, 主页id, 住院次数,
           -1 * 结帐金额
    From 病人结帐记录
    Where ID = n_原id;

  Update 病人结帐记录 Set 记录状态 = 3 Where ID = n_原id;

  --作废收回票据(可能以前没有使用票据,无法收回)
  If v_打印ids Is Not Null Then
    Insert Into 票据使用明细
      (ID, 票种, 号码, 性质, 原因, 领用id, 打印id, 使用时间, 使用人)
      Select 票据使用明细_Id.Nextval, 票种, 号码, 2, 2, 领用id, 打印id, d_Date, 操作员姓名_In
      From 票据使用明细
      Where 打印id In (Select Column_Value From Table(f_Str2list(v_打印ids))) And 票种 In (1,3) And 性质 = 1;
  End If;

  Select 病人预交记录_Id.Nextval Into n_预交id From Dual;
  --插入结算方式为NULL的结算方式
  Insert Into 病人预交记录
    (ID, NO, 实际票号, 记录性质, 记录状态, 病人id, 主页id, 科室id, 金额, 结算方式, 结算号码, 摘要, 缴款单位, 单位开户行, 单位帐号, 收款时间, 操作员姓名, 操作员编号, 冲预交, 结帐id,
     缴款组id, 预交类别, 卡类别id, 结算卡序号, 卡号, 交易流水号, 交易说明, 合作单位, 结算性质, 校对标志)
    Select n_预交id, No_In, v_实际票号, 12, 1, n_病人id, r_Pati.主页id, r_Pati.出院科室id, Null, Null, Null, Null, Null, Null, Null,
           d_Date, 操作员姓名_In, 操作员编号_In, -1 * Sum(冲预交), n_结帐id, n_组id, Null, Null, Null, Null As 卡号, Null As 交易流水号,
           Null As 交易说明, Null As 合作单位, 2, 1
    From 病人预交记录
    Where 结帐id = n_原id;

  --确定结帐的费用记录来源
  Begin
    Select Case
             When Nvl(Max(住院), 0) = 1 And Nvl(Max(门诊), 0) = 1 Then
              3
             When Nvl(Max(住院), 0) = 1 Then
              2
             Else
              1
           End
    Into n_来源
    From (Select 1 As 住院, 0 As 门诊
           From 住院费用记录
           Where 结帐id = n_原id And Rownum = 1
           Union All
           Select 0 As 住院, 1 As 门诊 From 门诊费用记录 Where 结帐id = n_原id And Rownum = 1);
  
  Exception
    When Others Then
      n_来源 := 3;
  End;

  If n_来源 = 2 Or n_来源 = 3 Then
    --作废结帐对应的费用记录:不包含原始结帐产生的误差项目
    Insert Into 住院费用记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 多病人单, 记帐单id, 病人id, 主页id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 床号, 病人病区id,
       病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人, 开单部门id,
       开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, 结帐金额, 结帐id, 保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要,
       缴款组id, 医疗小组id)
      Select 病人费用记录_Id.Nextval, NO, 实际票号, To_Number('1' || Substr(记录性质, Length(记录性质), 1)), 记录状态, 序号, 从属父号, 价格父号, 多病人单,
             记帐单id, 病人id, 主页id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次,
             加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, Null, Null, 划价人, 开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 执行人,
             执行时间, 操作员姓名, 操作员编号, -1 * 结帐金额, n_结帐id, 保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id, 医疗小组id
      From 住院费用记录
      Where 结帐id = n_原id;
  End If;

  If n_来源 = 1 Or n_来源 = 3 Then
    Insert Into 门诊费用记录
      (ID, NO, 实际票号, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 记帐单id, 病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 付款方式, 病人科室id, 费别, 收费类别,
       收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志, 婴儿费, 记帐费用, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 划价人, 开单部门id, 开单人, 发生时间, 登记时间,
       执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号, 结帐金额, 结帐id, 保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id, 主页id)
      Select 病人费用记录_Id.Nextval, NO, 实际票号, To_Number('1' || Substr(记录性质, Length(记录性质), 1)), 记录状态, 序号, 从属父号, 价格父号, 记帐单id,
             病人id, 医嘱序号, 门诊标志, 姓名, 性别, 年龄, 标识号, 付款方式, 病人科室id, 费别, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志, 婴儿费,
             记帐费用, 收入项目id, 收据费目, 标准单价, Null, Null, 划价人, 开单部门id, 开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 执行人, 执行时间, 操作员姓名, 操作员编号,
             -1 * 结帐金额, n_结帐id, 保险项目否, 保险大类id, 统筹金额, 是否急诊, 保险编码, 费用类型, 摘要, 缴款组id, 主页id
      From 门诊费用记录
      Where 结帐id = n_原id;
  End If;

  For r_Moneyrow In c_Money(n_结帐id) Loop
    --病人余额 ,所以不需要更新这两个汇总表
  
    If Nvl(r_Moneyrow.门诊标志, 0) = 1 Or Nvl(r_Moneyrow.门诊标志, 0) = 2 Then
      n_预交类别 := r_Moneyrow.门诊标志;
    Elsif Nvl(r_Moneyrow.主页id, 0) = 0 Or Nvl(r_Moneyrow.门诊标志, 0) = 4 Then
      --体检:门诊病人
      n_预交类别 := 1;
    Else
      n_预交类别 := 2;
    End If;
  
    If Nvl(r_Moneyrow.门诊标志, 0) <> 4 Then
      Update 病人余额
      Set 费用余额 = Nvl(费用余额, 0) - r_Moneyrow.结帐金额 --注:新的结帐ID产生的是负数金额
      Where 病人id = r_Moneyrow.病人id And 类型 = n_预交类别 And 性质 = 1
      Returning 费用余额 Into n_返回值;
    
      If Sql%RowCount = 0 Then
        Insert Into 病人余额
          (病人id, 性质, 类型, 预交余额, 费用余额)
        Values
          (r_Moneyrow.病人id, 1, n_预交类别, 0, -1 * r_Moneyrow.结帐金额);
        n_返回值 := -1 * r_Moneyrow.结帐金额;
      End If;
      If Nvl(n_返回值, 0) = 0 Then
        Delete 病人余额
        Where 病人id = r_Moneyrow.病人id And 性质 = 1 And Nvl(预交余额, 0) = 0 And Nvl(费用余额, 0) = 0;
      End If;
    End If;
  
    --病人未结费用
    Update 病人未结费用
    Set 金额 = Nvl(金额, 0) - r_Moneyrow.结帐金额
    Where 病人id = r_Moneyrow.病人id And Nvl(主页id, 0) = Nvl(r_Moneyrow.主页id, 0) And
          Nvl(病人病区id, 0) = Nvl(r_Moneyrow.病人病区id, 0) And Nvl(病人科室id, 0) = Nvl(r_Moneyrow.病人科室id, 0) And
          Nvl(开单部门id, 0) = Nvl(r_Moneyrow.开单部门id, 0) And Nvl(执行部门id, 0) = Nvl(r_Moneyrow.执行部门id, 0) And
          收入项目id + 0 = r_Moneyrow.收入项目id And 来源途径 + 0 = r_Moneyrow.门诊标志;
  
    If Sql%RowCount = 0 Then
      Insert Into 病人未结费用
        (病人id, 主页id, 病人病区id, 病人科室id, 开单部门id, 执行部门id, 收入项目id, 来源途径, 金额)
      Values
        (r_Moneyrow.病人id, Decode(r_Moneyrow.主页id, Null, Null, 0, Null, r_Moneyrow.主页id),
         Decode(r_Moneyrow.病人病区id, Null, Null, 0, Null, r_Moneyrow.病人病区id), r_Moneyrow.病人科室id, r_Moneyrow.开单部门id,
         r_Moneyrow.执行部门id, r_Moneyrow.收入项目id, r_Moneyrow.门诊标志, -1 * r_Moneyrow.结帐金额);
    End If;
  
  End Loop;
  Close c_Pati;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人结帐记录_Cancel;
/

--96536:马政,2016-05-20,卫材调价成本价显示调整
Create Or Replace Procedure Zl_材料收发记录_成本价调价(材料id_In In 药品收发记录.药品id%Type) As
  v_No         药品收发记录.No%Type;
  v_应付id     应付记录.Id%Type; --应付记录的ID 
  v_应付单据号 应付记录.No%Type;
  d_调价时间   Date;
  n_序号       Number(8);
  n_库房id     药品收发记录.库房id%Type;
  n_入出类别id 药品收发记录.入出类别id%Type;
  n_入出系数   药品收发记录.入出系数%Type;
  n_收发id     药品收发记录.Id%Type;
  n_调整额     药品收发记录.零售金额%Type;
  n_原成本价   药品收发记录.成本价%Type;
  n_新成本价   药品收发记录.成本价%Type;
  n_平均成本价 药品库存.平均成本价%Type;
  v_调价id     成本价调价信息.Id%Type;
  v_调价汇总号 成本价调价信息.调价汇总号%Type;
  n_Count      Number(1) := 0;

  Cursor c_Stock Is --当前库存 
    Select 上次供应商id, a.库房id, a.药品id As 材料id, Nvl(a.批次, 0) As 批次, a.上次批号, a.效期, a.上次产地, a.灭菌效期,
           Decode(Sign(Nvl(a.批次, 0)), 1, a.上次采购价, a.平均成本价) As 原成本价
    From 药品库存 A
    Where a.性质 = 1 And Nvl(a.实际数量, 0) <> 0 And a.药品id = 材料id_In
    Order By a.库房id;

  v_Stock c_Stock%RowType;
Begin
  d_调价时间 := Sysdate;
  n_库房id   := 0;

  --判断是否存在无库存调价 
  Begin
    Select ID, 新成本价, 调价汇总号
    Into v_调价id, n_新成本价, v_调价汇总号
    From 成本价调价信息
    Where 执行日期 Is Null And Nvl(库房id, 0) = 0 And 药品id = 材料id_In;
  Exception
    When Others Then
      v_调价id   := 0;
      n_新成本价 := Null;
  End;

  --无库存调价 
  If v_调价id > 0 Then
    --根据当前库存重新产生调价信息 
    For v_Stock In c_Stock Loop
      Zl_材料成本调价_Insert(v_Stock.上次供应商id, v_Stock.库房id, v_Stock.材料id, v_Stock.批次, v_Stock.上次批号, v_Stock.原成本价, n_新成本价,
                       Null, Null, 0, 0, v_调价汇总号);
      n_Count := n_Count + 1;
    End Loop;
  
    If n_Count > 0 Then
      --如果当前有库存记录，则删除无库存调价记录 
      Delete 成本价调价信息 Where ID = v_调价id;
    Else
      Update 成本价调价信息 Set 执行日期 = d_调价时间 Where ID = v_调价id;
    
      Update 材料特性 Set 成本价 = n_新成本价 Where 材料id = 材料id_In And 成本价 <> n_新成本价;
    End If;
  End If;

  --取库存差价调整的入出类别ID 
  Select b.Id, b.系数
  Into n_入出类别id, n_入出系数
  From 药品单据性质 A, 药品入出类别 B
  Where a.类别id = b.Id And a.单据 = 33 And Rownum < 2;

  For c_成本调整 In (Select a.库房id, a.药品id As 材料id, Nvl(a.批次, 0) 批次, a.上次供应商id, a.实际数量, a.实际金额, a.实际差价, a.上次产地 As 产地,
                        a.上次批号 As 批号, a.灭菌效期, a.效期, a.上次生产日期 As 生产日期, a.批准文号, Nvl(a.平均成本价, 0) As 原成本价, b.新成本价, b.发票号,
                        b.发票日期, b.发票金额, Nvl(a.上次采购价, 0) As 上次采购价, b.Id As 调价id
                 From 药品库存 A, 成本价调价信息 B
                 Where a.药品id = b.药品id And Nvl(a.上次供应商id, 0) = Nvl(b.供药单位id, 0) And a.库房id = b.库房id And
                       Nvl(a.批次, 0) = Nvl(b.批次, 0) And a.性质 = 1 And b.执行日期 Is Null And a.药品id = 材料id_In
                 Order By a.库房id) Loop
    If n_库房id <> c_成本调整.库房id Then
      n_序号   := 1;
      n_库房id := c_成本调整.库房id;
      v_No     := Nextno(71, n_库房id);
    Else
      n_序号 := n_序号 + 1;
    End If;
  
    Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
    If Nvl(c_成本调整.实际数量, 0) = 0 And Nvl(c_成本调整.实际金额, 0) = 0 And Nvl(c_成本调整.实际差价, 0) = 0 Then
      --数量,金额、差价都为0，则表示数据是填单下可用数量出库产生的单据，此单据还没有审核，因此只需要更新调价信息，其他不更新
      Update 材料特性 Set 成本价 = c_成本调整.新成本价 Where 材料id = c_成本调整.材料id;
    
      Update 成本价调价信息
      Set 收发id = n_收发id, 执行日期 = d_调价时间, 效期 = c_成本调整.效期, 灭菌效期 = c_成本调整.灭菌效期, 产地 = c_成本调整.产地, 批号 = c_成本调整.批号
      Where ID = c_成本调整.调价id;
    Elsif Nvl(c_成本调整.实际数量, 0) = 0 And (Nvl(c_成本调整.实际金额, 0) <> 0 Or Nvl(c_成本调整.实际差价, 0) <> 0) Then
      --数量=0 金额或差价<>0时只更新库存表中对应的平均成本价和特性表中成本价，并产生成本价修正数据但是差价差=0，只记录最新成本价 
      --产生调价记录，只记录最新成本价 
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期, 审核人,
         审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率)
      Values
        (n_收发id, 1, 18, v_No, n_序号, c_成本调整.库房id, n_入出类别id, c_成本调整.上次供应商id, n_入出系数, c_成本调整.材料id, c_成本调整.批次, c_成本调整.产地,
         c_成本调整.批号, c_成本调整.效期, 0, c_成本调整.实际金额, c_成本调整.实际差价, 0, '卫生材料成本价调价', Zl_Username, d_调价时间, Zl_Username, d_调价时间,
         c_成本调整.生产日期, c_成本调整.批准文号, c_成本调整.新成本价, 1, c_成本调整.原成本价);
      --更新库存 
      Update 药品库存
      Set 平均成本价 = c_成本调整.新成本价, 上次采购价 = c_成本调整.新成本价
      Where 库房id = c_成本调整.库房id And 药品id = c_成本调整.材料id And Nvl(批次, 0) = c_成本调整.批次 And 性质 = 1;
      Update 材料特性 Set 成本价 = c_成本调整.新成本价 Where 材料id = c_成本调整.材料id;
    
      Update 成本价调价信息
      Set 收发id = n_收发id, 执行日期 = d_调价时间, 效期 = c_成本调整.效期, 灭菌效期 = c_成本调整.灭菌效期, 产地 = c_成本调整.产地, 批号 = c_成本调整.批号
      Where ID = c_成本调整.调价id;
    Else
      --调整相应的库存:原成本金额-实新成本金额 
      n_调整额   := (c_成本调整.实际金额 - c_成本调整.实际差价) - Round(c_成本调整.新成本价 * c_成本调整.实际数量, 2);
      n_原成本价 := c_成本调整.原成本价;
    
      If n_原成本价 <= 0 Then
        n_原成本价 := c_成本调整.上次采购价;
      End If;
    
      --目前：收发记录对应: 
      -- 扣率--> 原成本价 
      -- 单量-->新成本价 
      -- 填写数量-->库存实际数量 
      -- 零售价-->库存实际金额 
      -- 成本价-->库存实际差价 
      -- 差价-->本次调整额 
    
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期, 审核人,
         审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率)
      Values
        (n_收发id, 1, 18, v_No, n_序号, c_成本调整.库房id, n_入出类别id, c_成本调整.上次供应商id, n_入出系数, c_成本调整.材料id, c_成本调整.批次, c_成本调整.产地,
         c_成本调整.批号, c_成本调整.效期, c_成本调整.实际数量, c_成本调整.实际金额, c_成本调整.实际差价, n_调整额, '卫生材料成本价调价', Zl_Username, d_调价时间,
         Zl_Username, d_调价时间, c_成本调整.生产日期, c_成本调整.批准文号, c_成本调整.新成本价, 1, n_原成本价);
    
      --更新库存 
      Update 药品库存
      Set 实际差价 = Nvl(实际差价, 0) + n_调整额
      Where 库房id = c_成本调整.库房id And 药品id = c_成本调整.材料id And Nvl(批次, 0) = Nvl(c_成本调整.批次, 0) And 性质 = 1;
    
      If Sql%NotFound Then
        Insert Into 药品库存
          (库房id, 药品id, 批次, 性质, 实际差价, 上次批号, 效期, 上次产地, 上次供应商id, 上次生产日期, 批准文号, 灭菌效期)
        Values
          (c_成本调整.库房id, c_成本调整.材料id, c_成本调整.批次, 1, n_调整额, c_成本调整.批号, c_成本调整.效期, c_成本调整.产地, c_成本调整.上次供应商id, c_成本调整.生产日期,
           c_成本调整.批准文号, c_成本调整.灭菌效期);
      End If;
    
      Update 药品库存
      Set 上次采购价 = c_成本调整.新成本价
      Where 药品id = c_成本调整.材料id And 上次采购价 <> c_成本调整.新成本价;
    
      Update 材料特性
      Set 成本价 = c_成本调整.新成本价
      Where 材料id = c_成本调整.材料id And 成本价 <> c_成本调整.新成本价;
    
      --重新计算库存表中的平均成本价 
      Update 药品库存
      Set 平均成本价 = Decode(Nvl(批次, 0), 0, Decode((实际金额 - 实际差价) / 实际数量, 0, 上次采购价, (实际金额 - 实际差价) / 实际数量), 上次采购价)
      Where 药品id = c_成本调整.材料id And Nvl(批次, 0) = Nvl(c_成本调整.批次, 0) And 库房id = c_成本调整.库房id And 性质 = 1 And
            Nvl(实际数量, 0) <> 0;
      If Sql%NotFound Then
        Select 成本价 Into n_平均成本价 From 材料特性 Where 材料id = c_成本调整.材料id;
        Update 药品库存
        Set 平均成本价 = n_平均成本价
        Where 药品id = c_成本调整.材料id And 库房id = c_成本调整.库房id And Nvl(批次, 0) = Nvl(c_成本调整.批次, 0) And 性质 = 1;
      End If;
    
      --更新成本价调价信息 
      Update 成本价调价信息
      Set 收发id = n_收发id, 执行日期 = d_调价时间, 原成本价 = n_原成本价, 效期 = c_成本调整.效期, 灭菌效期 = c_成本调整.灭菌效期, 产地 = c_成本调整.产地,
          批号 = c_成本调整.批号
      Where ID = c_成本调整.调价id;
    End If;
  End Loop;

  --产生应付记录 
  For c_应付 In (Select Distinct a.供药单位id, a.药品id, a.发票号, a.发票日期, a.发票金额, b.名称, b.计算单位, b.规格
               From 成本价调价信息 A, 收费项目目录 B
               Where a.药品id = b.Id And Nvl(a.应付款变动, 0) = 1 And Nvl(a.供药单位id, 0) <> 0 And a.药品id = 材料id_In
               Order By a.供药单位id) Loop
  
    v_应付单据号 := Nextno(67);
  
    Select 应付记录_Id.Nextval Into v_应付id From Dual;
  
    Insert Into 应付记录
      (ID, 记录性质, 记录状态, 单位id, NO, 系统标识, 发票号, 发票日期, 发票金额, 品名, 规格, 填制人, 填制日期, 审核人, 审核日期, 摘要)
    Values
      (v_应付id, 1, 1, c_应付.供药单位id, v_应付单据号, 5, c_应付.发票号, c_应付.发票日期, c_应付.发票金额, c_应付.名称, c_应付.规格, Zl_Username, d_调价时间,
       Zl_Username, d_调价时间, '成本价调价自动产生应付款变动记录');
  
    If Nvl(c_应付.供药单位id, 0) <> 0 Then
      Update 应付余额 Set 金额 = Nvl(金额, 0) + Nvl(c_应付.发票金额, 0) Where 单位id = c_应付.供药单位id And 性质 = 1;
      If Sql%NotFound Then
        Insert Into 应付余额 (单位id, 性质, 金额) Values (c_应付.供药单位id, 1, Nvl(c_应付.发票金额, 0));
      End If;
    End If;
  End Loop;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_材料收发记录_成本价调价;
/

--96471:马政,2016-06-03,结存初始化问题调整
Create Or Replace Procedure Zl1_Autocloseaccount Is
  v_Lngid    药品结存记录.Id%Type;
  d_开始日期 药品结存记录.期初日期%Type;
  d_结束日期 药品结存记录.期末日期%Type;
  n_结存时点 Number(2);
  v_Error    Varchar2(255);
  Err_Custom Exception;
  d_计算日期     药品结存记录.期末日期%Type;
  n_结存id       药品结存记录.Id%Type;
  n_未审核结存id 药品结存记录.Id%Type;

  Cursor c_Stock Is
    Select Distinct b.Id
    From 部门性质说明 A, 部门表 B
    Where a.部门id = b.Id And a.工作性质 In ('西药库', '成药库', '中药库', '西药房', '成药房', '中药房', '制剂室') And
          To_Char(b.撤档时间, 'yyyy-MM-dd') = '3000-01-01'
    Order By b.Id;
  r_Stock c_Stock%RowType;
Begin
  --取结存时点，默认每月最后一日结存
  n_结存时点 := Nvl(zl_GetSysParameter(221), 0);

  --只有自动结存才走此过程，手工结存不在走此过程
  If n_结存时点 <> -1 Then
    --计算本次结存的结束日期；因为自动结存是对前一天数据进行结存，所以需要按当前日期提前一天来计算或判断，
    If n_结存时点 = 0 Or n_结存时点 > To_Number(To_Char(Trunc(Last_Day(Sysdate - 1)), 'dd')) Then
      --指定按每月最后一天结存；或者结存时点大于了本月最大天数，也按本月最后一天结存
      d_结束日期 := Trunc(Last_Day(Sysdate - 1)) + 1 - 1 / 24 / 60 / 60;
    Else
      d_结束日期 := Trunc(Sysdate - 1, 'MONTH') + n_结存时点 - 1 / 24 / 60 / 60;
    End If;
  
    --检查日期，在结存时点后才能进行自动结存
    If Sysdate - d_结束日期 > 0 Then
      For r_Stock In c_Stock Loop
        --判断期间内是否有结存(不算转结)
        --此处不再通过“期间”字段进行判断，而是通过结存时间点来判断：如2016-05-28 23：59：59，如有则不结存，无则结存
        Select Nvl(Max(ID), 0)
        Into n_结存id
        From 药品结存记录
        Where 库房id = r_Stock.Id And 期末日期 = d_结束日期 And 取消人 Is Null;
      
        If n_结存id > 0 Then
          --如果当前期间已经结存过了，就不再结存，一个期间只结存一次
          Null;
        Else
          --取库房最大的结存ID和本次结存的开始日期
          Select Nvl(Max(ID), 0), Max(期末日期) + 1 / 24 / 60 / 60
          Into n_结存id, d_开始日期
          From 药品结存记录
          Where 库房id = r_Stock.Id And 取消人 Is Null;
        
          --开始时间不能大于结束时间
          If d_开始日期 <= d_结束日期 Then
            If n_结存id > 0 Then
              --检查是否存在未审核的结存，如果存在则自动审核(通常情况都是在期间内手工审核)
              Select Nvl(Max(ID), 0)
              Into n_未审核结存id
              From 药品结存记录
              Where 库房id = r_Stock.Id And 审核日期 Is Null;
            
              If n_未审核结存id > 0 Then
                Zl_药品结存记录_Verify(n_未审核结存id, Zl_Username);
              End If;
            
              --产生新的结存记录
              Select 药品结存记录_Id.Nextval Into v_Lngid From Dual;
            
              Insert Into 药品结存记录
                (ID, 库房id, 期初日期, 期末日期, 填制人, 填制日期, 上次结存id, 期间, 性质)
              Values
                (v_Lngid, r_Stock.Id, d_开始日期, d_结束日期, Nvl(Zl_Username, 'zlhis'), Sysdate, n_结存id,
                 To_Char(Trunc(d_结束日期), 'yyyymm'), 1);
            
              --产生药品结存明细表，本期期末=本期期初(上期期末)+期间发生
              Insert Into 药品结存明细
                (结存id, 库房id, 药品id, 批次, 期初数量, 期初金额, 期初差价, 期末数量, 期末金额, 期末差价)
                Select v_Lngid, 库房id, 药品id, 批次, Sum(期初数量), Sum(期初金额), Sum(期初差价), Sum(期末数量), Sum(期末金额), Sum(期末差价)
                From (Select a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, a.期末数量 As 期初数量, a.期末金额 As 期初金额, a.期末差价 As 期初差价, a.期末数量,
                              a.期末金额, a.期末差价
                       From 药品结存明细 A, 药品规格 B
                       Where a.药品id = b.药品id And a.结存id = n_结存id
                       Union All
                       Select a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, 0 As 期初数量, 0 As 期初金额, 0 As 期初差价,
                              a.入出系数 * a.实际数量 * Nvl(a.付数, 1) As 期末数量, a.入出系数 * a.零售金额 As 期末金额, a.入出系数 * a.差价 As 期末差价
                       From 药品收发记录 A, 药品规格 B
                       Where a.药品id = b.药品id And a.库房id + 0 = r_Stock.Id And a.审核日期 Between d_开始日期 And d_结束日期)
                Group By 库房id, 药品id, 批次
                Order By 库房id, 药品id, 批次;
            
              --计算误差：本期期末-库存记录(减去本期期末时间后发生的数据)
              Insert Into 药品结存误差
                (ID, 结存id, 库房id, 药品id, 批次, 数量差, 金额差, 差价差)
                Select 药品结存误差_Id.Nextval, v_Lngid, a.库房id, a.药品id, a.批次, a.实际数量 As 数量差, a.实际金额 As 金额差, a.实际差价 As 差价差
                From (Select 库房id, 药品id, 批次, Sum(实际数量) As 实际数量, Sum(实际金额) As 实际金额, Sum(实际差价) As 实际差价
                       From (Select a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, Nvl(a.期末数量, 0) As 实际数量, Nvl(a.期末金额, 0) As 实际金额,
                                     Nvl(a.期末差价, 0) As 实际差价
                              From 药品结存明细 A, 药品规格 B
                              Where a.药品id = b.药品id And a.结存id = v_Lngid
                              Union All
                              Select a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, -1 * Nvl(a.实际数量, 0) As 实际数量,
                                     -1 * Nvl(a.实际金额, 0) As 实际金额, -1 * Nvl(实际差价, 0) As 实际差价
                              From 药品库存 A, 药品规格 B
                              Where a.药品id = b.药品id And a.性质 = 1 And a.库房id = r_Stock.Id
                              Union All
                              Select a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, a.入出系数 * a.实际数量 * Nvl(a.付数, 1) As 实际数量,
                                     a.入出系数 * a.零售金额 As 实际金额, a.入出系数 * a.差价 As 实际差价
                              From 药品收发记录 A, 药品规格 B
                              Where a.药品id = b.药品id And a.库房id = r_Stock.Id And a.审核日期 > d_结束日期) A
                       Group By 库房id, 药品id, 批次) A
                Where a.实际数量 <> 0 Or a.实际金额 <> 0 Or a.实际差价 <> 0;
              --自动结存后立马审核结存信息
              Zl_药品结存记录_Verify(v_Lngid, Zl_Username);
            End If;
          End If;
        End If;
        --按库房提交
        Commit;
      End Loop;
    End If;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl1_Autocloseaccount;
/

--91646:张德婷,2016-05-19,配药收取材料费
CREATE OR REPLACE Procedure Zl_输液配药记录_配药
(
  配药id_In   In Varchar2, --ID串:ID1,ID2....
  操作人员_In In 输液配药记录.操作人员%Type := Null,
  操作时间_In In 输液配药记录.操作时间%Type := Null
) Is
  v_Tansid   Varchar2(20);
  v_Tmp      Varchar2(4000);
  v_No       Varchar2(20);
  v_Usercode Varchar2(100);
  n_操作状态 输液配药记录.操作状态%Type;
  v_Error    Varchar2(255);
  n_People      number(1);
  n_row      number(2);
  d_执行时间 date;
  v_配药类型 varchar2(50);
  n_项目id   number(18);
  v_收费项目id varchar2(200);
  v_info    varchar2(200);
  v_id varchar2(20);
  n_数次 number(2);
  n_count number(18);
  n_Out number(10);
  n_OutNum number(10);
  n_打包状态 number(1);
  Err_Custom Exception;

  Cursor c_Bill Is
    Select a.病人id, a.主页id, a.标识号, a.姓名, a.性别, a.年龄, a.床号, a.费别, a.病人病区id, a.病人科室id, a.婴儿费, e.药品id, b.库房id,f.配药类型
    From 住院费用记录 A, 药品收发记录 B, 输液配药记录 C, 输液配药内容 D, 药品规格 E, 输液药品属性 F
    Where a.Id = b.费用id And b.Id = d.收发id And d.记录id = c.Id And b.药品id = e.药品id And b.药品id = f.药品id And Nvl(c.是否打包, 0) <> 1 And c.Id = v_Tansid;

Begin
  v_Usercode := Zl_Identity;
  v_Usercode := Substr(v_Usercode, Instr(v_Usercode, ';') + 1);
  v_Usercode := Substr(v_Usercode, Instr(v_Usercode, ',') + 1);
  v_Usercode := Substr(v_Usercode, 1, Instr(v_Usercode, ',') - 1);
  n_People:=Nvl(zl_GetSysParameter('配置费按病人收取', 1345), 0);
  n_Out:=Nvl(zl_GetSysParameter('出院病人不收配置费', 1345), 0);

  If 配药id_In Is Null Then
    v_Tmp := Null;
  Else
    v_Tmp := 配药id_In || ',';
  End If;

  While v_Tmp Is Not Null Loop
    --分解单据ID串
    v_Tansid := Substr(v_Tmp, 1, Instr(v_Tmp, ',') - 1);
    v_Tmp    := Replace(',' || v_Tmp, ',' || v_Tansid || ',');

    --检查当前输液单的状态是否为待摆药状态
    Begin
      Select 操作状态,执行时间,nvl(是否打包,0)  Into n_操作状态,d_执行时间,n_打包状态 From 输液配药记录 Where ID = v_Tansid;

      if n_操作状态>3 then
        v_Error := '该数据已被操作，不能进行发药！';
        Raise Err_Custom;
      end if;
    Exception
      When Others Then
        v_Error := '该数据已被操作！';
        Raise Err_Custom;
    End;

    Update 输液配药记录 Set 操作状态 = 4, 操作人员 = 操作人员_In, 操作时间 = 操作时间_In Where ID = v_Tansid;
    Insert Into 输液配药状态 (配药id, 操作类型, 操作人员, 操作时间) Values (v_Tansid, 4, 操作人员_In, 操作时间_In);

    if n_打包状态=0 then
      n_count:=0;
      Select Nextno(14) Into v_No From Dual;
      For r_Bill In c_Bill Loop
        Select count(病人id) into n_OutNum From 病案主页 where 主页ID=r_Bill.主页id And 病人ID=r_Bill.病人id  And (Nvl(状态,0)=3 Or 出院日期 Is Not NULL);
        if n_count=0 and (n_OutNum=0 or n_out=0) then
          --收取材料费
          --v_收费项目id:='6970,2;6971,1;';
          select zl_fun_PIVACustom(v_Tansid) into  v_收费项目id from dual;
          While v_收费项目id Is Not Null Loop
             v_info:= Substr(v_收费项目id, 1, Instr(v_收费项目id, ';') - 1);
             v_收费项目id := Replace(';' || v_收费项目id, ';' || v_info || ';');
             

             v_id:= Substr(v_info, 1, Instr(v_info, ',') - 1);
             v_info := Replace(',' || v_info, ',' || v_id || ',');

            For r_Item In (Select a.Id 收费细目id, a.类别 收费类别, a.计算单位, a.加班加价 加班标志, d.Id 收入项目id, d.收据费目, b.现价
                           From 收费项目目录 A, 收费价目 B, 收入项目 D
                           Where a.Id = b.收费细目id And b.收入项目id = d.Id And a.id=v_id and
                                 b.执行日期 <= Sysdate And
                                 (b.终止日期 >= Sysdate Or b.终止日期 Is Null)) Loop
              if n_count=0 then
                Insert Into 输液配药附费 (配药id, NO,病人id) Values (v_Tansid, v_No, r_Bill.病人id);
              end if;

              n_count:=n_count+1;
              Zl_住院记帐记录_Insert(v_No, n_count, r_Bill.病人id, r_Bill.主页id, r_Bill.标识号, r_Bill.姓名, r_Bill.性别, r_Bill.年龄, r_Bill.床号,
                               r_Bill.费别, r_Bill.病人病区id, r_Bill.病人科室id, r_Item.加班标志, r_Bill.婴儿费, r_Bill.库房id, 操作人员_In, Null,
                               r_Item.收费细目id, r_Item.收费类别, r_Item.计算单位, Null, Null, Null, 1,v_info, Null, r_Bill.库房id, Null,
                               r_Item.收入项目id, r_Item.收据费目, r_Item.现价, r_Item.现价*v_info, r_Item.现价*v_info, Null, Sysdate, Sysdate, Null, Null,
                               v_Usercode, 操作人员_In);
            End Loop;
          end loop;
        end if;


        select count(项目id) into n_项目id from 配置收费方案 where 配药类型=substr(r_Bill.配药类型,INSTR(r_Bill.配药类型,'-',1,1)+1);
        if n_项目id<>0 then
          n_row:=0;
          select nvl(项目id,0) into n_项目id from 配置收费方案 where 配药类型=substr(r_Bill.配药类型,INSTR(r_Bill.配药类型,'-',1,1)+1);
          if n_People=1 then
            select count(配药id) into n_row from 输液配药附费 A,住院费用记录 B,输液配药记录 C where A.No=b.no and A.配药ID=C.id and b.病人id=r_Bill.病人id And B.记录状态=1 and B.收费细目id=n_项目id and d_执行时间 Between Trunc(c.执行时间) And Trunc(c.执行时间+1) - 1 / 24 / 60 / 60;
          end if;
        else
          n_row:=1;
        end if;

        if n_row=0 and (n_OutNum=0 or n_out=0) then
          For r_Item In (Select a.Id 收费细目id, a.类别 收费类别, a.计算单位, a.加班加价 加班标志, d.Id 收入项目id, d.收据费目, b.现价
                         From 收费项目目录 A, 收费价目 B, 收入项目 D
                         Where a.Id = b.收费细目id And b.收入项目id = d.Id And a.id=n_项目id and
                               b.执行日期 <= Sysdate And
                               (b.终止日期 >= Sysdate Or b.终止日期 Is Null)) Loop
            if n_count=0 then
              Insert Into 输液配药附费 (配药id, NO,病人id) Values (v_Tansid, v_No, r_Bill.病人id);
            end if;

            n_count:=n_count+1;
            Zl_住院记帐记录_Insert(v_No, n_count, r_Bill.病人id, r_Bill.主页id, r_Bill.标识号, r_Bill.姓名, r_Bill.性别, r_Bill.年龄, r_Bill.床号,
                             r_Bill.费别, r_Bill.病人病区id, r_Bill.病人科室id, r_Item.加班标志, r_Bill.婴儿费, r_Bill.库房id, 操作人员_In, Null,
                             r_Item.收费细目id, r_Item.收费类别, r_Item.计算单位, Null, Null, Null, 1, 1, Null, r_Bill.库房id, Null,
                             r_Item.收入项目id, r_Item.收据费目, r_Item.现价, r_Item.现价, r_Item.现价, Null, Sysdate, Sysdate, Null, Null,
                             v_Usercode, 操作人员_In);
          End Loop;
        end if;

        if n_People<>1 and n_row=0 then
          Exit;
        end if;
      End Loop;
    end if;
  End Loop;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_输液配药记录_配药;
/

--96319:马政,2016-05-17,费用精度和流通精度调整
--98760:李业庆,2016-07-20,修改不产生修正记录问题
Create Or Replace Procedure Zl_药品收发记录_调价修正(收发id_In In 药品收发记录.Id%Type) Is
  v_单据         药品收发记录.单据%Type;
  v_入出类别id   药品收发记录.入出类别id%Type;
  v_售价精度     Number;
  v_金额精度     Number;
  v_收发id       药品收发记录.Id%Type;
  v_原价         药品收发记录.零售价%Type;
  v_现价         药品收发记录.零售价%Type;
  v_是否变价     收费项目目录.是否变价%Type;
  v_价格id       收费价目.Id%Type;
  v_库房id       药品收发记录.库房id%Type;
  v_药品id       药品收发记录.药品id%Type;
  v_批次         药品收发记录.批次%Type;
  v_实际数量     药品收发记录.实际数量%Type;
  v_修正金额     药品收发记录.零售金额%Type;
  v_入出系数     药品收发记录.入出系数%Type;
  v_执行修正     Number;
  v_审核日期     药品收发记录.审核日期%Type;
  v_零售金额     药品收发记录.零售金额%Type;
  v_差价         药品收发记录.差价%Type;
  v_No           药品收发记录.No%Type;
  n_记录状态     药品收发记录.记录状态%Type;
  n_当前数量     药品库存.实际数量%Type;
  n_是否门诊记录 Number;
Begin
  --修正规则
  --售价：
  --定价：以收费价目现价与冲销价格判断，如果不等产生修正
  --时价：时价无库存不修正有库存则判断当前价格与冲销价格是否不同，不同则处理否则不处理

  --成本价：
  --有调价则修正，无调价检查库存与冲销价格是否不同，不同则修正，否则不修正

  --1、售价调剂处理
  --提取单据信息，原价，现价及药价属性
  Select a.单据, a.记录状态, a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, Nvl(a.实际数量, 0) * Nvl(a.付数, 1) As 实际数量, a.入出系数,
         Nvl(a.零售价, 0) 原价, b.现价, Nvl(c.是否变价, 0) 是否变价, b.Id As 价格id
  Into v_单据, n_记录状态, v_库房id, v_药品id, v_批次, v_实际数量, v_入出系数, v_原价, v_现价, v_是否变价, v_价格id
  From 药品收发记录 A, 收费价目 B, 收费项目目录 C
  Where a.药品id = b.收费细目id And a.药品id = c.Id And
        (Sysdate Between b.执行日期 And b.终止日期 Or Sysdate >= b.执行日期 And b.终止日期 Is Null) And a.Id = 收发id_In;

  --定价直接取收费价目中现价
  v_执行修正 := 0;
  If v_是否变价 = 0 Then
    v_执行修正 := 1;
  Else
    --时价药品的现售价，不能从收费价目中取，因为时价调价可能是按库房和批次来调整的
    v_现价 := 0;
  
    --从库存记录中取现价
    Begin
      Select Nvl(零售价, 0)
      Into v_现价
      From 药品库存
      Where 性质 = 1 And 库房id + 0 = v_库房id And 药品id = v_药品id And Nvl(批次, 0) = v_批次;
    Exception
      When Others Then
        v_现价 := 0;
    End;
    If v_现价 > 0 Then
      v_执行修正 := 1;
    End If;
  End If;

  If v_执行修正 = 1 Then
    If v_单据 = 8 Or v_单据 = 9 Or v_单据 = 10 Then
      If v_单据 = 8 Then
        n_是否门诊记录 := 1;
      Else
        Begin
          Select 1
          Into n_是否门诊记录
          From 药品收发记录 A, 门诊费用记录 B
          Where a.费用id = b.Id And Rownum < 2 And a.Id = 收发id_In;
        Exception
          When Others Then
            n_是否门诊记录 := 0;
        End;
      End If;
    
      --由于封装问题，门诊是采用流通精度，而住院未封装采用费用精度
      If n_是否门诊记录 = 1 Then
        --获取金额小数位数
        Select 精度 Into v_售价精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 2 And 单位 = 1 And 性质 = 0;
        Select 精度 Into v_金额精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5 And 性质 = 0;
      Else
        Select Zl_To_Number(Nvl(zl_GetSysParameter(157), '5')) Into v_售价精度 From Dual;
        Select Zl_To_Number(Nvl(zl_GetSysParameter(9), '2')) Into v_金额精度 From Dual;
      End If;
    Else
      v_售价精度 := 7;
      Select 精度 Into v_金额精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5 And 性质 = 0;
    End If;
  
    --比较原价和现价，不同则处理
    If v_原价 <> Round(v_现价, v_售价精度) Then
      Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
      Select Nextno(147) Into v_No From Dual;
    
      Select 类别id Into v_入出类别id From 药品单据性质 Where 单据 = 13;
    
      v_修正金额 := Round(v_入出系数 * (Round(v_现价, v_售价精度) - v_原价) * v_实际数量, v_金额精度);
    
      --产生调价修正记录
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 扣率, 零售金额, 差价, 摘要, 填制人,
         填制日期, 库房id, 入出系数, 价格id, 审核人, 审核日期, 费用id)
        Select v_收发id, 1, 13, v_No, 序号, v_入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, Abs(v_实际数量), 0, v_原价, 0, Round(v_现价, v_售价精度),
               扣率, v_修正金额, v_修正金额, '自动修正调价变动', 审核人, 审核日期, 库房id, 1, v_价格id, 审核人, 审核日期, 收发id_In
        From 药品收发记录
        Where ID = 收发id_In;
    
      --更新药品库存
      Zl_药品库存_Update(v_收发id, 2, 0);
    
    End If;
  End If;

  --2、成本价调价，只有冲销业务才修正
  If Mod(n_记录状态, 3) = 2 Then
    Select 库房id, 药品id, Nvl(批次, 0) 批次, 入出系数 * Nvl(实际数量, 0) * Nvl(付数, 1) As 实际数量, 入出系数 * 零售金额, 入出系数 * 差价, 成本价
    Into v_库房id, v_药品id, v_批次, v_实际数量, v_零售金额, v_差价, v_原价
    From 药品收发记录
    Where ID = 收发id_In;
  
    --取原始单据的审核时间
    Select a.审核日期
    Into v_审核日期
    From 药品收发记录 A, 药品收发记录 B
    Where b.Id = 收发id_In And a.单据 = b.单据 And a.No = b.No And a.库房id = b.库房id And a.药品id + 0 = b.药品id And a.序号 = b.序号 And
          (a.记录状态 = 1 Or Mod(a.记录状态, 3) = 0);
  
    v_执行修正 := 0;
    Begin
      Select 1, 新成本价
      Into v_执行修正, v_现价
      From 成本价调价信息
      Where 库房id + 0 = v_库房id And 药品id + 0 = v_药品id And Nvl(批次, 0) = v_批次 And 执行日期 > v_审核日期 And Rownum = 1
      Order By 执行日期 Desc;
    
    Exception
      When Others Then
        v_执行修正 := 0;
        v_现价     := 0;
      
        --可能是无库存调价，那么不需要库房id和批次判断，只需要用药品id和批次
        Begin
          Select 1, 新成本价
          Into v_执行修正, v_现价
          From 成本价调价信息
          Where 药品id + 0 = v_药品id And Nvl(批次, 0) = v_批次 And 执行日期 > v_审核日期 And Rownum = 1
          Order By 执行日期 Desc;
        
        Exception
          When Others Then
            --可能出现冲销或者退库很久以前的数据,这个时候价格可能已经发生变化,所以需要修正
            Begin
              Select 平均成本价, Nvl(实际数量, 0) As 实际数量
              Into v_现价, n_当前数量
              From 药品库存
              Where 库房id = v_库房id And 药品id = v_药品id And Nvl(批次, 0) = v_批次 And 性质 = 1;
            
              --无库存不处理，有库存可能有两种情况，1.原始有库存，冲销抵消后还有数量，2、原始无数量，新库存数据来源于冲销单据
              --如果是1情况那么则通过库存表原价格与冲销价格比较即可
              --如果是2情况那么则需要判断当前库存数量与冲销数量*入出系数是否相等，如果相等则说明肯定是原始无库存然后冲销根据产生的
              --这个时候需要粗略判断，只能通过规格来，另加一下价格判断，如价格为负数
              If n_当前数量 = v_实际数量 Then
                --说明是第二种情况产生的库存数据，这个时候可能产生的数据很离谱，则需要简单判断
                --93696问题需要将此语句屏蔽掉，及无库存退库时不再处理离谱数据
                Null;
              Else
                --说明是第一种情况产生的数据，直接用当前库存与冲销单据价格比较即可
                If Round(v_现价, 2) <> Round(v_原价, 2) Then
                  v_执行修正 := 1;
                End If;
              End If;
            Exception
              When Others Then
                v_执行修正 := 0;
            End;
        End;
    End;
  
    If v_执行修正 = 1 Then
      Select 精度 Into v_金额精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5 And 性质 = 0;
    
      v_修正金额 := (v_零售金额 - v_差价) - Round(Round(v_现价, 5) * v_实际数量, v_金额精度);
    
      If v_修正金额 <> 0 Then
        Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
        Select b.Id, b.系数
        Into v_入出类别id, v_入出系数
        From 药品单据性质 A, 药品入出类别 B
        Where a.类别id = b.Id And a.单据 = 5 And Rownum < 2;
      
        v_No := Nextno(25, v_库房id);
      
        --产生库存差价调整单
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期,
           审核人, 审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率, 灭菌效期, 费用id)
          Select v_收发id, 1, 5, v_No, 1, 库房id, v_入出类别id, 供药单位id, v_入出系数, 药品id, 批次, 产地, 批号, 效期, v_实际数量, v_零售金额, v_差价,
                 v_修正金额, '自动修正调价变动', 审核人, 审核日期, 审核人, 审核日期, 生产日期, 批准文号, v_现价, 1, 成本价, 灭菌效期, 收发id_In
          From 药品收发记录
          Where ID = 收发id_In;
      
        --更新库存
        Zl_药品库存_Update(v_收发id, 2, 0);
      
      End If;
    End If;
  End If;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品收发记录_调价修正;
/

--96319:马政,2016-05-17,费用精度和流通精度调整
Create Or Replace Procedure Zl_药品收发记录_销售出库
(
  Id_In           In 门诊费用记录.Id%Type,
  药品摘要_In     药品收发记录.摘要%Type := Null,
  频次_In         药品收发记录.频次%Type := Null,
  单量_In         药品收发记录.单量%Type := Null,
  用法_In         药品收发记录.用法%Type := Null,
  煎法_In         药品收发记录.外观%Type := Null,
  期效_In         药品收发记录.扣率%Type := Null,
  计价特性_In     药品收发记录.扣率%Type := Null,
  主页id_In       未发药品记录.主页id%Type := Null,
  备货材料_In     Number := 0,
  备货材料批次_In 药品收发记录.批次%Type := Null
) Is
  ----------------------------------
  --功能：收费、划价时按照参数设置分解药品并产生相应的收发记录
  --规则：
  --      1、循环游标判断总出库数量与游标中每条记录数量是否充足，如果充足就是总数量，不充足挨个遍历直到数量直到遍历完并退出
  --      2、金额计算方式：定价取收费价目表现价，时价分批取库存表零售价，时价不分批，零售金额/实际数量，并将所有批次的金额累加起来为总出库金额
  --参数：
  --      Id_In：门诊费用记录或者住院费用记录ID
  --      备货材料_In：只有高值卫材才需要传入，非0表示是高值卫材
  --      备货材料批次_In：只有高值卫材才需要传入，非空表示是高值卫材的批次
  --      药品摘要_In：可选参数
  --      频次_In；单量_In；用法_In；煎法_In；期效_In；计价特性_In，可选参数，医嘱记录产生
  -----------------------------------
  Cursor c_Stock
  (
    n_Outmode  Number,
    n_库房id   药品收发记录.库房id%Type,
    n_药品id   药品收发记录.药品id%Type,
    n_备货材料 Number,
    n_备货批次 药品收发记录.批次%Type
  ) Is
    Select 库房id, 药品id, 批次, 效期, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次供应商id, 上次采购价, 上次批号, 上次生产日期, 上次产地, 灭菌效期, 批准文号, 平均成本价, 零售价,
           上次扣率, 商品条码, 内部条码
    From 药品库存
    Where 药品id = n_药品id And 库房id = n_库房id And 性质 = 1 And
          Decode(n_备货材料, 0, 0, Nvl(n_备货批次, 0)) = Decode(n_备货材料, 0, 0, Nvl(n_备货批次, 0)) And
          (Nvl(批次, 0) = 0 Or 效期 Is Null Or 效期 > Trunc(Sysdate)) And Nvl(可用数量, 0) > 0
    Order By Decode(n_Outmode, 1, 效期, Null), Nvl(批次, 0);
  r_Stock c_Stock%RowType;

  n_Outmode      Number;
  n_分批         药品规格.药房分批%Type;
  n_时价         收费项目目录.是否变价%Type;
  n_当前数量     药品库存.实际数量%Type;
  n_费用金额小数 Number;
  n_费用单价小数 Number;
  n_流通金额小数 Number;
  n_流通单价小数 Number;
  n_标准单价     收费价目.现价%Type;
  n_当前单价     收费价目.现价%Type;
  n_类别         药品单据性质.类别id%Type;
  n_总金额       Number;
  n_总数量       药品库存.实际数量%Type;
  n_单据         药品单据性质.单据%Type;
  n_跟踪在用     材料特性.跟踪在用%Type;
  n_序号         门诊费用记录.序号%Type;
  v_名称         收费项目目录.名称%Type;
  n_虚拟库房id   部门表.Id%Type;
  n_库房id       部门表.Id%Type;
  n_优先级       身份.优先级%Type;
  n_Count        Number;
  Err_Custom Exception;
  v_Rust     Varchar2(300);
  v_Error    Varchar2(255);
  v_部门名称 部门表.名称%Type;

  v_单据类别   Varchar2(10);
  v_No         药品收发记录.No%Type;
  n_对方部门id 药品收发记录.对方部门id%Type;
  n_收费细目id 药品收发记录.药品id%Type;
  n_总出库数量 药品库存.实际数量%Type;
  n_发药库房id 药品收发记录.库房id%Type;
  n_记录性质   门诊费用记录.记录性质%Type;
  v_收费类别   门诊费用记录.收费类别%Type;
  n_多病人单   住院费用记录.多病人单%Type;
  n_医嘱序号   门诊费用记录.医嘱序号%Type;
  v_姓名       门诊费用记录.姓名%Type;
  n_付数       门诊费用记录.付数%Type;
  v_操作员     门诊费用记录.操作员姓名%Type;
  d_登记时间   门诊费用记录.登记时间%Type;
  n_门诊标志   门诊费用记录.门诊标志%Type;
  n_病人科室id 门诊费用记录.病人科室id%Type;
  n_标识号     门诊费用记录.标识号%Type;
  v_性别       门诊费用记录.性别%Type;
  n_年龄       门诊费用记录.年龄%Type;
  n_病人id     门诊费用记录.病人id%Type;
  v_发药窗口   门诊费用记录.发药窗口%Type;
  n_记录状态   门诊费用记录.记录状态%Type;

  --药品收发记录
  n_收发id   药品收发记录.Id%Type;
  n_扣率     药品收发记录.扣率%Type;
  d_灭菌效期 药品收发记录.灭菌效期%Type;
  d_灭菌日期 药品收发记录.灭菌日期%Type;

  v_其他出库no 药品收发记录.No%Type;
  n_出库序号   药品收发记录.序号%Type;
  n_定价售价   收费价目.现价%Type;
Begin
  Begin
    Select 类别, NO, 序号, 对方部门id, 收费细目id, 总出库数量, 发药库房id, 记录性质, 收费类别, 多病人单, 医嘱序号, 姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别,
           年龄, 病人id, 发药窗口, 记录状态, 标准单价
    Into v_单据类别, v_No, n_序号, n_对方部门id, n_收费细目id, n_总出库数量, n_发药库房id, n_记录性质, v_收费类别, n_多病人单, n_医嘱序号, v_姓名, n_付数, v_操作员,
         d_登记时间, n_门诊标志, n_病人科室id, n_标识号, v_性别, n_年龄, n_病人id, v_发药窗口, n_记录状态, n_标准单价
    From (Select '门诊' As 类别, NO, 序号, 病人科室id As 对方部门id, 收费细目id, 付数 * 数次 As 总出库数量, 执行部门id As 发药库房id, 记录性质, 收费类别, 0 As 多病人单,
                  医嘱序号, 姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别, 年龄, 病人id, 发药窗口, 记录状态, 标准单价
           From 门诊费用记录
           Where ID = Id_In
           Union All
           Select '住院' As 类别, NO, 序号, 病人科室id As 对方部门id, 收费细目id, 付数 * 数次 As 总出库数量, 执行部门id As 发药库房id, 记录性质, 收费类别, 多病人单, 医嘱序号,
                  姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别, 年龄, 病人id, 发药窗口, 记录状态, 标准单价
           From 住院费用记录
           Where ID = Id_In);
  Exception
    When Others Then
      v_No         := Null;
      n_对方部门id := 0;
      n_总出库数量 := 0;
  End;

  n_跟踪在用 := 0;
  --只处理有数量的
  If n_总出库数量 <> 0 Then
    --药品分批出库方式
    Select Zl_To_Number(Nvl(zl_GetSysParameter(150), 0)) Into n_Outmode From Dual;
    --金额小数位数
    Select Zl_To_Number(Nvl(zl_GetSysParameter(9), '2')), Zl_To_Number(Nvl(zl_GetSysParameter(157), '5'))
    Into n_费用金额小数, n_费用单价小数
    From Dual;
  
    --取流通业务精度位数
    --类别:1-药品 2-卫材
    --内容：2-零售价 4-金额
    --单位：药品:1-售价 5-金额单位
    If v_收费类别 = '4' Then
      Select 精度 Into n_流通单价小数 From 药品卫材精度 Where 类别 = 2 And 内容 = 2 And 单位 = 1;
      Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 2 And 内容 = 4 And 单位 = 5;
    Else
      Select 精度 Into n_流通单价小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 2 And 单位 = 1;
      Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5;
    End If;
  
    n_总数量 := n_总出库数量;
  
    If v_收费类别 = '4' Then
      --收费类别=4表示是卫材单据
      If v_单据类别 = '门诊' Then
        If n_记录性质 = 1 Then
          n_单据 := 24;
        Else
          n_单据 := 25;
        End If;
      Elsif v_单据类别 = '住院' Then
        If n_多病人单 = 1 Then
          n_单据 := 26;
        Else
          n_单据 := 25;
        End If;
      End If;
    
      Select Nvl(a.在用分批, 0), Nvl(b.是否变价, 0), b.名称, c.现价
      Into n_分批, n_时价, v_名称, n_定价售价
      From 材料特性 A, 收费项目目录 B, 收费价目 C
      Where a.材料id = b.Id And b.Id = n_收费细目id And b.Id = c.收费细目id And Sysdate Between c.执行日期 And c.终止日期;
      --跟踪在用
      Select 跟踪在用 Into n_跟踪在用 From 材料特性 Where 材料id = n_收费细目id;
      --备货卫材需要判断是否设置了虚拟库房对照
      If Nvl(备货材料_In, 0) = 1 Then
        Begin
          Select 虚拟库房id Into n_虚拟库房id From 虚拟库房对照 Where 科室id = n_发药库房id And Rownum <= 1;
        Exception
          When Others Then
            n_虚拟库房id := 0;
        End;
        If Nvl(n_虚拟库房id, 0) = 0 Then
          Begin
            Select 名称 Into v_Error From 部门表 Where ID = n_发药库房id;
          Exception
            When Others Then
              v_Error := '';
          End;
          v_Error := '执行部门"' || Nvl(v_Error, '') || '"未设置虚拟部门,请在卫材参数设置中设置.';
          Raise Err_Custom;
        End If;
      End If;
    Else
      --收费类别<>4表示是药品单据，收费类别有"5，6，7"
      If v_单据类别 = '门诊' Then
        If n_记录性质 = 1 Then
          n_单据 := 8;
        Else
          n_单据 := 9;
        End If;
      Elsif v_单据类别 = '住院' Then
        If n_多病人单 = 1 Then
          n_单据 := 10;
        Else
          n_单据 := 9;
        End If;
      End If;
    
      Select Nvl(a.药房分批, 0), Nvl(b.是否变价, 0), b.名称, c.现价
      Into n_分批, n_时价, v_名称, n_定价售价
      From 药品规格 A, 收费项目目录 B, 收费价目 C
      Where a.药品id = b.Id And b.Id = n_收费细目id And b.Id = c.收费细目id And Sysdate Between c.执行日期 And c.终止日期;
    End If;
  
    --可能分批时价药品分解的批次变了
    If n_时价 = 1 Then
      --只有一个批次时,直接取该批次的单价
      --按照最小单位进行格式化
      v_Rust     := Zl_Fun_Getprice(n_收费细目id, n_发药库房id, n_总出库数量, 备货材料_In, 备货材料批次_In);
      n_当前单价 := Round(To_Number(Substr(v_Rust, 1, Instr(v_Rust, '|') - 1)), n_费用单价小数);
      n_标准单价 := Round(n_标准单价, n_费用单价小数);
    
      If n_当前单价 <> n_标准单价 Then
        If n_医嘱序号 Is Null Then
          If v_收费类别 = '4' Then
            v_Error := '第 ' || n_序号 || ' 行的时价卫生材料"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          Else
            v_Error := '第 ' || n_序号 || ' 行的时价药品"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          End If;
        Else
          If v_收费类别 = '4' Then
            v_Error := '在处理病人"' || v_姓名 || '"时发现时价卫生材料"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                       '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          Else
            v_Error := '在处理病人"' || v_姓名 || '"时发现时价药品"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                       '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          End If;
        End If;
        Raise Err_Custom;
      End If;
    End If;
  
    If v_收费类别 In ('5', '6', '7') Or (v_收费类别 = '4' And Nvl(n_跟踪在用, 0) = 1) Then
      If Nvl(备货材料_In, 0) = 1 And v_收费类别 = '4' Then
        n_库房id := n_虚拟库房id;
      Else
        n_库房id := n_发药库房id;
      End If;
    
      If v_收费类别 = '4' Then
        Select 类别id Into n_类别 From 药品单据性质 Where 单据 = n_单据 + 16;
      Else
        Select 类别id Into n_类别 From 药品单据性质 Where 单据 = n_单据;
      End If;
    
      n_总金额 := 0;
      --打开游标
      Open c_Stock(n_Outmode, n_库房id, n_收费细目id, 备货材料_In, 备货材料批次_In);
      --循环遍历
      While n_总出库数量 <> 0 Loop
        Fetch c_Stock
          Into r_Stock;
        If c_Stock%NotFound Then
          --第一次就没有库存,分批或时价都不允许。
          --分批药品数量分解不完,也就是库存不足。
          If n_分批 = 1 Or n_时价 = 1 Then
            Close c_Stock;
            If n_单据 = 8 Or n_单据 = 24 Then
              If v_收费类别 = '4' Then
                v_Error := '第 ' || n_序号 || ' 行的分批或时价卫生材料"' || v_名称 || '"没有可用的库存！';
              Else
                v_Error := '第 ' || n_序号 || ' 行的分批或时价药品"' || v_名称 || '"没有可用的药品库存！';
              End If;
            Else
              --单据=9，10，25，26是记账单提示不一样
              If n_医嘱序号 Is Null Then
                If v_收费类别 = '4' Then
                  If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                    v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                  Else
                    v_Error := '第 ' || n_序号 || ' 行的分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                                 When Nvl(备货材料_In, 0) = 0 Then
                                  '！'
                                 Else
                                  ',不能进行备货记帐！'
                               End;
                  End If;
                Else
                  v_Error := '第 ' || n_序号 || ' 行的分批或时价药品"' || v_名称 || '"没有足够的库存！';
                End If;
              Else
                If v_收费类别 = '4' Then
                  If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                    v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                  Else
                    v_Error := '在处理病人"' || v_姓名 || '"时发现分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                                 When Nvl(备货材料_In, 0) = 0 Then
                                  '！'
                                 Else
                                  ',不能进行备货记帐！'
                               End;
                  End If;
                Else
                  v_Error := '在处理病人"' || v_姓名 || '"时发现分批或时价药品"' || v_名称 || '"没有足够的库存！';
                End If;
              End If;
            End If;
            Raise Err_Custom;
          End If;
        Elsif (n_分批 = 1 And Nvl(r_Stock.批次, 0) = 0) Or (n_分批 = 0 And Nvl(r_Stock.批次, 0) <> 0) Then
          Close c_Stock;
          If n_医嘱序号 Is Null Then
            If v_收费类别 = '4' Then
              v_Error := '第 ' || n_序号 || ' 行卫生材料"' || v_名称 || '"的在用分批属性与库存记录不相符,请检查材料数据的正确性！';
            Else
              v_Error := '第 ' || n_序号 || ' 行药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
            End If;
          Else
            If v_收费类别 = '4' Then
              v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"的分批属性与库存记录不相符,请检查材料数据的正确性！';
            Else
              v_Error := '在处理病人"' || v_姓名 || '"时发现药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
            End If;
          End If;
          Raise Err_Custom;
        End If;
      
        If c_Stock%Found Then
          If Nvl(r_Stock.实际数量, 0) = 0 And (n_总出库数量 > 0 Or n_时价 = 1) Then
            --实际数量为零时，不正常，不允许出库
            --实际数量不为零，金额为零，可能是正常的零价格管理。
            --负数的情况相当于入库,这种情况应是允许的；但时价需要计算价格，必须要有实际数量。
            Close c_Stock;
            If n_医嘱序号 Is Null Then
              If v_收费类别 = '4' Then
                v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
              Else
                v_Error := '第 ' || n_序号 || ' 行药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
              End If;
            Else
              If v_收费类别 = '4' Then
                v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
              Else
                v_Error := '在处理病人"' || v_姓名 || '"时发现药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
              End If;
            End If;
            Raise Err_Custom;
          End If;
        End If;
      
        If n_分批 = 1 Or n_时价 = 1 Then
          --对于不分批的时价只可能分解一次,分解不完上面判断了.它分解是为了计算单价.
          --每次分解取小者,库存不够分解不完在上面判断.
          If n_总出库数量 <= Nvl(r_Stock.可用数量, 0) Then
            n_当前数量 := n_总出库数量;
          Else
            n_当前数量 := Nvl(r_Stock.可用数量, 0);
          End If;
          If n_时价 = 1 Then
            n_当前单价 := Nvl(r_Stock.零售价, Nvl(r_Stock.实际金额 / r_Stock.实际数量, 0));
          Elsif n_分批 = 1 Then
            n_当前单价 := n_定价售价;
          End If;
        Else
          --定价不分批
          --非门诊单据且是高值卫材需要检查库存
          If n_单据 <> 8 Or n_单据 <> 24 Then
            If Nvl(备货材料_In, 0) = 1 And v_收费类别 = '4' Then
              If n_总出库数量 > Nvl(r_Stock.可用数量, 0) Then
                --不分批, 但又是备货卫材方式出库的,则需要检查当前库存是否充足.
                v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                Raise Err_Custom;
              End If;
            End If;
          End If;
          n_当前数量 := n_总出库数量;
          n_当前单价 := n_定价售价;
        End If;
      
        --药品收发记录
        If c_Stock%Found Then
          --卫材灭菌效期:一次性材料且有效期
          If v_收费类别 = '4' Then
            n_Count := 0;
            Begin
              Select 灭菌效期 Into n_Count From 材料特性 Where Nvl(一次性材料, 0) = 1 And 材料id = n_收费细目id;
            Exception
              When Others Then
                Null;
            End;
            If Nvl(n_Count, 0) > 0 Then
              d_灭菌效期 := r_Stock.灭菌效期;
              d_灭菌日期 := d_灭菌效期 - n_Count * 30;
            End If;
          End If;
        End If;
      
        Select Nvl(Max(序号), 0) + 1 Into n_序号 From 药品收发记录 Where 单据 = n_单据 And 记录状态 = 1 And NO = v_No;
      
        n_扣率 := Null;
        If 期效_In Is Not Null Or 计价特性_In Is Not Null Then
          n_扣率 := Nvl(期效_In, 0) || Nvl(计价特性_In, 0);
        End If;
      
        --分批药品,如果是只使用了一个批次,则要填写付数
        If n_分批 = 1 And n_当前数量 <> n_总数量 Then
          n_Count := 1;
        Else
          n_Count := 0;
        End If;
      
        Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        --修改的原单据号存放在摘要中
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
           填制日期, 费用id, 频次, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
        Values
          (n_收发id, 1, n_单据, v_No, n_序号, n_发药库房id, n_对方部门id, n_类别, -1, n_收费细目id, r_Stock.批次, r_Stock.上次产地, r_Stock.上次批号,
           r_Stock.效期, Decode(n_Count, 1, 1, n_付数), Decode(n_Count, 1, n_当前数量, n_当前数量 / n_付数),
           Decode(n_Count, 1, n_当前数量, n_当前数量 / n_付数),
           Decode(v_单据类别, '门诊', Round(n_当前单价, n_流通单价小数), Round(n_当前单价, n_费用单价小数)),
           Decode(v_单据类别, '门诊', Round(n_当前单价 * n_当前数量, n_流通金额小数), Round(n_当前单价 * n_当前数量, n_费用金额小数)), 药品摘要_In, v_操作员,
           d_登记时间, Id_In, 频次_In, 单量_In, 用法_In, 煎法_In, n_扣率, d_灭菌效期, d_灭菌日期, r_Stock.上次供应商id, r_Stock.上次生产日期,
           r_Stock.批准文号, r_Stock.商品条码, r_Stock.内部条码);
      
        --药品库存(普通情况可能没有记录)
        Zl_药品库存_Update(n_收发id, 0, 1);
      
        --产生其他出库单 ，只有高值卫材才需要处理
        If v_收费类别 = '4' And Nvl(备货材料_In, 0) = 1 Then
          Begin
            Select Max(a.No), Max(a.序号)
            Into v_其他出库no, n_出库序号
            From 药品收发记录 A, 住院费用记录 B
            Where a.费用id = b.Id And b.No = v_No And 记录性质 = 2 And b.门诊标志 = n_门诊标志 And
                  Instr(',8,9,10,21,24,25,26,', ',' || a.单据 || ',') > 0;
          Exception
            When Others Then
              v_其他出库no := Null;
          End;
          If v_其他出库no Is Null Then
            v_其他出库no := Nextno(74, n_虚拟库房id, Null, 1);
          End If;
          If v_其他出库no Is Null Then
            v_Error := '在生成卫生材料的其他出库单时,获取相关的出库NO有误,请检查出库单的规则是否有误!';
            Raise Err_Custom;
          End If;
          If Nvl(n_病人科室id, 0) <> 0 Then
            Select 名称 Into v_部门名称 From 部门表 Where ID = n_病人科室id;
          End If;
          v_Error := LPad(' ', 4);
          v_Error := Substr('病人姓名:' || v_姓名 || v_Error || '性别:' || v_性别 || v_Error || '年龄' || n_年龄 || v_Error || '门诊号:' ||
                            Nvl(n_标识号, '') || v_Error || '病人科室:' || v_部门名称, 1, 100);
        
          n_出库序号 := Nvl(n_出库序号, 0) + 1;
          Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        
          --高值卫材类别id默认19是为了方便统计，因为其他出库可以设置很多类别，所以默认19
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
             填制日期, 费用id, 频次, 发药窗口, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
          Values
            (n_收发id, 1, 21, v_其他出库no, n_出库序号, n_虚拟库房id, n_对方部门id, 19, -1, n_收费细目id, r_Stock.批次, r_Stock.上次产地,
             r_Stock.上次批号, r_Stock.效期, 1, n_当前数量, n_当前数量,
             Decode(v_单据类别, '门诊', Round(n_当前单价, n_流通单价小数), Round(n_当前单价, n_费用单价小数)),
             Decode(v_单据类别, '门诊', Round(n_当前单价 * n_当前数量, n_流通金额小数), Round(n_当前单价 * n_当前数量, n_费用金额小数)), v_Error, v_操作员,
             d_登记时间, Id_In, 频次_In, v_发药窗口, 单量_In, 用法_In, 煎法_In, n_扣率, d_灭菌效期, d_灭菌日期, r_Stock.上次供应商id, r_Stock.上次生产日期,
             r_Stock.批准文号, r_Stock.商品条码, r_Stock.内部条码);
        
          --药品库存(普通情况可能没有记录)
          Zl_药品库存_Update(n_收发id, 0, 1);
        End If;
      
        v_Error      := '';
        n_总出库数量 := n_总出库数量 - n_当前数量;
        n_总金额     := n_总金额 + n_当前数量 * n_当前单价;
      End Loop;
    
      --未发药品记录
      Update 未发药品记录
      Set 病人id = n_病人id, 姓名 = v_姓名, 发药窗口 = v_发药窗口, 主页id = 主页id_In
      Where 单据 = n_单据 And NO = v_No And Nvl(库房id, 0) = Nvl(n_发药库房id, 0);
      If Sql%RowCount = 0 Then
        --取身份优先级
        Begin
          Select b.优先级 Into n_优先级 From 病人信息 A, 身份 B Where a.身份 = b.名称(+) And a.病人id = n_病人id;
        Exception
          When Others Then
            Null;
        End;
        Insert Into 未发药品记录
          (单据, NO, 病人id, 主页id, 姓名, 优先级, 库房id, 对方部门id, 填制日期, 已收费, 打印状态, 发药窗口)
        Values
          (n_单据, v_No, n_病人id, 主页id_In, v_姓名, n_优先级, n_发药库房id, n_对方部门id, d_登记时间, n_记录状态, 0, v_发药窗口);
      End If;
    
      --处理未发药记录状态
      Zl_Prescription_Type_Update(v_No, n_记录性质, n_收费细目id, v_收费类别);
    
      Close c_Stock;
    End If;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品收发记录_销售出库;
/

