--100258:余智勇,2016-09-05,解决zlReports锁表问题
Declare
  n_C1  Number(10);
  n_C2  Number(10);
  v_Sql Varchar2(2000);
Begin
  Begin
    Select Count(1)
    Into n_C1
    From All_Tab_Columns
    Where Owner = 'ZLTOOLS' And Table_Name = 'ZLREPORTS' And Column_Name = '执行人员ID';
  Exception
    When Others Then
      n_C1 := 0;
  End;

  Begin
    Select Count(1)
    Into n_C2
    From All_Tab_Columns
    Where Owner = 'ZLTOOLS' And Table_Name = 'ZLRPTRUNHISTORY' And Column_Name = '执行人员ID';
  Exception
    When Others Then
      n_C2 := 0;
  End;

  If n_C1 = 1 Then
    v_Sql := 'Update Zltools.Zlreports A Set a.执行人员 =  (Select 姓名 From 人员表 B Where a.执行人员id = b.Id), a.最后执行时间 = a.执行开始时间 Where a.执行人员id Is Not Null';
    Execute Immediate v_Sql;
  End If;
  If n_C2 = 1 Then
    v_Sql := 'Update Zltools.Zlrptrunhistory A Set a.执行人员 = (Select 姓名 From 人员表 B Where a.执行人员id = b.Id) Where a.执行人员id Is Not Null';
    Execute Immediate v_Sql;
  End If;
End;
/

Alter Table zlTools.Zlrptrunhistory Drop Column 执行人员ID;
Alter Table zlTools.zlReports Drop Column 执行人员ID;


