--101414:冉俊明,2016-10-18,出诊表另存为模板功能调整
Declare
  --修正数据：
  --1.一个模板中若只含有按月排班的号源或只含有按周排班的号源则只修改模板类型即可
  --2.一个模板中若同时含有按月排班的号源又有按周排班的号源则将其分离为两个模板，缺省分别在出诊表名后追加"_周排班"、"_月排班"
  n_出诊id 临床出诊表.Id%Type;
Begin
  For c_出诊表 In (Select ID, Count(1) As 方式种类, Max(排班方式) As 排班方式
                From (Select a.Id, Nvl(c.排班方式, 0) As 排班方式
                       From 临床出诊表 A, 临床出诊安排 B, 临床出诊号源 C
                       Where a.Id = b.出诊id And b.号源id = c.Id And a.排班方式 = 3 And Nvl(c.排班方式, 0) In (1, 2)
                       Group By a.Id, Nvl(c.排班方式, 0))
                Group By ID) Loop
  
    If Nvl(c_出诊表.方式种类, 0) = 1 Then
      --1.调整模板的模板类型
      If Nvl(c_出诊表.排班方式, 0) = 1 Then
        Update 临床出诊表 Set 模板类型 = 1 Where ID = c_出诊表.Id;
      Else
        Update 临床出诊表 Set 模板类型 = 0 Where ID = c_出诊表.Id;
      End If;
    Elsif Nvl(c_出诊表.方式种类, 0) = 2 Then
      --2.1分离周排班模板
      Select 临床出诊表_Id.Nextval Into n_出诊id From Dual;
      Insert Into 临床出诊表
        (ID, 排班方式, 出诊表名, 应用范围, 科室id, 备注, 发布人, 发布时间, 模板类型)
        Select n_出诊id, 排班方式, 出诊表名 || '_周排班', 应用范围, 科室id, 备注, 发布人, 发布时间, 0
        From 临床出诊表
        Where ID = c_出诊表.Id;
    
      For c_安排 In (Select 临床出诊安排_Id.Nextval As 安排id, a.Id, a.号源id, a.项目id, a.医生id, a.医生姓名, a.排班规则, a.是否周六出诊, a.是否周日出诊,
                          a.操作员姓名, a.登记时间
                   From 临床出诊安排 A, 临床出诊号源 B
                   Where a.号源id = b.Id And Nvl(b.排班方式, 0) = 2 And a.出诊id = c_出诊表.Id) Loop
      
        Insert Into 临床出诊安排
          (ID, 出诊id, 号源id, 项目id, 医生id, 医生姓名, 排班规则, 是否周六出诊, 是否周日出诊, 操作员姓名, 登记时间)
        Values
          (c_安排.安排id, n_出诊id, c_安排.号源id, c_安排.项目id, c_安排.医生id, c_安排.医生姓名, c_安排.排班规则, c_安排.是否周六出诊, c_安排.是否周日出诊,
           c_安排.操作员姓名, c_安排.登记时间);
      
        For c_限制 In (Select ID From 临床出诊限制 Where 安排id = c_安排.Id) Loop
          Zl_临床出诊限制_Copy(c_限制.Id, c_安排.安排id);
        End Loop;
      End Loop;
    
      --2.2删除月排班模板中按周排班的号源安排
      For c_安排 In (Select a.Id
                   From 临床出诊安排 A, 临床出诊号源 B
                   Where a.号源id = b.Id And a.出诊id = c_出诊表.Id And Nvl(b.排班方式, 0) = 2) Loop
        Zl_临床出诊安排_Delete(c_安排.Id);
      End Loop;
    
      --2.3将原模板调整为月排班模板
      Update 临床出诊表 Set 出诊表名 = 出诊表名 || '_月排班', 模板类型 = 1 Where ID = c_出诊表.Id;
    End If;
  End Loop;
End;
/


