Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'--显示退费时,汇总到记录状态和执行状态一级,否则汇总到序号这一级' From Dual Union All
  Select 2,'--应收金额<>0是为了过滤结帐作废的记录(并且这种记录实收金额<>0,省略了条件)' From Dual Union All
  Select 3,'--实收金额<>0是为了过滤打折后实收为零或主从项汇总计算折扣后主项实收为零的记录' From Dual Union All
  Select 4,Null From Dual Union All
  Select 5,'--1.明细' From Dual Union All
  Select 6,'Select 病人id, 单据号, 日期, 类型, 项目, 数量, 单价, 费用金额, 自付金额' From Dual Union All
  Select 7,'              From (Select 病人id,A.NO As 单据号, To_Char(A.发生时间, ''YYYY-MM-DD'') As 日期, Nvl(B.费用类型, '' '') As 类型,' From Dual Union All
  Select 8,'       Nvl(C.名称, B.名称) As 项目, LTrim(To_Char(A.付数 * A.数次, ''999999990.00000'')) As 数量,' From Dual Union All
  Select 9,'       LTrim(To_Char(A.标准单价, ''999999990.00000'')) As 单价, LTrim(To_Char(A.实收金额, ''9999999990.00'')) As 费用金额,' From Dual Union All
  Select 10,'       LTrim(To_Char(A.实收金额 * Decode(B.费用类型, ''甲类药'', 0, ''A类诊疗'', 0, ''乙类药'', 0.2, ''C10诊疗'', 0.1, ''C15诊疗'',' From Dual Union All
  Select 11,'                                          0.15, ''C20诊疗'', 0.2, 1), ''9999999990.00'')) As 自付金额' From Dual Union All
  Select 12,'From (Select 病人id,Mod(记录性质, 10) As 记录性质, NO, Nvl(价格父号, 序号) As 序号, 收费细目id, 计算单位,' From Dual Union All
  Select 13,'              Avg(Nvl(付数, 1)) As 付数, Avg(数次) As 数次, Avg(标准单价) 标准单价, Sum(应收金额) As 应收金额,' From Dual Union All
  Select 14,'              Sum(实收金额) As 实收金额, 发生时间, 费用类型' From Dual Union All
  Select 15,'       From 住院费用记录 A,Table(f_str2list([0])) B' From Dual Union All
  Select 16,'       Where 1 = [3] And 记录状态 <> 0 And 记帐费用 = 1 And A.病人ID=B.Column_Value And' From Dual Union All
  Select 17,'             病人病区id = Decode([5], 0, 病人病区id, [5]) And [6] Between [1] And [2]' From Dual Union All
  Select 18,'       Group By 病人id,Mod(记录性质, 10), NO, 记录状态, Nvl(价格父号, 序号), 收费细目id, 计算单位, 执行状态, 发生时间, 费用类型' From Dual Union All
  Select 19,'       Having Nvl(Decode([4],1,Sum(应收金额),Sum(实收金额)), 0) <> 0 union all' From Dual Union All
  Select 20,'       Select 病人id,Mod(记录性质, 10) As 记录性质, NO, Nvl(价格父号, 序号) As 序号, 收费细目id, 计算单位,' From Dual Union All
  Select 21,'              Avg(Nvl(付数, 1)) As 付数, Avg(数次) As 数次, Avg(标准单价) 标准单价, Sum(应收金额) As 应收金额,' From Dual Union All
  Select 22,'              Sum(实收金额) As 实收金额, 发生时间, 费用类型' From Dual Union All
  Select 23,'       From 门诊费用记录 A,Table(f_str2list([0])) B' From Dual Union All
  Select 24,'       Where 1 = [3] And 记录状态 <> 0 And 记帐费用 = 1 And A.病人ID=B.Column_Value And [6] Between [1] And [2]' From Dual Union All
  Select 25,'       Group By 病人id,Mod(记录性质, 10), NO, 记录状态, Nvl(价格父号, 序号), 收费细目id, 计算单位, 执行状态, 发生时间, 费用类型' From Dual Union All
