Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,Null From Dual Union All
  Select 2,'Select Rownum As 序号, Rownum + 页号 - 1 As 病人页数, 病人ID,姓名, 性别, 年龄, 住院号, 身份证号, 医疗证号, 科室, 出院病床, 退休证号, 入院日期, 打印日期' From Dual Union All
  Select 3,'From (Select Distinct a.病人ID,a.姓名, a.性别, a.年龄, a.住院号, a.身份证号,            ' From Dual Union All
  Select 4,'                       c.医保号 As 医疗证号, d.名称 As 科室, b.出院病床,          ' From Dual Union All
  Select 5,'                       c.退休证号, To_Char(b.入院日期, ''YYYY-MM-DD'') As 入院日期,           ' From Dual Union All
  Select 6,'                       To_Char(Sysdate, ''YYYY-MM-DD'') As 打印日期,' From Dual Union All
  Select 7,'                       Ceil(Row_Number() Over(Partition By a.病人ID Order By a.病人ID) / 49) As 页号' From Dual Union All
  Select 8,'       From (Select 病人id, 单据号, 日期, 类型, 项目, 数量, 单价, 费用金额, 自付金额' From Dual Union All
  Select 9,'              From (Select 病人id, a.No As 单据号, To_Char(a.发生时间, ''YYYY-MM-DD'') As 日期, Nvl(b.费用类型, '' '') As 类型, Nvl(c.名称, b.名称) As 项目,' From Dual Union All
  Select 10,'                     LTrim(To_Char(a.付数 * a.数次, ''999999990.00000'')) As 数量, LTrim(To_Char(a.标准单价, ''999999990.00000'')) As 单价,' From Dual Union All
  Select 11,'                     LTrim(To_Char(a.实收金额, ''9999999990.00'')) As 费用金额,' From Dual Union All
  Select 12,'                     LTrim(To_Char(a.实收金额 * Decode(b.费用类型, ''甲类药'', 0, ''A类诊疗'', 0, ''乙类药'', 0.2, ''C10诊疗'', 0.1, ''C15诊疗'', 0.15,' From Dual Union All
  Select 13,'                                                    ''C20诊疗'', 0.2, 1), ''9999999990.00'')) As 自付金额' From Dual Union All
  Select 14,'              From (Select 病人id, Mod(记录性质, 10) As 记录性质, NO, Nvl(价格父号, 序号) As 序号, 收费细目id, 计算单位, Avg(Nvl(付数, 1)) As 付数,' From Dual Union All
  Select 15,'                            Avg(数次) As 数次, Avg(标准单价) 标准单价, Sum(应收金额) As 应收金额, Sum(实收金额) As 实收金额, 发生时间, 费用类型' From Dual Union All
  Select 16,'                     From 住院费用记录 A, Table(f_Str2list([ 0 ])) B' From Dual Union All
  Select 17,'                     Where 1 = [ 3 ] And 记录状态 <> 0 And 记帐费用 = 1 And a.病人id = b.Column_Value And' From Dual Union All
  Select 18,'                           病人病区id = Decode([ 5 ], 0, 病人病区id, [ 5 ]) And [ 6 ] Between [ 1 ] And [ 2 ]' From Dual Union All
  Select 19,'                     Group By 病人id, Mod(记录性质, 10), NO, 记录状态, Nvl(价格父号, 序号), 收费细目id, 计算单位, 执行状态, 发生时间, 费用类型' From Dual Union All
  Select 20,'                     Having Nvl(Decode([ 4 ], 1, Sum(应收金额), Sum(实收金额)), 0) <> 0' From Dual Union All
  Select 21,'                     Union All' From Dual Union All
  Select 22,'                     Select 病人id, Mod(记录性质, 10) As 记录性质, NO, Nvl(价格父号, 序号) As 序号, 收费细目id, 计算单位, Avg(Nvl(付数, 1)) As 付数,' From Dual Union All
