                               Where 计划id = n_计划id And 限制项目 = v_星期 And 数量 <> 0
                               Order By 序号) Loop
                    Exit When r_序号.状态 = 5 And r_序号.操作员姓名 = v_操作员姓名 And r_序号.机器名 = v_机器名;
