                          (号码, 日期, 序号, 状态, 操作员姓名, 备注, 登记时间, 机器名)
                        Values
                          (v_号码, Trunc(d_日期), n_号序, 5, v_操作员姓名, '移动锁号', Sysdate, v_机器名);
                    End;
