                        Nvl(挂号状态, 0) = 0 And To_Char(开始时间, 'hh24:mi') < To_Char(d_时段结束, 'hh24:mi') And Rownum < 2;
                  If Nvl(n_序号, 0) = 0 Then
                    If d_时段结束 Is Null Then
                      If Trunc(d_日期) = Trunc(Sysdate) Then
                        Select Min(序号)
                        Into n_序号
                        From 临床出诊序号控制
                        Where 记录id = n_记录id And 开始时间 >= d_日期 And Nvl(挂号状态, 0) = 0;
