                        Into n_序号
                        From 临床出诊序号控制
                        Where 记录id = n_记录id And 开始时间 >= d_日期 And Nvl(挂号状态, 0) = 0;
                        If Nvl(n_序号, 0) = 0 Then
                          Select Max(序号) + 1 Into n_序号 From 临床出诊序号控制 Where 记录id = n_记录id;
