  --获取原型片段目录根据原型ID>>>>>>>>>>>>>>>>>>>>>>>>>>>
  Procedure p_Get_FragmentDirectory_ByAid(
    Val           Out t_Refcur,
	Aid_In 影像报告原型片段.原型ID%Type
	) As
  Begin
    Open Val For
      Select 名称,
             RawToHex(ID) As ID,
             4 as ImageIndex,
             RawToHex(上级ID) As 上级ID,
             '<NULL/>' As 组成,
             编码,
             节点类型,
             是否私有,
             作者,
             标签,
			 说明,
             学科
        From 影像报告片段清单
       Where ID In (Select ID
                      From 影像报告片段清单
                     Start With ID In (Select 片段ID
                                         From 影像报告原型片段
                                        Where 原型ID = Aid_In)
                    Connect By Prior 上级ID = ID)
       order by 编码;
