  --1、接收RIS状态改变
  Procedure Receiverisstate(医嘱id_In   病人医嘱发送.医嘱id%Type,
                            Risid_In    病人医嘱报告.Risid%Type,
                            状态_In     Number,
                            操作人员_In 病人医嘱发送.完成人%Type,
                            执行时间_In 病人医嘱发送.完成时间%Type := Null,
                            执行说明_In 病人医嘱发送.执行说明%Type := Null,
                            单独执行_In Number := 0) Is
  
    --参数：医嘱ID_IN - 单独执行的医嘱ID。
    --      状态_IN - -1-删除；0-预约；1-登记；3-检查完成；4-检查中止；9-初步报告；12-报告审核；15-发放
    --     单独执行_In -0-全部执行；1-单独执行；检查医嘱组合是否采用对每个项目分散单独执行的方式
  
    Cursor c_Adviceinfo Is
      Select ID,
             相关id,
             Nvl(相关id, ID) As 组id,
             诊疗类别,
             病人来源,
             执行科室id
        From 病人医嘱记录
       Where ID = 医嘱id_In;
    r_Adviceinfo c_Adviceinfo%RowType;
  
    v_执行状态 病人医嘱发送.执行状态%Type;
    v_执行过程 病人医嘱发送.执行过程%Type;
    n_执行     Number; --标记是否需要更新状态，1：需要更新，其他不需要更新
    v_Count    Number;
    v_完成人   病人医嘱发送.完成人%Type;
    v_完成时间 病人医嘱发送.完成时间%Type;
  
  Begin
  
    v_执行状态 := 0;
    v_执行过程 := 0;
  
    --提取医嘱的主医嘱ID，及组ID
    Open c_Adviceinfo;
    Fetch c_Adviceinfo
      Into r_Adviceinfo;
    Close c_Adviceinfo;
  
    --根据状态_IN执行医嘱
    ---1-删除；0-预约；1-登记；3-检查完成；4-检查中止；9-初步报告；12-报告审核；13-取消审核；14-报告删除；15-发放
  
    If 状态_In = -1 Or 状态_In = 0 Then
      v_执行状态 := 0; --未执行
      v_执行过程 := 0;
    Elsif 状态_In = 1 Then
      v_执行状态 := 3; --正在执行
      v_执行过程 := 2; --已报到
    Elsif 状态_In = 3 Or 状态_In = 14 Then
      v_执行状态 := 3; --正在执行
      v_执行过程 := 3; --已检查
    Elsif 状态_In = 4 Then
      --不改变
      v_执行状态 := v_执行状态;
    Elsif 状态_In = 9 Or 状态_In = 13 Then
      v_执行状态 := 3; --正在执行
      v_执行过程 := 4; --已报告
    Elsif 状态_In = 12 Then
      v_执行状态 := 3; --正在执行
      v_执行过程 := 5; --已审核
    Elsif 状态_In = 15 Then
      v_执行状态 := 1; --完全执行
      v_执行过程 := 6; --已完成
      v_完成人   := 操作人员_In;
      v_完成时间 := 执行时间_In;
    End If;
  
    n_执行 := 1; --默认都要更新状态
  
    If 状态_In = 13 Or 状态_In = 14 Then
      --删除对应报告数据
      Delete From 电子病历记录
       Where ID = (Select 病历id
                     From 病人医嘱报告
                    Where 医嘱id = 医嘱id_In
                      And Risid = Risid_In);
      Delete From 病人医嘱报告
       Where 医嘱id = 医嘱id_In
         And Risid = Risid_In;
    
      --删除后判断是否还存在报告，若存在则医嘱状态保持不变，若报告全部删除则更新医嘱状态
      Select Count(1)
        Into v_Count
        From 病人医嘱报告
       Where 医嘱id = 医嘱id_In;
    
      If v_Count > 0 Then
        n_执行 := 0; --若存在则医嘱状态保持不变
      End If;
    End If;
  
    --开始执行医嘱
    If n_执行 = 1 Then
      If Nvl(单独执行_In, 0) = 1 Then
        -- 单个部位医嘱单独执行
        Update 病人医嘱发送
           Set 执行状态 = v_执行状态,
               执行过程 = v_执行过程,
               执行说明 = 执行说明_In,
               完成人   = v_完成人,
               完成时间 = v_完成时间
         Where 医嘱id = 医嘱id_In;
      Else
        Update 病人医嘱发送
           Set 执行状态 = v_执行状态,
               执行过程 = v_执行过程,
               执行说明 = 执行说明_In,
               完成人   = v_完成人,
               完成时间 = v_完成时间
         Where 医嘱id In
               (Select ID
                  From 病人医嘱记录
                 Where (ID = r_Adviceinfo.组id Or 相关id = r_Adviceinfo.组id));
      End If;
    End If;
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End Receiverisstate;

  --2、费用确认
  Procedure 影像费用执行(医嘱id_In     影像检查记录.医嘱id%Type,
                   单独执行_In   Number := 0,
                   操作员编号_In 人员表.编号%Type := Null,
                   操作员姓名_In 人员表.姓名%Type := Null,
                   执行部门id_In 门诊费用记录.执行部门id%Type := Null) Is
    --参数：医嘱ID_IN=单独执行的医嘱ID。
    --      单独执行_In=检查医嘱组合是否采用对每个项目分散单独执行的方式,0-不单独执行
    Cursor c_Advice Is
      Select ID, 相关id, Nvl(相关id, ID) As 组id, 诊疗类别, 病人来源
        From 病人医嘱记录
       Where ID = 医嘱id_In;
    r_Advice c_Advice%RowType;
  
    v_Temp     Varchar2(255);
    v_人员编号 人员表.编号%Type;
    v_人员姓名 人员表.姓名%Type;
    v_部门id   部门表.Id%Type;
    v_费用性质 病人医嘱发送.记录性质%Type;
    v_发送号   病人医嘱发送.发送号%Type;
    v_执行过程 病人医嘱发送.执行过程%Type;
    v_Count    Number;
    v_Error    Varchar2(255);
    Err_Custom Exception;
  Begin
  
    --取主医嘱ID
    Open c_Advice;
    Fetch c_Advice
      Into r_Advice;
    Close c_Advice;
  
    Select 发送号, 执行过程
      Into v_发送号, v_执行过程
      From 病人医嘱发送
     Where 医嘱id = r_Advice.组id;
  
    --登记和完成才执行费用  2-登记，3-检查，4-报告，5-审核，6-完成
    If v_执行过程 >= 2 Or v_执行过程 <= 6 Then
    
      --先检查是否已经出院的住院病人，已经预出院或者出院的检查申请，不允执行费用
      Select Count(*)
        Into v_Count
        From 病人医嘱记录 A, 病案主页 B
       Where a.病人id = b.病人id
         And a.主页id = b.主页id
         And (b.出院日期 Is Not Null Or b.状态 = 3)
         And a.Id = r_Advice.组id;
    
      If v_Count > 0 Then
        v_Error := '住院病人已经出院或者预出院，不能执行费用。';
        Raise Err_Custom;
      End If;
    
      --取当前操作人员
      If 操作员编号_In Is Not Null And 操作员姓名_In Is Not Null And
         执行部门id_In Is Not Null Then
        v_人员编号 := 操作员编号_In;
        v_人员姓名 := 操作员姓名_In;
        v_部门id   := 执行部门id_In;
      Else
        v_Temp     := Zl_Identity;
        v_部门id   := Substr(v_Temp, 1, Instr(v_Temp, ',') - 1);
        v_Temp     := Substr(v_Temp, Instr(v_Temp, ';') + 1);
        v_Temp     := Substr(v_Temp, Instr(v_Temp, ',') + 1);
        v_人员编号 := Substr(v_Temp, 1, Instr(v_Temp, ',') - 1);
        v_人员姓名 := Substr(v_Temp, Instr(v_Temp, ',') + 1);
      End If;
    
      If r_Advice.病人来源 = 2 Then
        Select Decode(记录性质, 1, 1, Decode(门诊记帐, 1, 1, 2))
          Into v_费用性质
          From 病人医嘱发送
         Where 发送号 = v_发送号
           And 医嘱id = 医嘱id_In;
      Else
        v_费用性质 := 1;
      End If;
    
      --执行费用和自动发料
      If v_费用性质 = 1 Then
        Zl_门诊医嘱执行_Finish(医嘱id_In,
                         v_发送号,
                         单独执行_In,
                         v_人员编号,
                         v_人员姓名,
                         r_Advice.组id,
                         r_Advice.诊疗类别,
                         v_部门id);
      Else
        Zl_住院医嘱执行_Finish(医嘱id_In,
                         v_发送号,
                         单独执行_In,
                         v_人员编号,
                         v_人员姓名,
                         r_Advice.组id,
                         r_Advice.诊疗类别,
                         v_部门id);
      End If;
    End If;
  
  Exception
    When Err_Custom Then
      Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End 影像费用执行;

  --3、取消费用确认
  Procedure 影像费用执行_Cancel(医嘱id_In     影像检查记录.医嘱id%Type,
                          单独执行_In   Number := 0,
                          操作员编号_In 人员表.编号%Type := Null,
                          操作员姓名_In 人员表.姓名%Type := Null,
                          执行部门id_In 门诊费用记录.执行部门id%Type := Null) Is
    --参数：
    --      医嘱ID_IN=单独执行的医嘱ID。
    --      单独执行_In=检查医嘱组合是否采用对每个项目分散单独执行的方式,0-不单独执行
  
    Cursor c_Advice Is
      Select ID, 相关id, Nvl(相关id, ID) As 组id
        From 病人医嘱记录
       Where ID = 医嘱id_In;
    r_Advice c_Advice%RowType;
  
    v_发送号 病人医嘱发送.发送号%Type;
    v_Count  Number;
    v_Error  Varchar2(255);
    Err_Custom Exception;
  
  Begin
  
    --取主医嘱ID
    Open c_Advice;
    Fetch c_Advice
      Into r_Advice;
    Close c_Advice;
  
    --先检查是否已经出院的住院病人，已经预出院或者出院的检查申请，不允执行费用
    Select Count(*)
      Into v_Count
      From 病人医嘱记录 A, 病案主页 B
     Where a.病人id = b.病人id
       And a.主页id = b.主页id
       And (b.出院日期 Is Not Null Or b.状态 = 3)
       And a.Id = r_Advice.组id;
  
    If v_Count > 0 Then
      v_Error := '住院病人已经出院或者预出院，不能取消费用。';
      Raise Err_Custom;
    End If;
  
    Select 发送号
      Into v_发送号
      From 病人医嘱发送
     Where 医嘱id = r_Advice.组id;
  
    --调用统一的医嘱执行Cancel过程
    Zl_病人医嘱执行_Cancel(医嘱id_In,
                     v_发送号,
                     Null,
                     单独执行_In,
                     执行部门id_In,
                     操作员编号_In,
                     操作员姓名_In);
  
  Exception
    When Err_Custom Then
      Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End 影像费用执行_Cancel;

  --4、接收RIS的报告
  Procedure Receivereport(医嘱id_In   病人医嘱发送.医嘱id%Type,
                          Risid_In    病人医嘱报告.Risid%Type,
                          报告所见_In 电子病历内容.内容文本%Type,
                          报告意见_In 电子病历内容.内容文本%Type,
                          报告建议_In 电子病历内容.内容文本%Type,
                          报告医生_In 电子病历记录.创建人%Type) Is
    --提取病人医嘱及报告的相关信息
    Cursor c_Advice(v_组id Number, v_Risid Number) Is
      Select e.Id,
             e.病人来源,
             e.病人id,
             e.主页id,
             e.婴儿,
             e.病人科室id,
             e.文件id,
             e.病历种类,
             e.病历名称,
             f.病历id,
             e.执行科室id
        From (Select c.Id,
                     c.病人来源,
                     c.病人id,
                     c.主页id,
                     c.婴儿,
                     c.病人科室id,
                     c.文件id,
                     d.种类       病历种类,
                     d.名称       病历名称,
                     c.执行科室id
                From (Select a.Id,
                             a.病人来源,
                             a.病人id,
                             a.主页id,
                             a.婴儿,
                             a.病人科室id,
                             b.病历文件id 文件id,
                             a.执行科室id
                        From 病人医嘱记录 A, 病历单据应用 B
                       Where a.Id = v_组id
                         And a.诊疗项目id = b.诊疗项目id(+)
                         And b.应用场合(+) = Decode(a.病人来源, 2, 2, 4, 4, 1)) C,
                     病历文件列表 D
               Where c.文件id = d.Id(+)) E,
             病人医嘱报告 F
       Where e.Id = f.医嘱id(+)
         And f.Risid(+) = v_Risid;
  
    --查找文件的组成元素
    Cursor c_File(v_File Number) Is
      Select a.Id,
             a.文件id,
             a.父id,
             a.对象序号,
             a.对象类型,
             a.对象标记,
             a.保留对象,
             a.对象属性,
             a.内容行次,
             a.内容文本,
             a.是否换行,
             a.预制提纲id,
             a.复用提纲,
             a.使用时机,
             a.诊治要素id,
             a.替换域,
             a.要素名称,
             a.要素类型,
             a.要素长度,
             a.要素小数,
             a.要素单位,
             a.要素表示,
             a.输入形态,
             a.要素值域
        From 病历文件结构 A
       Where a.文件id = v_File
       Order By a.对象序号;
  
    Cursor c_Report(v_电子病历记录id Number) Is
      Select b.Id, a.内容文本
        From 电子病历内容 A, 电子病历内容 B
       Where a.对象类型 = 3
         And a.Id = b.父id
         And b.对象类型 = 2
         And b.终止版 = 0
         And a.文件id = v_电子病历记录id;
  
    Cursor c_Content(v_文件id Number, v_表格id Number) Is
      Select a.Id,
             a.文件id,
             a.父id,
             a.对象序号,
             a.对象类型,
             a.对象标记,
             a.保留对象,
             a.对象属性,
             a.内容行次,
             a.内容文本,
             a.是否换行,
             a.预制提纲id,
             a.复用提纲,
             a.使用时机,
             a.诊治要素id,
             a.替换域,
             a.要素名称,
             a.要素类型,
             a.要素长度,
             a.要素小数,
             a.要素单位,
             a.要素表示,
             a.输入形态,
             a.要素值域
        From 病历文件结构 A
       Where 文件id = v_文件id
         And 父id = v_表格id;
  
    r_Advice        c_Advice%RowType;
    v_病历id        电子病历内容.文件id%Type;
    v_病历内容id    电子病历内容.Id%Type;
    v_病历内容idnew 电子病历内容.Id%Type;
    v_对象序号      电子病历内容.对象序号%Type;
    v_父id          电子病历内容.父id%Type;
    v_内容文本      电子病历内容.内容文本%Type;
    v_定义提纲id    电子病历内容.定义提纲id%Type;
    --v_格式内容    电子病历格式.内容%Type;
    v_Error Varchar2(255);
    Err_Custom Exception;
    v_主医嘱id 病人医嘱发送.医嘱id%Type;
    v_表格     Varchar2(300);
    n_数量     Number;
    n_rptCount Number;
    v_病历名称 电子病历记录.病历名称%Type;
  Begin
  
    -- 提取主医嘱ID ，防止因为传入部位医嘱，导致报告保存出错
    Select Nvl(相关id, ID) As 组id
      Into v_主医嘱id
      From 病人医嘱记录
     Where ID = 医嘱id_In;
  
    Open c_Advice(v_主医嘱id, Nvl(Risid_In, 0));
    Fetch c_Advice
      Into r_Advice;
  
    If Nvl(r_Advice.文件id, 0) = 0 Then
      v_Error := '本次检查项目没有对应相关的检查报告，请与管理员联系！';
      Raise Err_Custom;
    Else
      If Nvl(r_Advice.病历id, 0) > 0 Then
        ----产生过报告
        --找出检查已填写的报告提纲中含有"%所见%","%描述%","%建议%","%意见%",并用传入的参数更新
        For r_Report In c_Report(r_Advice.病历id) Loop
          If r_Report.内容文本 Like '%所见%' Then
            Update 电子病历内容
               Set 内容文本 = 报告所见_In || Chr(13) || Chr(13)
             Where ID = r_Report.Id;
          Elsif r_Report.内容文本 Like '%意见%' Then
            Update 电子病历内容
               Set 内容文本 = 报告意见_In || Chr(13) || Chr(13)
             Where ID = r_Report.Id;
          Elsif r_Report.内容文本 Like '%建议%' Then
            Update 电子病历内容
               Set 内容文本 = 报告建议_In || Chr(13) || Chr(13)
             Where ID = r_Report.Id;
          End If;
        End Loop;
        --更新保存时间
        Update 电子病历记录
           Set 完成时间 = Sysdate, 保存人 = 报告医生_In, 保存时间 = Sysdate
         Where ID = r_Advice.病历id;
      Else
        --先判断单据中是否有对应的提纲和表格
        If Nvl(报告所见_In, ' ') <> ' ' Then
          Select Count(1)
            Into n_数量
            From 病历文件结构 A, 病历文件结构 B
           Where a.父id = b.Id
             And a.对象类型 = 3
             And b.对象类型 = 1
             And a.内容文本 Like '%所见%'
             And a.文件id = r_Advice.文件id;
        
          If n_数量 <= 0 Then
            v_Error := '在诊疗单据中没有找到【所见】对应的提纲或表格，请联系管理员设置！';
            Raise Err_Custom;
          End If;
        End If;
        If Nvl(报告意见_In, ' ') <> ' ' Then
          Select Count(1)
            Into n_数量
            From 病历文件结构 A, 病历文件结构 B
           Where a.父id = b.Id
             And a.对象类型 = 3
             And b.对象类型 = 1
             And a.内容文本 Like '%意见%'
             And a.文件id = r_Advice.文件id;
        
          If n_数量 <= 0 Then
            v_Error := '在诊疗单据中没有找到【意见】对应的提纲或表格，请联系管理员设置！';
            Raise Err_Custom;
          End If;
        End If;
        If Nvl(报告建议_In, ' ') <> ' ' Then
          Select Count(1)
            Into n_数量
            From 病历文件结构 A, 病历文件结构 B
           Where a.父id = b.Id
             And a.对象类型 = 3
             And b.对象类型 = 1
             And a.内容文本 Like '%建议%'
             And a.文件id = r_Advice.文件id;
        
          If n_数量 <= 0 Then
            v_Error := '在诊疗单据中没有找到【建议】对应的提纲或表格，请联系管理员设置！';
            Raise Err_Custom;
          End If;
        End If;
      
        --产生电子病历记录
        Select 电子病历记录_Id.Nextval Into v_病历id From Dual;
        Select count(医嘱id)+1 into n_rptCount from 病人医嘱报告 where 医嘱ID = v_主医嘱id;
        if n_rptCount >1 then 
          v_病历名称 :=r_Advice.病历名称 || n_rptCount;
        else
          v_病历名称 :=r_Advice.病历名称;
        end if;
        Insert Into 电子病历记录
          (ID,
           病人来源,
           病人id,
           主页id,
           婴儿,
           科室id,
           病历种类,
           文件id,
           病历名称,
           创建人,
           创建时间,
           完成时间,
           保存人,
           保存时间,
           最后版本,
           签名级别)
        Values
          (v_病历id,
           r_Advice.病人来源,
           r_Advice.病人id,
           r_Advice.主页id,
           r_Advice.婴儿,
           r_Advice.病人科室id,
           r_Advice.病历种类,
           r_Advice.文件id,
           v_病历名称,
           报告医生_In,
           Sysdate,
           Sysdate,
           报告医生_In,
           Sysdate,
           1,
           2);
      
        --产生医嘱报告记录
        Insert Into 病人医嘱报告
          (医嘱id, 病历id, Risid)
        Values
          (v_主医嘱id, v_病历id, Risid_In);
      
        v_对象序号 := 0;
      
        --新产生报告内容
        For r_File In c_File(r_Advice.文件id) Loop
          Select 电子病历内容_Id.Nextval Into v_病历内容id From Dual;
          v_内容文本   := r_File.内容文本;
          v_定义提纲id := 0;
        
          If Nvl(r_File.对象类型, 0) = 1 And Nvl(r_File.父id, 0) = 0 Then
            --提纲
            v_定义提纲id := r_File.Id;
            v_父id       := v_病历内容id;
          End If;
        
          If Nvl(r_File.对象类型, 0) = 4 And r_File.要素名称 Is Not Null Then
            --元素
            v_内容文本 := Zl_Replace_Element_Value(r_File.要素名称,
                                               r_Advice.病人id,
                                               r_Advice.主页id,
                                               r_Advice.病人来源,
                                               r_Advice.Id);
          End If;
        
          If Nvl(r_File.父id, 0) <> 0 Then
            v_定义提纲id := 0;
          End If;
        
          v_对象序号 := v_对象序号 + 1;
        
          If Instr(v_表格, '|' || r_File.父id || '|') > 0 Then
            Null;
          Else
            Insert Into 电子病历内容
              (ID,
               文件id,
               开始版,
               终止版,
               父id,
               对象序号,
               对象类型,
               对象标记,
               保留对象,
               对象属性,
               内容行次,
               内容文本,
               是否换行,
               预制提纲id,
               复用提纲,
               使用时机,
               诊治要素id,
               替换域,
               要素名称,
               要素类型,
               要素长度,
               要素小数,
               要素单位,
               要素表示,
               输入形态,
               要素值域,
               定义提纲id)
            Values
              (v_病历内容id,
               v_病历id,
               1,
               0,
               Decode(v_定义提纲id, 0, v_父id, Null),
               v_对象序号,
               r_File.对象类型,
               r_File.对象标记,
               r_File.保留对象,
               r_File.对象属性,
               Null,
               v_内容文本,
               r_File.是否换行,
               r_File.预制提纲id,
               r_File.复用提纲,
               r_File.使用时机,
               r_File.诊治要素id,
               r_File.替换域,
               r_File.要素名称,
               r_File.要素类型,
               r_File.要素长度,
               r_File.要素小数,
               r_File.要素单位,
               r_File.要素表示,
               r_File.输入形态,
               r_File.要素值域,
               Decode(v_定义提纲id, 0, Null, v_定义提纲id));
          End If;
        
          --为表格时，插入文本内容
          If Nvl(r_File.对象类型, 0) = 3 And Nvl(r_File.父id, 0) <> 0 Then
            v_表格 := v_表格 || ',|' || r_File.Id || '|';
          
            If r_File.内容文本 Like '%所见%' Then
              v_内容文本 := 报告所见_In || Chr(13) || Chr(13);
            Elsif r_File.内容文本 Like '%意见%' Then
              v_内容文本 := 报告意见_In || Chr(13) || Chr(13);
            Else
              v_内容文本 := 报告建议_In || Chr(13) || Chr(13);
            End If;
          
            For r_Con In c_Content(r_Advice.文件id, r_File.Id) Loop
              Select 电子病历内容_Id.Nextval
                Into v_病历内容idnew
                From Dual;
              v_对象序号 := v_对象序号 + 1;
            
              Insert Into 电子病历内容
                (ID,
                 文件id,
                 开始版,
                 终止版,
                 父id,
                 对象序号,
                 对象类型,
                 对象标记,
                 保留对象,
                 对象属性,
                 内容行次,
                 内容文本,
                 是否换行,
                 预制提纲id,
                 复用提纲,
                 使用时机,
                 诊治要素id,
                 替换域,
                 要素名称,
                 要素类型,
                 要素长度,
                 要素小数,
                 要素单位,
                 要素表示,
                 输入形态,
                 要素值域,
                 定义提纲id)
              Values
                (v_病历内容idnew,
                 v_病历id,
                 1,
                 0,
                 v_病历内容id,
                 v_对象序号,
                 2,
                 r_Con.对象标记,
                 r_Con.保留对象,
                 r_Con.对象属性,
                 Null,
                 v_内容文本,
                 r_Con.是否换行,
                 r_Con.预制提纲id,
                 r_Con.复用提纲,
                 r_Con.使用时机,
                 r_Con.诊治要素id,
                 r_Con.替换域,
                 r_Con.要素名称,
                 r_Con.要素类型,
                 r_Con.要素长度,
                 r_Con.要素小数,
                 r_Con.要素单位,
                 r_Con.要素表示,
                 r_Con.输入形态,
                 r_Con.要素值域,
                 Decode(v_定义提纲id, 0, Null, v_定义提纲id));
            End Loop;
          End If;
        End Loop;
      
        --因电子病历格式中含了内容文字格式，此种方法导入之后内容文字将不可见
        --Select 内容 Into v_格式内容 From 病历文件格式 Where 文件ID=r_Advice.文件ID;
        --Insert Into 电子病历格式 (文件ID,内容) Values (v_病历id,v_格式内容);
      
      End If;
    End If;
    Close c_Advice;
  
  Exception
    When Err_Custom Then
      Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End Receivereport;

  --5、修改申请单信息
  Procedure 影像病人信息_修改(医嘱id_In       病人医嘱记录.Id%Type,
                      姓名_In         病人信息.姓名%Type,
                      性别_In         病人信息.性别%Type,
                      年龄_In         病人信息.年龄%Type,
                      费别_In         病人信息.费别%Type,
                      医疗付款方式_In 病人信息.医疗付款方式%Type,
                      民族_In         病人信息.民族%Type,
                      婚姻_In         病人信息.婚姻状况%Type,
                      职业_In         病人信息.职业%Type,
                      身份证号_In     病人信息.身份证号%Type,
                      家庭地址_In     病人信息.家庭地址%Type,
                      家庭电话_In     病人信息.家庭电话%Type,
                      家庭地址邮编_In 病人信息.家庭地址邮编%Type,
                      出生日期_In     病人信息.出生日期%Type := Null) As
  
    v_年龄     Varchar2(20);
    v_年龄单位 Varchar2(20);
    v_出生日期 Date;
    v_病人来源 病人医嘱记录.病人来源%Type;
    v_病人id   病人医嘱记录.病人id%Type;
  Begin
    Begin
      Select 病人来源, 病人id
        Into v_病人来源, v_病人id
        From 病人医嘱记录
       Where ID = 医嘱id_In;
    Exception
      When Others Then
        Return;
    End;
  
    If 出生日期_In Is Null And 年龄_In Is Not Null Then
      --根据年龄求出生日期
      v_年龄单位 := Substr(年龄_In, Length(年龄_In), 1);
      If Instr('岁,月,天', v_年龄单位) <= 0 Then
        v_年龄单位 := Null;
      Else
        v_年龄 := Replace(年龄_In, v_年龄单位, '');
      End If;
      Begin
        v_年龄 := To_Number(v_年龄);
      Exception
        When Others Then
          v_年龄 := Null;
      End;
      If v_年龄 Is Not Null And v_年龄单位 Is Not Null Then
        Select Decode(v_年龄单位,
                      '岁',
                      Add_Months(Sysdate, -12 * v_年龄),
                      '月',
                      Add_Months(Sysdate, -1 * v_年龄),
                      '天',
                      Sysdate - v_年龄)
          Into v_出生日期
          From Dual;
      End If;
    Else
      v_出生日期 := 出生日期_In;
    End If;
  
    If v_病人来源 = 3 Then
      Update 病人信息
         Set 姓名         = 姓名_In,
             性别         = Nvl(性别_In, 性别),
             年龄         = 年龄_In,
             出生日期     = v_出生日期,
             费别         = Nvl(费别_In, 费别),
             医疗付款方式 = Nvl(医疗付款方式_In, 医疗付款方式),
             民族         = Nvl(民族_In, 民族),
             婚姻状况     = Nvl(婚姻_In, 婚姻状况),
             职业         = Nvl(职业_In, 职业),
             身份证号     = 身份证号_In,
             家庭地址     = 家庭地址_In,
             家庭电话     = 家庭电话_In,
             家庭地址邮编 = 家庭地址邮编_In
       Where 病人id = v_病人id;
    
      --修改对应的医嘱记录
      Update 病人医嘱记录
         Set 姓名 = 姓名_In, 性别 = 性别_In, 年龄 = 年龄_In
       Where ID = 医嘱id_In
          Or 相关id = 医嘱id_In;
    Else
      Update 病人信息
         Set 民族         = Nvl(民族_In, 民族),
             婚姻状况     = Nvl(婚姻_In, 婚姻状况),
             职业         = Nvl(职业_In, 职业),
             家庭地址     = 家庭地址_In,
             家庭电话     = 家庭电话_In,
             家庭地址邮编 = 家庭地址邮编_In
       Where 病人id = v_病人id;
    End If;
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End 影像病人信息_修改;

  --6、取消申请单信息
  Procedure 取消检查申请单(医嘱id_In     病人医嘱执行.医嘱id%Type,
                    操作员编号_In 人员表.编号%Type := Null,
                    操作员姓名_In 人员表.姓名%Type := Null,
                    执行部门id_In 门诊费用记录.执行部门id%Type := 0,
                    拒绝原因_In   病人医嘱发送.执行说明%Type := Null) As
    --参数：医嘱ID_IN=单独执行的医嘱ID
  
    v_发送号 病人医嘱执行.发送号%Type;
  
  Begin
  
    Begin
      Select 发送号
        Into v_发送号
        From 病人医嘱发送
       Where 医嘱id = 医嘱id_In;
    Exception
      When Others Then
        Return;
    End;
  
    Zl_病人医嘱执行_拒绝执行(医嘱id_In,
                   v_发送号,
                   操作员编号_In,
                   操作员姓名_In,
                   执行部门id_In,
                   拒绝原因_In);
  
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End 取消检查申请单;

  --7、插入医嘱操作失败记录
  Procedure RIS医嘱失败记录_Insert(病人来源_In   In RIS医嘱失败记录.病人来源%Type,
                             病人ID_In     In RIS医嘱失败记录.病人ID%Type,
                             主页ID_In     In RIS医嘱失败记录.主页ID%Type,
                             挂号单号_In   In RIS医嘱失败记录.挂号单号%Type,
                             发送号_In     In RIS医嘱失败记录.发送号%Type,
                             体检任务ID_In In RIS医嘱失败记录.体检任务ID%Type,
                             体检报到号_In In RIS医嘱失败记录.体检报到号%Type,
                             发送类型_In   In RIS医嘱失败记录.发送类型%Type) Is
  Begin
    Insert Into RIS医嘱失败记录
      (ID,
       病人来源,
       病人ID,
       主页ID,
       挂号单号,
       发送号,
       体检任务ID,
       体检报到号,
       发送类型,
       发送时间,
       重发次数)
    Values
      (RIS医嘱失败记录_Id.Nextval,
       病人来源_In,
       病人ID_In,
       主页ID_In,
       挂号单号_In,
       发送号_In,
       体检任务ID_In,
       体检报到号_In,
       发送类型_In,
       sysdate,
       0);
  
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End RIS医嘱失败记录_Insert;

  --8、更新医嘱操作失败记录
  Procedure RIS医嘱失败记录_重发(ID_In       In RIS医嘱失败记录.ID%Type,
                         操作类型_In In Number) Is
    v_重发次数 RIS医嘱失败记录.重发次数%Type;
  Begin
    --操作类型_In -- 1 重发成功，删除记录；2--重发失败
  
    if 操作类型_In = 1 then
      delete from RIS医嘱失败记录 where ID = ID_In;
    else
      select 重发次数
        into v_重发次数
        from RIS医嘱失败记录
       where ID = ID_In;
      if v_重发次数 >= 99 then
        v_重发次数 := 99;
      else
        v_重发次数 := v_重发次数 + 1;
      end if;
      update RIS医嘱失败记录
         set 发送时间 = sysdate, 重发次数 = v_重发次数
       where ID = ID_In;
    end if;
  
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End RIS医嘱失败记录_重发;

  --9、销账后新建住院记账单据
  Procedure 病人医嘱_重建单据(医嘱ID_In In 病人医嘱发送.医嘱ID%Type,
                      No_In     In 病人医嘱发送.No%Type,
                      Action_In In number) Is
    -- Action_In: 1 重建单据；2 取消重建单据
    v_No 病人医嘱发送.no%Type;
  Begin
    if Action_In = 1 then
      select NextNo(14) into v_No from dual;
    
      update 病人医嘱发送
         set no = v_No, 计费状态 = 0
       where 医嘱ID in (select id
                        from 病人医嘱记录
                       where id = 医嘱ID_In
                          or 相关ID = 医嘱ID_In);
      update 住院费用记录 set 医嘱序号 = null where no = No_In;
    elsif Action_In = 2 then
      update 住院费用记录 set 医嘱序号 = 医嘱ID_In where no = No_In;
      update 病人医嘱发送
         set no = No_In, 计费状态 = 4
       where 医嘱ID in (select id
                        from 病人医嘱记录
                       where id = 医嘱ID_In
                          or 相关ID = 医嘱ID_In);
    end if;
  
  Exception
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End 病人医嘱_重建单据;

End b_Zlxwinterface;

