--112953:梁唐彬,2017-09-11,药品说明书知识库
create table 药品说明书
(
  ID    VARCHAR2(100),
  通用名称  VARCHAR2(4000),
  商品名   VARCHAR2(4000),
  汉语拼音  VARCHAR2(4000),
  英文名称  VARCHAR2(4000),
  药物规格  VARCHAR2(4000),
  药物剂型  VARCHAR2(4000),
  主要成分  VARCHAR2(4000),
  生产企业  VARCHAR2(4000),
  批准文号  VARCHAR2(4000),
  化学名称  CLOB,
  性状    CLOB,
  药理毒理  CLOB,
  药代动力学 CLOB,
  适应症   CLOB,
  用法用量  CLOB,
  不良反应  CLOB,
  禁忌症   CLOB,
  注意事项  CLOB,
  孕妇用药  CLOB,
  儿童用药  CLOB,
  老年人用药 CLOB,
  相互作用  CLOB,
  药物过量  CLOB,
  贮藏条件  CLOB
)
tablespace ZL9BASEITEM;

alter table 药品说明书 add constraint 药品说明书_PK primary key (ID) using index tablespace zl9Indexhis;


