--111722:张永康,2017-07-18,解决无法查询远程历史库的病历
Declare
  v_Sql    Varchar2(1000);
  v_Htable Varchar2(30);
  n_Count  Number(8);
Begin
  For R In (Select Distinct Table_Name
            From User_Tab_Columns A
            Where Data_Type In ('BLOB', 'CLOB') And a.Table_Name In (Select 表名 From zlBakTables)) Loop
    v_Htable := 'H' || r.Table_Name;
  
    Select Count(1) Into n_Count From User_Views Where View_Name = v_Htable;
    If n_Count > 0 Then
      v_Sql := ' Drop View ' || v_Htable;
      Execute Immediate v_Sql;
    End If;
  
    Select Count(1) Into n_Count From User_Tables Where Table_Name = v_Htable And Temporary = 'Y';
    If n_Count = 0 Then
      v_Sql := 'Create Global Temporary table ' || v_Htable || ' On Commit Delete Rows as select * from ' ||
               r.Table_Name || ' where 1 = 0';
      Execute Immediate v_Sql;
    End If;
  End Loop;
End;
/


