--113570:李业庆,2017-08-28,新增药品价格表处理
Declare
  Cursor c_Planadjust Is
    Select 2 As 价格类型, a.药品id, a.库房id, a.批次, a.原成本价 As 原价, a.新成本价 As 现价, d.执行日期,
           To_Date('3000-01-01', 'yyyy-mm-dd') As 终止日期, 0 As 记录状态, 1 As 调价类型, '成本价调价' As 调价说明, 'ZLHIS' As 调价人, a.调价汇总号,
           a.供药单位id, a.批号, a.效期, a.产地
    From 成本价调价信息 A, 药品规格 C, 收费价目 D
    Where a.药品id = c.药品id And a.药品id = d.收费细目id And a.调价汇总号 = d.调价汇总号 And d.变动原因 = 0 And a.执行日期 Is Null And Not Exists
     (Select 1
           From 药品价格记录 C
           Where c.价格类型 = 2 And Nvl(c.库房id, 0) = Nvl(a.库房id, 0) And c.药品id = a.药品id And Nvl(c.批次, 0) = Nvl(a.批次, 0) And
                 记录状态 = 0);

  r_Planadjust c_Planadjust%RowType;
  n_原价id     药品价格记录.原价id%Type;
Begin
  --产生时价售价价格
  Insert Into 药品价格记录
    (ID, 原价id, 价格类型, 药品id, 库房id, 批次, 原价, 现价, 执行日期, 终止日期, 记录状态, 调价类型, 调价说明, 调价人, 调价汇总号, 供药单位id, 批号, 效期, 产地)
    Select 药品价格记录_Id.Nextval As ID, Null As 原价id, 1 As 价格类型, a.药品id, a.库房id, a.批次, Null As 原价, a.零售价 As 现价,
           Sysdate As 执行日期, To_Date('3000-01-01', 'yyyy-mm-dd') As 终止日期, 1 As 记录状态, 0 As 调价类型, '时价药品价格初始数据' As 调价说明,
           'ZLHIS' As 调价人, Null As 调价汇总号, a.上次供应商id, a.上次批号, a.效期, a.上次产地
    From 药品库存 A, 药品规格 C
    Where a.药品id = c.药品id And Exists
     (Select 1 From 收费项目目录 B Where b.Id = a.药品id And b.是否变价 = 1) And a.性质 = 1 And Not Exists
     (Select 1
           From 药品价格记录 C
           Where c.价格类型 = 1 And c.库房id = a.库房id And c.药品id = a.药品id And c.批次 = Nvl(a.批次, 0));

  --产生成本价记录
  Insert Into 药品价格记录
    (ID, 原价id, 价格类型, 药品id, 库房id, 批次, 原价, 现价, 执行日期, 终止日期, 记录状态, 调价类型, 调价说明, 调价人, 调价汇总号, 供药单位id, 批号, 效期, 产地)
    Select 药品价格记录_Id.Nextval As ID, Null As 原价id, 2 As 价格类型, a.药品id, a.库房id, a.批次, Null As 原价, a.平均成本价 As 现价,
           Sysdate As 执行日期, To_Date('3000-01-01', 'yyyy-mm-dd') As 终止日期, 1 As 记录状态, 0 As 调价类型, '药品成本价初始数据' As 调价说明,
           'ZLHIS' As 调价人, Null As 调价汇总号, a.上次供应商id, a.上次批号, a.效期, a.上次产地
    From 药品库存 A, 药品规格 C
    Where a.药品id = c.药品id And a.性质 = 1 And Not Exists
     (Select 1
           From 药品价格记录 C
           Where c.价格类型 = 2 And c.库房id = a.库房id And c.药品id = a.药品id And c.批次 = Nvl(a.批次, 0));

  --产生预调价记录
  For r_Planadjust In c_Planadjust Loop
    Begin
      Select ID
      Into n_原价id
      From 药品价格记录
      Where 价格类型 = r_Planadjust.价格类型 And 库房id = r_Planadjust.库房id And 药品id = r_Planadjust.药品id And 批次 = r_Planadjust.批次 And
            记录状态 = 1;
    Exception
      When Others Then
        n_原价id := Null;
    End;
  
    Insert Into 药品价格记录
      (ID, 原价id, 价格类型, 药品id, 库房id, 批次, 原价, 现价, 执行日期, 终止日期, 记录状态, 调价类型, 调价说明, 调价人, 调价汇总号, 供药单位id, 批号, 效期, 产地)
    Values
      (药品价格记录_Id.Nextval, n_原价id, 2, r_Planadjust.药品id, r_Planadjust.库房id, r_Planadjust.批次, r_Planadjust.原价,
       r_Planadjust.现价, r_Planadjust.执行日期, To_Date('3000-01-01', 'yyyy-mm-dd'), r_Planadjust.记录状态, r_Planadjust.调价类型,
       r_Planadjust.调价说明, r_Planadjust.调价人, r_Planadjust.调价汇总号, r_Planadjust.供药单位id, r_Planadjust.批号, r_Planadjust.效期,
       r_Planadjust.产地);
  
    Update 药品价格记录 Set 终止日期 = r_Planadjust.执行日期 - 1 / 24 / 60 / 60 Where ID = n_原价id;
  End Loop;

  --删除原成本价调价信息表中的预调价记录
  Delete From 成本价调价信息 Where 执行日期 Is Null;
End;
/

