--112013:李南春,2017-07-25,自动返回时间调整
Update Zluserparas Set 参数值 = 参数值 || '|' || Substr(参数值,1,instr(参数值 ,'|',1)-1)
Where 用户名 is NULL 
  And 参数ID in (Select ID From Zlparameters Where 模块 in (1802,1803) And 系统 = &n_System And 参数名= '自动返回时间')
  And instr(参数值,'|',instr(参数值,'|',1) + 1) = 0;

Update Zlparameters Set 缺省值 = '10|0|10', 影响控制说明 = '正常页面自动返回时间：主要是控制在自助挂号的除错误页面和成功页面之外的界面中无任何操作时,自动返回到首页的秒数。'||CHR(13)||'错误页面自动返回时间：主要是控制在自助挂号的错误页面中无任何操作时,自动返回到首页的秒数。'||CHR(13)||'成功页面自动返回时间：主要是控制在自助挂号的成功页面中无任何操作时,自动返回到首页的秒数。',
                        参数值含义 = '格式：正常页面自动返回时间|错误页面自动返回时间|成功页面自动返回时间。'||CHR(13)||'>0:表示自动返回的秒数'||CHR(13)||'=0:表示不自动返回', 适用说明 = '适用于控制不同页面无任何操作时的停留时间'
Where 模块 = 1802 And 系统 = &n_System And 参数名= '自动返回时间';

Update Zlparameters Set 缺省值 = '10|0|10', 影响控制说明 = '正常页面自动返回时间：主要是控制在自助预约的除错误页面和成功页面之外的界面中无任何操作时,自动返回到首页的秒数。'||CHR(13)||'错误页面自动返回时间：主要是控制在自助预约的错误页面中无任何操作时,自动返回到首页的秒数。'||CHR(13)||'成功页面自动返回时间：主要是控制在自助预约的成功页面中无任何操作时,自动返回到首页的秒数。',
                        参数值含义 = '格式：正常页面自动返回时间|错误页面自动返回时间|成功页面自动返回时间。'||CHR(13)||'>0:表示自动返回的秒数'||CHR(13)||'=0:表示不自动返回', 适用说明 = '适用于控制不同页面无任何操作时的停留时间'
Where 模块 = 1803 And 系统 = &n_System And 参数名= '自动返回时间';

