--100165:秦龙,2017-07-14,修正药品加成率数据
Declare
  n_序号 Number(10);
Begin
  Update 药品加成方案 Set 类型 = 0 Where 类型 Is Null;
  If Sql%RowCount <> 0 Then
    Select Max(序号) Into n_序号 From 药品加成方案;
    Insert Into 药品加成方案
      (序号, 最低价, 最高价, 加成率, 差价额, 说明, 类型)
      Select 序号 + n_序号, 最低价, 最高价, 加成率, 差价额, 说明, 1 From 药品加成方案 Where 类型 = 0;
  End If;
End;
/


