Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材结存记录_数据','库房,202|开始日期,202|结束日期,202',User||'.部门表,'||User||'.材料结存记录',0,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'select a.名称 as 库房, To_Char(b.期初日期, ''YYYY-MM-DD HH24:MI:SS'') as 开始日期, To_Char(b.期末日期, ''YYYY-MM-DD HH24:MI:SS'') as 结束日期 ' From Dual Union All
  Select 2,'from 部门表 a, 材料结存记录 b where a.id=b.库房id and b.性质=1 and b.id=[0]' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+) and a.性质=1
Order By 库房id, ID','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材目录_数据','日期,202|RID,130|单据,139|单据号,202|摘要,202|入库成本金额,139|入库售价金额,139|出库成本金额,139|出库售价金额,139|结存成本金额,139|结存售价金额,139|记录状态,139',User||'.收费项目目录,'||User||'.材料结存明细,'||User||'.材料结存记录,'||User||'.药品收发记录,'||User||'.供应商,'||User||'.部门表',0,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'SELECT '''' AS 日期,' From Dual Union All
  Select 2,'       ''A'' AS RID,' From Dual Union All
  Select 3,'       99 AS 单据,' From Dual Union All
  Select 4,'       '''' AS 单据号,' From Dual Union All
  Select 5,'       ''期初发生'' AS 摘要,' From Dual Union All
  Select 6,'       0 AS 入库成本金额,' From Dual Union All
  Select 7,'       0 AS 入库售价金额,' From Dual Union All
  Select 8,'       0 as 出库成本金额,' From Dual Union All
  Select 9,'       0 AS 出库售价金额,' From Dual Union All
  Select 10,'       SUM(期初金额)-SUM(期初差价) AS 结存成本金额,' From Dual Union All
  Select 11,'       SUM(期初金额) AS 结存售价金额,' From Dual Union All
  Select 12,'       0 as 记录状态' From Dual Union All
  Select 13,'  FROM (SELECT SUM(decode(c.性质,1,a.期初金额,a.期末金额)) AS 期初金额, SUM(decode(c.性质,1,a.期初差价,a.期末差价)) AS 期初差价' From Dual Union All
  Select 14,'          FROM 材料结存明细 A,(SELECT ID FROM 收费项目目录 WHERE 类别 IN (''4'')) B,材料结存记录 C' From Dual Union All
  Select 15,'         WHERE A.结存ID = C.id And C.id = [0] AND ' From Dual Union All
  Select 16,'               A.材料ID = B.Id )' From Dual Union All
  Select 17,'HAVING SUM(期初金额) <> 0' From Dual Union All
  Select 18,'UNION ALL' From Dual Union All
  Select 19,'  SELECT TO_CHAR(A.审核日期, ''YYYY-MM-DD'') AS 日期,' From Dual Union All
  Select 20,'         ''B'' AS RID,' From Dual Union All
  Select 21,'         单据,' From Dual Union All
  Select 22,'         A.NO AS 单据号,' From Dual Union All
  Select 23,'         LTRIM(C.名称) AS 摘要,' From Dual Union All
  Select 24,'         ABS(SUM(A.入库成本金额)) AS 入库成本金额,' From Dual Union All
  Select 25,'         SUM(A.入库售价金额) AS 入库售价金额,' From Dual Union All
  Select 26,'         SUM(A.出库成本金额) AS 出库成本金额,' From Dual Union All
  Select 27,'         SUM(A.出库售价金额) AS 出库售价金额,' From Dual Union All
  Select 28,'         SUM(a.结存成本金额) AS 结存成本金额,' From Dual Union All
  Select 29,'         SUM(A.结存售价金额) AS 结存售价金额,A.记录状态' From Dual Union All
  Select 30,'    FROM (SELECT ''A'' || A.ID AS RID,' From Dual Union All
  Select 31,'                 单据 AS 单据,' From Dual Union All
  Select 32,'                 DECODE(单据,15,''外购'',16,''自制'',17,''入库'',18,''差价'',19,''移库'',20,''领用'',21,''出库'',22,''盘点'',24,''处方'',25,''处方'',26,''摆料'',''其他'') || NO AS NO,' From Dual Union All
  Select 33,'                 审核日期,' From Dual Union All
  Select 34,'                 供药单位ID AS 供药单位ID,' From Dual Union All
  Select 35,'                 库房ID AS 库房ID,' From Dual Union All
  Select 36,'                 入出系数 * Nvl(差价,0) AS 差价,' From Dual Union All
  Select 37,'                 Decode(入出系数, -1, 0, 1) * Nvl(零售金额,0) As 入库售价金额, ' From Dual Union All
  Select 38,'                 Decode(入出系数, 1, 0, 1) * Nvl(零售金额,0) As 出库售价金额,  ' From Dual Union All
  Select 39,'		 Decode(入出系数, -1, 0, 1) * Nvl(零售金额,0) - Decode(入出系数, -1, 0, 1) * Nvl(差价,0) As 入库成本金额,' From Dual Union All
  Select 40,'                 Decode(入出系数, 1, 0, 1) * Nvl(零售金额,0) - Decode(入出系数, 1, 0, 1) * Nvl(差价,0) As 出库成本金额, ' From Dual Union All
  Select 41,'	         入出系数 * Nvl(零售金额,0) As 结存售价金额,' From Dual Union All
  Select 42,'                 入出系数 * Nvl(零售金额,0) -  入出系数 * Nvl(差价,0) As 结存成本金额,         ' From Dual Union All
  Select 43,'                 A.记录状态' From Dual Union All
  Select 44,'            FROM 药品收发记录 A, (SELECT ID FROM 收费项目目录 WHERE 类别 IN (''4'')) X' From Dual Union All
  Select 45,'           WHERE A.药品ID = X.ID ' From Dual Union All
  Select 46,'                 AND 库房ID = (Select 库房id from 材料结存记录 Where id=[0])' From Dual Union All
  Select 47,'                 AND 审核日期 Between (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And (Select 期末日期 From 材料结存记录 Where ID = [0])                            ' From Dual Union All
  Select 48,'                 AND 审核人 IS NOT NULL) A,' From Dual Union All
  Select 49,'         部门表 B,' From Dual Union All
  Select 50,'         (Select * From 供应商 Where substr(类型,1,1)= 1) C' From Dual Union All
  Select 51,'   WHERE A.供药单位ID = C.ID(+) AND A.库房ID = B.ID(+)' From Dual Union All
  Select 52,'   GROUP BY A.审核日期, A.单据, A.NO, C.名称,A.记录状态' From Dual Union All
  Select 53,'  HAVING SUM(A.入库成本金额) <> 0 OR SUM(A.入库售价金额) <> 0 OR SUM(A.出库成本金额)<>0 or SUM(A.出库售价金额) <> 0 Or SUM(A.差价) <> 0' From Dual Union All
  Select 54,'   ORDER BY RID, 日期' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
