Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'Select A.业务大类, A.业务分类, A.库房id, A.材料id, ' From Dual Union All
  Select 2,'       Sum(A.金额) As 零售金额, Sum(A.金额)-Sum(A.差价) As 成本金额' From Dual Union All
  Select 3,'From (Select A.库房id, ''1-期初'' As 业务大类, '''' As 业务分类, A.材料id As 材料id,  Sum(A.期初金额) As 金额, Sum(A.期初差价) As 差价' From Dual Union All
  Select 4,'       From 材料结存明细 A' From Dual Union All
  Select 5,'       Where 结存id = [0]' From Dual Union All
  Select 6,'       Group By A.库房id, A.材料id' From Dual Union All
  Select 7,'	   Union All' From Dual Union All
  Select 8,'       Select A.库房id, Decode(B.系数, 1, ''2-入库'', ''3-出库'') As 业务大类, B.名称 As 业务分类, A.药品id As 药品id,' From Dual Union All
  Select 9,'               Sum(Nvl(A.零售金额, 0)) As 金额, Sum(Nvl(A.差价, 0)) As 差价' From Dual Union All
  Select 10,'       From 药品收发记录 A, 药品入出类别 B' From Dual Union All
  Select 11,'       Where A.入出类别id = B.ID And A.审核日期 Between (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 12,'             (Select 期末日期 From 材料结存记录 Where ID = [0]) and A.库房id=(Select 库房id from 材料结存记录 Where id=[0])' From Dual Union All
  Select 13,'       Group By A.库房id, A.药品id, B.名称, Decode(B.系数, 1, ''2-入库'', ''3-出库'')       ' From Dual Union All
  Select 14,'       Union All' From Dual Union All
  Select 15,'       select A.库房id, ''4-期末'' As 业务大类, '''' As 业务分类, A.材料id, Sum(A.期末金额) As 金额, Sum(A.期末差价) As 差价' From Dual Union All
  Select 16,'       From 材料结存明细 A' From Dual Union All
  Select 17,'       Where 结存id = [0]' From Dual Union All
  Select 18,'       Group By A.库房id, A.材料id) A, 收费项目目录 B, 材料特性 C' From Dual Union All
  Select 19,'Where A.材料id = B.ID And A.材料id = C.材料id ' From Dual Union All
  Select 20,'Group By A.业务大类, A.业务分类, A.库房id, A.材料id, B.类别' From Dual Union All
  Select 21,'Order By A.业务大类, A.业务分类, A.库房id, A.材料id, B.类别' From Dual) a;
