Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+) and a.性质=1
Order By 库房id, ID','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材库存_数据','标志,202|材料ID,131|编码,202|名称,202|规格,202|单位,202|期初数量,139|期初金额,139|期初成本,139|入库数量,139|入库金额,139|入库售价,139|入库成本价,139|入库成本,139|调价金额,139|调价成本,139|出库数量,139|出库金额,139|出库售价,139|出库成本价,139|出库成本,139|期末数量,139|期末金额,139|期末成本,139|药品ID,130',User||'.材料结存记录,'||User||'.材料结存明细,'||User||'.药品收发记录,'||User||'.诊疗项目目录,'||User||'.收费项目目录,'||User||'.材料特性',0,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'Select 标志, 材料id, 编码, 名称, 规格, 单位, 期初数量, 期初金额, 期初金额-期初差价 as 期初成本, ' From Dual Union All
  Select 2,'       入库数量, 入库金额, 入库金额 / Decode(入库数量, Null, 1, 0, 1, 入库数量) as 入库售价, (入库金额-入库差价)/ Decode(入库数量, Null, 1, 0, 1, 入库数量) as 入库成本价, 入库金额-入库差价 as 入库成本, ' From Dual Union All
  Select 3,'       调价金额, 调价金额-调价差价 as 调价成本, ' From Dual Union All
  Select 4,'       出库数量, 出库金额, 出库金额 / Decode(出库数量, Null, 1, 0, 1, 出库数量) as 出库售价, (出库金额-出库差价)/ Decode(出库数量, Null, 1, 0, 1, 出库数量) as 出库成本价, 出库金额-出库差价 as 出库成本, ' From Dual Union All
  Select 5,'       期末数量, 期末金额, 期末金额-期末差价 as 期末成本,''0'' 药品id' From Dual Union All
  Select 6,'From (Select Distinct Decode(To_Char(b.撤档时间, ''yyyy-MM-dd''), Null, '''', ''3000-01-01'', '''', ''√'') 标志, b.Id As 材料id, b.编码 As 编码,' From Dual Union All
  Select 7,'                       x.名称 As 名称, b.规格 As 规格, Decode([1], 2, d.包装单位, b.计算单位) As 单位,' From Dual Union All
  Select 8,'                       c.期初数量 / Decode([1], 2, d.换算系数, 1) As 期初数量, c.期初金额, c.期初差价,' From Dual Union All
  Select 9,'                       c.入库数量 / Decode([1], 2, d.换算系数, 1) As 入库数量, c.入库金额, c.入库差价,' From Dual Union All
  Select 10,'                       c.调价金额, c.调价差价, c.出库数量 / Decode([1], 2, d.换算系数, 1) As 出库数量,' From Dual Union All
  Select 11,'                       c.出库金额, c.出库差价, c.期末数量 / Decode([1], 2, d.换算系数, 1) As 期末数量,' From Dual Union All
  Select 12,'                       c.期末金额, c.期末差价' From Dual Union All
  Select 13,'       From (Select 材料id, Sum(期初数量) As 期初数量, Sum(期初金额) As 期初金额, Sum(期初差价) As 期初差价, Sum(入库数量) As 入库数量, Sum(入库金额) As 入库金额,' From Dual Union All
  Select 14,'                     Sum(入库差价) As 入库差价, Sum(调价金额) As 调价金额, Sum(调价差价) As 调价差价, Sum(出库数量) As 出库数量, Sum(出库金额) As 出库金额,' From Dual Union All
  Select 15,'                     Sum(出库差价) As 出库差价, Sum(期末数量) As 期末数量, Sum(期末金额) As 期末金额, Sum(期末差价) As 期末差价' From Dual Union All
  Select 16,'              From (Select 材料id, 期初数量, 期初金额, 期初差价, 0 As 入库数量, 0 As 入库金额, 0 As 入库差价, 0 As 调价金额, 0 As 调价差价, 0 As 出库数量,' From Dual Union All
  Select 17,'                            0 As 出库金额, 0 As 出库差价, 期末数量, 期末金额, 期末差价' From Dual Union All
  Select 18,'                     From 材料结存明细' From Dual Union All
  Select 19,'                     Where 结存id = [0]' From Dual Union All
  Select 20,'                     Union All' From Dual Union All
  Select 21,'                     Select 药品id, 0 As 期初数量, 0 As 期初金额, 0 As 期初差价, Decode(入出系数, -1, 0, 1) * Nvl(付数, 1) * 实际数量 As 入库数量,' From Dual Union All
  Select 22,'                            Decode(入出系数, -1, 0, 1) * 零售金额 As 入库金额, Decode(入出系数, -1, 0, 1) * 差价 As 入库差价, 0 As 调价金额, 0 As 调价差价,' From Dual Union All
  Select 23,'                            Decode(入出系数, -1, 1, 0) * Nvl(付数, 1) * 实际数量 As 出库数量, Decode(入出系数, -1, 1, 0) * 零售金额 As 出库金额,' From Dual Union All
  Select 24,'                            Decode(入出系数, -1, 1, 0) * 差价 As 出库差价, 0 As 期末数量, 0 As 期末金额, 0 As 期末差价' From Dual Union All
  Select 25,'                     From 药品收发记录' From Dual Union All
  Select 26,'                     Where 库房id = (Select 库房id From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 27,'                           审核日期 Between (Select 期初日期 From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 28,'                           (Select 期末日期 From 材料结存记录 Where ID = [0]) And 单据 Not In (18)' From Dual Union All
  Select 29,'                     Union All' From Dual Union All
  Select 30,'                     Select 药品id, 0 As 期初数量, 0 As 期初金额, 0 As 期初差价, 0 As 入库数量, 0 As 入库金额, 0 As 入库差价, 入出系数 * 零售金额 As 调价金额,' From Dual Union All
  Select 31,'                            入出系数 * 差价 As 调价差价, 0 As 出库数量, 0 As 出库金额, 0 As 出库差价, 0 As 期末数量, 0 As 期末金额, 0 As 期末差价' From Dual Union All
  Select 32,'                     From 药品收发记录' From Dual Union All
  Select 33,'                     Where 库房id = (Select 库房id From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 34,'                           审核日期 Between (Select 期初日期 From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 35,'                           (Select 期末日期 From 材料结存记录 Where ID = [0]) And 单据 In (18))' From Dual Union All
  Select 36,'              Group By 材料id) C, 收费项目目录 B,' From Dual Union All
  Select 37,'            (Select ID, 名称' From Dual Union All
  Select 38,'              From 诊疗项目目录' From Dual Union All
  Select 39,'              ) X, 材料特性 D' From Dual Union All
  Select 40,'       Where b.类别 In (''4'') And b.Id = c.材料id And d.诊疗id = x.Id + 0 And b.Id = d.材料id)' From Dual Union All
  Select 41,'Order By 材料id' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+) and a.性质=1
Order By 库房id, ID','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
