Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材结存记录_数据','库房,202|开始日期,202|结束日期,202',User||'.部门表,'||User||'.材料结存记录',0,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'select a.名称 as 库房, To_Char(b.期初日期, ''YYYY-MM-DD HH24:MI:SS'') as 开始日期, To_Char(b.期末日期, ''YYYY-MM-DD HH24:MI:SS'') as 结束日期 ' From Dual Union All
  Select 2,'from 部门表 a, 材料结存记录 b where a.id=b.库房id and b.性质=1 and b.id=[0]' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+) and a.性质=1
Order By 库房id, ID','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材目录_数据','材料ID,131|用途分类ID,131|编码,202|名称,202|规格,202|产地,202|单位,202',User||'.收费项目别名,'||User||'.收费项目目录,'||User||'.材料特性,'||User||'.诊疗项目目录',0,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'SELECT B.材料ID,' From Dual Union All
  Select 2,'       A.分类ID AS 用途分类ID,' From Dual Union All
  Select 3,'       M.编码,' From Dual Union All
  Select 4,'       DECODE(C.名称, NULL, A.名称, C.名称) AS 名称,' From Dual Union All
  Select 5,'       M.规格,' From Dual Union All
  Select 6,'       M.产地,' From Dual Union All
  Select 7,'       Decode([1], 2,B.包装单位, M.计算单位) AS 单位' From Dual Union All
  Select 8,'  FROM 收费项目目录 M,' From Dual Union All
  Select 9,'       材料特性 B,' From Dual Union All
  Select 10,'       诊疗项目目录 A,' From Dual Union All
  Select 11,'       (select * from 收费项目别名 where 性质 = 3 and 码类 <> 2) C' From Dual Union All
  Select 12,' WHERE A.ID = B.诊疗ID AND M.ID = B.材料id AND B.材料ID = C.收费细目ID(+) and' From Dual Union All
  Select 13,'       B.材料id = [0]' From Dual Union All
  Select 14,' order by M.编码' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'材料',3,'选择器定义…',0,Null,'Select id,
上级id,
名称,
decode(类型, 7, ''卫材'') as 材质
From 诊疗分类目录
where 类型 in (1, 2, 3) and
decode(类型, 7, ''卫材'') in
(Select Distinct 功能
From (Select A.系统, A.序号, A.功能
From zlRoleGrant A, Session_Roles B
Where A.角色 = B.Role And A.序号 = 1309 And A.系统 = 100
Union All
Select A.系统, B.序号, B.功能
From zlPrograms A, zlProgFuncs B
Where A.序号 = B.序号 And A.系统 = B.系统 And
A.序号 = 1309 And A.系统 = 100 And
(Exists
(Select 1 From Session_Roles Where Role = ''DBA'') Or
A.系统 in (Select 编号
From zlSystems
Where Upper(所有者) = USER))
))
start with 上级id is null
connect by prior id = 上级id','SELECT DISTINCT M.材料ID,
M.分类ID AS 用途分类ID,
M.编码,
DECODE(C.名称, NULL, M.名称, C.名称) AS 名称,
M.规格,
M.产地
FROM (
SELECT M.撤档时间,M.编码,M.规格,M.产地,A.分类ID,B.材料ID,A.名称
FROM 收费项目目录 M,
材料特性 B,
诊疗项目目录 A
WHERE M.ID=B.材料ID
AND B.诊疗ID=A.ID
AND M.类别 IN (''4'')
) M,
(
SELECT * FROM 收费项目别名 where 性质=3 and 码类<>2
) C
WHERE M.材料ID = C.收费细目ID(+)
AND (M.撤档时间 = to_date(''3000-01-01'',''yyyy-mm-dd'') OR M.撤档时间 IS NULL)
AND (M.编码 like ''[*]%'' or C.简码 like ''%[*]%'' or C.名称 like ''%[*]%'')
order by M.编码','ID,131,'||CHR(38)||'R|上级ID,131,|名称,202,'||CHR(38)||'S|材质,202,','材料ID,131,'||CHR(38)||'B|用途分类ID,131,'||CHR(38)||'R|编码,202,'||CHR(38)||'S|名称,202,'||CHR(38)||'S'||CHR(38)||'D|规格,202,'||CHR(38)||'S|产地,202,'||CHR(38)||'S',User||'.收费项目目录,'||User||'.材料特性,'||User||'.诊疗项目目录,'||User||'.收费项目别名|SYS.SESSION_ROLES,ZLTOOLS.ZLSYSTEMS,ZLTOOLS.ZLROLEGRANT,ZLTOOLS.ZLPROGRAMS,ZLTOOLS.ZLPROGFUNCS,'||User||'.诊疗分类目录',0);
