Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'SELECT RID,记录状态,单据,NO1,日期,单据号,摘要,批号,' From Dual Union All
  Select 2,'         入库数量,入库金额/DECODE(入库数量,NULL,1,0,1,入库数量) 入库单价,入库金额,入库金额-入库差价 as 入库成本金额,' From Dual Union All
  Select 3,'         出库数量,出库金额/DECODE(出库数量,NULL,1,0,1,出库数量) 出库单价,出库金额,出库金额-出库差价 as 出库成本金额,' From Dual Union All
  Select 4,'         结存数量,结存金额,结存金额-结存差价 as 结存成本金额' From Dual Union All
  Select 5,'FROM (' From Dual Union All
  Select 6,'  SELECT RID,记录状态,单据,NO1,日期,单据号, 摘要,批号,           ' From Dual Union All
  Select 7,'     W.入库数量/DECODE([2],2,M.换算系数,1) AS 入库数量,' From Dual Union All
  Select 8,'       入库金额,入库差价,                      ' From Dual Union All
  Select 9,'     W.出库数量/DECODE([2],2,M.换算系数,1) AS 出库数量,' From Dual Union All
  Select 10,'       出库金额,出库差价,           ' From Dual Union All
  Select 11,'     W.结存数量/DECODE([2],2,M.换算系数,1) AS 结存数量,' From Dual Union All
  Select 12,'       结存金额,结存差价' From Dual Union All
  Select 13,'  FROM  ' From Dual Union All
  Select 14,'    (SELECT ''A'' AS RID,99 AS 记录状态,99 AS 单据,'''' NO1,'''' AS 日期,'''' AS 单据号, ''期初发生'' AS 摘要,'''' AS 批号,           ' From Dual Union All
  Select 15,'        0 AS 入库数量,0 AS 入库金额,0 AS 入库差价,                      ' From Dual Union All
  Select 16,'        0 AS 出库数量,0 AS 出库金额,0 AS 出库差价,           ' From Dual Union All
  Select 17,'        SUM(期初数量) AS 结存数量,' From Dual Union All
  Select 18,'        SUM(期初金额) AS 结存金额,' From Dual Union All
  Select 19,'        SUM(期初差价) AS 结存差价' From Dual Union All
  Select 20,'      FROM 材料结存明细 ' From Dual Union All
  Select 21,'      WHERE  材料ID=[1]  AND 结存id=[0]' From Dual Union All
  Select 22,'    UNION ALL ' From Dual Union All
  Select 23,'    SELECT ''B'' AS RID,A.记录状态,A.单据,A.NO AS NO1,TO_CHAR(A.审核日期,''YYYY-MM-DD HH24:MI:SS'') AS 日期,' From Dual Union All
  Select 24,'      DECODE(A.单据,15,''外购'',16,''自制'',17,''入库'',18,''差价'',19,''移库'',20,''领用'',21,''出库'',22,''盘点'',13,''调价'',27,''留存'')||A.NO AS 单据号,' From Dual Union All
  Select 25,'      DECODE(A.单据,13,RTRIM(A.摘要)||DECODE(A.单据,15,''采购价:''||A.成本价||''元'','''')||DECODE(B.发票号,NULL,'' '','' 发票号:'')||发票号,' From Dual Union All
  Select 26,'      DECODE(MOD(A.记录状态,3),2,''冲销单据'',RTRIM(A.摘要)||DECODE(A.单据,15,''采购价:''||A.成本价||''元'','''')||DECODE(B.发票号,NULL,'' '','' 发票号:'')||发票号)) AS 摘要,A.批号,' From Dual Union All
  Select 27,'      (DECODE(入出系数,1,1,0)*A.实际数量*A.付数/1) AS 入库数量,' From Dual Union All
  Select 28,'      (DECODE(入出系数,1,1,0)*A.零售金额) AS 入库金额,' From Dual Union All
  Select 29,'      (DECODE(入出系数,1,1,0)*A.差价) AS 入库差价, ' From Dual Union All
  Select 30,'      (DECODE(入出系数,1,0,1)*A.实际数量*A.付数/1) AS 出库数量,' From Dual Union All
  Select 31,'      (DECODE(入出系数,1,0,1)*A.零售金额) AS  出库金额,' From Dual Union All
  Select 32,'      (DECODE(入出系数,1,0,1)*A.差价) AS  出库差价,' From Dual Union All
  Select 33,'      0 AS 结存数量   ,' From Dual Union All
  Select 34,'      0 AS 结存金额,' From Dual Union All
  Select 35,'      0 AS 结存差价           ' From Dual Union All
  Select 36,'    FROM 药品收发记录 A,(Select * From 应付记录 Where 系统标识=1 And 记录性质<>-1) B   ' From Dual Union All
  Select 37,'    WHERE A.ID=B.收发ID(+)  ' From Dual Union All
  Select 38,'    AND A.药品ID+0= [1] AND A.库房ID= (Select 库房id from 材料结存记录 Where id=[0]) AND A.单据 IN (13,15, 16, 17, 18, 19, 20, 21, 22,27)' From Dual Union All
  Select 39,'    AND  A.审核日期 Between (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And (Select 期末日期 From 材料结存记录 Where ID = [0])     ' From Dual Union All
  Select 40,'    UNION ALL ' From Dual Union All
  Select 41,'    SELECT RID,记录状态,单据,NO1,日期,单据号,摘要,批号,' From Dual Union All
  Select 42,'      SUM(入库数量) 入库数量,SUM(入库金额) 入库金额,SUM(入库差价) 入库差价,' From Dual Union All
  Select 43,'      SUM(出库数量) 出库数量,SUM(出库金额) 出库金额,SUM(出库差价) 出库差价,' From Dual Union All
  Select 44,'      SUM(结存数量) 结存数量,SUM(结存金额) 结存金额,SUM(结存差价) 结存差价' From Dual Union All
  Select 45,'    FROM (' From Dual Union All
  Select 46,'      SELECT ''B'' AS RID,A.记录状态,A.单据,A.NO AS NO1,TO_CHAR(A.审核日期,''YYYY-MM-DD HH24:MI:SS'') AS 日期,' From Dual Union All
  Select 47,'        DECODE(A.单据,24,''处方'',25,''处方'',26,''摆料'')||A.NO AS 单据号,' From Dual Union All
  Select 48,'        DECODE(A.记录状态,1,''第1次发料'',DECODE(MOD(A.记录状态,3),0,''第1次发料'',1,''第''||(FLOOR(A.记录状态/3)+1)||''次发料'',2,''第''||(FLOOR(A.记录状态/3)+1)||''次退料'')) AS 摘要,A.批号,' From Dual Union All
  Select 49,'        (DECODE(入出系数,1,1,0)*A.实际数量*A.付数/1) AS 入库数量,' From Dual Union All
  Select 50,'        (DECODE(入出系数,1,1,0)*A.零售金额) AS 入库金额,' From Dual Union All
  Select 51,'        (DECODE(入出系数,1,1,0)*A.差价) AS 入库差价, ' From Dual Union All
  Select 52,'        (DECODE(入出系数,1,0,1)*A.实际数量*A.付数/1) AS 出库数量,' From Dual Union All
  Select 53,'        (DECODE(入出系数,1,0,1)*A.零售金额) AS  出库金额,' From Dual Union All
  Select 54,'        (DECODE(入出系数,1,0,1)*A.差价) AS  出库差价,' From Dual Union All
  Select 55,'        0 AS 结存数量   ,' From Dual Union All
  Select 56,'        0 AS 结存金额,' From Dual Union All
  Select 57,'        0 AS 结存差价           ' From Dual Union All
  Select 58,'      FROM 药品收发记录 A,(Select * From 应付记录 Where 系统标识=1 And 记录性质<>-1) B   ' From Dual Union All
  Select 59,'      WHERE A.ID=B.收发ID(+) ' From Dual Union All
  Select 60,'      AND A.药品ID+0= [1] AND A.库房ID= (Select 库房id from 材料结存记录 Where id=[0]) AND A.单据 IN (24,25,26) ' From Dual Union All
  Select 61,'      AND  A.审核日期 BETWEEN (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And (Select 期末日期 From 材料结存记录 Where ID = [0]))' From Dual Union All
  Select 62,'    GROUP BY RID,记录状态,单据,NO1,日期,单据号,摘要,批号' From Dual Union All
  Select 63,'    UNION ALL' From Dual Union All
  Select 64,'    SELECT  ''C'' AS RID,' From Dual Union All
  Select 65,'      99 AS 记录状态,99 AS 单据,'''' AS NO1, '''' AS 日期,'''' AS 单据号,' From Dual Union All
  Select 66,'      ''期末结存'' AS 摘要,'''' 批号,           ' From Dual Union All
  Select 67,'      SUM(入库数量) AS 入库数量,SUM(入库金额) AS 入库金额,SUM(入库差价) AS 入库差价,                      ' From Dual Union All
  Select 68,'      SUM(出库数量) AS 出库数量,SUM(出库金额) AS 出库金额,SUM(出库差价) AS 出库差价,           ' From Dual Union All
  Select 69,'      -SUM(入库数量)+SUM(出库数量) AS 结存数量   ,' From Dual Union All
  Select 70,'      -SUM(入库金额)+SUM(出库金额) AS 结存金额,' From Dual Union All
  Select 71,'      -SUM(入库差价)+SUM(出库差价) AS 结存差价    ' From Dual Union All
  Select 72,'    FROM ( ' From Dual Union All
  Select 73,'      SELECT 药品ID,DECODE(入出系数,1,1,0)*实际数量*付数/1  AS 入库数量,' From Dual Union All
  Select 74,'        DECODE(入出系数,1,1,0)*零售金额 AS 入库金额,       ' From Dual Union All
  Select 75,'        DECODE(入出系数,1,1,0)*差价 AS 入库差价,       ' From Dual Union All
  Select 76,'        DECODE(入出系数,-1,1,0)*实际数量*付数/1   AS 出库数量,       ' From Dual Union All
  Select 77,'        DECODE(入出系数,-1,1,0)*零售金额 AS 出库金额,     ' From Dual Union All
  Select 78,'        DECODE(入出系数,-1,1,0)*差价 AS 出库差价,     ' From Dual Union All
  Select 79,'        0 AS 期末数量,0 AS 期末金额,0 AS 期末差价' From Dual Union All
  Select 80,'      FROM 药品收发记录 ' From Dual Union All
  Select 81,'      WHERE 药品ID+0=[1] AND 库房ID = (Select 库房id from 材料结存记录 Where id=[0])' From Dual Union All
  Select 82,'      AND  审核日期 BETWEEN (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And (Select 期末日期 From 材料结存记录 Where ID = [0]))' From Dual Union All
  Select 83,'     )   W,材料特性 M ' From Dual Union All
  Select 84,'WHERE M.材料ID=[1])' From Dual Union All
  Select 85,'ORDER BY RID,日期,单据,单据号' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+) and a.性质=1
Order By 库房id, ID','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,1,'材料',3,'选择器定义…',0,Null,'Select id,
上级id,
名称,
decode(类型, 7, ''卫材'') as 材质
From 诊疗分类目录
where 类型 in (1, 2, 3) and
decode(类型, 7, ''卫材'') in
(Select Distinct 功能
From (Select A.系统, A.序号, A.功能
From zlRoleGrant A, Session_Roles B
Where A.角色 = B.Role And A.序号 = 1309 And A.系统 = 100
Union All
Select A.系统, B.序号, B.功能
From zlPrograms A, zlProgFuncs B
Where A.序号 = B.序号 And A.系统 = B.系统 And
A.序号 = 1309 And A.系统 = 100 And
(Exists
(Select 1 From Session_Roles Where Role = ''DBA'') Or
A.系统 in (Select 编号
From zlSystems
Where Upper(所有者) = USER))
))
start with 上级id is null
connect by prior id = 上级id','SELECT DISTINCT M.材料ID,
M.分类ID AS 用途分类ID,
M.编码,
DECODE(C.名称, NULL, M.名称, C.名称) AS 名称,
M.规格,
M.产地
FROM (
SELECT M.撤档时间,M.编码,M.规格,M.产地,A.分类ID,B.材料ID,A.名称
FROM 收费项目目录 M,
材料特性 B,
诊疗项目目录 A
WHERE M.ID=B.材料ID
AND B.诊疗ID=A.ID
AND M.类别 IN (''4'')
) M,
(
SELECT * FROM 收费项目别名 where 性质=3 and 码类<>2
) C
WHERE M.材料ID = C.收费细目ID(+)
AND (M.撤档时间 = to_date(''3000-01-01'',''yyyy-mm-dd'') OR M.撤档时间 IS NULL)
AND (M.编码 like ''[*]%'' or C.简码 like ''%[*]%'' or C.名称 like ''%[*]%'')
order by M.编码','ID,131,'||CHR(38)||'R|上级ID,131,|名称,202,'||CHR(38)||'S|材质,202,','材料ID,131,'||CHR(38)||'B|用途分类ID,131,'||CHR(38)||'R|编码,202,'||CHR(38)||'S|名称,202,'||CHR(38)||'S'||CHR(38)||'D|规格,202,'||CHR(38)||'S|产地,202,'||CHR(38)||'S',User||'.收费项目目录,'||User||'.材料特性,'||User||'.诊疗项目目录,'||User||'.收费项目别名|SYS.SESSION_ROLES,ZLTOOLS.ZLSYSTEMS,ZLTOOLS.ZLROLEGRANT,ZLTOOLS.ZLPROGRAMS,ZLTOOLS.ZLPROGFUNCS,'||User||'.诊疗分类目录',0);
