Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'Select b.名称 As 库房, To_Char(A.期初日期, ''YYYY-MM-DD HH24:MI:SS'') As 期初日期,' From Dual Union All
  Select 2,'       To_Char(A.期末日期, ''YYYY-MM-DD HH24:MI:SS'') As 期末日期' From Dual Union All
  Select 3,'From 材料结存记录 A,部门表 B' From Dual Union All
  Select 4,'where A.库房id=b.id and a.id=[0]' From Dual) a;
Insert Into zlRPTPars(源ID,组名,序号,名称,类型,缺省值,格式,值列表,分类SQL,明细SQL,分类字段,明细字段,对象,锁定) Values(zlRPTDatas_ID.CurrVal,Null,0,'结存',1,'选择器定义…',0,Null,'select ''['' || 编码 || '']'' || 名称 as 名称,ID From 部门表
Where id In (Select 部门id From 部门性质说明
Where 工作性质 In (''卫材库'',''发料部门''))
and (撤档时间 is null or 撤档时间 = to_date(''3000-01-01'',''YYYY-MM-DD''))
order by 编码','Select A.ID,A.库房id, b.名称 || ''('' || A.期间 || '')'' As 期间,A.期初日期,A.期末日期
From 材料结存记录 A, 部门表 B
Where A.库房id = B.ID(+)
Order By 库房id, A.id','名称,202,'||CHR(38)||'S|ID,131,'||CHR(38)||'R','ID,131,'||CHR(38)||'B|库房ID,131,'||CHR(38)||'R|期间,202,'||CHR(38)||'S'||CHR(38)||'D|期初日期,135,'||CHR(38)||'S|期末日期,135,'||CHR(38)||'S',User||'.材料结存记录,'||User||'.部门表|'||User||'.部门性质说明,'||User||'.部门表',0);
Insert Into zlRPTDatas(ID,报表ID,名称,字段,对象,类型,说明,数据连接编号) Values(zlRPTDatas_ID.NextVal,zlReports_ID.CurrVal,'卫材结存明细','业务大类,202|业务分类,202|库房ID,131|材料ID,131|数量,139|售价金额,139|成本金额,139',User||'.材料结存记录,'||User||'.药品收发记录,'||User||'.药品入出类别,'||User||'.材料结存明细,'||User||'.收费项目目录,'||User||'.材料特性',1,Null,Null);
Insert Into zlRPTSQLs(源ID,行号,内容)  Select zlRPTDatas_ID.CurrVal,a.* From (
  Select 1,'Select A.业务大类, A.业务分类, A.库房id, A.材料id,' From Dual Union All
  Select 2,'       Sum(A.数量/Decode([1],1,1,2,换算系数)) As 数量, Sum(A.金额) As 售价金额, Sum(A.金额)-Sum(A.差价) As 成本金额' From Dual Union All
  Select 3,'From (Select A.库房id, ''1-期初'' As 业务大类, '''' As 业务分类, A.材料id As 材料id, Sum(A.期初数量) As 数量, Sum(A.期初金额) As 金额, Sum(A.期初差价) As 差价' From Dual Union All
  Select 4,'       From 材料结存明细 A' From Dual Union All
  Select 5,'       Where 结存id = [0]' From Dual Union All
  Select 6,'       Group By A.库房id, A.材料id' From Dual Union All
  Select 7,'       Union All' From Dual Union All
  Select 8,'     Select * ' From Dual Union All
  Select 9,'     From (Select A.库房id, Decode(B.系数, 1, ''2-入库'', ''3-出库'') As 业务大类, B.名称 As 业务分类, A.药品id As 药品id,' From Dual Union All
  Select 10,'                Sum(Nvl(A.实际数量, 0) * Nvl(A.付数, 1)) As 数量, Sum(Nvl(A.零售金额, 0)) As 金额, Sum(Nvl(A.差价, 0)) As 差价' From Dual Union All
  Select 11,'         From 药品收发记录 A, 药品入出类别 B' From Dual Union All
  Select 12,'         Where A.入出类别id = B.ID And A.单据 In (15, 16, 17, 18, 19, 20, 21, 22, 24, 25, 26) And' From Dual Union All
  Select 13,'               A.审核日期 Between (Select Nvl(期初日期, Sysdate) From 材料结存记录 Where ID = [0]) And' From Dual Union All
  Select 14,'               (Select 期末日期 From 材料结存记录 Where ID = [0]) and a.库房id=(select 库房id from 材料结存记录 where id=[0])' From Dual Union All
  Select 15,'         Group By A.库房id, A.药品id, B.名称, Decode(B.系数, 1, ''2-入库'', ''3-出库'')) X ' From Dual Union All
  Select 16,'     Where X.数量 <> 0 Or X.金额 <> 0 Or X.差价 <> 0' From Dual Union All
  Select 17,'       Union All' From Dual Union All
  Select 18,'       Select A.库房id, ''4-期末'' As 业务大类, '''' As 业务分类, A.材料id, Sum(A.期末数量) As 数量, Sum(A.期末金额) As 金额, Sum(A.期末差价) As 差价' From Dual Union All
  Select 19,'       From 材料结存明细 A' From Dual Union All
  Select 20,'       Where 结存id = [0]' From Dual Union All
  Select 21,'       Group By A.库房id, A.材料id) A, 收费项目目录 B, 材料特性 C' From Dual Union All
  Select 22,'Where A.材料id = B.ID And A.材料id = C.材料id' From Dual Union All
  Select 23,'Group By A.业务大类, A.业务分类, A.库房id, A.材料id, B.类别' From Dual Union All
  Select 24,'Order By A.业务大类, A.业务分类, A.库房id, A.材料id, B.类别' From Dual) a;
