End Zl_病人医嘱报告_Edit;
/

--98876:冉俊明,2017-08-04,发药窗口闲忙分配模式，若有多个很闲的则再按平均方式分配一次。
Create Or Replace Function Zl_Get发药窗口
(
  药房id_In   药品库存.库房id%Type,
  分配方式_In Number := Null,
  登记时间_In 门诊费用记录.登记时间%Type := Null
)
--功能:获取发药窗口
  --返回:返回发药窗口
 Return Varchar2 Is
  Pragma Autonomous_Transaction;

  v_发药窗口 Varchar2(200);
  n_Assign   Number(5);
  n_Temp     Number(18);
  v_编码     发药窗口.编码%Type;

  d_Date 门诊费用记录.登记时间%Type;
  n_Num  Number(18);
Begin
  --n_Assign:0-忙闲优化方式;1-平均分配方式
  n_Assign := Nvl(分配方式_In, Nvl(zl_GetSysParameter(19), 0));
  d_Date   := Nvl(登记时间_In, Sysdate);

  v_发药窗口 := Null;
  v_编码     := Null;
  n_Temp     := 0;
  If n_Assign = 0 Then
    --0-忙闲优化方式
    --先根据闲忙状态来分,如果有多个很闲的，则再根据平均法来分
    For c_窗口 In (Select 编码, 发药窗口, 活动, Nvl(Sum(Num), 0) As Num
                 From (Select 编码, 名称 As 发药窗口, Nvl(当前分配, 0) As 活动, 0 As Num
                        From 发药窗口
                        Where 上班否 = 1 And Nvl(专家, 0) = 0 And 药房id = 药房id_In
                        Union
                        Select b.编码, a.发药窗口, Nvl(b.当前分配, 0) As 活动, Nvl(Count(a.发药窗口), 0) As Num
                        From 未发药品记录 A, 发药窗口 B
                        Where a.发药窗口 = b.名称 And a.库房id = b.药房id And a.填制日期 Between Trunc(d_Date) And
                              Trunc(d_Date + 1) - 1 / 24 / 60 / 60 And b.上班否 = 1 And Nvl(b.专家, 0) = 0 And b.药房id = 药房id_In And
                              Not Exists
                         (Select 1
                               From 门诊费用记录 B
                               Where b.No = a.No And b.记录性质 = Decode(a.单据, 8, 1, 24, 1, 0) And Nvl(b.费用状态, 0) = 1)
                        Group By 编码, 发药窗口, Nvl(b.当前分配, 0))
                 Group By 编码, 发药窗口, 活动
                 Order By Num, 编码) Loop
    
      If v_编码 Is Null Then
        v_编码     := c_窗口.编码;
        v_发药窗口 := c_窗口.发药窗口;
        n_Num      := c_窗口.Num;
      End If;
      If Nvl(c_窗口.活动, 0) = 1 Then
        n_Temp := 1;
      End If;
    
      If c_窗口.Num > n_Num Then
        --大于了第一个窗口的已分配数量，肯定更忙
        Exit;
      End If;
      If n_Temp = 1 And Nvl(c_窗口.活动, 0) = 0 Then
        v_发药窗口 := c_窗口.发药窗口;
        v_编码     := c_窗口.编码;
        --已找到活动窗口的下一个窗口
        Exit;
      End If;
    End Loop;
  
  Else
    --1-平均分配方式
    For c_窗口 In (Select 编码, 名称 As 发药窗口, Nvl(当前分配, 0) As 活动
                 From 发药窗口
                 Where 上班否 = 1 And Nvl(专家, 0) = 0 And 药房id = 药房id_In
                 Order By 编码) Loop
    
      If v_编码 Is Null Then
        v_编码     := c_窗口.编码;
        v_发药窗口 := c_窗口.发药窗口;
      End If;
      If Nvl(c_窗口.活动, 0) = 1 Then
        n_Temp := 1;
      End If;
      If n_Temp = 1 And Nvl(c_窗口.活动, 0) = 0 Then
        v_发药窗口 := c_窗口.发药窗口;
        v_编码     := c_窗口.编码;
        --已找到活动窗口的下一个窗口
        Exit;
      End If;
    End Loop;
  End If;

  --调整当前分配标志
  If v_编码 Is Not Null Then
    Update 发药窗口 Set 当前分配 = Decode(编码, v_编码, 1, 0) Where 药房id = 药房id_In;
    Commit;
  End If;
  Return v_发药窗口;
Exception
  When Others Then
    Return v_发药窗口;
End Zl_Get发药窗口;
/

--112272:刘尔旋,2017-08-04,三方票据接口处理
Create Or Replace Procedure Zl_票据起始号_Update
(
  No_In     门诊费用记录.No%Type,
  票据号_In 票据使用明细.号码%Type,
  票种_In   票据使用明细.票种%Type
) As
  --在费用数据产生时填写开始票据号,用于医保上传 
  --如果票据号传入空将清空开始票据号,用于打印失败或取消打印时调用 
Begin
  If 票种_In = 1 Then
    Update 门诊费用记录 Set 实际票号 = 票据号_In Where 记录性质 = 1 And NO = No_In;
    If Sql%NotFound Then
      Update 费用补充记录 Set 实际票号 = 票据号_In Where 记录性质 = 1 And NO = No_In;
    End If;
  Elsif 票种_In = 3 Then
    Update 病人结帐记录 Set 实际票号 = 票据号_In Where 记录状态 = 1 And NO = No_In;
  Elsif 票种_In = 4 Then
    Update 门诊费用记录 Set 实际票号 = 票据号_In Where 记录性质 = 4 And NO = No_In;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_票据起始号_Update;
/

--95359:刘尔旋,2017-08-03,新增HIS用三方接口
Create Or Replace Procedure Zl_三方机构结帐_Insert
(
  结帐类型_In  病人结帐记录.结帐类型%Type,
  病人id_In    病人信息.病人id%Type,
  主页id_In    病案主页.主页id%Type,
  结帐金额_In  病人预交记录.冲预交%Type,
  结帐时间_In  病人结帐记录.收费时间%Type := Null,
  结算信息1_In In Varchar2 := Null,
  结算信息2_In In Varchar2 := Null,
  结算信息3_In In Varchar2 := Null,
  返回信息_Out Out Varchar2
) As
  --结帐类型_In:1-门诊,2-住院
  --主页ID_In:门诊不传
  --结算信息:由于Oracle最多能传入4000个字符，所以规划了三个结算信息,三个结算信息(包含三个，结算信息1_In,结算信息2_In,结算信息3_In)组合在一起，构成完成的XML串,XML_串格式如下:
  --         <JS>
  --              <JSKLB>支付卡类别</JSKLB>
  --              <JSKH>支付卡号</ JSKH>
  --              <JSFS>支付方式</JSFS> //支付方式:现金;支票,如果是三方卡,可以传空
  --              <JSJE>支付金额</JSJE>
  --              <JYLSH>交易流水号</JYLSH>
  --              <ZY>摘要</ZY>
  --              <SFCYJ>是否冲预交</SFCYJ>  //允冲预交时,只填JSJE节点:1-冲预交
  --              <SFXFK>是否消费卡</SFXFK>  //(1-是消费卡),消费卡时,传入结算卡类别,结算卡号,结算金额等接点
  --              <EXPENDLIST>  //扩展交易信息
  --                  <EXPEND>
  --                        <JYMC >交易名称</交易名称>
  --                        <JYLR>交易内容</JYLR>
  --                  </EXPEND>
  --              </EXPENDLIST>
  --         </JS>
  v_Error Varchar(255);
  Err_Custom Exception;
  v_Temp     Varchar2(20000);
  v_冲预交   Varchar2(2000);
  v_结算扩展 Varchar2(20000);
  v_扩展信息 Varchar2(20000);
  v_当前结算 Varchar2(5000);
  v_结算项目 三方结算交易.交易项目%Type;
  v_结算内容 三方结算交易.交易内容%Type;
  n_结算金额 病人预交记录.冲预交%Type;
  Xml_In     Xmltype;
  Xml_Out    Xmltype;
  x_Templet  Xmltype;
Begin
  x_Templet := Xmltype('<IN></IN>');
  v_Temp    := '<JSLX>' || 结帐类型_In || '</JSLX>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;
  v_Temp := '<BRID>' || 病人id_In || '</BRID>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;
  v_Temp := '<ZYID>' || 主页id_In || '</ZYID>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;
  v_Temp := '<JE>' || 结帐金额_In || '</JE>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;
  v_Temp := '<JZSJ>' || 结帐时间_In || '</JZSJ>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;

  v_Temp := '<JSLIST>' || Nvl(结算信息1_In, '') || Nvl(结算信息2_In, '') || Nvl(结算信息3_In, '') || '</JSLIST>';
  Select Appendchildxml(x_Templet, '/IN', Xmltype(v_Temp)) Into x_Templet From Dual;

  Xml_In := x_Templet;
  Zl_Third_Settlement(Xml_In, Xml_Out);
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]+' || v_Error || '+[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_三方机构结帐_Insert;
/

--95359:刘尔旋,2017-08-03,新增HIS用三方接口
Create Or Replace Procedure Zl_三方机构收费_Insert
(
  Nos_In       In Varchar2,
  费用金额_In  In 门诊费用记录.实收金额%Type,
  病人id_In    In 门诊费用记录.病人ID%Type,
  误差费_In    In 病人预交记录.冲预交%Type,
  结算信息1_In In Varchar2 := Null,
  结算信息2_In In Varchar2 := Null,
  结算信息3_In In Varchar2 := Null,
  返回信息_Out Out Varchar2
) Is
  --NOs_In:收费单据号串,逗号分隔多个单据号
  --费用金额_IN:总金额,用以验证界面的总额与实际传入的划价总额是否一致
  --误差费_In:本次产生的误差总额。
  --结算信息:由于Oracle最多能传入4000个字符，所以规划了三个结算信息,三个结算信息(包含三个，结算信息1_In,结算信息2_In,结算信息3_In)组合在一起，构成完成的XML串,XML_串格式如下:
  --         <JS>
  --              <JSKLB>支付卡类别</JSKLB>
  --              <JSKH>支付卡号</ JSKH>
  --              <JSFS>支付方式</JSFS> //支付方式:现金;支票,如果是三方卡,可以传空
  --              <JSJE>支付金额</JSJE>
  --              <JYLSH>交易流水号</JYLSH>
  --              <ZY>摘要</ZY>
  --              <SFCYJ>是否冲预交</SFCYJ>  //允冲预交时,只填JSJE节点:1-冲预交
  --              <SFXFK>是否消费卡</SFXFK>  //(1-是消费卡),消费卡时,传入结算卡类别,结算卡号,结算金额等接点
  --              <EXPENDLIST>  //扩展交易信息
  --                  <EXPEND>
  --                        <JYMC >交易名称</交易名称>
  --                        <JYLR>交易内容</JYLR>
  --                  </EXPEND>
  --              </EXPENDLIST>
  --         </JS>

  Xml_In  Xmltype;
  Xml_Out Xmltype;

  v_Temp    Varchar2(32767); --临时XML 
  v_Err_Msg Varchar2(200);
  Err_Item Exception;
Begin

  v_Temp := '<NO>' || Nos_In || '<>';
  v_Temp := v_Temp || '<JE>' || 费用金额_In || '</JE>';
  v_Temp := v_Temp || '<BRID>' || 病人id_In || '</BRID>';
  v_Temp := v_Temp || '<SFGH>0</SFGH>';
  v_Temp := v_Temp || '<WCJE>' || 误差费_In || '</WCJE>';

  v_Temp := '<IN>' || v_Temp || '</IN>';

  Xml_In := Xmltype(v_Temp);

  v_Temp := '<JSLIST>' || Nvl(结算信息1_In, '') || Nvl(结算信息2_In, '') || Nvl(结算信息3_In, '') || '</JSLIST>';

  Select Appendchildxml(Xml_In, '/IN', Xmltype(v_Temp)) Into Xml_In From Dual;

  Zl_Third_Payment(Xml_In, Xml_Out);

Exception
  When Err_Item Then
    v_Temp := '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]';
    Raise_Application_Error(-20101, v_Temp);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_三方机构收费_Insert;
/

--90750:刘尔旋,2017-08-03,合作单位默认禁用规则
Create Or Replace Procedure Zl_挂号安排计划_Insert
(
  Id_In       In 挂号安排计划.Id%Type,
  安排id_In   In 挂号安排计划.安排id%Type,
  号码_In     In 挂号安排计划.号码%Type,
  生效时间_In In 挂号安排计划.生效时间%Type,
  失效时间_In In 挂号安排计划.失效时间%Type,
  周日_In     In 挂号安排计划.周日%Type,
  周一_In     In 挂号安排计划.周一%Type,
  周二_In     In 挂号安排计划.周二%Type,
  周三_In     In 挂号安排计划.周三%Type,
  周四_In     In 挂号安排计划.周四%Type,
  周五_In     In 挂号安排计划.周五%Type,
  周六_In     In 挂号安排计划.周六%Type,
  限号控制_In In Varchar2,
  分诊方式_In In 挂号安排计划.分诊方式%Type,
  序号控制_In In 挂号安排计划.序号控制%Type,
  项目id_In   In 挂号安排计划.项目id%Type,
  医生姓名_In In 挂号安排计划.医生姓名%Type,
  医生id_In   In 挂号安排计划.医生id%Type,
  诊室_In     Varchar2,
  新增_In     Number := 1,
  处理类型_In Number := 0,
  时段间隔_In 挂号安排计划.默认时段间隔%Type := 0
) Is
  --  限号控制_IN:|周一,22(限号),13(限约)|周二,20(限号),11(限约)....
  --  处理类型 0-对时段信息不处理
  --            新增时 不提取安排的时段数据
  --            修改时 修改了排班后对时段数据不进行处理 ,只删除 去掉了排班的时段)
  --         1.新增时  自动提取挂号安排时段的数据作为计划时段的数据
  --           修改时  清除该计划所有的计划时段的数据
  --  时段间隔_IN:为计划设置时段是默认时间段间隔
  --立即启用_IN,立即审核_IN,此两个参数,暂时未使用
  Err_Item Exception;
  v_Err_Msg      Varchar2(500);
  v_User_Name    人员表.姓名%Type;
  v_诊室         Varchar2(4000);
  d_Date         Date;
  n_限号数       挂号安排限制.限号数%Type;
  n_限约数       挂号安排限制.限约数%Type;
  v_限制项目     挂号安排限制.限制项目%Type;
  v_限制控制     Varchar2(4000);
  v_当前项目     Varchar2(4000);
  v_禁用合作单位 Varchar2(4000);
  v_Rowtotal     Number(9) := 1;
  v_Firstid      挂号安排计划.Id%Type;
  n_Add          Number(3);
Begin
  Begin
    Select p.姓名 Into v_User_Name From 上机人员表 O, 人员表 P Where o.人员id = p.Id And 用户名 = User;
  Exception
    When Others Then
      v_User_Name := Null;
  End;
  If v_User_Name Is Null Then
    v_Err_Msg := '[ZLSOFT]当前用户未设置对应的人员信息,请与' || Chr(10) || Chr(13) || '系统管理员联系,先到用户授权管理中设置！[ZLSOFT]';
    Raise Err_Item;
  End If;
  --之所以要减,主要是避免当前立即生效的结果
  Select Sysdate - 20 * 1 / 24 / 60 / 60 Into d_Date From Dual;
  If 生效时间_In < d_Date Then
    v_Err_Msg := '[ZLSOFT]计划生效时间,必须大于当前时间,请检查！[ZLSOFT]';
    Raise Err_Item;
  End If;
  If 新增_In = 1 Then
    Select Count(1) Into v_Rowtotal From 挂号安排计划 Where 安排id = 安排id_In;
  
    Insert Into 挂号安排计划
      (ID, 安排id, 号码, 生效时间, 失效时间, 周日, 周一, 周二, 周三, 周四, 周五, 周六, 分诊方式, 序号控制, 安排人, 安排时间, 审核人, 审核时间, 实际生效, 项目id, 医生姓名, 医生id,
       默认时段间隔)
      Select Id_In, 安排id_In, 号码_In, 生效时间_In, 失效时间_In, 周日_In, 周一_In, 周二_In, 周三_In, 周四_In, 周五_In, 周六_In, 分诊方式_In, 序号控制_In,
             v_User_Name, Sysdate, Null, Null, To_Date('3000-01-01', 'yyyy-mm-dd'), 项目id_In, 医生姓名_In, 医生id_In,
             Nvl(时段间隔_In, 5)
      From Dual;
    For r_安排单位 In (Select Distinct 合作单位 From 合作单位安排控制 Where 安排id = 安排id_In) Loop
      Select Count(1)
      Into n_Add
      From 合作单位安排控制
      Where 安排id = 安排id_In And 合作单位 = r_安排单位.合作单位 And 序号 = 0 And 数量 = 0;
      If n_Add = 7 Then
        v_禁用合作单位 := v_禁用合作单位 || ',' || r_安排单位.合作单位;
      End If;
    End Loop;
    If v_禁用合作单位 Is Not Null Then
      v_禁用合作单位 := Substr(v_禁用合作单位, 2);
    End If;
    For r_合作单位 In (Select 名称
                   From 挂号合作单位
                   Where 名称 In (Select Column_Value From Table(f_Str2list(v_禁用合作单位)))) Loop
      Insert Into 合作单位计划控制
        (合作单位, 计划id, 限制项目, 序号, 数量)
        Select r_合作单位.名称, Id_In, '周日', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周一', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周二', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周三', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周四', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周五', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周六', 0, 0 From Dual;
    End Loop;
    --处理首次添加计划时 把原来挂号安排中的数据作为一个长期计划添加的挂号安排计划以及相关的表中
    If v_Rowtotal = 0 Then
      Select 挂号安排计划_Id.Nextval Into v_Firstid From Dual;
      Insert Into 挂号安排计划
        (ID, 安排id, 号码, 生效时间, 失效时间, 周日, 周一, 周二, 周三, 周四, 周五, 周六, 分诊方式, 序号控制, 安排人, 安排时间, 审核人, 审核时间, 实际生效, 项目id, 医生姓名, 医生id,
         默认时段间隔)
        Select v_Firstid As ID, ID As 安排id, 号码, Nvl(开始时间, To_Date('1900-01-01', 'yyyy-mm-dd')),
               Nvl(终止时间, To_Date('3000-01-01', 'yyyy-mm-dd')), 周日, 周一, 周二, 周三, 周四, 周五, 周六, 分诊方式, 序号控制, v_User_Name,
               Sysdate, v_User_Name, Sysdate, To_Date('3000-01-01', 'yyyy-mm-dd'), 项目id, 医生姓名, 医生id, Nvl(默认时段间隔, 5)
        From 挂号安排
        Where ID = 安排id_In;
      Insert Into 挂号计划诊室
        (计划id, 门诊诊室)
        Select v_Firstid As 计划id, 门诊诊室 From 挂号安排诊室 Where 号表id = 安排id_In;
      Insert Into 挂号计划限制
        (计划id, 限号数, 限约数, 限制项目)
        Select v_Firstid As 计划id, 限号数, 限约数, 限制项目 From 挂号安排限制 Where 安排id = 安排id_In;
      Insert Into 挂号计划时段
        (计划id, 序号, 开始时间, 结束时间, 限制数量, 是否预约, 星期)
        Select v_Firstid As 计划id, 序号, 开始时间, 结束时间, 限制数量, 是否预约, 星期
        From 挂号安排时段
        Where 安排id = 安排id_In;
      Insert Into 合作单位计划控制
        (合作单位, 计划id, 限制项目, 序号, 数量)
        Select 合作单位, v_Firstid, 限制项目, 序号, 数量 From 合作单位安排控制 Where 安排id = 安排id_In;
    End If;
  
  Else
    Delete 挂号计划诊室 Where 计划id = Id_In;
    --修改计划时 先处理时段信息
    If Nvl(处理类型_In, 0) = 0 Then
      If Nvl(处理类型_In, 0) = 1 Then
        Delete 挂号计划时段 Where 计划id = Id_In; --
      Else
        --针对存在时段这种情况需要先处理排班修改后的时段 在后再修改安排
        Delete 挂号计划时段
        Where 计划id = Id_In And 星期 In (Select 星期
                                      From (Select Case 周一
                                                       When 周一_In Then
                                                        Null
                                                       Else
                                                        '周一'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周二
                                                       When 周二_In Then
                                                        Null
                                                       Else
                                                        '周二'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周三
                                                       When 周三_In Then
                                                        Null
                                                       Else
                                                        '周三'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周四
                                                       When 周四_In Then
                                                        Null
                                                       Else
                                                        '周四'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周五
                                                       When 周五_In Then
                                                        Null
                                                       Else
                                                        '周五'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周六
                                                       When 周六_In Then
                                                        Null
                                                       Else
                                                        '周六'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In
                                             Union All
                                             Select Case 周日
                                                       When 周日_In Then
                                                        Null
                                                       Else
                                                        '周日'
                                                     End As 星期
                                             From 挂号安排计划
                                             Where ID = Id_In) A
                                      Where a.星期 Is Not Null);
      End If;
    Else
      Delete 挂号计划时段 Where 计划id = Id_In;
    End If;
  
    Update 挂号安排计划
    Set ID = Id_In, 安排id = 安排id_In, 号码 = 号码_In, 生效时间 = 生效时间_In, 失效时间 = 失效时间_In, 周日 = 周日_In, 周一 = 周一_In, 周二 = 周二_In,
        周三 = 周三_In, 周四 = 周四_In, 周五 = 周五_In, 周六 = 周六_In, 分诊方式 = 分诊方式_In, 序号控制 = 序号控制_In, 安排人 = v_User_Name,
        安排时间 = Sysdate, 项目id = Nvl(项目id_In, 项目id), 医生姓名 = 医生姓名_In, 医生id = Decode(医生id_In, 0, Null, 医生id_In),
        默认时段间隔 = Decode(Nvl(时段间隔_In, 0), 0, 默认时段间隔, 时段间隔_In)
    Where ID = Id_In And 审核时间 Is Null;
    If Sql%NotFound Then
      v_Err_Msg := '[ZLSOFT]该计划安排已经被他人审核或删除,不能再修改![ZLSOFT]';
      Raise Err_Item;
    End If;
    Delete 挂号计划限制 Where 计划id = Id_In;
  End If;

  v_限制控制 := 限号控制_In || '|';
  While v_限制控制 Is Not Null Loop
    v_当前项目 := Substr(v_限制控制, 1, Instr(v_限制控制, '|') - 1);
    v_限制项目 := Substr(v_当前项目, 1, Instr(v_当前项目, ',') - 1);
    v_当前项目 := Substr(v_当前项目, Instr(v_当前项目, ',') + 1);
    n_限号数   := To_Number(Substr(v_当前项目, 1, Instr(v_当前项目, ',') - 1));
    n_限约数   := To_Number(Substr(v_当前项目, Instr(v_当前项目, ',') + 1));
    Insert Into 挂号计划限制
      (计划id, 限制项目, 限号数, 限约数)
    Values
      (Id_In, v_限制项目, Decode(n_限号数, 0, Null, n_限号数), n_限约数);
    v_限制控制 := Substr(v_限制控制, Instr(v_限制控制, '|') + 1);
  End Loop;
  --对挂号计划时段进行处理
  If Nvl(新增_In, 0) = 1 And 处理类型_In = 1 Then
    --提取挂号安排时段的数据作为计划时段的数据
    Insert Into 挂号计划时段
      (计划id, 序号, 开始时间, 结束时间, 限制数量, 是否预约, 星期)
      Select Id_In, 序号, 开始时间, 结束时间, 限制数量, 是否预约, 星期 From 挂号安排时段 Where 安排id = 安排id_In;
  End If;
  --挂号安排诊室处理
  If 诊室_In Is Not Null Then
    v_诊室 := 诊室_In || ';';
    While v_诊室 Is Not Null Loop
      Insert Into 挂号计划诊室 (计划id, 门诊诊室) Values (Id_In, Substr(v_诊室, 1, Instr(v_诊室, ';') - 1));
      v_诊室 := Substr(v_诊室, Instr(v_诊室, ';') + 1);
    End Loop;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_挂号安排计划_Insert;
/

--90750:刘尔旋,2017-08-03,合作单位默认禁用规则
Create Or Replace Procedure Zl_挂号安排_Insert
(
  Id_In           挂号安排.Id%Type,
  号码_In         挂号安排.号码%Type,
  号类_In         挂号安排.号类%Type,
  科室id_In       挂号安排.科室id%Type,
  项目id_In       挂号安排.项目id%Type,
  医生_In         挂号安排.医生姓名%Type,
  医生id_In       挂号安排.医生id%Type,
  病案必须_In     挂号安排.病案必须%Type,
  周日_In         挂号安排.周日%Type,
  周一_In         挂号安排.周一%Type,
  周二_In         挂号安排.周二%Type,
  周三_In         挂号安排.周三%Type,
  周四_In         挂号安排.周四%Type,
  周五_In         挂号安排.周五%Type,
  周六_In         挂号安排.周六%Type,
  限号控制_In     Varchar2,
  分诊方式_In     挂号安排.分诊方式%Type,
  诊室_In         Varchar2,
  开始时间_In     挂号安排.开始时间%Type,
  终止时间_In     挂号安排.终止时间%Type,
  新增_In         Number,
  序号控制_In     挂号安排.序号控制%Type,
  处理类型_In     Number := 0,
  默认时间间隔_In 挂号安排.默认时段间隔%Type := 0,
  预约天数_In     挂号安排.预约天数%Type := Null
) As
  -----------------------------------------------------------
  --参数：
  --  诊室_IN=以';'号分隔的多个诊室名称
  --  限号控制_IN:|周一,22(限号),13(限约)|周二,20(限号),11(限约)....
  --  处理类型_IN:修改安排时 对时段数据的处理 0--不处理 1--删除时段信息
  --  默认时间间隔_In:为安排设置时段时默认的时间段间隔
  -----------------------------------------------------------
  v_诊室  Varchar2(1000);
  n_Count Number(18);
  Err_Item Exception;
  v_Err_Msg      Varchar2(100);
  n_序号         Number(18);
  v_操作员姓名   挂号安排计划.审核人%Type;
  n_限号数       挂号安排限制.限号数%Type;
  n_限约数       挂号安排限制.限约数%Type;
  v_限制项目     挂号安排限制.限制项目%Type;
  v_限制控制     Varchar2(4000);
  v_当前项目     Varchar2(4000);
  v_禁用合作单位 Varchar2(4000);

Begin
  Begin
    If Nvl(医生id_In, 0) <> 0 Then
      Select Nvl(Max(序号), 0) + 1
      Into n_序号
      From 挂号安排
      Where 科室id = 科室id_In And 项目id = 项目id_In And 医生id = 医生id_In And ID + 0 <> Id_In;
    Else
      Select Nvl(Max(序号), 0) + 1
      Into n_序号
      From 挂号安排
      Where 科室id = 科室id_In And 项目id = 项目id_In And 医生姓名 = 医生_In And Nvl(医生id, 0) = 0 And ID + 0 <> Id_In;
    End If;
  Exception
    When Others Then
      n_序号 := 1;
  End;

  If 新增_In = 1 Then
    --新增挂号安排
    Begin
      Select b.姓名 Into v_操作员姓名 From 上机人员表 A, 人员表 B Where a.人员id = b.Id And a.用户名 = User;
    Exception
      When Others Then
        Null;
    End;
    Insert Into 挂号安排
      (ID, 号码, 号类, 科室id, 项目id, 医生姓名, 医生id, 序号, 病案必须, 周日, 周一, 周二, 周三, 周四, 周五, 周六, 分诊方式, 开始时间, 终止时间, 序号控制, 默认时段间隔, 预约天数)
    Values
      (Id_In, 号码_In, 号类_In, 科室id_In, 项目id_In, 医生_In, Decode(医生id_In, 0, Null, 医生id_In), n_序号, 病案必须_In, 周日_In, 周一_In,
       周二_In, 周三_In, 周四_In, 周五_In, 周六_In, 分诊方式_In, 开始时间_In, 终止时间_In, 序号控制_In, 默认时间间隔_In, 预约天数_In);
    v_禁用合作单位 := zl_GetSysParameter('禁用合作单位', 1110);
    v_禁用合作单位 := Replace(v_禁用合作单位, '|', ',');
    For r_合作单位 In (Select 名称
                   From 挂号合作单位
                   Where 名称 In (Select Column_Value From Table(f_Str2list(v_禁用合作单位)))) Loop
      Insert Into 合作单位安排控制
        (合作单位, 安排id, 限制项目, 序号, 数量)
        Select r_合作单位.名称, Id_In, '周日', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周一', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周二', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周三', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周四', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周五', 0, 0
        From Dual
        Union
        Select r_合作单位.名称, Id_In, '周六', 0, 0 From Dual;
    End Loop;
  
  Else
    --修改指定ID的挂号安排
    --1.先检查是否存在计划,存在计划，不能更改
    Begin
      Select Count(*) Into n_Count From 挂号安排计划 Where 安排id = Id_In;
    Exception
      When Others Then
        n_Count := 0;
    End;
    If n_Count > 0 Then
      v_Err_Msg := '[ZLSOFT]该安排已经制定了相关的计划,,不能再修改![ZLSOFT]';
      Raise Err_Item;
    End If;
  
    If Nvl(处理类型_In, 0) = 1 Then
      Delete 挂号安排时段 Where 安排id = Id_In;
    Else
      --针对存在时段这种情况需要先处理排班修改后的时段 在后再修改安排
      Delete 挂号安排时段
      Where 安排id = Id_In And 星期 In (Select 星期
                                    From (Select Case 周一
                                                     When 周一_In Then
                                                      Null
                                                     Else
                                                      '周一'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周二
                                                     When 周二_In Then
                                                      Null
                                                     Else
                                                      '周二'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周三
                                                     When 周三_In Then
                                                      Null
                                                     Else
                                                      '周三'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周四
                                                     When 周四_In Then
                                                      Null
                                                     Else
                                                      '周四'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周五
                                                     When 周五_In Then
                                                      Null
                                                     Else
                                                      '周五'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周六
                                                     When 周六_In Then
                                                      Null
                                                     Else
                                                      '周六'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In
                                           Union All
                                           Select Case 周日
                                                     When 周日_In Then
                                                      Null
                                                     Else
                                                      '周日'
                                                   End As 星期
                                           From 挂号安排
                                           Where ID = Id_In) A
                                    Where a.星期 Is Not Null);
    End If;
  
    Update 挂号安排
    Set 号码 = 号码_In, 号类 = 号类_In, 科室id = 科室id_In, 项目id = 项目id_In, 医生姓名 = 医生_In, 医生id = Decode(医生id_In, 0, Null, 医生id_In),
        序号 = n_序号, 病案必须 = 病案必须_In, 周日 = 周日_In, 周一 = 周一_In, 周二 = 周二_In, 周三 = 周三_In, 周四 = 周四_In, 周五 = 周五_In, 周六 = 周六_In,
        开始时间 = 开始时间_In, 终止时间 = 终止时间_In, 分诊方式 = 分诊方式_In, 序号控制 = 序号控制_In,
        默认时段间隔 = Decode(Nvl(默认时间间隔_In, 0), 0, 默认时段间隔, 默认时间间隔_In), 预约天数 = 预约天数_In
    Where ID = Id_In;
  
    Delete 挂号安排限制 Where 安排id = Id_In;
  End If;

  v_限制控制 := 限号控制_In || '|';
  While v_限制控制 Is Not Null Loop
    v_当前项目 := Substr(v_限制控制, 1, Instr(v_限制控制, '|') - 1);
    v_限制项目 := Substr(v_当前项目, 1, Instr(v_当前项目, ',') - 1);
    v_当前项目 := Substr(v_当前项目, Instr(v_当前项目, ',') + 1);
    n_限号数   := To_Number(Substr(v_当前项目, 1, Instr(v_当前项目, ',') - 1));
    n_限约数   := To_Number(Substr(v_当前项目, Instr(v_当前项目, ',') + 1));
    Insert Into 挂号安排限制
      (安排id, 限制项目, 限号数, 限约数)
    Values
      (Id_In, v_限制项目, Decode(n_限号数, 0, Null, n_限号数), n_限约数);
    v_限制控制 := Substr(v_限制控制, Instr(v_限制控制, '|') + 1);
  End Loop;

  If 新增_In = 0 Then
    --挂号安排诊室处理
    Delete From 挂号安排诊室 Where 号表id = Id_In;
  End If;

  If 诊室_In Is Not Null Then
    v_诊室 := 诊室_In || ';';
    While v_诊室 Is Not Null Loop
      Insert Into 挂号安排诊室 (号表id, 门诊诊室) Values (Id_In, Substr(v_诊室, 1, Instr(v_诊室, ';') - 1));
      v_诊室 := Substr(v_诊室, Instr(v_诊室, ';') + 1);
    End Loop;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_挂号安排_Insert;
/

--113243:焦博,2017-09-06,传入操作员为空时,设置默认操作员
--112349:刘尔旋,2017-08-02,记帐传入批次参数兼容处理
Create Or Replace Procedure Zl_住院记帐记录_Insert
(
  No_In             住院费用记录.No%Type,
  序号_In           住院费用记录.序号%Type,
  病人id_In         住院费用记录.病人id%Type,
  主页id_In         住院费用记录.主页id%Type,
  标识号_In         住院费用记录.标识号%Type,
  姓名_In           住院费用记录.姓名%Type,
  性别_In           住院费用记录.性别%Type,
  年龄_In           住院费用记录.年龄%Type,
  床号_In           住院费用记录.床号%Type,
  费别_In           住院费用记录.费别%Type,
  病区id_In         住院费用记录.病人病区id%Type,
  科室id_In         住院费用记录.病人科室id%Type,
  加班标志_In       住院费用记录.加班标志%Type,
  婴儿费_In         住院费用记录.婴儿费%Type,
  开单部门id_In     住院费用记录.开单部门id%Type,
  开单人_In         住院费用记录.开单人%Type,
  从属父号_In       住院费用记录.从属父号%Type,
  收费细目id_In     住院费用记录.收费细目id%Type,
  收费类别_In       住院费用记录.收费类别%Type,
  计算单位_In       住院费用记录.计算单位%Type,
  保险项目否_In     住院费用记录.保险项目否%Type,
  保险大类id_In     住院费用记录.保险大类id%Type,
  保险编码_In       住院费用记录.保险编码%Type,
  付数_In           住院费用记录.付数%Type,
  数次_In           住院费用记录.数次%Type,
  附加标志_In       住院费用记录.附加标志%Type,
  执行部门id_In     住院费用记录.执行部门id%Type,
  价格父号_In       住院费用记录.价格父号%Type,
  收入项目id_In     住院费用记录.收入项目id%Type,
  收据费目_In       住院费用记录.收据费目%Type,
  标准单价_In       住院费用记录.标准单价%Type,
  应收金额_In       住院费用记录.应收金额%Type,
  实收金额_In       住院费用记录.实收金额%Type,
  统筹金额_In       住院费用记录.统筹金额%Type,
  发生时间_In       住院费用记录.发生时间%Type,
  登记时间_In       住院费用记录.登记时间%Type,
  药品摘要_In       药品收发记录.摘要%Type,
  划价_In           Number,
  操作员编号_In     住院费用记录.操作员编号%Type,
  操作员姓名_In     住院费用记录.操作员姓名%Type,
  多病人单_In       Number := 0,
  类别id_In         药品单据性质.类别id%Type := Null,
  记帐单id_In       住院费用记录.记帐单id%Type := Null,
  费用摘要_In       住院费用记录.摘要%Type := Null,
  是否急诊_In       住院费用记录.是否急诊%Type := 0,
  医嘱序号_In       住院费用记录.医嘱序号%Type := Null,
  频次_In           药品收发记录.频次%Type := Null,
  单量_In           药品收发记录.单量%Type := Null,
  用法_In           药品收发记录.用法%Type := Null, --用法[|煎法]
  期效_In           药品收发记录.扣率%Type := Null,
  计价特性_In       药品收发记录.扣率%Type := Null,
  简单记帐_In       Number := 0,
  费用类型_In       住院费用记录.费用类型%Type := Null,
  医技补临床费用_In Number := 0,
  领药部门id_In     药品收发记录.对方部门id%Type := Null,
  中药形态_In       住院费用记录.结论%Type := Null,
  医疗小组id_In     住院费用记录.医疗小组id%Type := -1,
  备货材料_In       Number := 0,
  批次_In           药品收发记录.批次%Type := Null
) As
  --功能：新收一张住院记帐单据
  --参数：
  --   药品摘要_IN:存放医嘱中的附加说明或修改保存新单据时用。目前仅用于存放于药品收发记录的摘要中。
  --         原单据(记录状态=2)记录修改产生的新单据号。
  --         新单据(记录状态=1)记录所修改的原单据号。
  --   划价-是否属于住院划价。
  --   医技补临床费用_in:医技站补费时,如果开单科室为临床科室则划价人和记帐人填写为不同,用于销帐申请时区分填写审核科室
  --   备货材料_IN: 医技站的卫生材料备货处理方式:0-正常记帐单;1-备货材料记帐
  --   批次_In:当备货材料_IN=1时有效.传入指定的卫生材料的批次
  v_费用id 住院费用记录.Id%Type;
  v_优先级 未发药品记录.优先级%Type;

  --药房分批、时价药品--
  ------------------------------------------------------------
  --该游标用于分批药品数量分解
  Cursor c_Stock
  (
    n_Outmode Number,
    n_库房id  药品收发记录.库房id%Type
  ) Is
    Select 库房id, 药品id, 批次, 上次批号, 可用数量, 实际数量, 实际金额, 上次供应商id, 批准文号, 上次产地, 上次生产日期, 灭菌效期, 效期, 零售价, 商品条码, 内部条码
    From 药品库存
    Where 药品id = 收费细目id_In And 库房id = n_库房id And 性质 = 1 And (Nvl(批次, 0) = Nvl(批次_In, 0) Or Nvl(批次_In, 0) = 0) And
          (Nvl(批次, 0) = 0 Or 效期 Is Null Or 效期 > Trunc(Sysdate)) And Nvl(可用数量, 0) > 0
    Order By Decode(n_Outmode, 1, 效期, Null), Nvl(批次, 0);
  r_Stock c_Stock%RowType;

  --属性
  n_分批 药品规格.药房分批%Type;
  n_时价 收费项目目录.是否变价%Type;
  v_名称 收费项目目录.名称%Type;
  --临时变量
  n_总数量   Number;
  n_当前数量 Number;
  n_总金额   Number;
  n_当前单价 Number;
  --药品收发记录
  n_批次       药品收发记录.批次%Type;
  n_序号       药品收发记录.序号%Type;
  n_扣率       药品收发记录.扣率%Type;
  n_领药部门id 药品收发记录.对方部门id%Type;
  n_供药单位id 药品收发记录.供药单位id%Type;
  v_商品条码   药品收发记录.商品条码%Type;
  v_内部条码   药品收发记录.内部条码%Type;
  d_效期       药品收发记录.效期%Type;
  d_灭菌效期   药品收发记录.灭菌效期%Type;
  d_灭菌日期   药品收发记录.灭菌日期%Type;
  d_生产日期   药品收发记录.生产日期%Type;
  n_虚拟库房id 药品收发记录.库房id%Type;
  v_产地       药品收发记录.产地%Type;
  v_批号       药品收发记录.批号%Type;
  v_批准文号   药品收发记录.批准文号%Type;
  v_其他出库no 药品收发记录.No%Type;
  n_Aval       药品库存.可用数量%Type;
  v_部门名称   部门表.名称%Type;
  ------------------------------------------------------------
  v_用法       药品收发记录.用法%Type;
  v_煎法       药品收发记录.外观%Type;
  n_单价小数   Number;
  n_医疗小组id 住院费用记录.医疗小组id%Type;
  n_库房id     药品库存.库房id%Type;
  n_修正库房id 药品库存.库房id%Type;
  n_Outmode    Number(1);
  v_Dec        Number;
  v_Count      Number;
  v_Err_Msg    Varchar2(255);
  Err_Item Exception;
  n_出库序号       药品收发记录.序号%Type;
  n_审核标志       病案主页.审核标志%Type;
  n_住院状态       病案主页.状态%Type;
  n_病人审核方式   Number(2);
  n_未入科禁止记账 Number(2);
  n_流通金额小数   Number;
  n_标准单价       药品收发记录.零售价%Type;
  v_操作员编号     人员表.编号%Type;
  v_操作员姓名     人员表.姓名%Type;
Begin
  If 操作员编号_In Is Null Then
    v_操作员编号 := 操作员编号_In;
    v_操作员姓名 := 操作员姓名_In;
  Else
    Select Max(b.编号), Max(b.姓名)
    Into v_操作员编号, v_操作员姓名
    From 上机人员表 A, 人员表 B
    Where a.用户名 = User And a.人员id = b.Id;
  End If;
  --根据执行库房取虚拟库房ID
  Begin
    Select 虚拟库房id Into n_虚拟库房id From 虚拟库房对照 Where 科室id = 执行部门id_In And Rownum <= 1;
  Exception
    When Others Then
      n_虚拟库房id := 0;
  End;
  If Nvl(批次_In, 0) <> 0 Then
    Select Nvl(Sum(可用数量), 0)
    Into n_Aval
    From 药品库存
    Where 药品id = 收费细目id_In And 批次 = 批次_In And 库房id = 执行部门id_In;
    If n_Aval <= 0 Then
      n_修正库房id := n_虚拟库房id;
    End If;
  End If;

  If Nvl(备货材料_In, 0) = 1 And 收费类别_In = '4' Then
    If Nvl(n_虚拟库房id, 0) = 0 Then
      Begin
        Select 名称 Into v_Err_Msg From 部门表 Where ID = 执行部门id_In;
      Exception
        When Others Then
          v_Err_Msg := '';
      End;
      v_Err_Msg := '执行部门"' || Nvl(v_Err_Msg, '') || '"未设置虚拟部门,请在卫材参数设置中设置.';
      Raise Err_Item;
    End If;
  End If;
  If Nvl(多病人单_In, 0) = 1 Or Nvl(序号_In, 0) = 1 Then
    --记帐表,全部检查,如果是记帐单只检查第一条,其他不检查
  
    n_病人审核方式   := Nvl(zl_GetSysParameter(185), 0);
    n_未入科禁止记账 := Nvl(zl_GetSysParameter(215), 0);
    If n_病人审核方式 = 1 Or n_未入科禁止记账 = 1 Then
      Begin
        Select 审核标志, 状态
        Into n_审核标志, n_住院状态
        From 病案主页
        Where 病人id = Nvl(病人id_In, 0) And 主页id = Nvl(主页id_In, 0);
      Exception
        When Others Then
          n_审核标志 := 0;
          n_住院状态 := 0;
      End;
      If n_未入科禁止记账 = 1 And n_住院状态 = 1 Then
        v_Err_Msg := '病人未入科,禁止对病人相关费用的操作!';
        Raise Err_Item;
      End If;
    
      If n_病人审核方式 = 1 Then
      
        If Nvl(n_审核标志, 0) = 1 Then
          v_Err_Msg := '该病人目前正在审核费用,不能进行费用相关调整!';
          Raise Err_Item;
        End If;
        If Nvl(n_审核标志, 0) = 2 Then
          v_Err_Msg := '该病人目前已经完成了费用审核,不能进行费用相关调整!';
          Raise Err_Item;
        End If;
      End If;
    End If;
  End If;
  ----------------------------------------------------------------------------------------------------------------------------------------------------------------------
  --领药部门确认规则:
  -- 1.传入 :领药部门ID_IN,直接以传入的为准
  -- 2.领药部门ID_IN=NULL的情况, 如果”开单科室=病人科室”，则填为”病人病区”，如果”开单科室<>病人科室”，则填写为”开单科室”
  If Nvl(领药部门id_In, 0) = 0 Then
    If Nvl(科室id_In, 0) = Nvl(开单部门id_In, 0) Then
      --如果”开单科室=病人科室”，则填为”病人病区”(如果没有入科,即病匹为空这种情况,则以病人科室为准,由于一般这种情况较少(护土开单),因此,这种情况应该不会存在)
      n_领药部门id := Nvl(病区id_In, 0);
      If Nvl(n_领药部门id, 0) = 0 Then
        n_领药部门id := 科室id_In;
      End If;
    Else
      --如果”开单科室<>病人科室”，则填写为”开单科室”
      n_领药部门id := 开单部门id_In;
    End If;
  Else
    n_领药部门id := 领药部门id_In;
  End If;
  --需要检查0这种情况,回为有关联
  If Nvl(n_领药部门id, 0) = 0 Then
    n_领药部门id := Null;
  End If;
  ----------------------------------------------------------------------------------------------------------------------------------------------------------------------

  --药品用法煎法分解
  If 用法_In Is Not Null Then
    If Instr(用法_In, '|') > 0 Then
      v_用法 := Substr(用法_In, 1, Instr(用法_In, '|') - 1);
      v_煎法 := Substr(用法_In, Instr(用法_In, '|') + 1);
    Else
      v_用法 := 用法_In;
    End If;
  End If;

  --金额小数位数
  Select Zl_To_Number(Nvl(zl_GetSysParameter(9), '2')), Zl_To_Number(Nvl(zl_GetSysParameter(157), '5'))
  Into v_Dec, n_单价小数
  From Dual;

  --取流通业务精度位数
  --类别:1-药品 2-卫材
  --内容：2-零售价 4-金额
  --单位：药品:1-售价 5-金额单位
  If 收费类别_In = '4' Then
    Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 2 And 内容 = 4 And 单位 = 5;
  Else
    Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5;
  End If;

  If 收费类别_In In ('4', '5', '6', '7') Then
    --取定价药品/卫材价格
    Select c.现价
    Into n_标准单价
    From 收费价目 C
    Where c.收费细目id = 收费细目id_In And Sysdate Between c.执行日期 And c.终止日期;
  End If;

  --住院费用记录
  Select 病人费用记录_Id.Nextval Into v_费用id From Dual;
  n_医疗小组id := 医疗小组id_In;

  If Nvl(医疗小组id_In, 0) < 0 Then
    n_医疗小组id := Zl_医疗小组_Get(开单部门id_In, 开单人_In, 病人id_In, 主页id_In, 发生时间_In);
  End If;

  Insert Into 住院费用记录
    (ID, 记录性质, NO, 记录状态, 序号, 从属父号, 价格父号, 多病人单, 门诊标志, 病人id, 主页id, 标识号, 姓名, 性别, 年龄, 床号, 病人病区id, 病人科室id, 费别, 收费类别, 收费细目id,
     计算单位, 保险项目否, 保险大类id, 保险编码, 费用类型, 发药窗口, 付数, 数次, 加班标志, 附加标志, 婴儿费, 收入项目id, 收据费目, 标准单价, 应收金额, 实收金额, 统筹金额, 记帐费用, 开单部门id,
     开单人, 发生时间, 登记时间, 执行部门id, 执行状态, 划价人, 操作员编号, 操作员姓名, 记帐单id, 摘要, 是否急诊, 医嘱序号, 结论, 医疗小组id)
  Values
    (v_费用id, 2, No_In, Decode(划价_In, 1, 0, 1), 序号_In, Decode(从属父号_In, 0, Null, 从属父号_In),
     Decode(价格父号_In, 0, Null, 价格父号_In), 多病人单_In, 2, 病人id_In, 主页id_In, Decode(标识号_In, 0, Null, 标识号_In), 姓名_In, 性别_In,
     年龄_In, 床号_In, Decode(病区id_In, 0, Null, 病区id_In), Decode(科室id_In, 0, Null, 科室id_In), 费别_In, 收费类别_In, 收费细目id_In,
     计算单位_In, 保险项目否_In, 保险大类id_In, 保险编码_In, 费用类型_In, Decode(Nvl(简单记帐_In, 0), 0, Null, 收费类别_In), 付数_In, 数次_In, 加班标志_In,
     附加标志_In, 婴儿费_In, 收入项目id_In, 收据费目_In, 标准单价_In, 应收金额_In, 实收金额_In, 统筹金额_In, 1, 开单部门id_In, 开单人_In, 发生时间_In, 登记时间_In,
     执行部门id_In, 0, Decode(划价_In, 1, v_操作员姓名, Decode(医技补临床费用_In, 1, '补临床费', Null)), Decode(划价_In, 1, Null, v_操作员编号),
     Decode(划价_In, 1, Null, v_操作员姓名), 记帐单id_In, 费用摘要_In, 是否急诊_In, 医嘱序号_In, 中药形态_In, n_医疗小组id);

  Select Max(使用限量 - Nvl(已用数量, 0))
  Into n_当前数量
  From 病人审批项目
  Where 病人id = 病人id_In And 主页id = 主页id_In And 项目id = 收费细目id_In And Nvl(使用限量, 0) <> 0;
  If 付数_In * 数次_In <= Nvl(n_当前数量, 0) Then
    Update 病人审批项目
    Set 已用数量 = Nvl(已用数量, 0) + 付数_In * 数次_In
    Where 病人id = 病人id_In And 主页id = 主页id_In And 项目id = 收费细目id_In And Nvl(使用限量, 0) <> 0;
  Elsif Not n_当前数量 Is Null Then
    v_Err_Msg := '第 ' || 序号_In || ' 行输入的数次超过了批准的可用数量' || n_当前数量 || '.'; --简化为不转换,直接以售价单位提示.
    Raise Err_Item;
  End If;

  --相关汇总表的处理
  If Nvl(划价_In, 0) = 0 Then
    --病人余额
    Update 病人余额 Set 费用余额 = Nvl(费用余额, 0) + 实收金额_In Where 病人id = 病人id_In And 类型 = 2 And 性质 = 1;
    If Sql%RowCount = 0 Then
      Insert Into 病人余额 (病人id, 类型, 性质, 费用余额, 预交余额) Values (病人id_In, 2, 1, 实收金额_In, 0);
    End If;
  
    --病人未结费用
    Update 病人未结费用
    Set 金额 = Nvl(金额, 0) + 实收金额_In
    Where 病人id = 病人id_In And Nvl(主页id, 0) = Nvl(主页id_In, 0) And Nvl(病人病区id, 0) = Nvl(病区id_In, 0) And
          Nvl(病人科室id, 0) = Nvl(科室id_In, 0) And Nvl(开单部门id, 0) = Nvl(开单部门id_In, 0) And
          Nvl(执行部门id, 0) = Nvl(执行部门id_In, 0) And 收入项目id + 0 = 收入项目id_In And 来源途径 + 0 = 2;
    If Sql%RowCount = 0 Then
      Insert Into 病人未结费用
        (病人id, 主页id, 病人病区id, 病人科室id, 开单部门id, 执行部门id, 收入项目id, 来源途径, 金额)
      Values
        (病人id_In, 主页id_In, 病区id_In, 科室id_In, 开单部门id_In, 执行部门id_In, 收入项目id_In, 2, 实收金额_In);
    End If;
  End If;

  --药品和卫生材料部分
  v_Count := 0; --@@@
  If 收费类别_In = '4' Then
    --跟踪在用的卫材才处理
    Select 跟踪在用 Into v_Count From 材料特性 Where 材料id = 收费细目id_In;
  End If;
  If 收费类别_In In ('5', '6', '7') Or (收费类别_In = '4' And Nvl(v_Count, 0) = 1) Then
    If 收费类别_In = '4' Then
      Select Nvl(a.在用分批, 0), Nvl(b.是否变价, 0), b.名称
      Into n_分批, n_时价, v_名称
      From 材料特性 A, 收费项目目录 B
      Where a.材料id = b.Id And b.Id = 收费细目id_In;
    Else
      Select Nvl(a.药房分批, 0), Nvl(b.是否变价, 0), b.名称
      Into n_分批, n_时价, v_名称
      From 药品规格 A, 收费项目目录 B
      Where a.药品id = b.Id And b.Id = 收费细目id_In;
    End If;
  
    --药品分批出库方式
    Select Zl_To_Number(Nvl(zl_GetSysParameter(150), 0)) Into n_Outmode From Dual;
  
    n_总数量 := 付数_In * 数次_In;
    n_总金额 := 0;
    If Nvl(备货材料_In, 0) = 1 And 收费类别_In = '4' Then
      n_库房id := n_虚拟库房id;
      Open c_Stock(n_Outmode, n_虚拟库房id);
    Else
      If Nvl(n_修正库房id, 0) <> 0 Then
        n_库房id := n_修正库房id;
        Open c_Stock(n_Outmode, n_修正库房id);
      Else
        n_库房id := 执行部门id_In;
        Open c_Stock(n_Outmode, 执行部门id_In);
      End If;
    End If;
  
    While n_总数量 <> 0 Loop
      Fetch c_Stock
        Into r_Stock;
      If c_Stock%NotFound Then
        --第一次就没有库存,分批或时价都不允许(包含备货卫材)。
        --分批药品数量分解不完,也就是库存不足。
        If n_分批 = 1 Or n_时价 = 1 Or (Nvl(备货材料_In, 0) = 1 And 收费类别_In = '4') Then
          Close c_Stock;
          If 医嘱序号_In Is Null Then
            If 收费类别_In = '4' Then
              If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                v_Err_Msg := '第 ' || 序号_In || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
              Else
                v_Err_Msg := '第 ' || 序号_In || ' 行的分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                               When Nvl(备货材料_In, 0) = 0 Then
                                '！'
                               Else
                                ',不能进行备货记帐！'
                             End;
              End If;
            Else
              v_Err_Msg := '第 ' || 序号_In || ' 行的分批或时价药品"' || v_名称 || '"没有足够的药品库存！';
            End If;
          Else
            If 收费类别_In = '4' Then
              If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
              Else
                v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                               When Nvl(备货材料_In, 0) = 0 Then
                                '！'
                               Else
                                ',不能进行备货记帐！'
                             End;
              End If;
            Else
              v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现分批或时价药品"' || v_名称 || '"没有足够的药品库存！';
            End If;
          End If;
          Raise Err_Item;
        End If;
      Elsif (n_分批 = 1 And Nvl(r_Stock.批次, 0) = 0) Or (n_分批 = 0 And Nvl(r_Stock.批次, 0) <> 0) Then
        Close c_Stock;
        If 医嘱序号_In Is Null Then
          If 收费类别_In = '4' Then
            v_Err_Msg := '第 ' || 序号_In || ' 行卫生材料"' || v_名称 || '"的分批属性与库存记录不相符,请检查材料数据的正确性！';
          Else
            v_Err_Msg := '第 ' || 序号_In || ' 行药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
          End If;
        Else
          If 收费类别_In = '4' Then
            v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现卫生材料"' || v_名称 || '"的分批属性与库存记录不相符,请检查材料数据的正确性！';
          Else
            v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
          End If;
        End If;
        Raise Err_Item;
      End If;
    
      If c_Stock%Found Then
        If Nvl(r_Stock.实际数量, 0) = 0 And (n_总数量 > 0 Or n_时价 = 1) Then
          --实际数量为零时，不正常，不允许出库
          --实际数量不为零，金额为零，可能是正常的零价格管理。
          --负数的情况相当于入库,这种情况应是允许的；但时价需要计算价格，必须要有实际数量。
          Close c_Stock;
          If 医嘱序号_In Is Null Then
            If 收费类别_In = '4' Then
              v_Err_Msg := '第 ' || 序号_In || ' 行的卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
            Else
              v_Err_Msg := '第 ' || 序号_In || ' 行药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
            End If;
          Else
            If 收费类别_In = '4' Then
              v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
            Else
              v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
            End If;
          End If;
          Raise Err_Item;
        End If;
      End If;
    
      --确定本次分解数量
      If n_分批 = 1 Or n_时价 = 1 Then
        --对于不分批的时价只可能分解一次,分解不完上面判断了.它分解是为了计算单价.
        --每次分解取小者,库存不够分解不完在上面判断.
        If n_总数量 <= Nvl(r_Stock.可用数量, 0) Then
          n_当前数量 := n_总数量;
        Else
          n_当前数量 := Nvl(r_Stock.可用数量, 0);
        End If;
        If n_时价 = 1 Then
          n_当前单价 := Nvl(r_Stock.零售价, Nvl(r_Stock.实际金额 / r_Stock.实际数量, 0));
        Elsif n_分批 = 1 Then
          n_当前单价 := n_标准单价;
        End If;
      Else
        --普通药品
        --不管够不够,程序中已根据参数判断
        If Nvl(备货材料_In, 0) = 1 And 收费类别_In = '4' Then
          If n_总数量 > Nvl(r_Stock.可用数量, 0) Then
            --不分批, 但又是备货卫材方式出库的,则需要检查当前库存是否充足.
            v_Err_Msg := '第 ' || 序号_In || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
            Raise Err_Item;
          End If;
        End If;
        n_当前数量 := n_总数量;
        n_当前单价 := n_标准单价;
      End If;
    
      --药品库存(普通情况可能没有记录)
      If c_Stock%Found Then
        Update 药品库存
        Set 可用数量 = Nvl(可用数量, 0) - n_当前数量
        Where 库房id = n_库房id And 药品id = 收费细目id_In And Nvl(批次, 0) = Nvl(r_Stock.批次, 0) And 性质 = 1
        Returning 可用数量 Into v_Count;
        If n_分批 = 1 Or n_时价 = 1 Then
          If v_Count < 0 Then
            If 收费类别_In = '4' Then
              v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现卫生材料"' || v_名称 || '"当前库存实际数量不足，当前不能出库，可能是由于网络并发操作引起，请刷新后再试！。';
            Else
              v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现药品"' || v_名称 || '"当前库存实际数量不足，当前不能出库，可能是由于网络并发操作引起，请刷新后再试！。';
            End If;
            Raise Err_Item;
          End If;
        End If;
      Elsif n_库房id Is Not Null Then
        --只有不分批非时价药品可能库存不足出库
        Update 药品库存
        Set 可用数量 = Nvl(可用数量, 0) - n_当前数量
        Where 库房id = n_库房id And 药品id = 收费细目id_In And Nvl(批次, 0) = 0 And 性质 = 1;
        If Sql%RowCount = 0 Then
          Insert Into 药品库存
            (库房id, 药品id, 性质, 可用数量, 商品条码, 内部条码)
          Values
            (n_库房id, 收费细目id_In, 1, -1 * n_当前数量, r_Stock.商品条码, r_Stock.内部条码);
        End If;
      End If;
    
      If Nvl(备货材料_In, 0) = 1 And 收费类别_In = '4' Then
        Update 药品库存
        Set 可用数量 = Nvl(可用数量, 0) - n_当前数量
        Where 库房id = 执行部门id_In And 药品id = 收费细目id_In And Nvl(批次, 0) = Nvl(r_Stock.批次, 0) And 性质 = 1;
      
        If Sql%RowCount = 0 Then
          Insert Into 药品库存
            (库房id, 药品id, 批次, 性质, 可用数量, 商品条码, 内部条码, 效期, 上次批号, 上次产地, 上次供应商id, 上次生产日期, 批准文号)
          Values
            (执行部门id_In, 收费细目id_In, Nvl(r_Stock.批次, 0), 1, -1 * n_当前数量, r_Stock.商品条码, r_Stock.内部条码, r_Stock.效期,
             r_Stock.上次批号, r_Stock.上次产地, r_Stock.上次供应商id, r_Stock.上次生产日期, r_Stock.批准文号);
        End If;
      End If;
    
      --药品收发记录
      n_批次       := Null;
      v_批号       := Null;
      d_效期       := Null;
      v_产地       := Null;
      d_灭菌效期   := Null;
      d_灭菌日期   := Null;
      n_供药单位id := Null;
      d_生产日期   := Null;
      v_批准文号   := Null;
      If c_Stock%Found Then
        n_批次       := r_Stock.批次;
        v_批号       := r_Stock.上次批号;
        d_效期       := r_Stock.效期;
        v_产地       := r_Stock.上次产地;
        n_供药单位id := r_Stock.上次供应商id;
        d_生产日期   := r_Stock.上次生产日期;
        v_批准文号   := r_Stock.批准文号;
        v_商品条码   := r_Stock.商品条码;
        v_内部条码   := r_Stock.内部条码;
      
        --卫材灭菌效期:一次性材料且有效期
        If 收费类别_In = '4' Then
          v_Count := 0;
          Begin
            Select 灭菌效期 Into v_Count From 材料特性 Where Nvl(一次性材料, 0) = 1 And 材料id = 收费细目id_In;
          Exception
            When Others Then
              Null;
          End;
          If Nvl(v_Count, 0) > 0 Then
            d_灭菌效期 := r_Stock.灭菌效期;
            d_灭菌日期 := d_灭菌效期 - v_Count * 30;
          End If;
        End If;
      End If;
    
      Select Nvl(Max(序号), 0) + 1
      Into n_序号
      From 药品收发记录
      Where NO = No_In And 记录状态 = 1 And 单据 = Decode(多病人单_In, 1, 10, 9) + Decode(收费类别_In, '4', 16, 0);
    
      n_扣率 := Null;
      If 期效_In Is Not Null Or 计价特性_In Is Not Null Then
        n_扣率 := Nvl(期效_In, 0) || Nvl(计价特性_In, 0);
      End If;
    
      --分批药品,如果是只使用了一个批次,则要填写付数
      If n_分批 = 1 And n_当前数量 <> 付数_In * 数次_In Then
        v_Count := 1;
      Else
        v_Count := 0;
      End If;
    
      --修改的原单据号存放在摘要中
      Insert Into 药品收发记录
        (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
         填制日期, 费用id, 频次, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
      Values
        (药品收发记录_Id.Nextval, 1, Decode(多病人单_In, 1, 10, 9) + Decode(收费类别_In, '4', 16, 0), No_In, n_序号, 执行部门id_In,
         n_领药部门id, 类别id_In, -1, 收费细目id_In, n_批次, v_产地, v_批号, d_效期, Decode(v_Count, 1, 1, 付数_In),
         Decode(v_Count, 1, n_当前数量, n_当前数量 / 付数_In), Decode(v_Count, 1, n_当前数量, n_当前数量 / 付数_In), n_当前单价,
         Round(n_当前单价 * n_当前数量, n_流通金额小数), 药品摘要_In, v_操作员姓名, 登记时间_In, v_费用id, 频次_In, 单量_In, v_用法, v_煎法, n_扣率, d_灭菌效期,
         d_灭菌日期, n_供药单位id, d_生产日期, v_批准文号, v_商品条码, v_内部条码);
    
      --产生其他出库单
      If 收费类别_In = '4' And (Nvl(备货材料_In, 0) = 1 Or Nvl(n_修正库房id, 0) <> 0) Then
        Begin
          Select Max(a.No), Max(a.序号)
          Into v_其他出库no, n_出库序号
          From 药品收发记录 A, 住院费用记录 B
          Where a.费用id = b.Id And a.单据 = 21 And b.No = No_In And b.记录性质 = 2;
        Exception
          When Others Then
            v_其他出库no := Null;
        End;
        If v_其他出库no Is Null Then
          v_其他出库no := Nextno(74, n_虚拟库房id, Null, 1);
        End If;
        If v_其他出库no Is Null Then
          v_Err_Msg := '在生成卫生材料的其他出库单时,获取相关的出库NO有误,请检查出库单的规则是否有误!';
          Raise Err_Item;
        End If;
        If Nvl(科室id_In, 0) <> 0 Then
          Select 名称 Into v_部门名称 From 部门表 Where ID = 科室id_In;
        End If;
        v_Err_Msg := LPad(' ', 4);
        v_Err_Msg := Substr('病人姓名:' || 姓名_In || v_Err_Msg || '性别:' || 性别_In || v_Err_Msg || '年龄' || 年龄_In || v_Err_Msg ||
                            '床号:' || 床号_In || v_Err_Msg || '住院号:' || Nvl(标识号_In, '') || v_Err_Msg || '病人科室:' || v_部门名称, 1,
                            100);
      
        n_出库序号 := Nvl(n_出库序号, 0) + 1;
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
           填制日期, 费用id, 频次, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
        Values
          (药品收发记录_Id.Nextval, 1, 21, v_其他出库no, n_出库序号, n_虚拟库房id, n_领药部门id, 类别id_In, -1, 收费细目id_In, n_批次, v_产地, v_批号,
           d_效期, Decode(v_Count, 1, 1, 付数_In), Decode(v_Count, 1, n_当前数量, n_当前数量 / 付数_In),
           Decode(v_Count, 1, n_当前数量, n_当前数量 / 付数_In), n_当前单价, Round(n_当前单价 * n_当前数量, n_流通金额小数), v_Err_Msg, v_操作员姓名,
           登记时间_In, v_费用id, 频次_In, 单量_In, v_用法, v_煎法, n_扣率, d_灭菌效期, d_灭菌日期, n_供药单位id, d_生产日期, v_批准文号, v_商品条码, v_内部条码);
      End If;
      v_Err_Msg := '';
      n_总数量  := n_总数量 - n_当前数量;
      n_总金额  := n_总金额 + n_当前数量 * n_当前单价;
    End Loop;
  
    --未发药品记录
    Update 未发药品记录
    Set 病人id = 病人id_In, 主页id = 主页id_In, 姓名 = 姓名_In
    Where 单据 = Decode(多病人单_In, 1, 10, 9) + Decode(收费类别_In, '4', 16, 0) And NO = No_In And
          Nvl(库房id, 0) = Nvl(执行部门id_In, 0);
    If Sql%RowCount = 0 Then
      --取身份优先级
      Begin
        Select b.优先级 Into v_优先级 From 病人信息 A, 身份 B Where a.身份 = b.名称(+) And a.病人id = 病人id_In;
      Exception
        When Others Then
          Null;
      End;
      Insert Into 未发药品记录
        (单据, NO, 病人id, 主页id, 姓名, 优先级, 对方部门id, 库房id, 填制日期, 已收费, 打印状态)
      Values
        (Decode(多病人单_In, 1, 10, 9) + Decode(收费类别_In, '4', 16, 0), No_In, 病人id_In, 主页id_In, 姓名_In, v_优先级, n_领药部门id,
         执行部门id_In, 登记时间_In, Decode(划价_In, 1, 0, 1), 0);
    End If;
    Zl_Prescription_Type_Zy_Update(No_In, 2, 收费细目id_In, 收费类别_In);
    --可能时价药品的库存金额和数量变化了
    If n_时价 = 1 Then
      --只有一个批次时,直接取该批次的单价
      If n_当前数量 <> 付数_In * 数次_In Then
        n_当前单价 := n_总金额 / (付数_In * 数次_In);
      End If;
      If Round(n_当前单价, n_单价小数) <> 标准单价_In Then
        Close c_Stock;
        If 医嘱序号_In Is Null Then
          If 收费类别_In = '4' Then
            v_Err_Msg := '第 ' || 序号_In || ' 行的时价卫生材料"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          Else
            v_Err_Msg := '第 ' || 序号_In || ' 行的时价药品"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          End If;
        Else
          --医嘱摆药时是按病人分次计算并提交数据库,因此不同病人使用相同实价药品没有问题。
          --但同一病人同时使用两笔以上相同实价药品则会有问题。
          If 收费类别_In = '4' Then
            v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现时价卫生材料"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                         '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          Else
            v_Err_Msg := '在处理病人"' || 姓名_In || '"时发现时价药品"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                         '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          End If;
        End If;
        Raise Err_Item;
      End If;
    End If;
    Close c_Stock;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_住院记帐记录_Insert;
/

--93390:张德婷,2017-08-02,诊疗项目新增复制增加功能
CREATE OR REPLACE Procedure Zl_诊疗收费_Insert
( 
  诊疗项目id_In In 诊疗收费关系.诊疗项目id%Type, 
  当前项目id_In In 诊疗收费关系.诊疗项目id%Type
) Is 
 
Begin 
  Insert Into 诊疗收费关系
    Select 诊疗项目id_In, 收费项目id, 收费数量, 固有对照, 从属项目, 费用性质, 检查部位, 检查方法, 收费方式, 适用科室id, 病人来源
    From 诊疗收费关系
    Where 诊疗项目id = 当前项目id_In;  
Exception 
  When Others Then 
    Zl_Errorcenter(Sqlcode, Sqlerrm); 
End Zl_诊疗收费_Insert;
/

--93390:张德婷,2017-08-02,诊疗项目新增复制增加功能
CREATE OR REPLACE Procedure Zl_诊疗项目_Insert
(
  类别_In             In 诊疗项目目录.类别%Type := Null,
  分类id_In           In 诊疗项目目录.分类id%Type := Null,
  Id_In               In 诊疗项目目录.Id%Type,
  编码_In             In 诊疗项目目录.编码%Type := Null,
  名称_In             In 诊疗项目目录.名称%Type := Null,
  名称拼音_In         In 诊疗项目别名.简码%Type := Null,
  名称五笔_In         In 诊疗项目别名.简码%Type := Null,
  别名_In             诊疗项目目录.名称%Type := Null,
  别名拼音_In         诊疗项目别名.简码%Type := Null,
  别名五笔_In         诊疗项目别名.简码%Type := Null,
  操作类型_In         In 诊疗项目目录.操作类型%Type := Null,
  执行频率_In         In 诊疗项目目录.执行频率%Type := Null,
  单独应用_In         In 诊疗项目目录.单独应用%Type := Null,
  计算方式_In         In 诊疗项目目录.计算方式%Type := Null,
  计算单位_In         In 诊疗项目目录.计算单位%Type := Null,
  适用性别_In         In 诊疗项目目录.适用性别%Type := Null,
  执行安排_In         In 诊疗项目目录.执行安排%Type := Null,
  服务对象_In         In 诊疗项目目录.服务对象%Type := Null,
  组合项目_In         In 诊疗项目目录.组合项目%Type := Null,
  标本部位_In         In 诊疗项目目录.标本部位%Type := Null,
  手术操作id_In       In 疾病诊断对照.疾病id%Type := Null,
  执行科室_In         In 诊疗项目目录.执行科室%Type := Null,
  门诊执行_In         In 诊疗执行科室.执行科室id%Type := Null,
  住院执行_In         In 诊疗执行科室.执行科室id%Type := Null,
  定向执行_In         In Varchar2, --开单科室定向执行的说明串，以'|'分割，每个定向按'开单科室id^执行科室id'形式组织
  参考目录id_In       In 诊疗项目目录.参考目录id%Type := Null,
  应用范围_In         In Number := 0,
  录入限量_In         In 诊疗项目目录.录入限量%Type := Null,
  限量范围_In         In Number := 0,
  执行标记_In         In Number := 0,
  执行分类_In         In 诊疗项目目录.执行分类%Type := 0,
  站点_In             In 诊疗项目目录.站点%Type := Null,
  项目频率_In         In Varchar2 := Null, --该项目的频率设置串：编码|编码......
  计算规则_In         In 诊疗项目目录.计算规则%Type := Null,
  使用科室_In         In Varchar2 := Null, --使用科室的IDs,用逗号分隔
  使用科室应用范围_In In Number := 0, --使用科室应用的范围  0-本项，1-应用于同级，2-分类下所有，3-应用于当前类别
  First_In            In Number := 1, --First：1-需要删除执行科室，再新增，0-不删除执行科室，直接新增
  计算系数_In         In 诊疗项目目录.计算系数%Type := Null,
  输血检验对照_In     In Varchar2 :=null,
  诊疗频率编码_In     In varchar2:=null,
  原始id_IN           In 诊疗项目目录.Id%Type:=0
) Is
  Type t_诊疗项目 Is Ref Cursor;
  c_诊疗项目   t_诊疗项目;
  t_Id         t_Numlist;
  v_Id         诊疗项目目录.Id%Type;
  v_Records    Varchar2(4000); --临时记录开单科室定向执行科室的字符串
  v_Currrec    Varchar2(1000); --包含在定向执行科室字符串中的一个定向
  v_Fields     Varchar2(1000);
  v_开单科室id 诊疗执行科室.开单科室id%Type := Null;
  v_执行科室id 诊疗执行科室.执行科室id%Type := Null;
  n_序号       Number;
  v_编号       Varchar2(1000);
  v_Strtmp     Varchar2(1000);
  v_Strinput   Varchar2(1000);
Begin
  If First_In = 1 Then
    Insert Into 诊疗项目目录
      (类别, 分类id, ID, 编码, 名称, 操作类型, 执行频率, 单独应用, 计算方式, 计算单位, 适用性别, 执行安排, 服务对象, 执行科室, 组合项目, 标本部位, 建档时间, 撤档时间, 参考目录id, 录入限量,
       执行标记, 执行分类, 计算规则, 站点, 计算系数,诊疗频率编码)
    Values
      (类别_In, 分类id_In, Id_In, 编码_In, 名称_In, 操作类型_In, 执行频率_In, 单独应用_In, 计算方式_In, 计算单位_In, 适用性别_In, 执行安排_In, 服务对象_In,
       执行科室_In, 组合项目_In, Decode(类别_In, 'D', Decode(组合项目_In, 1, '', 标本部位_In), 标本部位_In), Sysdate,
       To_Date('3000-01-01', 'YYYY-MM-DD'), 参考目录id_In, 录入限量_In, 执行标记_In, 执行分类_In, 计算规则_In, 站点_In, 计算系数_In,诊疗频率编码_In);
    If 手术操作id_In Is Not Null Then
      Insert Into 疾病诊断对照 (疾病id, 诊断id, 手术id) Values (手术操作id_In, Null, Id_In);
    End If;
    If 名称拼音_In Is Not Null Then
      Insert Into 诊疗项目别名 (诊疗项目id, 名称, 性质, 简码, 码类) Values (Id_In, 名称_In, 1, 名称拼音_In, 1);
    End If;
    If 名称五笔_In Is Not Null Then
      Insert Into 诊疗项目别名 (诊疗项目id, 名称, 性质, 简码, 码类) Values (Id_In, 名称_In, 1, 名称五笔_In, 2);
    End If;
    If 别名_In Is Not Null And 别名拼音_In Is Not Null Then
      Insert Into 诊疗项目别名 (诊疗项目id, 名称, 性质, 简码, 码类) Values (Id_In, 别名_In, 9, 别名拼音_In, 1);
    End If;
    If 别名_In Is Not Null And 别名五笔_In Is Not Null Then
      Insert Into 诊疗项目别名 (诊疗项目id, 名称, 性质, 简码, 码类) Values (Id_In, 别名_In, 9, 别名五笔_In, 2);
    End If;
  End If;
  If 应用范围_In = 1 Then
    If 分类id_In Is Null Then
      Open c_诊疗项目 For
        Select ID From 诊疗项目目录 Where 分类id Is Null Order By 编码;
    Else
      Open c_诊疗项目 For
        Select ID From 诊疗项目目录 Where 分类id = 分类id_In Order By 编码;
    End If;
  Elsif 应用范围_In = 2 Then
    If 分类id_In Is Null Then
      Open c_诊疗项目 For
        Select c.Id
        From 诊疗项目目录 C, (Select ID From 诊疗分类目录 Start With 上级id Is Null Connect By Prior ID = 上级id) D
        Where d.Id = c.分类id
        Order By 编码;
    Else
      Open c_诊疗项目 For
        Select c.Id
        From 诊疗项目目录 C, (Select ID From 诊疗分类目录 Start With ID = 分类id_In Connect By Prior ID = 上级id) D
        Where d.Id = c.分类id
        Order By 编码;
    End If;
  Elsif 应用范围_In = 3 Then
    Open c_诊疗项目 For
      Select ID From 诊疗项目目录 Where 类别 = 类别_In Order By 编码;
  Else
    Open c_诊疗项目 For
      Select ID From 诊疗项目目录 Where ID = Id_In;
  End If;

  Loop
    Fetch c_诊疗项目
      Into v_Id;
    Exit When c_诊疗项目%NotFound;

    If First_In = 1 Then
      Delete From 诊疗执行科室 Where 诊疗项目id = v_Id;
      If 执行科室_In = 4 And 门诊执行_In Is Not Null Then
        Insert Into 诊疗执行科室 (诊疗项目id, 病人来源, 开单科室id, 执行科室id) Values (v_Id, 1, Null, 门诊执行_In);
      End If;
      If 执行科室_In = 4 And 住院执行_In Is Not Null Then
        Insert Into 诊疗执行科室 (诊疗项目id, 病人来源, 开单科室id, 执行科室id) Values (v_Id, 2, Null, 住院执行_In);
      End If;
    End If;
    If 执行科室_In <> 4 Or 定向执行_In Is Null Then
      v_Records := Null;
    Else
      v_Records := 定向执行_In || '|';
    End If;

    While v_Records Is Not Null Loop
      v_Currrec    := Substr(v_Records, 1, Instr(v_Records, '|') - 1);
      v_Fields     := v_Currrec;
      v_开单科室id := To_Number(Substr(v_Fields, 1, Instr(v_Fields, '^') - 1));
      v_Fields     := Substr(v_Fields, Instr(v_Fields, '^') + 1);
      v_执行科室id := To_Number(v_Fields);
      Insert Into 诊疗执行科室
        (诊疗项目id, 病人来源, 开单科室id, 执行科室id)
      Values
        (v_Id, Null, Decode(v_开单科室id, 0, Null, v_开单科室id), v_执行科室id);
      v_Records := Replace('|' || v_Records, '|' || v_Currrec || '|');
    End Loop;
    If 应用范围_In <> 0 Then
      Update 诊疗项目目录 Set 执行科室 = 执行科室_In Where ID = v_Id;
    End If;
  End Loop;
  Close c_诊疗项目;

  If First_In = 1 Then
    If 类别_In = 'C' Or 类别_In = 'F' Or 类别_In = 'K' Then
      Insert Into 病历单据应用
        (病历文件id, 应用场合, 诊疗项目id)
        Select a.病历文件id, 1, Id_In
        From 病历单据应用 A, 诊疗项目目录 I
        Where a.诊疗项目id = i.Id And i.类别 = 类别_In And 应用场合 = 1 And (服务对象_In = 0 Or 服务对象_In = 1) And Rownum < 2;
      Insert Into 病历单据应用
        (病历文件id, 应用场合, 诊疗项目id)
        Select a.病历文件id, 2, Id_In
        From 病历单据应用 A, 诊疗项目目录 I
        Where a.诊疗项目id = i.Id And i.类别 = 类别_In And 应用场合 = 2 And (服务对象_In = 0 Or 服务对象_In = 2) And Rownum < 2;
    Elsif 类别_In = 'D' Or 类别_In = 'E' Then
      Insert Into 病历单据应用
        (病历文件id, 应用场合, 诊疗项目id)
        Select a.病历文件id, 1, Id_In
        From 病历单据应用 A, 诊疗项目目录 I
        Where a.诊疗项目id = i.Id And i.类别 = 类别_In And 操作类型 = 操作类型_In And 应用场合 = 1 And (服务对象_In = 0 Or 服务对象_In = 1) And
              Rownum < 2;
      Insert Into 病历单据应用
        (病历文件id, 应用场合, 诊疗项目id)
        Select a.病历文件id, 2, Id_In
        From 病历单据应用 A, 诊疗项目目录 I
        Where a.诊疗项目id = i.Id And i.类别 = 类别_In And 操作类型 = 操作类型_In And 应用场合 = 2 And (服务对象_In = 0 Or 服务对象_In = 2) And
              Rownum < 2;
    End If;
  End If;

  If 限量范围_In = 1 Then
    If 分类id_In Is Null Then
      Update 诊疗项目目录 Set 录入限量 = 录入限量_In Where 分类id Is Null;
    Else
      Update 诊疗项目目录 Set 录入限量 = 录入限量_In Where 分类id = 分类id_In;
    End If;
  Elsif 限量范围_In = 2 Then
    If 分类id_In Is Null Then
      Update 诊疗项目目录
      Set 录入限量 = 录入限量_In
      Where 分类id In (Select ID From 诊疗分类目录 Start With 上级id Is Null Connect By Prior ID = 上级id);
    Else
      Update 诊疗项目目录
      Set 录入限量 = 录入限量_In
      Where 分类id In (Select ID From 诊疗分类目录 Start With ID = 分类id_In Connect By Prior ID = 上级id);
    End If;
  Elsif 限量范围_In = 3 Then
    Update 诊疗项目目录 Set 录入限量 = 录入限量_In Where 类别 = 类别_In;
  Elsif 限量范围_In = 4 Then
    Update 诊疗项目目录 Set 录入限量 = 录入限量_In;
  End If;

  --该项目的频率设置
  If 类别_In <> 'C' Then
    Delete 诊疗用法用量 Where 项目id = Id_In;
    If 项目频率_In Is Not Null Then
      v_Strinput := 项目频率_In || '|';
      n_序号     := 0;

      While v_Strinput Is Not Null Loop
        v_Strtmp := Substr(v_Strinput, 1, Instr(v_Strinput, '|') - 1);
        v_编号   := v_Strtmp;
        n_序号   := n_序号 + 1;

        Insert Into 诊疗用法用量 (项目id, 性质, 频次) Values (Id_In, n_序号, v_编号);
        v_Strinput := Replace('|' || v_Strinput, '|' || v_Strtmp || '|');
      End Loop;
    End If;
  End If;
  --使用科室
  If 使用科室应用范围_In = 1 Then
    If 分类id_In Is Null Then
      Open c_诊疗项目 For
        Select ID From 诊疗项目目录 Where 分类id Is Null Order By 编码;
    Else
      Open c_诊疗项目 For
        Select ID From 诊疗项目目录 Where 分类id = 分类id_In Order By 编码;
    End If;
  Elsif 使用科室应用范围_In = 2 Then
    If 分类id_In Is Null Then
      Open c_诊疗项目 For
        Select c.Id
        From 诊疗项目目录 C, (Select ID From 诊疗分类目录 Start With 上级id Is Null Connect By Prior ID = 上级id) D
        Where d.Id = c.分类id
        Order By 编码;
    Else
      Open c_诊疗项目 For
        Select c.Id
        From 诊疗项目目录 C, (Select ID From 诊疗分类目录 Start With ID = 分类id_In Connect By Prior ID = 上级id) D
        Where d.Id = c.分类id
        Order By 编码;
    End If;
  Elsif 使用科室应用范围_In = 3 Then
    Open c_诊疗项目 For
      Select ID From 诊疗项目目录 Where 类别 = 类别_In Order By 编码;
  Else
    Open c_诊疗项目 For
      Select ID From 诊疗项目目录 Where ID = Id_In;
  End If;
  Fetch c_诊疗项目 Bulk Collect
    Into t_Id;
  Close c_诊疗项目;

  Forall I In 1 .. t_Id.Count
    Delete 诊疗适用科室 Where 项目id = t_Id(I) And Instr(',' || 使用科室_In || ',', ',' || 科室id || ',') = 0;

  If 使用科室_In Is Not Null Then
    Forall I In 1 .. t_Id.Count
      Insert Into 诊疗适用科室
        (项目id, 科室id)
        Select t_Id(I), Column_Value
        From Table(f_Num2list(使用科室_In)) A
        Where Not Exists (Select 1 From 诊疗适用科室 Where 科室id = Column_Value And 项目id = t_Id(I));
  End If;
  --输血检验对照
  If 类别_In = 'K' And 输血检验对照_In Is Not Null Then
    v_Strinput := 输血检验对照_In || '|';

    While v_Strinput Is Not Null Loop
      v_Strtmp := Substr(v_Strinput, 1, Instr(v_Strinput, '|') - 1);
      v_Id     := v_Strtmp;

      Insert Into 输血检验对照 (项目id, 检验项目id) Values (Id_In, v_Id);
      v_Strinput := Replace('|' || v_Strinput, '|' || v_Strtmp || '|');
    End Loop;
  End If;
  
  if 原始id_IN<>0 then
    Zl_诊疗收费_Insert(id_In,原始id_IN);
  end if;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_诊疗项目_Insert;
/

--112251:冉俊明,2017-08-03,修正转病区费用时，转出费用数量求取错误的问题
Create Or Replace Procedure Zl_Turntoward_Fee
(
  操作_In       Number,
  病人id_In     病案主页.病人id%Type,
  主页id_In     病案主页.病人id%Type,
  变动id_In     病人变动记录.Id%Type,
  原病区id_In   病案主页.当前病区id%Type,
  目标病区id_In 病案主页.当前病区id%Type,
  操作员编号_In 住院费用记录.操作员编号%Type,
  操作员姓名_In 住院费用记录.操作员姓名%Type,
  变动时间_In   住院费用记录.登记时间%Type := Null
) Is
  --功能:病人转病区费用的转入，转出处理
  --入参:操作_In: 0-病区变动,1-撤消病区变动
  --   1、操作_IN=0(病区变动)时
  --       变动ID_In: 原病区的变动记录的ID
  --       原病区ID_IN：原病区ID
  --       目标病区ID_IN:目标病区ID
  --   2、操作_IN=1(撤消病区变动)时
  --       变动ID_In: 恢复的原始病区的变动记录的ID
  --       原病区ID_IN：被撤消的病区ID
  --       目标病区ID_IN:恢复的原始病区ID
  --转入，转出规则:
  --1.病区执行的非药品和卫生材料，处理规则为
  --   1)将原记录进行销帐处理
  --   2)新增一条新病区的费用，病人科室，发生时间不变
  --2.病区执行的药品和卫生材料
  --   这个卫材退的处理在转病区时的界面中进行确认(可以打印核查清单)，在转病区发起的时候确认。
  --   a)卫材在原病区通过销帐申请来处理，新病区手工计卫材；
  --   b)撤消转病区时，自动撤消销帐申请，如果已经销帐审核了，则询问提示并且不作卫材费用处理，手工去处理。
  Err_Item Exception;
  v_Err_Msg Varchar2(200);

  v_No             住院费用记录.No%Type;
  n_Max序号        住院费用记录.序号%Type;
  n_Dec            Number;
  n_病人审核方式   Number(2);
  n_未入科禁止记账 Number(2);
  n_审核标志       病案主页.审核标志%Type;
  n_住院状态       病案主页.状态%Type;
  n_已检查         Number(2);
  v_责任护士       病人变动记录.责任护士%Type;
  d_登记时间       Date;
  n_Count          Number(18);

  n_未发料数量   病人费用销帐.数量%Type;
  n_申请未发数量 病人费用销帐.数量%Type;
  n_申请已发数量 病人费用销帐.数量%Type;
  n_申请销账数量 病人费用销帐.数量%Type;
  n_申请取消数量 病人费用销帐.数量%Type;

  v_原病区名称   部门表.名称%Type;
  v_目标病区名称 部门表.名称%Type;
  n_应收金额     住院费用记录.应收金额%Type;
  n_实收金额     住院费用记录.实收金额%Type;
  n_记录状态     费用变动记录.记录状态%Type;

  n_原变动id   病人变动记录.Id%Type;
  n_目标变动id 病人变动记录.Id%Type;
  n_变动id     病人变动记录.Id%Type;

  Procedure 销帐申请_Insert
  (
    费用id_In     病人费用销帐.费用id%Type,
    申请类别_In   病人费用销帐.申请类别%Type,
    收费细目id_In 病人费用销帐.收费细目id%Type,
    申请部门id_In 病人费用销帐.申请部门id%Type,
    审核部门id_In 病人费用销帐.审核部门id%Type,
    数量_In       病人费用销帐.数量%Type,
    申请人_In     病人费用销帐.申请人%Type,
    申请时间_In   病人费用销帐.申请时间%Type,
    状态_In       病人费用销帐.状态%Type,
    销帐原因_In   病人费用销帐.销帐原因%Type
  ) Is
  Begin
    --全部都执行了，肯定销帐数量为已执行的
    Insert Into 病人费用销帐
      (费用id, 申请类别, 收费细目id, 审核部门id, 申请部门id, 数量, 申请人, 申请时间, 状态, 销帐原因)
    Values
      (费用id_In, 申请类别_In, 收费细目id_In, 审核部门id_In, 申请部门id_In, 数量_In, 申请人_In, 申请时间_In, 状态_In, 销帐原因_In);
  End;
Begin
  If 目标病区id_In Is Null Then
    Return;
  End If;

  Begin
    Select ID
    Into n_变动id
    From 病人变动记录
    Where 病人id = 病人id_In And 主页id = 主页id_In And 开始原因 = 15 And 终止时间 Is Null;
  Exception
    When Others Then
      v_Err_Msg := '未找到病人的原始变动记录，禁止操作!';
      Raise Err_Item;
  End;

  If Nvl(操作_In, 0) = 0 Then
    n_原变动id   := 变动id_In;
    n_目标变动id := n_变动id;
    n_记录状态   := 1;
  Else
    n_原变动id   := n_变动id;
    n_目标变动id := 变动id_In;
    n_记录状态   := 2;
  End If;

  Select Max(责任护士), Count(1) Into v_责任护士, n_Count From 病人变动记录 Where ID = n_原变动id;
  If Nvl(n_Count, 0) = 0 Then
    v_Err_Msg := '未找到病人的原始变动记录，禁止操作！';
    Raise Err_Item;
  End If;

  Select Max(Decode(ID, 原病区id_In, 名称, Null)), Max(Decode(ID, 目标病区id_In, 名称, Null))
  Into v_原病区名称, v_目标病区名称
  From 部门表
  Where ID In (原病区id_In, 目标病区id_In);

  d_登记时间 := 变动时间_In;
  If d_登记时间 Is Null Then
    d_登记时间 := Sysdate;
  End If;

  n_病人审核方式   := Nvl(zl_GetSysParameter(185), 0);
  n_未入科禁止记账 := Nvl(zl_GetSysParameter(215), 0);
  --金额小数位数
  n_Dec := Zl_To_Number(Nvl(zl_GetSysParameter(9), '2'));

  n_审核标志 := 0;
  n_住院状态 := 0;
  n_已检查   := 0;

  If n_病人审核方式 = 1 Or n_未入科禁止记账 = 1 Then
    Select Nvl(审核标志, 0), Nvl(状态, 0)
    Into n_审核标志, n_住院状态
    From 病案主页
    Where 病人id = 病人id_In And 主页id = 主页id_In;
  End If;

  n_Max序号 := 0;
  v_No      := '-~';
  --当前病区的医嘱费用，不包含医嘱附费及药品
  For c_费用 In (Select a.No, a.序号, a.记录状态, a.收费细目id, Nvl(剩余数量, 0) - Nvl(已执行数, 0) As 准退数, a.卫材, a.医嘱id, b.发送号, a.费用id,
                      a. 执行状态, a. 标准单价, a. 应收金额, a. 实收金额, a.收费类别, b.费用id As 医嘱费用id
               From (Select a.No, a.序号, Max(a.收费细目id) As 收费细目id, Max(a.医嘱序号) As 医嘱id, Sum(数量) As 剩余数量,
                             Max(Decode(a.记录状态, 2, 0, a.费用id)) As 费用id, Max(a.记录状态) As 记录状态, Max(a.执行状态) As 执行状态,
                             Max(a.标准单价) As 标准单价, Sum(a.应收金额) As 应收金额, Sum(a.实收金额) As 实收金额, Max(a.收费类别) As 收费类别,
                             Nvl(Max(c.跟踪在用), 0) As 卫材
                      From (Select a.No, 记录状态, a.医嘱序号, Nvl(a.价格父号, 序号) As 序号, 收费细目id, a.执行状态, Avg(Nvl(a.付数, 1) * a.数次) As 数量,
                                    Sum(a.标准单价) As 标准单价, Sum(a.应收金额) As 应收金额, Sum(a.实收金额) As 实收金额, Max(a.收费类别) As 收费类别,
                                    Max(Decode(a.价格父号, Null, a.Id, 0)) As 费用id
                             From 住院费用记录 A, 病人医嘱发送 B
                             Where a.记录性质 = b.记录性质 And a.医嘱序号 = b.医嘱id And a.No = b.No And a.记录性质 = 2 And
                                   a.执行部门id = 原病区id_In And a.医嘱序号 Is Not Null And a.病人id = 病人id_In And a.主页id = 主页id_In And
                                   Instr(',5,6,7,', ',' || a.收费类别 || ',') = 0
                             Group By a.No, 记录状态, a.医嘱序号, Nvl(a.价格父号, 序号), 收费细目id, a.执行状态) A, 材料特性 C
                      Where a.收费细目id = c.材料id(+)
                      Group By a.No, a.序号) A,
                    (Select b.No, b.医嘱id, b.发送号, c.收费细目id, Nvl(c.费用id, 0) As 费用id,
                             Sum(Decode(Nvl(c.执行状态, 0), 1, c.数量, 0)) As 已执行数
                      From 病人医嘱记录 A, 病人医嘱发送 B, 医嘱执行计价 C, 材料特性 D
                      Where a.Id = b.医嘱id And b.发送号 = c.发送号(+) And b.医嘱id = c.医嘱id(+) And b.记录性质 = 2 And
                            Nvl(b.执行状态, 0) <> 1 And c.收费细目id = d.材料id(+) And Nvl(d.跟踪在用, 0) = 0 And a.病人id = 病人id_In And
                            a.主页id = 主页id_In
                      Group By b.No, b.医嘱id, b.发送号, c.收费细目id, Nvl(c.费用id, 0)
                      Union All
                      Select Null As NO, Null As 医嘱id, Null As 发送号, Null As 收费细目id, Null As 费用id, Null As 已执行数
                      From Dual) B
               Where (a.No = b.No And a.医嘱id = b.医嘱id And a.收费细目id = b.收费细目id And
                     (a.费用id = b.费用id Or Nvl(b.费用id, 0) = 0) And Nvl(剩余数量, 0) - Nvl(已执行数, 0) > 0) Or
                     (a.卫材 = 1 And b.No Is Null)
               Order By NO) Loop
  
    If v_No <> c_费用.No Then
      v_No := c_费用.No;
      Select Nvl(Max(序号), 0)
      Into n_Max序号
      From 住院费用记录
      Where NO = v_No And 记录性质 = 2 And 记录状态 In (0, 1, 3);
    End If;
  
    --填写 医嘱执行计价.费用ID
    If Nvl(c_费用.医嘱费用id, 0) = 0 And Nvl(c_费用.卫材, 0) = 0 Then
      For c_记录 In (Select ID, 收费细目id
                   From 住院费用记录
                   Where 记录性质 = 2 And 记录状态 <> 2 And NO = c_费用.No And 医嘱序号 = c_费用.医嘱id And 价格父号 Is Null) Loop
        Update 医嘱执行计价
        Set 费用id = c_记录.Id
        Where 医嘱id = c_费用.医嘱id And 发送号 = c_费用.发送号 And 收费细目id = c_记录.收费细目id And 费用id Is Null;
      End Loop;
    End If;
  
    --只有存在费用变动时才需要检查
    If Nvl(n_已检查, 0) = 0 Then
      --只检查一次
      If n_未入科禁止记账 = 1 And n_住院状态 = 1 Then
        v_Err_Msg := '病人未入科，禁止对病人相关费用的操作！';
        Raise Err_Item;
      End If;
      If n_病人审核方式 = 1 Then
        If Nvl(n_审核标志, 0) = 1 Then
          v_Err_Msg := '该病人目前正在审核费用，不能进行费用相关调整！';
          Raise Err_Item;
        End If;
        If Nvl(n_审核标志, 0) = 2 Then
          v_Err_Msg := '该病人目前已经完成了费用审核，不能进行费用相关调整！';
          Raise Err_Item;
        End If;
      End If;
      n_已检查 := 1;
    End If;
  
    --1.卫生材料在病区执行的，直接发出销帐申请
    If Nvl(c_费用.卫材, 0) = 1 Then
      If c_费用.记录状态 = 0 Then
        v_Err_Msg := '单据 ' || c_费用.No || ' 还未进行审核，禁止转病区操作！';
        Raise Err_Item;
      End If;
      If v_责任护士 Is Null Then
        v_Err_Msg := '原病区的责任护士不存在，不能进行卫材销帐申请！';
        Raise Err_Item;
      End If;
    
      Select Sum(b.实际数量)
      Into n_未发料数量
      From 药品收发记录 B
      Where 费用id = c_费用.费用id And Mod(b.记录状态, 3) = 1 And b.单据 In (24, 25, 26) And b.审核人 Is Null;
    
      Select Sum(Decode(申请类别, 0, 1, 0) * 数量), Sum(Decode(申请类别, 0, 0, 1) * 数量)
      Into n_申请未发数量, n_申请已发数量
      From 病人费用销帐
      Where 费用id = c_费用.费用id And Nvl(状态, 0) = 0;
    
      n_申请销账数量 := 0;
      If Nvl(n_未发料数量, 0) = Nvl(c_费用.准退数, 0) Then
        --都未执行
        n_申请未发数量 := Nvl(c_费用.准退数, 0) - Nvl(n_申请未发数量, 0);
        If n_申请未发数量 > 0 Then
          n_申请销账数量 := n_申请销账数量 + n_申请未发数量;
          销帐申请_Insert(c_费用.费用id, 0, c_费用.收费细目id, 原病区id_In, 原病区id_In, n_申请未发数量, v_责任护士, d_登记时间, 0,
                      '从' || v_原病区名称 || '转到' || v_目标病区名称);
        End If;
      Elsif Nvl(n_未发料数量, 0) = 0 Then
        --全部都执行了，肯定销帐数量为已执行的
        n_申请已发数量 := Nvl(c_费用.准退数, 0) - Nvl(n_申请已发数量, 0);
        If n_申请已发数量 > 0 Then
          n_申请销账数量 := n_申请销账数量 + n_申请已发数量;
          销帐申请_Insert(c_费用.费用id, 1, c_费用.收费细目id, 原病区id_In, 原病区id_In, n_申请已发数量, v_责任护士, d_登记时间, 0,
                      '从' || v_原病区名称 || '转到' || v_目标病区名称);
        End If;
      Else
        --可能有部分对执行的进行销帐，一部分对未执行的销帐
        n_申请未发数量 := Nvl(n_未发料数量, 0) - Nvl(n_申请未发数量, 0);
        If n_申请未发数量 > 0 Then
          n_申请销账数量 := n_申请销账数量 + n_申请未发数量;
          销帐申请_Insert(c_费用.费用id, 0, c_费用.收费细目id, 原病区id_In, 原病区id_In, n_申请未发数量, v_责任护士, d_登记时间, 0,
                      '从' || v_原病区名称 || '转到' || v_目标病区名称);
        End If;
        --已执行部分
        n_申请已发数量 := Nvl(c_费用.准退数, 0) - Nvl(n_未发料数量, 0) - Nvl(n_申请已发数量, 0);
        If n_申请已发数量 > 0 Then
          n_申请销账数量 := n_申请销账数量 + n_申请已发数量;
          销帐申请_Insert(c_费用.费用id, 1, c_费用.收费细目id, 原病区id_In, 原病区id_In, n_申请已发数量, v_责任护士, d_登记时间, 0,
                      '从' || v_原病区名称 || '转到' || v_目标病区名称);
        End If;
      End If;
    
      --增加变动记录
      If Nvl(n_申请销账数量, 0) > 0 Then
        n_应收金额 := Round(n_申请销账数量 * Nvl(c_费用.标准单价, 0), n_Dec);
        n_实收金额 := 0;
        If Nvl(c_费用.应收金额, 0) <> 0 Then
          n_实收金额 := Round(Nvl(n_应收金额, 0) * Nvl(c_费用.实收金额, 0) / c_费用.应收金额, n_Dec);
        End If;
        Insert Into 费用变动记录
          (ID, 记录状态, 病人id, 主页id, 变动时间, 原变动id, 目标变动id, 原病区id, 目标病区id, 费用id, NO, 收费类别, 收费细目id, 医嘱序号, 数量, 单价, 应收金额, 实收金额,
           状态, 摘要, 操作员编号, 操作员姓名)
        Values
          (费用变动记录_Id.Nextval, n_记录状态, 病人id_In, 主页id_In, d_登记时间, n_原变动id, n_目标变动id, 原病区id_In, 目标病区id_In, c_费用.费用id,
           c_费用.No, c_费用.收费类别, c_费用.收费细目id, c_费用.医嘱id, n_申请销账数量, c_费用.标准单价, n_应收金额, n_实收金额, 2,
           Decode(操作_In, 0, '病区变动', '病区变动撤销') || '产生的销帐申请', 操作员编号_In, 操作员姓名_In);
      End If;
    End If;
  
    --2.其他收费项目(药品未包含)
    If Nvl(c_费用.卫材, 0) = 0 And c_费用.执行状态 <> 1 Then
      --处理规则:
      --1.对原始记录进行销帐
      --2.新增目标病区数据
      --3.如果是划价单，直接更改原记录病区id和执行部门
      If Nvl(c_费用.记录状态, 0) = 0 Then
        --直接修改(包含病人病区及目标病区)
        Update 住院费用记录
        Set 病人病区id = 目标病区id_In, 执行部门id = 目标病区id_In
        Where NO = c_费用.No And 记录性质 = 2 And 记录状态 = 0 And Nvl(价格父号, 序号) = c_费用.序号;
      
        Insert Into 费用变动记录
          (ID, 记录状态, 病人id, 主页id, 变动时间, 原变动id, 目标变动id, 原病区id, 目标病区id, 费用id, NO, 收费类别, 收费细目id, 医嘱序号, 数量, 单价, 应收金额, 实收金额,
           状态, 摘要, 操作员编号, 操作员姓名)
          Select 费用变动记录_Id.Nextval, n_记录状态, 病人id_In, 主页id_In, d_登记时间, n_原变动id, n_目标变动id, 原病区id_In, 目标病区id_In, 费用id, NO,
                 收费类别, 收费细目id, 医嘱序号, 数量, 标准单价, 应收金额, 实收金额, 0, Decode(操作_In, 0, '病区变动', '病区变动撤销') || '修改记帐划价单', 操作员编号_In,
                 操作员姓名_In
          From (Select Max(Decode(价格父号, Null, ID, 0)) As 费用id, NO, 收费类别, 收费细目id, 医嘱序号, Avg(Nvl(付数, 1) * 数次) As 数量,
                        Sum(标准单价) As 标准单价, Sum(应收金额) As 应收金额, Sum(实收金额) As 实收金额
                 From 住院费用记录
                 Where NO = c_费用.No And 记录性质 = 2 And 记录状态 = 0 And Nvl(价格父号, 序号) = c_费用.序号
                 Group By NO, 收费类别, 收费细目id, 医嘱序号, Nvl(价格父号, 序号));
      Elsif Nvl(c_费用.准退数, 0) > 0 Then
        --直接销帐处理
        --序号：格式如"1,3,5,7,8",或"1:2:33456,3:2,5:2,7:2,8:2",冒号前面的数字表示行号,中间的数字表示退的数量,后面的数字表示配药记录的ID,目前仅在销帐审核时才传入
        --1.先产生销帐记录
        Zl_住院记帐记录_Delete(c_费用.No, c_费用.序号 || ':' || Nvl(c_费用.准退数, 0), 操作员编号_In, 操作员姓名_In, 2, 2, 0, d_登记时间);
        --2.目标病区转入记录
        For c_明细 In (Select 病人费用记录_Id.Nextval As 费用id, NO, 记录性质, 1 As 记录状态, n_Max序号 + Rownum As 序号, 从属父号,
                            价格父号 + (n_Max序号 + Rownum - 序号) As 价格父号, 主页id, 病人id, 医嘱序号, 门诊标志, 多病人单, 婴儿费, 姓名, 性别, 年龄, 标识号,
                            床号, 费别, 目标病区id_In As 病人病区id, 病人科室id, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, -1 * 数次 As 数次, 加班标志, 附加标志,
                            收入项目id, 收据费目, 记帐费用, 标准单价, -1 * 应收金额 As 应收金额, -1 * 实收金额 As 实收金额, 开单部门id, 开单人,
                            目标病区id_In As 执行部门id, 划价人, 执行人, c_费用.执行状态 As 执行状态, 执行时间, 操作员编号_In As 操作员编号, 操作员姓名_In As 操作员姓名,
                            发生时间, d_登记时间 As 登记时间, 保险项目否, 保险大类id, -1 * 统筹金额 As 统筹金额, 保险编码, 记帐单id, 摘要, 费用类型, 是否急诊, 结论,
                            医疗小组id
                     From 住院费用记录
                     Where NO = c_费用.No And Nvl(价格父号, 序号) = c_费用.序号 And 记录状态 = 2 And 登记时间 = d_登记时间) Loop
          Insert Into 住院费用记录
            (ID, NO, 记录性质, 记录状态, 序号, 从属父号, 价格父号, 主页id, 病人id, 医嘱序号, 门诊标志, 多病人单, 婴儿费, 姓名, 性别, 年龄, 标识号, 床号, 费别, 病人病区id,
             病人科室id, 收费类别, 收费细目id, 计算单位, 付数, 发药窗口, 数次, 加班标志, 附加标志, 收入项目id, 收据费目, 记帐费用, 标准单价, 应收金额, 实收金额, 开单部门id, 开单人,
             执行部门id, 划价人, 执行人, 执行状态, 执行时间, 操作员编号, 操作员姓名, 发生时间, 登记时间, 保险项目否, 保险大类id, 统筹金额, 保险编码, 记帐单id, 摘要, 费用类型, 是否急诊,
             结论, 医疗小组id)
          Values
            (c_明细.费用id, c_明细.No, c_明细.记录性质, c_明细.记录状态, c_明细.序号, c_明细.从属父号, c_明细.价格父号, c_明细.主页id, c_明细.病人id, c_明细.医嘱序号,
             c_明细.门诊标志, c_明细.多病人单, c_明细.婴儿费, c_明细.姓名, c_明细.性别, c_明细.年龄, c_明细.标识号, c_明细.床号, c_明细.费别, c_明细.病人病区id,
             c_明细.病人科室id, c_明细.收费类别, c_明细.收费细目id, c_明细.计算单位, c_明细.付数, c_明细.发药窗口, c_明细.数次, c_明细.加班标志, c_明细.附加标志,
             c_明细.收入项目id, c_明细.收据费目, c_明细.记帐费用, c_明细.标准单价, c_明细.应收金额, c_明细.实收金额, c_明细.开单部门id, c_明细.开单人, c_明细.执行部门id,
             c_明细.划价人, c_明细.执行人, /*c_明细.执行状态*/ 0, c_明细.执行时间, c_明细.操作员编号, c_明细.操作员姓名, c_明细.发生时间, c_明细.登记时间, c_明细.保险项目否,
             c_明细.保险大类id, c_明细.统筹金额, c_明细.保险编码, c_明细.记帐单id, c_明细.摘要, c_明细.费用类型, c_明细.是否急诊, c_明细.结论, c_明细.医疗小组id);
        
          Update 费用变动记录
          Set 单价 = Nvl(单价, 0) + Nvl(c_明细.标准单价, 0), 应收金额 = Nvl(应收金额, 0) + Nvl(c_明细.应收金额, 0),
              实收金额 = Nvl(实收金额, 0) + Nvl(c_明细.实收金额, 0)
          Where 费用id = c_费用.费用id And 变动时间 = d_登记时间 And 目标变动id = n_目标变动id And 收费细目id = c_费用.收费细目id And
                病人id + 0 = c_明细.病人id;
          If Sql%NotFound Then
            Insert Into 费用变动记录
              (ID, 记录状态, 病人id, 主页id, 变动时间, 原变动id, 目标变动id, 原病区id, 目标病区id, 费用id, NO, 收费类别, 收费细目id, 医嘱序号, 数量, 单价, 应收金额,
               实收金额, 状态, 摘要, 操作员编号, 操作员姓名)
            Values
              (费用变动记录_Id.Nextval, n_记录状态, 病人id_In, 主页id_In, d_登记时间, n_原变动id, n_目标变动id, 原病区id_In, 目标病区id_In, c_费用.费用id,
               c_费用.No, c_费用.收费类别, c_费用.收费细目id, c_费用.医嘱id, Round(c_明细.数次 * Nvl(c_明细.付数, 1), 5), c_明细.标准单价, c_明细.应收金额,
               c_明细.实收金额, 1, Decode(操作_In, 0, '病区变动', '病区变动撤销') || '修改记帐单', 操作员编号_In, 操作员姓名_In);
          End If;
        
          Update 病人审批项目
          Set 已用数量 = Nvl(已用数量, 0) + Round(c_明细.数次 * Nvl(c_明细.付数, 1), 5)
          Where 病人id = 病人id_In And 主页id = 主页id_In And 项目id = c_明细.收费细目id And Nvl(使用限量, 0) <> 0;
        
          --病人余额
          Update 病人余额
          Set 费用余额 = Nvl(费用余额, 0) + c_明细.实收金额
          Where 病人id = c_明细.病人id And 类型 = 2 And 性质 = 1;
          If Sql%RowCount = 0 Then
            Insert Into 病人余额
              (病人id, 类型, 性质, 费用余额, 预交余额)
            Values
              (c_明细.病人id, 2, 1, c_明细.实收金额, 0);
          End If;
          --病人未结费用
          Update 病人未结费用
          Set 金额 = Nvl(金额, 0) + c_明细.实收金额
          Where 病人id = c_明细.病人id And Nvl(主页id, 0) = Nvl(c_明细.主页id, 0) And Nvl(病人病区id, 0) = Nvl(c_明细.病人病区id, 0) And
                Nvl(病人科室id, 0) = Nvl(c_明细.病人科室id, 0) And Nvl(开单部门id, 0) = Nvl(c_明细.开单部门id, 0) And
                Nvl(执行部门id, 0) = Nvl(c_明细.执行部门id, 0) And 收入项目id + 0 = c_明细.收入项目id And 来源途径 + 0 = 2;
          If Sql%RowCount = 0 Then
            Insert Into 病人未结费用
              (病人id, 主页id, 病人病区id, 病人科室id, 开单部门id, 执行部门id, 收入项目id, 来源途径, 金额)
            Values
              (c_明细.病人id, c_明细.主页id, c_明细.病人病区id, c_明细.病人科室id, c_明细.开单部门id, c_明细.执行部门id, c_明细.收入项目id, 2, c_明细.实收金额);
          End If;
        
          --更新医嘱执行计价的费用ID
          If Nvl(c_明细.价格父号, 0) = 0 Then
            Update 医嘱执行计价
            Set 费用id = c_明细.费用id
            Where 医嘱id = c_费用.医嘱id And 发送号 = c_费用.发送号 And 费用id = c_费用.费用id And 执行状态 = 0;
          End If;
        
          n_Max序号 := c_明细.序号;
        End Loop;
      End If;
    End If;
  End Loop;

  If Nvl(操作_In, 0) = 1 Then
    --撤消操作,需要删除卫生材料部分中未审核部分
    For c_销账 In (Select a.费用id, a.病人id, a.主页id, a.No, a.收费类别, a.收费细目id, a.医嘱序号, a.数量, a.单价, a.应收金额, a.实收金额
                 From 费用变动记录 A
                 Where a.原变动id = n_目标变动id And a.目标变动id = n_原变动id And a.状态 = 2) Loop
    
      Select Sum(数量) Into n_申请取消数量 From 病人费用销帐 Where 费用id = c_销账.费用id And 状态 In (0, 2);
    
      If Nvl(n_申请取消数量, 0) > 0 Then
        n_应收金额 := Round(n_申请取消数量 * Nvl(c_销账.单价, 0), n_Dec);
        n_实收金额 := 0;
        If Nvl(c_销账.应收金额, 0) <> 0 Then
          n_实收金额 := Round(Nvl(n_应收金额, 0) * Nvl(c_销账.实收金额, 0) / c_销账.应收金额, n_Dec);
        End If;
      
        Insert Into 费用变动记录
          (ID, 记录状态, 病人id, 主页id, 变动时间, 原变动id, 目标变动id, 原病区id, 目标病区id, 费用id, NO, 收费类别, 收费细目id, 医嘱序号, 数量, 单价, 应收金额, 实收金额,
           状态, 摘要, 操作员编号, 操作员姓名)
        Values
          (费用变动记录_Id.Nextval, n_记录状态, c_销账.病人id, c_销账.主页id, d_登记时间, n_原变动id, n_目标变动id, 原病区id_In, 目标病区id_In, c_销账.费用id,
           c_销账.No, c_销账.收费类别, c_销账.收费细目id, c_销账.医嘱序号, n_申请取消数量, c_销账.单价, n_应收金额, n_实收金额, 3, '病区撤销后删除销帐申请', 操作员编号_In,
           操作员姓名_In);
      End If;
    
      Delete 病人费用销帐 Where 费用id = c_销账.费用id And 状态 In (0, 2);
    End Loop;
  End If;
Exception
  When Err_Item Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Err_Msg || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Turntoward_Fee;
/

--112263:余伟节,2017-07-31,出院后长期担保自动失效
Create Or Replace Procedure Zl_病人变动记录_Out
(
  病人id_In       病案主页.病人id%Type,
  主页id_In       病案主页.主页id%Type,
  疾病id_In       病人诊断记录.疾病id%Type,
  诊断id_In       病人诊断记录.诊断id%Type,
  出院诊断_In     病人诊断记录.诊断描述%Type,
  出院情况_In     病人诊断记录.出院情况%Type,
  中医疾病id_In   病人诊断记录.疾病id%Type,
  中医诊断id_In   病人诊断记录.诊断id%Type,
  中医诊断_In     病人诊断记录.诊断描述%Type,
  中医出院情况_In 病人诊断记录.出院情况%Type,
  是否疑诊_In     病案主页.是否确诊%Type, --同时作为西医的是否疑诊
  出院方式_In     病案主页.出院方式%Type,
  出院时间_In     病案主页.出院日期%Type,
  随诊标志_In     病案主页.随诊标志%Type, --0/NULL-不随诊，1-月，2-年，3-周，4-天，9-终身
  随诊期限_In     病案主页.随诊期限%Type,
  尸检标志_In     病案主页.尸检标志%Type,
  操作员编号_In   病人变动记录.操作员编号%Type,
  操作员姓名_In   病人变动记录.操作员姓名%Type,
  确诊日期_In     病案主页.确诊日期%Type := Null
) As
  -----------------------------------------------------------
  --说明：病人出院
  -----------------------------------------------------------
  Cursor c_Bedinfo Is
    Select 病区id, 床号 From 床位状况记录 Where 病人id = 病人id_In;

  v_Count Number;
  Err_Custom Exception;
  v_Error      Varchar2(255);
  v_随诊期限   Date;
  v_应发时间   Date;
  v_共享号     Zlsystems.共享号%Type;
  v_年龄       病人信息.年龄%Type;
  v_Sql        Varchar2(1000);
  v_住院医师   Varchar2(20);
  v_出院科室   Number;
  v_姓名       病人信息.姓名%Type;
  n_婴儿科室id 病案主页.婴儿科室id%Type;
Begin
  --首先判断该病人是否已出院
  Select Count(*) Into v_Count From 病案主页 Where 病人id = 病人id_In And 主页id = 主页id_In And 出院日期 Is Null;

  If v_Count = 0 Then
    v_Error := '操作失败,该病人可能已经出院！';
    Raise Err_Custom;
  End If;

  --首先判断该病人是否处于等待转科或入科状态
  Select Count(*)
  Into v_Count
  From 病案主页
  Where 病人id = 病人id_In And 主页id = 主页id_In And 出院日期 Is Null And Nvl(状态, 0) In (0, 3);
  If v_Count = 0 Then
    v_Error := '操作失败,该病人正处于转科状态或尚未入科,不能出院！';
    Raise Err_Custom;
  End If;

  --临床路径正在执行时不允许出院
  Select Max(b.状态)
  Into v_Count
  From 病案主页 A, 病人临床路径 B
  Where a.病人id = 病人id_In And a.主页id = 主页id_In And a.病人id = b.病人id And a.主页id = b.主页id And a.出院科室id = b.科室id;
  If v_Count = 1 Then
    v_Error := '该病人的临床路径正在执行中,不能出院。';
    Raise Err_Custom;
  End If;

  --母婴分离婴儿未出院则母亲不能出院
  Select 婴儿科室id Into n_婴儿科室id From 病案主页 Where 病人id = 病人id_In And 主页id = 主页id_In;
  If n_婴儿科室id Is Not Null Then
    Select Count(*)
    Into v_Count
    From 病人医嘱记录 A, 诊疗项目目录 B
    Where a.病人id = 病人id_In And a.主页id = 主页id_In And a.诊疗项目id = b.Id And b.操作类型 = '5' And b.类别 = 'Z' And
          Nvl(a.婴儿, 0) <> 0 And a.医嘱状态 <> 4;
    If v_Count = 0 Then
      v_Error := '该病人的婴儿还在其他科室，并未下达出院医嘱,请先给婴儿下达出院医嘱后再出院。';
      Raise Err_Custom;
    End If;
  End If;

  v_Count := Nvl(zl_GetSysParameter('特殊医嘱发送前检查未生效医嘱', 1254), 0);
  If v_Count = 1 Then
    v_姓名 := Null;
    Select Max(姓名)
    Into v_姓名
    From 病人医嘱记录
    Where 病人id = 病人id_In And 主页id = 主页id_In And 医嘱状态 = 1 And Rownum = 1;
    If v_姓名 Is Not Null Then
      v_Error := v_姓名 || '存在未校对的医嘱，请删除或校对后再继续。';
      Raise Err_Custom;
    End If;
  
    v_姓名 := Null;
    Select Max(姓名)
    Into v_姓名
    From 病人医嘱记录
    Where 病人id = 病人id_In And 主页id = 主页id_In And 医嘱期效 = 1 And 医嘱状态 In (2, 3) And Nvl(执行标记, 0) <> -1 And
          Nvl(执行性质, 0) <> 0 And Rownum = 1;
    If v_姓名 Is Not Null Then
      v_Error := v_姓名 || '存在校对后未发送的临嘱，请发送或作废后再继续。';
      Raise Err_Custom;
    End If;
  End If;

  --判断是否产生住院日报
  Select Nvl(出院科室id, 入院科室id), 住院医师
  Into v_出院科室, v_住院医师
  From 病案主页
  Where 病人id = 病人id_In And 主页id = 主页id_In;
  Select Zl_住院日报_Count(v_出院科室, 出院时间_In) Into v_Count From Dual;
  If v_Count > 0 Then
    v_Error := '已产生业务时间内的住院日报,不能办理该业务!';
    Raise Err_Custom;
  End If;

  --判断是否与病案系统共享
  Begin
    Select 共享号 Into v_共享号 From zlSystems Where Floor(编号 / 100) = 3;
  Exception
    When Others Then
      Null;
  End;
  --出院变动
  Update 病人变动记录
  Set 终止时间 = 出院时间_In, 终止原因 = 1, 终止人员 = 操作员姓名_In, 上次计算时间 = Decode(Sign(Nvl(上次计算时间, 出院时间_In) - 出院时间_In), 1, Null, 上次计算时间)
  Where 病人id = 病人id_In And 主页id = 主页id_In And 终止时间 Is Null;
  
  Update 病人自动计算
  Set 终止时间 = 出院时间_In, 终止原因 = 1, 终止人员 = 操作员姓名_In, 上次计算时间 = Decode(Sign(Nvl(上次计算时间, 出院时间_In) - 出院时间_In), 1, Null, 上次计算时间)
  Where 病人id = 病人id_In And 主页id = 主页id_In And 终止时间 Is Null;
  
  --床位记录
  For r_Bedrow In c_Bedinfo Loop
    Update 床位状况记录
    Set 状态 = '空床', 病人id = Null, 科室id = Decode(共用, 1, Null, 科室id)
    Where 病区id = r_Bedrow.病区id And 床号 = r_Bedrow.床号;
  End Loop;

  --病案主页
  Update 病案主页
  Set 状态 = 0, 出院日期 = 出院时间_In, 出院方式 = 出院方式_In,
      住院天数 = Decode(Trunc(出院时间_In) - Trunc(入院日期), 0, 1, Trunc(出院时间_In) - Trunc(入院日期)), 随诊标志 = 随诊标志_In,
      随诊期限 = Decode(随诊期限_In, 0, Null, 随诊期限_In), 尸检标志 = 尸检标志_In, 是否确诊 = Decode(Nvl(是否疑诊_In, 0), 0, 0, 1), 确诊日期 = 确诊日期_In
  Where 病人id = 病人id_In And 主页id = 主页id_In;

  --产生病历书写时机
  Zl_电子病历时机_Insert(病人id_In, 主页id_In, 2, '出院', v_出院科室, v_住院医师, 出院时间_In, 出院时间_In);
  --增加随诊记录
  If v_共享号 = 100 Then
    If Nvl(随诊期限_In, 0) <> 0 Then
      If 随诊标志_In = 1 Then
        v_随诊期限 := Add_Months(出院时间_In, 随诊期限_In);
      Elsif 随诊标志_In = 2 Then
        v_随诊期限 := Add_Months(出院时间_In, 12 * 随诊期限_In);
      Elsif 随诊标志_In = 3 Then
        v_随诊期限 := 出院时间_In + 7 * 随诊期限_In;
      Elsif 随诊标志_In = 4 Then
        v_随诊期限 := 出院时间_In + 随诊期限_In;
      End If;
    Else
      v_随诊期限 := To_Date('3000-1-1', 'YYYY-MM-DD');
    End If;
  
    If 随诊标志_In = 1 Or 随诊标志_In = 2 Or 随诊标志_In = 3 Or 随诊标志_In = 4 Then
      If v_随诊期限 > Add_Months(出院时间_In, 3) Then
        v_应发时间 := Trunc(Add_Months(出院时间_In, 3));
      Else
        v_应发时间 := v_随诊期限;
      End If;
      v_Sql := 'Insert Into 随诊记录 (ID, 病人id, 主页id, 随诊期限, 应发时间) Values (随诊记录_Id.Nextval,:1,:2,:3,:4)';
      Execute Immediate v_Sql
        Using 病人id_In, 主页id_In, To_Date(To_Char(v_随诊期限, 'YYYY-MM-DD'), 'YYYY-MM-DD'), To_Date(To_Char(v_应发时间, 'YYYY-MM-DD'), 'YYYY-MM-DD');
    End If;
  End If;
  --病人信息
  v_年龄 := Zl_Age_Calc(病人id_In);
  Update 病人信息
  Set 当前科室id = Null, 当前病区id = Null, 当前床号 = Null, 出院时间 = 出院时间_In, 年龄 = v_年龄, 在院 = Null
  Where 病人id = 病人id_In;
  --在院病人
  Delete From 在院病人 Where 病人id = 病人id_In;
  --出院诊断
  If 出院诊断_In Is Not Null Or 疾病id_In Is Not Null Or 诊断id_In Is Not Null Then
    Delete From 病人诊断记录
    Where 病人id = 病人id_In And 主页id = 主页id_In And 诊断类型 = 3 And 诊断次序 = 1 And 记录来源 = 2;
    Insert Into 病人诊断记录
      (ID, 病人id, 主页id, 记录来源, 诊断类型, 诊断次序, 疾病id, 诊断id, 诊断描述, 是否疑诊, 出院情况, 记录日期, 记录人)
    Values
      (病人诊断记录_Id.Nextval, 病人id_In, 主页id_In, 2, 3, 1, 疾病id_In, 诊断id_In, 出院诊断_In, Decode(Nvl(是否疑诊_In, 0), 0, 1, 0),
       出院情况_In, Sysdate, 操作员姓名_In);
  End If;

  --中医出院诊断
  If 中医诊断_In Is Not Null Or 中医疾病id_In Is Not Null Or 中医诊断id_In Is Not Null Then
    Delete From 病人诊断记录
    Where 病人id = 病人id_In And 主页id = 主页id_In And 诊断类型 = 13 And 诊断次序 = 1 And 记录来源 = 2;
    Insert Into 病人诊断记录
      (ID, 病人id, 主页id, 记录来源, 诊断类型, 诊断次序, 疾病id, 诊断id, 诊断描述, 是否疑诊, 出院情况, 记录日期, 记录人)
    Values
      (病人诊断记录_Id.Nextval, 病人id_In, 主页id_In, 2, 13, 1, 中医疾病id_In, 中医诊断id_In, 中医诊断_In, Null, 中医出院情况_In, Sysdate, 操作员姓名_In);
  End If;

  --正常担保记录出院后自动失效    
  Update 病人担保记录
  Set 到期时间 = Sysdate
  Where 病人id = 病人id_In And 主页id = 主页id_In And Nvl(到期时间, Sysdate + 1) > Sysdate And 删除标志 = 1 And Nvl(担保性质, 0) = 0;

Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_病人变动记录_Out;
/

--111458:焦博,2017-07-28,支付宝预约挂号时禁止预约的号不允许锁定
Create Or Replace Procedure Zl_Third_Lockno
(
  Xml_In  In Xmltype,
  Xml_Out Out Xmltype
) Is
  --------------------------------------------------------------------------------------------------
  --功能:HIS锁号
  --入参:Xml_In:
  --<IN>
  --  <HM>5</HM>           //号码
  --  <CZJLID>1</CZJLID>       //出诊记录ID,出诊表排班模式下传入
  --  <RQ>2013-11-21 09:00</RQ>     //预约时间
  --  <CZ>1</CZ>           //操作
  --  <HX></HX>          //号序
  --  <HZDW>支付宝</HZDW>   //合作单位
  --  <JQM>机器名</JQM>        //机器名
  --  <SJD>时间段</SJD>        //锁号的时间段
  --</IN>

  --出参:Xml_Out
  --<OUTPUT>
  -- <HX>号序</HX>          //锁号操作并且成功时返回
  -- 错误信息  //出错时返回
  --</ OUTPUT>
  --------------------------------------------------------------------------------------------------
  v_号码         挂号安排.号码%Type;
  d_日期         Date;
  n_操作类型     Number(3);
  n_序号控制     Number(3);
  n_存在         Number(3);
  n_分时段       Number(3);
  n_限号数       挂号安排限制.限号数%Type;
  n_安排id       挂号安排.Id%Type;
  n_计划id       挂号安排计划.Id%Type;
  n_号序         挂号序号状态.序号%Type;
  v_星期         挂号安排限制.限制项目%Type;
  v_操作员姓名   挂号序号状态.操作员姓名%Type;
  v_机器名       挂号序号状态.机器名%Type;
  v_验证姓名     挂号序号状态.操作员姓名%Type;
  v_验证机器名   挂号序号状态.机器名%Type;
  n_状态         挂号序号状态.状态%Type;
  v_合作单位     合作单位安排控制.合作单位%Type;
  n_合约模式     Number(3);
  n_启用合作单位 Number(3);
  v_Temp         Varchar2(32767); --临时XML
  v_Optemp       Varchar2(300);
  x_Templet      Xmltype; --模板XML
  n_Exists       Number(2);
  n_记录id       临床出诊记录.Id%Type;
  n_序号         临床出诊序号控制.序号%Type;
  n_数量         临床出诊序号控制.数量%Type;
  n_顺序号       临床出诊序号控制.预约顺序号%Type;
  d_启用时间     Date;
  v_时间段       Varchar2(200);
  v_Para         Varchar2(2000);
  n_挂号模式     Number(3);
  d_时段开始     Date;
  d_序号时间     Date;
  d_时段结束     Date;
  n_出诊         Number(3);
  Err_Item Exception;
Begin
  x_Templet := Xmltype('<OUTPUT></OUTPUT>');

  Select Extractvalue(Value(A), 'IN/HM'), To_Date(Extractvalue(Value(A), 'IN/RQ'), 'YYYY-MM-DD hh24:mi:ss'),
         Extractvalue(Value(A), 'IN/CZ'), Extractvalue(Value(A), 'IN/HX'), Extractvalue(Value(A), 'IN/HZDW'),
         Extractvalue(Value(A), 'IN/JQM'), Extractvalue(Value(A), 'IN/CZJLID'), Extractvalue(Value(A), 'IN/SJD')
  Into v_号码, d_日期, n_操作类型, n_号序, v_合作单位, v_机器名, n_记录id, v_时间段
  From Table(Xmlsequence(Extract(Xml_In, 'IN'))) A;

  v_Para     := zl_GetSysParameter(256);
  n_挂号模式 := Substr(v_Para, 1, 1);
  Begin
    d_启用时间 := To_Date(Substr(v_Para, 3), 'yyyy-mm-dd hh24:mi:ss');
  Exception
    When Others Then
      d_启用时间 := Null;
  End;
  If Sysdate - 10 > Nvl(d_启用时间, Sysdate - 30) Then
    If n_挂号模式 = 1 And Nvl(d_日期, Sysdate) > Nvl(d_启用时间, Sysdate - 30) And n_记录id Is Null Then
      v_Temp := '系统当前处于出诊表排班模式，传入的参数无法确定挂号安排，请重试！';
      Raise Err_Item;
    End If;
  Else
    If n_挂号模式 = 1 And Nvl(d_日期, Sysdate) > Nvl(d_启用时间, Sysdate - 30) And n_记录id Is Null Then
      Begin
        Select a.Id
        Into n_记录id
        From 临床出诊记录 A, 临床出诊号源 B
        Where a.号源id = b.Id And b.号码 = v_号码 And Nvl(d_日期, Sysdate) Between a.开始时间 And a.终止时间;
      Exception
        When Others Then
          v_Temp := '系统当前处于出诊表排班模式，传入的参数无法确定挂号安排，请重试！';
          Raise Err_Item;
      End;
    End If;
  End If;

  If v_时间段 Is Not Null And n_操作类型 = 1 Then
    Begin
      d_时段开始 := To_Date(To_Char(d_日期, 'yyyy-mm-dd') || Substr(v_时间段, 1, Instr(v_时间段, '-') - 1), 'yyyy-mm-dd hh24:mi:ss');
      If Substr(v_时间段, Instr(v_时间段, '-') + 1) Is Null Then
        d_时段结束 := Null;
      Else
        d_时段结束 := To_Date(To_Char(d_日期, 'yyyy-mm-dd') || Substr(v_时间段, Instr(v_时间段, '-') + 1), 'yyyy-mm-dd hh24:mi:ss');
      End If;
    Exception
      When Others Then
        v_Temp := '无法解析传入的时间段格式，请检查！';
        Raise Err_Item;
    End;
  End If;

  If v_机器名 Is Null Then
    Select Terminal Into v_机器名 From V$session Where Audsid = Userenv('sessionid');
  End If;
  v_Optemp := Zl_Identity(1);
  Select Substr(v_Optemp, Instr(v_Optemp, ',') + 1) Into v_Optemp From Dual;
  Select Substr(v_Optemp, Instr(v_Optemp, ',') + 1) Into v_操作员姓名 From Dual;

  If n_记录id Is Null Then
    If n_操作类型 = 0 Then
      --解锁
      Select Nvl(Max(1), 0)
      Into n_Exists
      From 挂号序号状态
      Where 机器名 = v_机器名 And 操作员姓名 = v_操作员姓名 And 状态 = 5 And 序号 = n_号序 And Trunc(日期) = Trunc(d_日期) And 号码 = v_号码 And
            Rownum < 2;
      If n_Exists = 1 Then
        Delete 挂号序号状态
        Where 机器名 = v_机器名 And 操作员姓名 = v_操作员姓名 And 状态 = 5 And 序号 = n_号序 And Trunc(日期) = Trunc(d_日期) And 号码 = v_号码;
        v_Temp := '<HX>' || n_号序 || '</HX>';
        Select Appendchildxml(x_Templet, '/OUTPUT', Xmltype(v_Temp)) Into x_Templet From Dual;
      Else
        v_Temp := '没有发现需要解锁的序号';
        Raise Err_Item;
      End If;
    End If;
  
    If n_操作类型 = 1 Then
      --锁号
      Select Decode(To_Char(d_日期, 'D'), '1', '周日', '2', '周一', '3', '周二', '4', '周三', '5', '周四', '6', '周五', '7', '周六',
                     Null)
      Into v_星期
      From Dual;
      Begin
        Select 序号控制, ID
        Into n_序号控制, n_计划id
        From (Select 序号控制, ID
               From 挂号安排计划
               Where 号码 = v_号码 And d_日期 Between Nvl(生效时间, To_Date('1900-01-01', 'YYYY-MM-DD')) And
                     Nvl(失效时间, To_Date('3000-01-01', 'YYYY-MM-DD')) And 审核时间 Is Not Null
               Order By 生效时间 Desc)
        Where Rownum < 2;
      Exception
        When Others Then
          Select 序号控制, ID Into n_序号控制, n_安排id From 挂号安排 Where 号码 = v_号码;
      End;
      If n_序号控制 = 1 Then
        If Nvl(n_计划id, 0) <> 0 Then
          Select Nvl(Max(1), 0)
          Into n_分时段
          From 挂号计划时段
          Where 星期 = v_星期 And 计划id = n_计划id And Rownum < 2;
          Select Nvl(Max(1), 0)
          Into n_启用合作单位
          From 合作单位计划控制
          Where 限制项目 = v_星期 And 计划id = n_计划id And 合作单位 = v_合作单位 And Rownum < 2;
          If v_时间段 Is Null Then
            Begin
              Select 1, a.状态, a.操作员姓名, a.机器名
              Into n_存在, n_状态, v_验证姓名, v_验证机器名
              From 挂号序号状态 A, 挂号计划时段 B
              Where a.号码 = v_号码 And Trunc(a.日期) = Trunc(d_日期) And a.序号 = b.序号 And b.计划id = n_计划id And b.星期 = v_星期 And
                    To_Char(b.开始时间, 'hh24:mi') = To_Char(d_日期, 'hh24:mi') And Rownum < 2;
            Exception
              When Others Then
                n_存在 := 0;
            End;
          Else
            n_存在 := 0;
          End If;
        
          If n_存在 = 1 Then
            If n_状态 = 5 And v_验证姓名 = v_操作员姓名 And v_机器名 = v_验证机器名 Then
              Null;
            Else
              --传入时间的序号已经被使用
              v_Temp := '传入时间' || d_日期 || '的序号已被使用';
              Raise Err_Item;
            End If;
          Else
            If n_分时段 = 1 Then
              If v_时间段 Is Null Then
                --精确定位序号
                Begin
                  Select 序号
                  Into n_号序
                  From 挂号计划时段
                  Where 计划id = n_计划id And 星期 = v_星期 And To_Char(开始时间, 'hh24:mi') = To_Char(d_日期, 'hh24:mi') And
                        Rownum < 2;
                Exception
                  When Others Then
                    Select Max(序号) + 1
                    Into n_号序
                    From (Select Distinct 序号
                           From 挂号计划时段
                           Where 计划id = n_计划id And 星期 = v_星期
                           Union
                           Select Distinct 序号 From 挂号序号状态 Where 号码 = v_号码 And Trunc(日期) = Trunc(d_日期));
                  
                End;
                Begin
                  Select 1 Into n_存在 From 挂号序号状态 Where 号码 = v_号码 And 日期 = d_日期 And 序号 = n_号序;
                Exception
                  When Others Then
                    Insert Into 挂号序号状态
                      (号码, 日期, 序号, 状态, 操作员姓名, 备注, 登记时间, 机器名)
                    Values
                      (v_号码, d_日期, n_号序, 5, v_操作员姓名, '移动锁号', Sysdate, v_机器名);
                End;
              Else
                --时间段自动取序号
                Begin
                  If Trunc(d_日期) = Trunc(Sysdate) Then
                    Select Min(a.序号)
                    Into n_号序
                    From 挂号计划时段 A, 挂号序号状态 B
                    Where a.序号 = b.序号(+) And Nvl(b.状态, 0) = 0 And b.号码(+) = v_号码 And Trunc(b.日期(+)) = Trunc(d_日期) And
                          a.计划id = n_计划id And a.星期 = v_星期 And To_Char(a.开始时间, 'hh24:mi') >= To_Char(d_时段开始, 'hh24:mi') And
                          To_Char(a.开始时间, 'hh24:mi') < To_Char(d_时段结束, 'hh24:mi');
                  Else
                    Select Min(a.序号)
                    Into n_号序
                    From 挂号计划时段 A, 挂号序号状态 B
                    Where a.序号 = b.序号(+) And Nvl(b.状态, 0) = 0 And b.号码(+) = v_号码 And Trunc(b.日期(+)) = Trunc(d_日期) And
                          a.计划id = n_计划id And a.星期 = v_星期 And To_Char(a.开始时间, 'hh24:mi') >= To_Char(d_时段开始, 'hh24:mi') And
                          To_Char(a.开始时间, 'hh24:mi') < To_Char(d_时段结束, 'hh24:mi') And a.是否预约 = 1;
                  End If;
                  If Nvl(n_号序, 0) = 0 Then
                    If d_时段结束 Is Null Then
                      Select Max(序号) + 1
                      Into n_号序
                      From (Select Distinct 序号
                             From 挂号计划时段
                             Where 计划id = n_计划id And 星期 = v_星期
                             Union
                             Select Distinct 序号 From 挂号序号状态 Where 号码 = v_号码 And Trunc(日期) = Trunc(d_日期));
                    Else
                      v_Temp := '传入时间段' || v_时间段 || '的序号已被使用完';
                      Raise Err_Item;
                    End If;
                  End If;
                Exception
                  When Others Then
                    If d_时段结束 Is Null Then
                      Select Max(序号) + 1
                      Into n_号序
                      From (Select Distinct 序号
                             From 挂号计划时段
