  (
    Id_In       影像报告驳回.Id%Type,
    报告id_In   影像报告驳回.检查报告id%Type,
    报告状态_In 影像报告记录.报告状态%Type
  ) Is
  Begin
    Update 影像报告驳回 Set 是否撤销 = 1 Where Id = Id_In;
    Update 影像报告记录 Set 报告状态 = 报告状态_In, 待处理人 = '' Where Id = 报告id_In;
  Exception
    When Others Then
      Zl_Errorcenter(Sqlcode, Sqlerrm);
  End;

