--111635:梁唐彬,2017-07-14,XML自定义申请单
Create Or Replace Procedure Zl_诊疗单据目录_Edit
(
  操作_In  In Number, --1:增加;2-修改;3-删除
  Id_In    In 病历文件列表.ID%Type,
  编号_In  In 病历文件列表.编号%Type := Null,
  名称_In  In 病历文件列表.名称%Type := Null,
  说明_In  In 病历文件列表.说明%Type := Null,
  保留_In  In 病历文件列表.保留%Type := Null,
  通用_In  In 病历文件列表.通用%Type := Null,
  附项_In  In Varchar2 := Null, --按chr(10)行分隔，chr(9)字段分隔
  密码1_In In Varchar2 := Null,
  密码2_In In Varchar2 := Null,
  子类_In  In 病历文件列表.子类%Type := Null,
  格式_In  In 病历文件列表.格式%Type := Null
) Is
  v_编号 病历文件列表.编号%Type; --原编号
  n_系统 zlTools.zlReports.系统%Type;
  v_密码 zlTools.zlReports.密码%Type := 'Wait';

  --获得当前系统号
  Function f_Cur_Sys Return Number Is
    n_Sys_No zlTools.zlSystems.编号%Type;
  Begin
    Select Min(编号)
    Into n_Sys_No
    From zlTools.zlSystems
    Where 所有者 In
          (Select Owner From All_Objects Where Object_Name = Upper('Zl_诊疗单据目录_Edit') And Object_Type = 'PROCEDURE');
    Return n_Sys_No;
  End f_Cur_Sys;

  --填写申请附项
  Procedure p_Append_Items Is
    v_All    Varchar2(2000);
    v_Row    Varchar2(1000);
    v_Val    Varchar2(1000);
    v_项目   病历单据附项.项目%Type;
    n_必填   病历单据附项.必填%Type := 0;
    n_排列   病历单据附项.排列%Type := 0;
    n_要素id 病历单据附项.要素id%Type := Null;
    v_内容   病历单据附项.内容%Type;
    n_只读   病历单据附项.只读%Type := 0;
  Begin
    Delete 病历单据附项 Where 文件id = Id_In;
    
    If 附项_In Is Null Then
      Return;
    End If;
    
    v_All := 附项_In || Chr(10);
    Loop
      v_Row  := Substr(v_All, 1, Instr(v_All, Chr(10)) - 1);
      v_项目 := Substr(v_Row, 1, Instr(v_Row, Chr(9)) - 1);

      v_Val := Substr(v_Row, Instr(v_Row, Chr(9), 1, 1) + 1, Instr(v_Row, Chr(9), 1, 2) - Instr(v_Row, Chr(9), 1, 1) - 1);
      If v_Val Is Null Then
        n_必填 := 0;
      Else
        n_必填 := To_Number(v_Val);
      End If;
      
      v_Val := Substr(v_Row, Instr(v_Row, Chr(9), 1, 2) + 1, Instr(v_Row, Chr(9), 1, 3) - Instr(v_Row, Chr(9), 1, 2) - 1);
      If v_Val Is Null Then
        n_只读 := 0;
      Else
        n_只读 := To_Number(v_Val);
      End If;
            
      v_Val := Substr(v_Row, Instr(v_Row, Chr(9), 1, 3) + 1, Instr(v_Row, Chr(9), 1, 4) - Instr(v_Row, Chr(9), 1, 3) - 1);
      If v_Val Is Null Then
        n_要素id := Null;
      Else
        n_要素id := To_Number(v_Val);
      End If;
      
      v_Val  := Substr(v_Row, Instr(v_Row, Chr(9), 1, 4) + 1);
      v_内容 := v_Val;

      n_排列 := n_排列 + 1;
      Insert Into 病历单据附项
        (文件id, 项目, 必填, 排列, 要素id, 内容,只读)
      Values
        (Id_In, v_项目, n_必填, n_排列, n_要素id, v_内容,n_只读);
      v_All := Substr(v_All, Instr(v_All, Chr(10)) + 1);
      Exit When v_All Is Null;
    End Loop;
    
    delete 病历附项模板 a where a.病历文件Id=Id_In and not exists(select 1 from 病历单据附项 where 项目=a.单据附项);
  End p_Append_Items;

  --按诊疗单据报表模板添加本单据对应报表
  Procedure p_Add_Report(Form_In Number) Is
    --参数：form_In=1,申请; form_In=2,报告
    n_Mdl_Id zlTools.zlReports.ID%Type;
    n_Rpt_Id zlTools.zlReports.ID%Type;
    n_Dat_Id zlTools.zlRPTDatas.ID%Type;
    e_Mod_Lost Exception;
  Begin
    Begin
      Select ID Into n_Mdl_Id From zlReports Where 系统 = n_系统 And Upper(编号) = 'ZLEMRBILLMOLD1-' || Form_In;
    Exception
      When Others Then
        n_Mdl_Id := 0;
    End;
    If n_Mdl_Id = 0 Then
      Raise e_Mod_Lost;
    End If;
    -- 11698 产生的报表，缺密码，不能直接设计
    If Form_In = 1 Then
      v_密码 := 密码1_In;
    Elsif Form_In = 2 Then
      v_密码 := 密码2_In;
    End If;
    If v_密码 Is Null Then
      v_密码 := 'Wait...';
    End If;
    Select zlTools.Zlreports_Id.Nextval Into n_Rpt_Id From Dual;
    Insert Into zlTools.zlReports
      (ID, 编号, 名称, 说明, 密码, 进纸, 打印机, 票据, 系统, 程序id, 功能, 修改时间, 发布时间)
      Select n_Rpt_Id, 'ZLCISBILL00' || 编号_In || '-' || Form_In, 名称_In, 说明_In, v_密码, 进纸, 打印机, 票据, 系统, Null, Null,
             Sysdate, Null
      From zlTools.zlReports
      Where ID = n_Mdl_Id;
    For r_Rptdatas In (Select ID From zlTools.zlRPTDatas Where 报表id = n_Mdl_Id) Loop
      Select zlTools.Zlrptdatas_Id.Nextval Into n_Dat_Id From Dual;
      Insert Into zlTools.zlRPTDatas
        (ID, 报表id, 名称, 字段, 对象, 类型)
        Select n_Dat_Id, n_Rpt_Id, 名称, 字段, 对象, 类型 From zlTools.zlRPTDatas Where ID = r_Rptdatas.ID;
      Insert Into zlTools.zlRPTPars
        (源id, 组名, 序号, 名称, 类型, 缺省值, 格式, 值列表, 分类sql, 明细sql, 分类字段, 明细字段, 对象)
        Select n_Dat_Id, 组名, 序号, 名称, 类型, 缺省值, 格式, 值列表, 分类sql, 明细sql, 分类字段, 明细字段, 对象
        From zlTools.zlRPTPars
        Where 源id = r_Rptdatas.ID;
      Insert Into zlTools.zlRPTSQLs
        (源id, 行号, 内容)
        Select n_Dat_Id, 行号, 内容 From zlTools.zlRPTSQLs Where 源id = r_Rptdatas.ID;
    End Loop;
    Insert Into zlTools.zlRPTFMTs
      (报表id, 序号, 说明, W, H, 纸张, 纸向, 动态纸张, 图样)
      Select n_Rpt_Id, 序号, 说明, W, H, 纸张, 纸向, 动态纸张, 图样 From zlTools.zlRPTFMTs Where 报表id = n_Mdl_Id;
    For r_Rptitems In (Select ID From zlTools.zlRPTItems Where 报表id = n_Mdl_Id Order By ID) Loop
      Insert Into zlTools.zlRPTItems
        (ID, 报表id, 格式号, 名称, 类型, 上级id, 序号, 参照, 性质, 内容, 表头, X, Y, W, H, 行高, 对齐, 自调, 字体, 字号, 粗体, 斜体, 下线, 前景, 背景, 边框, 排序,
         格式, 汇总, 分栏, 网格, 系统)
        Select zlTools.Zlrptitems_Id.Nextval, n_Rpt_Id, 格式号, 名称, 类型, zlTools.Zlrptitems_Id.Nextval - (ID - 上级id), 序号, 参照,
               性质, 内容, 表头, X, Y, W, H, 行高, 对齐, 自调, 字体, 字号, 粗体, 斜体, 下线, 前景, 背景, 边框, 排序, 格式, 汇总, 分栏, 网格, 系统
        From zlTools.zlRPTItems
        Where ID = r_Rptitems.ID;
    End Loop;
    Update zlTools.zlRPTItems Set 内容 = 名称_In Where 报表id = n_Rpt_Id And 名称 = 'ZLBILLCAPTION';
  Exception
    When e_Mod_Lost Then
      Raise_Application_Error(-20101, '[ZLSOFT]诊疗单据模扳丢失，请联系系统管理员！[ZLSOFT]');
    When Others Then
      zl_ErrorCenter(SQLCode, SQLErrM);
  End p_Add_Report;

  --主过程
Begin
  n_系统 := f_Cur_Sys;
  If 操作_In = 1 Then
    Insert Into 病历页面格式 (种类, 编号, 名称) Values (7, 编号_In, 名称_In);
    Insert Into 病历文件列表
      (ID, 种类, 编号, 名称, 说明, 保留, 通用, 页面,子类, 格式)
    Values
      (Id_In, 7, 编号_In, 名称_In, 说明_In, 保留_In, 通用_In, 编号_In,子类_In, 格式_In);
    p_Append_Items;
    p_Add_Report(1);
    --If 通用_In = 2 Then
    -- 11547 新增诊疗单据时，只要“执行后有报告”有效，新增的诊疗单据需要有对应的自定义报表
    p_Add_Report(2);
    --End If;

  Elsif 操作_In = 2 Then
    Select 编号 Into v_编号 From 病历文件列表 Where ID = Id_In;
    Update 病历页面格式 Set 编号 = 编号_In, 名称 = 名称_In Where 种类 = 7 And 编号 = v_编号;
    Update 病历文件列表
    Set 编号 = 编号_In, 名称 = 名称_In, 说明 = 说明_In, 通用 = 通用_In, 子类=子类_In, 格式 = 格式_In
    Where 种类 = 7 And ID = Id_In;
    p_Append_Items;

    Update zlTools.zlReports
    Set 编号 = 'ZLCISBILL00' || 编号_In || '-1', 名称 = 名称_In, 说明 = 说明_In, 密码 = 密码1_In
    Where 系统 = n_系统 And 编号 = 'ZLCISBILL00' || v_编号 || '-1';
    If Sql%RowCount = 0 Then
      p_Add_Report(1);
    End If;
    --If 通用_In <> 2 Then
    -- 11323 改变诊疗单据的格式,不删除自定义报表(2007-08-15 陈东)
    -- Delete zlTools.zlReports Where 系统 = n_系统 And 编号 = 'ZLCISBILL00' || v_编号 || '-2';
    --  Null;
    --Else
    Update zlTools.zlReports
    Set 编号 = 'ZLCISBILL00' || 编号_In || '-2', 名称 = 名称_In, 说明 = 说明_In, 密码 = 密码2_In
    Where 系统 = n_系统 And 编号 = 'ZLCISBILL00' || v_编号 || '-2';
    If Sql%RowCount = 0 Then
      p_Add_Report(2);
    End If;
    --End If;

  Elsif 操作_In = 3 Then
    Select 编号 Into v_编号 From 病历文件列表 Where ID = Id_In;
    Delete 病历文件列表 Where ID = Id_In;
    Delete 病历页面格式 Where 种类 = 7 And 编号 = v_编号;

    Delete zlTools.zlReports Where 系统 = n_系统 And 编号 Like 'ZLCISBILL00' || v_编号 || '-_';
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_诊疗单据目录_Edit;
/

--111553:秦龙,2017-07-21,增加传参“修改类型_In”用来判断是否修改同品种下所有规格
--111166:秦龙,2017-07-14,增加传参“是否植入耗材”
Create Or Replace Procedure Zl_卫生材料_Update
(
  诊疗id_In         In 材料特性.诊疗id%Type,
  材料id_In         In 材料特性.材料id%Type,
  编码_In           In 收费项目目录.编码%Type,
  规格_In           In 收费项目目录.规格%Type,
  产地_In           In 收费项目目录.产地%Type := Null,
  标识主码_In       In 收费项目目录.标识主码%Type := Null,
  标识子码_In       In 收费项目目录.标识子码%Type := Null,
  备选码_In         In 收费项目目录.备选码%Type := Null,
  材料来源_In       In 材料特性.材料来源%Type := Null,
  货源情况_In       In 材料特性.货源情况%Type := Null,
  散装单位_In       In 收费项目目录.计算单位%Type := Null,
  包装单位_In       In 材料特性.包装单位%Type := Null,
  换算系数_In       In 材料特性.换算系数%Type := Null,
  是否变价_In       In 收费项目目录.是否变价%Type := Null,
  指导批发价_In     In 材料特性.指导批发价%Type := Null,
  扣率_In           In 材料特性.扣率%Type := 95,
  指导零售价_In     In 材料特性.指导零售价%Type := Null,
  指导差价率_In     In 材料特性.指导差价率%Type := Null,
  费用类型_In       In 收费项目目录.费用类型%Type := Null,
  服务对象_In       In 收费项目目录.服务对象%Type := Null,
  屏蔽费别_In       In 收费项目目录.屏蔽费别%Type := 0,
  库房分批_In       In 材料特性.库房分批%Type := Null,
  在用分批_In       In 材料特性.在用分批%Type := Null,
  最大效期_In       In 材料特性.最大效期%Type := Null,
  灭菌效期_In       In 材料特性.灭菌效期%Type := Null,
  无菌性材料_In     In 材料特性.无菌性材料%Type := Null,
  一次性材料_In     In 材料特性.一次性材料%Type := Null,
  原材料_In         In 材料特性.原材料%Type := Null,
  差价让利比_In     In 材料特性.差价让利比%Type := 0,
  成本价_In         In 材料特性.成本价%Type := 0,
  跟踪在用_In       In 材料特性.跟踪在用%Type := Null,
  核算材料_In       In 材料特性.核算材料%Type := 0,
  当前售价_In       In 收费价目.现价%Type := 0,
  收入id_In         In 收费价目.收入项目id%Type := Null,
  批准文号_In       In 材料特性.批准文号%Type := Null,
  注册商标_In       In 材料特性.注册商标%Type := Null,
  注册证号_In       In 材料特性.注册证号%Type := Null,
  许可证号_In       In 材料特性.许可证号%Type := Null,
  许可证有效期_In   In 材料特性.许可证有效期%Type := Null,
  材质分类_In       In 材料特性.材质分类%Type := Null,
  存储条件_In       In 材料特性.存储条件%Type := Null,
  跟踪病人_In       In 材料特性.跟踪病人%Type := 0,
  站点_In           In 收费项目目录.站点%Type := Null,
  品名_In           In 收费项目别名.名称%Type := Null,
  拼音_In           In 收费项目别名.简码%Type := Null,
  五笔_In           In 收费项目别名.简码%Type := Null,
  增值税率_In       In 材料特性.增值税率%Type := Null,
  说明_In           In 收费项目目录.说明%Type := Null,
  高值材料_In       In 材料特性.高值材料%Type := Null,
  条码管理_In       In 材料特性.是否条码管理%Type := Null,
  病案费目_In       In 收费项目目录.病案费目%Type := Null,
  器械包卫材单件_In In 材料特性.器械包卫材单件%Type := 0,
  注册证有效期_In   In 材料特性.注册证有效期%Type := Null,
  是否植入耗材_In   In 材料特性.是否植入耗材%Type := 0,
  修改类型_In       In Number := 0 --1-同步修改品种下所有注册证号和注册证有效期
) Is
  v_Err_Msg Varchar2(200);
  Err_Item Exception;
  v_发生     Integer;
  v_跟踪在用 Integer;
  v_Count    Integer;
  v_No       收费价目.No%Type;
  v_名称     诊疗项目目录.名称%Type;
  v_Temp     收费项目目录.病案费目%Type;
  v_病案费目 收费项目目录.病案费目%Type;

Begin
  v_Err_Msg := '无';

  v_病案费目 := 病案费目_In;
  --判断病案费目 
  If v_病案费目 Is Null Then
    If 收入id_In Is Not Null Then
      Begin
        Select 病案费目 Into v_Temp From 收入项目 Where ID = 收入id_In;
      Exception
        When Others Then
          v_Temp := Null;
      End;
      If v_Temp Is Not Null Then
        v_病案费目 := v_Temp;
      End If;
    End If;
  End If;
  --修改诊疗项目 
  Begin
    Select 跟踪在用 Into v_跟踪在用 From 材料特性 Where 材料id = 材料id_In;
  Exception
    When Others Then
      v_Err_Msg := '[ZLSOFT]不存在规格材料,可能被其他用户删除了,请检查![ZLSOFT]';
  End;
  If v_Err_Msg <> '无' Then
    Raise Err_Item;
  End If;

  Begin
    Select 名称 Into v_名称 From 诊疗项目目录 Where ID = 诊疗id_In;
  Exception
    When Others Then
      v_Err_Msg := 'Err';
  End;

  If v_Err_Msg = 'Err' Then
    v_Err_Msg := '[ZLSOFT]未找到指定的材料品种，可能已被其他用户删除！[ZLSOFT]';
    Raise Err_Item;
  End If;

  --如果更新前的材料为跟踪在用,如果改为了不跟踪则需判断库存 
  If v_跟踪在用 = 1 And 跟踪在用_In <> 1 Then
    Begin
      Select Count(*)
      Into v_Count
      From 药品库存
      Where 药品id = 材料id_In And (Nvl(可用数量, 0) <> 0 Or Nvl(实际数量, 0) <> 0 Or Nvl(实际金额, 0) <> 0 Or Nvl(实际差价, 0) <> 0);
      If v_Count <> 0 Then
        v_Err_Msg := '[ZLSOFT]该卫生材料存在库存,不能取消跟踪在用属性,请检查![ZLSOFT]';
      End If;
    Exception
      When Others Then
        Null;
    End;
  End If;

  If v_Err_Msg <> '无' Then
    Raise Err_Item;
  End If;

  --规格信息 
  Update 收费项目目录
  Set 编码 = 编码_In, 名称 = v_名称, 规格 = 规格_In, 标识主码 = 标识主码_In, 标识子码 = 标识子码_In, 备选码 = 备选码_In, 产地 = 产地_In, 是否变价 = 是否变价_In,
      计算单位 = 散装单位_In, 费用类型 = 费用类型_In, 服务对象 = 服务对象_In, 屏蔽费别 = 屏蔽费别_In, 站点 = 站点_In, 说明 = 说明_In, 病案费目 = v_病案费目
  Where ID = 材料id_In;

  If Sql%RowCount = 0 Then
    v_Err_Msg := '[ZLSOFT]该卫生材料可能被其他用户删除了,请检查![ZLSOFT]';
    Raise Err_Item;
  End If;

  --材料特性 
  Update 材料特性
  Set 最大效期 = 最大效期_In, 灭菌效期 = 灭菌效期_In, 无菌性材料 = 无菌性材料_In, 一次性材料 = 一次性材料_In, 原材料 = 原材料_In, 货源情况 = 货源情况_In, 包装单位 = 包装单位_In,
      换算系数 = 换算系数_In, 指导批发价 = 指导批发价_In, 指导零售价 = 指导零售价_In, 指导差价率 = 指导差价率_In, 扣率 = 扣率_In, 库房分批 = 库房分批_In, 在用分批 = 在用分批_In,
      材料来源 = 材料来源_In, 差价让利比 = 差价让利比_In, 成本价 = 成本价_In, 跟踪在用 = 跟踪在用_In, 核算材料 = 核算材料_In, 批准文号 = 批准文号_In, 注册商标 = 注册商标_In,
      注册证号 = 注册证号_In, 注册证有效期 = 注册证有效期_In, 材质分类 = 材质分类_In, 存储条件 = 存储条件_In, 许可证号 = 许可证号_In, 许可证有效期 = 许可证有效期_In,
      诊疗id = 诊疗id_In, 跟踪病人 = 跟踪病人_In, 增值税率 = 增值税率_In, 高值材料 = 高值材料_In, 是否条码管理 = 条码管理_In, 器械包卫材单件 = 器械包卫材单件_In,
      是否植入耗材 = 是否植入耗材_In
  Where 材料id = 材料id_In;

  --同步修改改品种下所有规格
  If 修改类型_In = 1 Then
    Update 材料特性 Set 注册证号 = 注册证号_In, 注册证有效期 = 注册证有效期_In Where 诊疗id = 诊疗id_In;
  End If;

  --别名的处理 
  Delete 收费项目别名 Where 收费细目id = 材料id_In And 性质 = 1;

  Insert Into 收费项目别名
    (收费细目id, 名称, 性质, 简码, 码类)
    Select 材料id_In, 名称, 性质, 简码, 码类 From 诊疗项目别名 Where 诊疗项目id = 诊疗id_In And 性质 = 1;

  If 品名_In Is Null Then
    Delete 收费项目别名 Where 收费细目id = 材料id_In And 性质 = 3;
  Else
    If 拼音_In Is Null Then
      Delete 收费项目别名 Where 收费细目id = 材料id_In And 性质 = 3 And 码类 = 1;
    Else
      Update 收费项目别名 Set 名称 = 品名_In, 简码 = 拼音_In Where 收费细目id = 材料id_In And 性质 = 3 And 码类 = 1;
      If Sql%RowCount = 0 Then
        Insert Into 收费项目别名 (收费细目id, 名称, 性质, 简码, 码类) Values (材料id_In, 品名_In, 3, 拼音_In, 1);
      End If;
    End If;
    If 五笔_In Is Null Then
      Delete 收费项目别名 Where 收费细目id = 材料id_In And 性质 = 3 And 码类 = 2;
    Else
      Update 收费项目别名 Set 名称 = 品名_In, 简码 = 五笔_In Where 收费细目id = 材料id_In And 性质 = 3 And 码类 = 2;
      If Sql%RowCount = 0 Then
        Insert Into 收费项目别名 (收费细目id, 名称, 性质, 简码, 码类) Values (材料id_In, 品名_In, 3, 五笔_In, 2);
      End If;
    End If;
  End If;

  --定价信息：如果已经有发生，则不允许直接更改这些信息 
  Select Nvl(Count(*), 0) Into v_发生 From 药品收发记录 Where 药品id = 材料id_In And Rownum < 2;

  If v_发生 = 0 Then
    Update 收费项目目录 Set 是否变价 = 是否变价_In Where ID = 材料id_In;
    Update 材料特性 Set 成本价 = 成本价_In Where 材料id = 材料id_In;
  
    If 收入id_In Is Not Null Then
      Update 收费价目
      Set 现价 = 当前售价_In, 缺省价格 = Decode(跟踪在用_In, 0, Decode(是否变价_In, 1, 当前售价_In, 缺省价格), 缺省价格), 收入项目id = 收入id_In, 变动原因 = 1,
          调价说明 = '修改定价', 调价人 = User
      Where 收费细目id = 材料id_In
           --And (终止日期 Is Null Or 终止日期=to_date('3000-01-01','YYYY-MM-DD')); 
            And (Sysdate Between 执行日期 And 终止日期 Or Sysdate >= 执行日期 And 终止日期 Is Null) And 变动原因 = 1;
    
      If Sql%RowCount = 0 Then
        v_No := Nextno(9);
        Insert Into 收费价目
          (ID, 原价id, 收费细目id, 原价, 现价, 缺省价格, 收入项目id, 变动原因, 调价说明, 调价人, 执行日期, 终止日期, NO, 序号)
        Values
          (收费价目_Id.Nextval, Null, 材料id_In, 0, 当前售价_In,
           Decode(跟踪在用_In, 0, Decode(是否变价_In, 1, 当前售价_In, Null), Null), 收入id_In, 1, '新增定价', User, Sysdate,
           To_Date('3000-01-01', 'YYYY-MM-DD'), v_No, 1);
      End If;
    End If;
  Else
    --有业务单据后不能直接修改价格，但是可以修改收入项目 
    Update 收费价目
    Set 收入项目id = 收入id_In
    Where 收费细目id = 材料id_In And (Sysdate Between 执行日期 And 终止日期 Or Sysdate >= 执行日期 And 终止日期 Is Null) And 变动原因 = 1;
  End If;

  --材料生产商比较增加 
  If 产地_In Is Not Null Then
    Update 材料生产商 Set 名称 = 产地_In Where 名称 = 产地_In;
    If Sql%RowCount = 0 Then
      Insert Into 材料生产商
        (编码, 名称, 简码)
        Select Nvl(Max(To_Number(编码)), 0) + 1, 产地_In, zlSpellCode(产地_In) From 材料生产商;
    End If;
  End If;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_卫生材料_Update;
/

--111166:秦龙,2017-07-14,增加传参“是否植入耗材”
Create Or Replace Procedure Zl_卫生材料_Insert
(
  诊疗id_In         In 材料特性.诊疗id%Type,
  材料id_In         In 材料特性.材料id%Type,
  编码_In           In 收费项目目录.编码%Type,
  规格_In           In 收费项目目录.规格%Type,
  产地_In           In 收费项目目录.产地%Type := Null,
  标识主码_In       In 收费项目目录.标识主码%Type := Null,
  标识子码_In       In 收费项目目录.标识子码%Type := Null,
  备选码_In         In 收费项目目录.备选码%Type := Null,
  材料来源_In       In 材料特性.材料来源%Type := Null,
  货源情况_In       In 材料特性.货源情况%Type := Null,
  散装单位_In       In 收费项目目录.计算单位%Type := Null,
  包装单位_In       In 材料特性.包装单位%Type := Null,
  换算系数_In       In 材料特性.换算系数%Type := Null,
  是否变价_In       In 收费项目目录.是否变价%Type := Null,
  指导批发价_In     In 材料特性.指导批发价%Type := Null,
  扣率_In           In 材料特性.扣率%Type := 95,
  指导零售价_In     In 材料特性.指导零售价%Type := Null,
  指导差价率_In     In 材料特性.指导差价率%Type := Null,
  费用类型_In       In 收费项目目录.费用类型%Type := Null,
  服务对象_In       In 收费项目目录.服务对象%Type := Null,
  屏蔽费别_In       In 收费项目目录.屏蔽费别%Type := 0,
  库房分批_In       In 材料特性.库房分批%Type := Null,
  在用分批_In       In 材料特性.在用分批%Type := Null,
  最大效期_In       In 材料特性.最大效期%Type := Null,
  灭菌效期_In       In 材料特性.灭菌效期%Type := Null,
  无菌性材料_In     In 材料特性.无菌性材料%Type := Null,
  一次性材料_In     In 材料特性.一次性材料%Type := Null,
  原材料_In         In 材料特性.原材料%Type := Null,
  差价让利比_In     In 材料特性.差价让利比%Type := 0,
  成本价_In         In 材料特性.成本价%Type := 0,
  跟踪在用_In       In 材料特性.跟踪在用%Type := Null,
  核算材料_In       In 材料特性.核算材料%Type := 0,
  当前售价_In       In 收费价目.现价%Type := 0,
  收入id_In         In 收费价目.收入项目id%Type := Null,
  批准文号_In       In 材料特性.批准文号%Type := Null,
  注册商标_In       In 材料特性.注册商标%Type := Null,
  注册证号_In       In 材料特性.注册证号%Type := Null,
  许可证号_In       In 材料特性.许可证号%Type := Null,
  许可证有效期_In   In 材料特性.许可证有效期%Type := Null,
  材质分类_In       In 材料特性.材质分类%Type := Null,
  存储条件_In       In 材料特性.存储条件%Type := Null,
  跟踪病人_In       In 材料特性.跟踪病人%Type := 0,
  站点_In           In 收费项目目录.站点%Type := Null,
  品名_In           In 收费项目别名.名称%Type := Null,
  拼音_In           In 收费项目别名.简码%Type := Null,
  五笔_In           In 收费项目别名.简码%Type := Null,
  增值税率_In       In 材料特性.增值税率%Type := Null,
  说明_In           In 收费项目目录.说明%Type := Null,
  高值材料_In       In 材料特性.高值材料%Type := Null,
  条码管理_In       In 材料特性.是否条码管理%Type := Null,
  病案费目_In       In 收费项目目录.病案费目%Type := Null,
  器械包卫材单件_In In 材料特性.器械包卫材单件%Type := 0,
  注册证有效期_In   In 材料特性.注册证有效期%Type := Null,
  是否植入耗材_In   In 材料特性.是否植入耗材%Type := 0
) Is
  Err_Item Exception;
  v_Err_Msg Varchar2(100);

  v_No       收费价目.No%Type;
  v_名称     诊疗项目目录.名称%Type;
  v_Temp     收费项目目录.病案费目%Type;
  v_病案费目 收费项目目录.病案费目%Type;

  Cursor c_Item Is
    Select ID
    From 部门表 D
    Where ID In (Select Distinct 部门id
                 From 部门性质说明 A
                 Where 工作性质 In ('发料部门', '物资库房', '卫材库', '制剂室', '虚拟库房'));
Begin
  v_Err_Msg := 'NO';

  v_病案费目 := 病案费目_In;
  --判断病案费目 
  If v_病案费目 Is Null Then
    If 收入id_In Is Not Null Then
      Begin
        Select 病案费目 Into v_Temp From 收入项目 Where ID = 收入id_In;
      Exception
        When Others Then
          v_Temp := Null;
      End;
      If v_Temp Is Not Null Then
        v_病案费目 := v_Temp;
      End If;
    End If;
  End If;
  Begin
    Select 名称
    Into v_名称
    From 诊疗项目目录
    Where ID = 诊疗id_In And (撤档时间 Is Null Or To_Char(撤档时间, 'yyyy-mm-dd') = '3000-01-01');
  Exception
    When Others Then
      v_Err_Msg := 'Err';
  End;
  If v_Err_Msg = 'Err' Then
    v_Err_Msg := '[ZLSOFT]未找到指定的材料品种，可能该品种已被其他用户删除或停用！[ZLSOFT]';
    Raise Err_Item;
  End If;
  --规格信息 
  Insert Into 收费项目目录
    (类别, ID, 编码, 名称, 规格, 产地, 标识主码, 标识子码, 备选码, 计算单位, 费用类型, 服务对象, 屏蔽费别, 是否变价, 站点, 建档时间, 撤档时间, 说明, 病案费目)
  Values
    (4, 材料id_In, 编码_In, v_名称, 规格_In, 产地_In, 标识主码_In, 标识子码_In, 备选码_In, 散装单位_In, 费用类型_In, 服务对象_In, 屏蔽费别_In, 是否变价_In,
     站点_In, Sysdate, To_Date('3000-01-01', 'YYYY-MM-DD'), 说明_In, v_病案费目);

  --材料特性 
  Insert Into 材料特性
    (材料id, 诊疗id, 最大效期, 灭菌效期, 无菌性材料, 一次性材料, 原材料, 货源情况, 包装单位, 换算系数, 指导批发价, 指导零售价, 指导差价率, 扣率, 库房分批, 在用分批, 材料来源, 差价让利比, 成本价,
     跟踪在用, 核算材料, 批准文号, 注册商标, 注册证号, 注册证有效期, 许可证号, 许可证有效期, 材质分类, 存储条件, 跟踪病人, 增值税率, 高值材料, 是否条码管理, 器械包卫材单件, 是否植入耗材)
  Values
    (材料id_In, 诊疗id_In, 最大效期_In, 灭菌效期_In, 无菌性材料_In, 一次性材料_In, 原材料_In, 货源情况_In, 包装单位_In, 换算系数_In, 指导批发价_In, 指导零售价_In,
     指导差价率_In, 扣率_In, 库房分批_In, 在用分批_In, 材料来源_In, 差价让利比_In, 成本价_In, 跟踪在用_In, 核算材料_In, 批准文号_In, 注册商标_In, 注册证号_In,
     注册证有效期_In, 许可证号_In, 许可证有效期_In, 材质分类_In, 存储条件_In, 跟踪病人_In, 增值税率_In, 高值材料_In, 条码管理_In, 器械包卫材单件_In, 是否植入耗材_In);

  --别名的处理 
  Insert Into 收费项目别名
    (收费细目id, 名称, 性质, 简码, 码类)
    Select 材料id_In, 名称, 性质, 简码, 码类 From 诊疗项目别名 Where 诊疗项目id = 诊疗id_In;
  If (品名_In Is Not Null) And (拼音_In Is Not Null) Then
    Insert Into 收费项目别名 (收费细目id, 名称, 性质, 简码, 码类) Values (材料id_In, 品名_In, 3, 拼音_In, 1);
  End If;
  If (品名_In Is Not Null) And (五笔_In Is Not Null) Then
    Insert Into 收费项目别名 (收费细目id, 名称, 性质, 简码, 码类) Values (材料id_In, 品名_In, 3, 五笔_In, 2);
  End If;

  For r_Item In c_Item Loop
    Insert Into 材料储备限额 (库房id, 材料id, 上限, 下限, 盘点属性) Values (r_Item.Id, 材料id_In, 0, 0, '1111');
  End Loop;
  --定价信息 
  If 收入id_In Is Not Null Then
    v_No := Nextno(9);
    --非跟踪在用的时价卫材，在销售时相当于一般的收费项目，在调价时应按”最低限价、最高限价、缺省价格”进行设置。 
    Insert Into 收费价目
      (ID, 原价id, 收费细目id, 原价, 现价, 缺省价格, 收入项目id, 变动原因, 调价说明, 调价人, 执行日期, 终止日期, NO, 序号)
    Values
      (收费价目_Id.Nextval, Null, 材料id_In, 0, 当前售价_In,
       Decode(跟踪在用_In, 0, Decode(是否变价_In, 1, 当前售价_In, Null), Null), 收入id_In, 1, '新增定价', User, Sysdate,
       To_Date('3000-01-01', 'YYYY-MM-DD'), v_No, 1);
  End If;

  --材料生产商比较增加 
  If 产地_In Is Not Null Then
    Update 材料生产商 Set 名称 = 产地_In Where 名称 = 产地_In;
    If Sql%RowCount = 0 Then
      Insert Into 材料生产商
        (编码, 名称, 简码)
        Select Nvl(Max(To_Number(编码)), 0) + 1, 产地_In, zlSpellCode(产地_In) From 材料生产商;
    End If;
  End If;

  --插入材料的服务科室 
  Insert Into 收费执行科室
    (收费细目id, 病人来源, 开单科室id, 执行科室id)
    Select 材料id_In, 病人来源, 开单科室id, 执行科室id From 诊疗执行科室 Where 诊疗项目id = 诊疗id_In;

Exception
  When Err_Item Then
    Raise_Application_Error(-20101, v_Err_Msg);
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_卫生材料_Insert;
/

--100165:秦龙,2017-07-14,增加传参“类型”
Create Or Replace Procedure Zl_药品加成方案_Insert
(
  序号_In   药品加成方案.序号%Type,
  最低价_In 药品加成方案.最低价%Type,
  最高价_In 药品加成方案.最高价%Type,
  加成率_In 药品加成方案.加成率%Type,
  差价额_In 药品加成方案.差价额%Type := Null,
  说明_In   药品加成方案.说明%Type := Null,
  类型_In   药品加成方案.类型%Type := 0
) Is
Begin
  Insert Into 药品加成方案
    (序号, 最低价, 最高价, 加成率, 差价额, 说明, 类型)
  Values
    (序号_In, 最低价_In, 最高价_In, 加成率_In, 差价额_In, 说明_In, 类型_In);
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品加成方案_Insert;
/

--111584:李业庆,2017-07-13,高值卫材按批次出库修改
Create Or Replace Procedure Zl_药品收发记录_销售出库
(
  Id_In           In 门诊费用记录.Id%Type,
  药品摘要_In     药品收发记录.摘要%Type := Null,
  频次_In         药品收发记录.频次%Type := Null,
  单量_In         药品收发记录.单量%Type := Null,
  用法_In         药品收发记录.用法%Type := Null,
  煎法_In         药品收发记录.外观%Type := Null,
  期效_In         药品收发记录.扣率%Type := Null,
  计价特性_In     药品收发记录.扣率%Type := Null,
  主页id_In       未发药品记录.主页id%Type := Null,
  备货材料_In     Number := 0,
  备货材料批次_In 药品收发记录.批次%Type := Null
) Is
  ----------------------------------
  --功能：收费、划价时按照参数设置分解药品并产生相应的收发记录
  --规则：
  --      1、循环游标判断总出库数量与游标中每条记录数量是否充足，如果充足就是总数量，不充足挨个遍历直到数量直到遍历完并退出
  --      2、金额计算方式：定价取收费价目表现价，时价分批取库存表零售价，时价不分批，零售金额/实际数量，并将所有批次的金额累加起来为总出库金额
  --参数：
  --      Id_In：门诊费用记录或者住院费用记录ID
  --      备货材料_In：只有高值卫材才需要传入，非0表示是高值卫材模式
  --      备货材料批次_In：支持高值卫材扫码确定批次出库，所以35.70支持材料非备货材料模式按批次出库；药品不支持这种模式，即药品批次都传空
  --      药品摘要_In：可选参数
  --      频次_In；单量_In；用法_In；煎法_In；期效_In；计价特性_In，可选参数，医嘱记录产生
  -----------------------------------
  Cursor c_Stock
  (
    n_Outmode  Number,
    n_库房id   药品收发记录.库房id%Type,
    n_药品id   药品收发记录.药品id%Type,
    n_备货材料 Number,
    n_备货批次 药品收发记录.批次%Type
  ) Is
    Select 库房id, 药品id, 批次, 效期, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次供应商id, 上次采购价, 上次批号, 上次生产日期, 上次产地, 灭菌效期, 批准文号, 平均成本价, 零售价,
           上次扣率, 商品条码, 内部条码
    From 药品库存
    Where 药品id = n_药品id And 库房id = n_库房id And 性质 = 1 And
          Decode(n_备货批次, Null, 0, Nvl(批次, 0)) = Decode(n_备货批次, Null, 0, Nvl(n_备货批次, 0)) And
          (Nvl(批次, 0) = 0 Or 效期 Is Null Or 效期 > Trunc(Sysdate)) And Nvl(可用数量, 0) > 0
    Order By Decode(n_Outmode, 1, 效期, Null), Nvl(批次, 0);
  r_Stock c_Stock%RowType;

  n_Outmode      Number;
  n_分批         药品规格.药房分批%Type;
  n_时价         收费项目目录.是否变价%Type;
  n_当前数量     药品库存.实际数量%Type;
  n_费用金额小数 Number;
  n_费用单价小数 Number;
  n_流通金额小数 Number;
  n_流通单价小数 Number;
  n_标准单价     收费价目.现价%Type;
  n_当前单价     收费价目.现价%Type;
  n_类别         药品单据性质.类别id%Type;
  n_总金额       Number;
  n_总数量       药品库存.实际数量%Type;
  n_单据         药品单据性质.单据%Type;
  n_跟踪在用     材料特性.跟踪在用%Type;
  n_序号         门诊费用记录.序号%Type;
  v_名称         收费项目目录.名称%Type;
  n_虚拟库房id   部门表.Id%Type;
  n_库房id       部门表.Id%Type;
  n_优先级       身份.优先级%Type;
  n_Count        Number;
  Err_Custom Exception;
  v_Rust     Varchar2(300);
  v_Error    Varchar2(255);
  v_部门名称 部门表.名称%Type;

  v_单据类别   Varchar2(10);
  v_No         药品收发记录.No%Type;
  n_对方部门id 药品收发记录.对方部门id%Type;
  n_收费细目id 药品收发记录.药品id%Type;
  n_总出库数量 药品库存.实际数量%Type;
  n_发药库房id 药品收发记录.库房id%Type;
  n_记录性质   门诊费用记录.记录性质%Type;
  v_收费类别   门诊费用记录.收费类别%Type;
  n_多病人单   住院费用记录.多病人单%Type;
  n_医嘱序号   门诊费用记录.医嘱序号%Type;
  v_姓名       门诊费用记录.姓名%Type;
  n_付数       门诊费用记录.付数%Type;
  v_操作员     门诊费用记录.操作员姓名%Type;
  d_登记时间   门诊费用记录.登记时间%Type;
  n_门诊标志   门诊费用记录.门诊标志%Type;
  n_病人科室id 门诊费用记录.病人科室id%Type;
  n_标识号     门诊费用记录.标识号%Type;
  v_性别       门诊费用记录.性别%Type;
  n_年龄       门诊费用记录.年龄%Type;
  n_病人id     门诊费用记录.病人id%Type;
  v_发药窗口   门诊费用记录.发药窗口%Type;
  n_记录状态   门诊费用记录.记录状态%Type;

  --药品收发记录
  n_收发id   药品收发记录.Id%Type;
  n_扣率     药品收发记录.扣率%Type;
  d_灭菌效期 药品收发记录.灭菌效期%Type;
  d_灭菌日期 药品收发记录.灭菌日期%Type;

  v_其他出库no 药品收发记录.No%Type;
  n_出库序号   药品收发记录.序号%Type;
  n_定价售价   收费价目.现价%Type;
  n_出库检查   Number(1);
Begin
  Begin
    Select 类别, NO, 序号, 对方部门id, 收费细目id, 总出库数量, 发药库房id, 记录性质, 收费类别, 多病人单, 医嘱序号, 姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别,
           年龄, 病人id, 发药窗口, 记录状态, 标准单价
    Into v_单据类别, v_No, n_序号, n_对方部门id, n_收费细目id, n_总出库数量, n_发药库房id, n_记录性质, v_收费类别, n_多病人单, n_医嘱序号, v_姓名, n_付数, v_操作员,
         d_登记时间, n_门诊标志, n_病人科室id, n_标识号, v_性别, n_年龄, n_病人id, v_发药窗口, n_记录状态, n_标准单价
    From (Select '门诊' As 类别, NO, 序号, 病人科室id As 对方部门id, 收费细目id, 付数 * 数次 As 总出库数量, 执行部门id As 发药库房id, 记录性质, 收费类别, 0 As 多病人单,
                  医嘱序号, 姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别, 年龄, 病人id, 发药窗口, 记录状态, 标准单价
           From 门诊费用记录
           Where ID = Id_In
           Union All
           Select '住院' As 类别, NO, 序号, 病人科室id As 对方部门id, 收费细目id, 付数 * 数次 As 总出库数量, 执行部门id As 发药库房id, 记录性质, 收费类别, 多病人单, 医嘱序号,
                  姓名, 付数, 划价人, 登记时间, 门诊标志, 病人科室id, 标识号, 性别, 年龄, 病人id, 发药窗口, 记录状态, 标准单价
           From 住院费用记录
           Where ID = Id_In);
  Exception
    When Others Then
      v_No         := Null;
      n_对方部门id := 0;
      n_总出库数量 := 0;
  End;

  n_跟踪在用 := 0;
  --只处理有数量的
  If n_总出库数量 <> 0 Then
    --药品分批出库方式
    Select Zl_To_Number(Nvl(zl_GetSysParameter(150), 0)) Into n_Outmode From Dual;
    --金额小数位数
    Select Zl_To_Number(Nvl(zl_GetSysParameter(9), '2')), Zl_To_Number(Nvl(zl_GetSysParameter(157), '5'))
    Into n_费用金额小数, n_费用单价小数
    From Dual;
  
    --取流通业务精度位数
    --类别:1-药品 2-卫材
    --内容：2-零售价 4-金额
    --单位：药品:1-售价 5-金额单位
    If v_收费类别 = '4' Then
      Select 精度 Into n_流通单价小数 From 药品卫材精度 Where 类别 = 2 And 内容 = 2 And 单位 = 1;
      Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 2 And 内容 = 4 And 单位 = 5;
    Else
      Select 精度 Into n_流通单价小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 2 And 单位 = 1;
      Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5;
    End If;
  
    n_总数量 := n_总出库数量;
  
    If v_收费类别 = '4' Then
      --收费类别=4表示是卫材单据
      If v_单据类别 = '门诊' Then
        If n_记录性质 = 1 Then
          n_单据 := 24;
        Else
          n_单据 := 25;
        End If;
      Elsif v_单据类别 = '住院' Then
        If n_多病人单 = 1 Then
          n_单据 := 26;
        Else
          n_单据 := 25;
        End If;
      End If;
    
      Select Nvl(a.在用分批, 0), Nvl(b.是否变价, 0), b.名称, c.现价
      Into n_分批, n_时价, v_名称, n_定价售价
      From 材料特性 A, 收费项目目录 B, 收费价目 C
      Where a.材料id = b.Id And b.Id = n_收费细目id And b.Id = c.收费细目id And Sysdate Between c.执行日期 And c.终止日期;
      --跟踪在用
      Select 跟踪在用 Into n_跟踪在用 From 材料特性 Where 材料id = n_收费细目id;
      --备货卫材需要判断是否设置了虚拟库房对照
      If Nvl(备货材料_In, 0) = 1 Then
        Begin
          Select 虚拟库房id Into n_虚拟库房id From 虚拟库房对照 Where 科室id = n_发药库房id And Rownum <= 1;
        Exception
          When Others Then
            n_虚拟库房id := 0;
        End;
        If Nvl(n_虚拟库房id, 0) = 0 Then
          Begin
            Select 名称 Into v_Error From 部门表 Where ID = n_发药库房id;
          Exception
            When Others Then
              v_Error := '';
          End;
          v_Error := '执行部门"' || Nvl(v_Error, '') || '"未设置虚拟部门,请在卫材参数设置中设置.';
          Raise Err_Custom;
        End If;
      End If;
    Else
      --收费类别<>4表示是药品单据，收费类别有"5，6，7"
      If v_单据类别 = '门诊' Then
        If n_记录性质 = 1 Then
          n_单据 := 8;
        Else
          n_单据 := 9;
        End If;
      Elsif v_单据类别 = '住院' Then
        If n_多病人单 = 1 Then
          n_单据 := 10;
        Else
          n_单据 := 9;
        End If;
      End If;
    
      Select Nvl(a.药房分批, 0), Nvl(b.是否变价, 0), b.名称, c.现价
      Into n_分批, n_时价, v_名称, n_定价售价
      From 药品规格 A, 收费项目目录 B, 收费价目 C
      Where a.药品id = b.Id And b.Id = n_收费细目id And b.Id = c.收费细目id And Sysdate Between c.执行日期 And c.终止日期;
    End If;
  
    --可能分批时价药品分解的批次变了
    If n_时价 = 1 Then
      --只有一个批次时,直接取该批次的单价
      --按照最小单位进行格式化
    
      If Nvl(备货材料_In, 0) = 1 And v_收费类别 = '4' Then
        v_Rust := Zl_Fun_Getprice(n_收费细目id, n_虚拟库房id, n_总出库数量, 备货材料_In, 备货材料批次_In);
      Else
        v_Rust := Zl_Fun_Getprice(n_收费细目id, n_发药库房id, n_总出库数量, 备货材料_In, 备货材料批次_In);
      End If;
      n_当前单价 := To_Number(Substr(v_Rust, 1, Instr(v_Rust, '|') - 1));
    
      If Round(n_当前单价, n_费用单价小数) <> Round(n_标准单价, n_费用单价小数) Then
        If n_医嘱序号 Is Null Then
          If v_收费类别 = '4' Then
            v_Error := '第 ' || n_序号 || ' 行的时价卫生材料"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          Else
            v_Error := '第 ' || n_序号 || ' 行的时价药品"' || v_名称 || '"当前计算单价不一致,请重新输入数量计算！';
          End If;
        Else
          If v_收费类别 = '4' Then
            v_Error := '在处理病人"' || v_姓名 || '"时发现时价卫生材料"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                       '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          Else
            v_Error := '在处理病人"' || v_姓名 || '"时发现时价药品"' || v_名称 || '"当前计算的单价发生变化。' || Chr(13) || Chr(10) ||
                       '请检查该病人是否同时使用了两笔相同的"' || v_名称 || '"！';
          End If;
        End If;
        Raise Err_Custom;
      End If;
    End If;
  
    If v_收费类别 In ('5', '6', '7') Or (v_收费类别 = '4' And Nvl(n_跟踪在用, 0) = 1) Then
      If Nvl(备货材料_In, 0) = 1 And v_收费类别 = '4' Then
        n_库房id := n_虚拟库房id;
      Else
        n_库房id := n_发药库房id;
      End If;
    
      Begin
        If v_收费类别 In ('5', '6', '7') Then
          Select 检查方式 Into n_出库检查 From 药品出库检查 Where 库房id = n_库房id;
        Else
          Select 检查方式 Into n_出库检查 From 材料出库检查 Where 库房id = n_库房id;
        End If;
      Exception
        When Others Then
          n_出库检查 := 0;
      End;
    
      If v_收费类别 = '4' Then
        Select 类别id Into n_类别 From 药品单据性质 Where 单据 = n_单据 + 16;
      Else
        Select 类别id Into n_类别 From 药品单据性质 Where 单据 = n_单据;
      End If;
    
      n_总金额 := 0;
      --打开游标
      Open c_Stock(n_Outmode, n_库房id, n_收费细目id, 备货材料_In, 备货材料批次_In);
      --循环遍历
      While n_总出库数量 <> 0 Loop
        Fetch c_Stock
          Into r_Stock;
        If c_Stock%NotFound Then
          --第一次就没有库存,分批或时价都不允许。
          --分批药品数量分解不完,也就是库存不足。
          If n_分批 = 1 Or n_时价 = 1 Then
            Close c_Stock;
            If n_单据 = 8 Or n_单据 = 24 Then
              If v_收费类别 = '4' Then
                v_Error := '第 ' || n_序号 || ' 行的分批或时价卫生材料"' || v_名称 || '"没有可用的库存！';
              Else
                v_Error := '第 ' || n_序号 || ' 行的分批或时价药品"' || v_名称 || '"没有可用的药品库存！';
              End If;
            Else
              --单据=9，10，25，26是记账单提示不一样
              If n_医嘱序号 Is Null Then
                If v_收费类别 = '4' Then
                  If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                    v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                  Else
                    v_Error := '第 ' || n_序号 || ' 行的分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                                 When Nvl(备货材料_In, 0) = 0 Then
                                  '！'
                                 Else
                                  ',不能进行备货记帐！'
                               End;
                  End If;
                Else
                  v_Error := '第 ' || n_序号 || ' 行的分批或时价药品"' || v_名称 || '"没有足够的库存！';
                End If;
              Else
                If v_收费类别 = '4' Then
                  If Nvl(备货材料_In, 0) = 1 And Not (n_分批 = 1 Or n_时价 = 1) Then
                    v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                  Else
                    v_Error := '在处理病人"' || v_姓名 || '"时发现分批或时价卫生材料"' || v_名称 || '"没有足够的材料库存' || Case
                                 When Nvl(备货材料_In, 0) = 0 Then
                                  '！'
                                 Else
                                  ',不能进行备货记帐！'
                               End;
                  End If;
                Else
                  v_Error := '在处理病人"' || v_姓名 || '"时发现分批或时价药品"' || v_名称 || '"没有足够的库存！';
                End If;
              End If;
            End If;
            Raise Err_Custom;
          End If;
        Elsif (n_分批 = 1 And Nvl(r_Stock.批次, 0) = 0) Or (n_分批 = 0 And Nvl(r_Stock.批次, 0) <> 0) Then
          Close c_Stock;
          If n_医嘱序号 Is Null Then
            If v_收费类别 = '4' Then
              v_Error := '第 ' || n_序号 || ' 行卫生材料"' || v_名称 || '"的在用分批属性与库存记录不相符,请检查材料数据的正确性！';
            Else
              v_Error := '第 ' || n_序号 || ' 行药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
            End If;
          Else
            If v_收费类别 = '4' Then
              v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"的分批属性与库存记录不相符,请检查材料数据的正确性！';
            Else
              v_Error := '在处理病人"' || v_姓名 || '"时发现药品"' || v_名称 || '"的分批属性与库存记录不相符,请检查药品数据的正确性！';
            End If;
          End If;
          Raise Err_Custom;
        End If;
      
        If c_Stock%Found Then
          If Nvl(r_Stock.实际数量, 0) = 0 And (n_总出库数量 > 0 Or n_时价 = 1) And n_出库检查 = 2 Then
            --实际数量为零时，如果严格控制库存，不允许出库
            --实际数量不为零，金额为零，可能是正常的零价格管理。
            --负数的情况相当于入库,这种情况应是允许的；但时价需要计算价格，必须要有实际数量。
            Close c_Stock;
            If n_医嘱序号 Is Null Then
              If v_收费类别 = '4' Then
                v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
              Else
                v_Error := '第 ' || n_序号 || ' 行药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
              End If;
            Else
              If v_收费类别 = '4' Then
                v_Error := '在处理病人"' || v_姓名 || '"时发现卫生材料"' || v_名称 || '"当前无库存实际数量，可能存在尚未退料的记录，当前不能出库。';
              Else
                v_Error := '在处理病人"' || v_姓名 || '"时发现药品"' || v_名称 || '"当前无库存实际数量，可能存在尚未退药的记录，当前不能出库。';
              End If;
            End If;
            Raise Err_Custom;
          End If;
        End If;
      
        If n_分批 = 1 Or n_时价 = 1 Then
          --对于不分批的时价只可能分解一次,分解不完上面判断了.它分解是为了计算单价.
          --每次分解取小者,库存不够分解不完在上面判断.
          If n_总出库数量 <= Nvl(r_Stock.可用数量, 0) Then
            n_当前数量 := n_总出库数量;
          Else
            n_当前数量 := Nvl(r_Stock.可用数量, 0);
          End If;
          If n_时价 = 1 Then
            n_当前单价 := Nvl(r_Stock.零售价, Nvl(r_Stock.实际金额 / r_Stock.实际数量, 0));
          Elsif n_分批 = 1 Then
            n_当前单价 := n_定价售价;
          End If;
        Else
          --定价不分批
          --非门诊单据且是高值卫材需要检查库存
          If n_单据 <> 8 Or n_单据 <> 24 Then
            If Nvl(备货材料_In, 0) = 1 And v_收费类别 = '4' Then
              If n_总出库数量 > Nvl(r_Stock.可用数量, 0) Then
                --不分批, 但又是备货卫材方式出库的,则需要检查当前库存是否充足.
                v_Error := '第 ' || n_序号 || ' 行的卫生材料"' || v_名称 || '"没有足够的材料库存,不能进行备货记帐！';
                Raise Err_Custom;
              End If;
            End If;
          End If;
          n_当前数量 := n_总出库数量;
          n_当前单价 := n_定价售价;
        End If;
      
        --药品收发记录
        If c_Stock%Found Then
          --卫材灭菌效期:一次性材料且有效期
          If v_收费类别 = '4' Then
            n_Count := 0;
            Begin
              Select 灭菌效期 Into n_Count From 材料特性 Where Nvl(一次性材料, 0) = 1 And 材料id = n_收费细目id;
            Exception
              When Others Then
                Null;
            End;
            If Nvl(n_Count, 0) > 0 Then
              d_灭菌效期 := r_Stock.灭菌效期;
              d_灭菌日期 := d_灭菌效期 - n_Count * 30;
            End If;
          End If;
        End If;
      
        Select Nvl(Max(序号), 0) + 1 Into n_序号 From 药品收发记录 Where 单据 = n_单据 And 记录状态 = 1 And NO = v_No;
      
        n_扣率 := Null;
        If 期效_In Is Not Null Or 计价特性_In Is Not Null Then
          n_扣率 := Nvl(期效_In, 0) || Nvl(计价特性_In, 0);
        End If;
      
        --分批药品,如果是只使用了一个批次,则要填写付数
        If n_分批 = 1 And n_当前数量 <> n_总数量 Then
          n_Count := 1;
        Else
          n_Count := 0;
        End If;
      
        Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        --修改的原单据号存放在摘要中
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
           填制日期, 费用id, 频次, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
        Values
          (n_收发id, 1, n_单据, v_No, n_序号, n_发药库房id, n_对方部门id, n_类别, -1, n_收费细目id, r_Stock.批次, r_Stock.上次产地, r_Stock.上次批号,
           r_Stock.效期, Decode(n_Count, 1, 1, n_付数), Decode(n_Count, 1, n_当前数量, n_当前数量 / n_付数),
           Decode(n_Count, 1, n_当前数量, n_当前数量 / n_付数), n_当前单价, Round(n_当前单价 * n_当前数量, n_流通金额小数), 药品摘要_In, v_操作员, d_登记时间,
           Id_In, 频次_In, 单量_In, 用法_In, 煎法_In, n_扣率, d_灭菌效期, d_灭菌日期, r_Stock.上次供应商id, r_Stock.上次生产日期, r_Stock.批准文号,
           r_Stock.商品条码, r_Stock.内部条码);
      
        --药品库存(普通情况可能没有记录)
        Zl_药品库存_Update(n_收发id, 0, 1);
      
        --产生其他出库单 ，只有高值卫材才需要处理
        If v_收费类别 = '4' And Nvl(备货材料_In, 0) = 1 Then
          Begin
            Select Max(a.No), Max(a.序号)
            Into v_其他出库no, n_出库序号
            From 药品收发记录 A, 住院费用记录 B
            Where a.费用id = b.Id And b.No = v_No And 记录性质 = 2 And b.门诊标志 = n_门诊标志 And
                  Instr(',8,9,10,21,24,25,26,', ',' || a.单据 || ',') > 0;
          Exception
            When Others Then
              v_其他出库no := Null;
          End;
          If v_其他出库no Is Null Then
            v_其他出库no := Nextno(74, n_虚拟库房id, Null, 1);
          End If;
          If v_其他出库no Is Null Then
            v_Error := '在生成卫生材料的其他出库单时,获取相关的出库NO有误,请检查出库单的规则是否有误!';
            Raise Err_Custom;
          End If;
          If Nvl(n_病人科室id, 0) <> 0 Then
            Select 名称 Into v_部门名称 From 部门表 Where ID = n_病人科室id;
          End If;
          v_Error := LPad(' ', 4);
          v_Error := Substr('病人姓名:' || v_姓名 || v_Error || '性别:' || v_性别 || v_Error || '年龄' || n_年龄 || v_Error || '门诊号:' ||
                            Nvl(n_标识号, '') || v_Error || '病人科室:' || v_部门名称, 1, 100);
        
          n_出库序号 := Nvl(n_出库序号, 0) + 1;
          Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        
          --高值卫材类别id默认19是为了方便统计，因为其他出库可以设置很多类别，所以默认19
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 库房id, 对方部门id, 入出类别id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 付数, 填写数量, 实际数量, 零售价, 零售金额, 摘要, 填制人,
             填制日期, 费用id, 频次, 发药窗口, 单量, 用法, 外观, 扣率, 灭菌效期, 灭菌日期, 供药单位id, 生产日期, 批准文号, 商品条码, 内部条码)
          Values
            (n_收发id, 1, 21, v_其他出库no, n_出库序号, n_虚拟库房id, n_对方部门id, 19, -1, n_收费细目id, r_Stock.批次, r_Stock.上次产地,
             r_Stock.上次批号, r_Stock.效期, 1, n_当前数量, n_当前数量, n_当前单价, Round(n_当前单价 * n_当前数量, n_流通金额小数), v_Error, v_操作员,
             d_登记时间, Id_In, 频次_In, v_发药窗口, 单量_In, 用法_In, 煎法_In, n_扣率, d_灭菌效期, d_灭菌日期, r_Stock.上次供应商id, r_Stock.上次生产日期,
             r_Stock.批准文号, r_Stock.商品条码, r_Stock.内部条码);
        
          --药品库存(普通情况可能没有记录)
          Zl_药品库存_Update(n_收发id, 0, 1);
        End If;
      
        v_Error      := '';
        n_总出库数量 := n_总出库数量 - n_当前数量;
        n_总金额     := n_总金额 + n_当前数量 * n_当前单价;
      End Loop;
    
      --未发药品记录
      Update 未发药品记录
      Set 病人id = n_病人id, 姓名 = v_姓名, 发药窗口 = v_发药窗口, 主页id = 主页id_In
      Where 单据 = n_单据 And NO = v_No And Nvl(库房id, 0) = Nvl(n_发药库房id, 0);
      If Sql%RowCount = 0 Then
        --取身份优先级
        Begin
          Select b.优先级 Into n_优先级 From 病人信息 A, 身份 B Where a.身份 = b.名称(+) And a.病人id = n_病人id;
        Exception
          When Others Then
            Null;
        End;
        Insert Into 未发药品记录
          (单据, NO, 病人id, 主页id, 姓名, 优先级, 库房id, 对方部门id, 填制日期, 已收费, 打印状态, 发药窗口)
        Values
          (n_单据, v_No, n_病人id, 主页id_In, v_姓名, n_优先级, n_发药库房id, n_对方部门id, d_登记时间, n_记录状态, 0, v_发药窗口);
      End If;
    
      --处理未发药记录状态
      Zl_Prescription_Type_Update(v_No, n_记录性质, n_收费细目id, v_收费类别);
    
      Close c_Stock;
    End If;
  End If;
Exception
  When Err_Custom Then
    Raise_Application_Error(-20101, '[ZLSOFT]' || v_Error || '[ZLSOFT]');
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品收发记录_销售出库;
/

--111584:李业庆,2017-07-13,高值卫材按批次出库修改
Create Or Replace Function Zl_Fun_Getprice
(
  药品id_In     In 药品库存.药品id%Type,
  库房id_In     In 药品库存.库房id%Type,
  总出库数量_In In 药品库存.实际数量%Type,
  备货材料_In   In Number := 0,
  备货批次_In   In Number := Null
) Return Varchar2 Is
  ----------------------------------
  --功能：根据传过来的参数返回出库药品、卫材的售价|原售价|成本价|剩余数量，中间用“|”隔断；剩余数量等于0表示数量足够，大于0则表示数量不够
  --规则：
  --      1、循环游标判断总出库数量与游标中每条记录数量是否充足，如果充足就是总数量，不充足挨个遍历直到数量直到遍历完并退出
  --      2、售价计算方式：定价取收费价目表现价，时价取药品库存表中零售价，取到的售价为平均价格
  --      3、成本价计算方式：库房等于0，则表示没有明确库房或者发药分离模式，那么成本价直接取规格里面价格，如果有库房id，则取药品库存表中平均成本价
  --参数：
  --      药品id_In：药品id、卫材id
  --      库房id_In：发药库房
  --      总出库数量_In；总出库数量
  --      备货材料_In：只有高值卫材才需要传入，非0表示是高值卫材模式
  --      备货材料批次_In：支持高值卫材扫码确定批次出库，所以35.70支持材料非备货材料模式按批次出库；药品不支持这种模式，即药品批次都传空
  -----------------------------------
  n_Outmode    Number;
  n_类别       Number;
  n_分批       药品规格.药房分批%Type;
  n_时价       收费项目目录.是否变价%Type;
  v_名称       收费项目目录.名称%Type;
  n_当前单价   收费价目.现价%Type;
  n_标准单价   收费价目.现价%Type;
  n_成本价     药品规格.成本价%Type;
  n_当前成本价 药品规格.成本价%Type;
  n_总金额     Number;
  n_总成本     Number;
  n_总出库数量 药品库存.实际数量%Type;
  n_当前数量   药品库存.实际数量%Type;
  n_数量       药品库存.实际数量%Type;

  Err_Custom Exception;
  v_Error Varchar2(255);

  Cursor c_Stock Is
    Select 库房id, 药品id, 批次, Nvl(可用数量, 0) As 可用数量, Nvl(实际数量, 0) As 实际数量, Nvl(实际金额, 0) As 实际金额, Nvl(实际差价, 0) As 实际差价, 零售价,
           Nvl(平均成本价, 0) As 平均成本价
    From 药品库存
    Where 药品id = 药品id_In And 库房id = 库房id_In And 性质 = 1 And Nvl(可用数量, 0) > 0 And
          Decode(备货批次_In, Null, 0, Nvl(批次, 0)) = Decode(备货批次_In, Null, 0, Nvl(备货批次_In, 0)) And
          (Nvl(批次, 0) = 0 Or 效期 Is Null Or 效期 > Trunc(Sysdate))
    Order By Decode(n_Outmode, 1, 效期, Null), Nvl(批次, 0);
Begin
  --药品分批出库方式 
  Select Zl_To_Number(Nvl(zl_GetSysParameter(150), 0)) Into n_Outmode From Dual;
  --判断是药品还是卫材，0是卫材，1是药品，2是其他
  Select Decode(类别, '4', 0, '5', 1, '6', 1, '7', 1, 2) As 类别 Into n_类别 From 收费项目目录 Where ID = 药品id_In;

  If n_类别 = 2 Then
    --只有药品和卫材才允许取值，其他不允许调用该函数
    Raise Err_Custom;
  End If;
  --取基本信息
  If n_类别 = 0 Then
    --卫材
    Select Nvl(a.在用分批, 0) As 分批, Nvl(b.是否变价, 0), b.名称, c.现价, a.成本价
    Into n_分批, n_时价, v_名称, n_标准单价, n_成本价
    From 材料特性 A, 收费项目目录 B, 收费价目 C
    Where a.材料id = b.Id And b.Id = c.收费细目id And b.Id = 药品id_In And Sysdate Between c.执行日期 And c.终止日期;
  Elsif n_类别 = 1 Then
    --药品
    Select Nvl(a.药房分批, 0) As 分批, Nvl(b.是否变价, 0), b.名称, c.现价, a.成本价
    Into n_分批, n_时价, v_名称, n_标准单价, n_成本价
    From 药品规格 A, 收费项目目录 B, 收费价目 C
    Where a.药品id = b.Id And b.Id = c.收费细目id And b.Id = 药品id_In And Sysdate Between c.执行日期 And c.终止日期;
  End If;

  n_总出库数量 := 总出库数量_In;
  n_总金额     := 0;
  n_总成本     := 0;
  n_数量       := 0;

  If 库房id_In = 0 Then
    --库房id等于0这种不确定库房的情况，随便显示一个价格给用户看
    n_当前成本价 := n_成本价;
    --售价,取售价库房id不为0的情况没有，随便取的一个价格
    n_当前单价 := n_标准单价;
    Return n_当前单价 || '|' || n_当前成本价 || '|' || n_总出库数量;
  End If;

  For r_Stock In c_Stock Loop
    If n_分批 = 1 Or n_时价 = 1 Then
      --对于不分批的时价只可能分解一次,分解不完上面判断了.它分解是为了计算单价. 
      --每次分解取小者,库存不够分解不完在上面判断. 
      If n_总出库数量 <= Nvl(r_Stock.可用数量, 0) Then
        n_当前数量 := n_总出库数量;
      Else
        n_当前数量 := Nvl(r_Stock.可用数量, 0);
      End If;
    
      --售价
      If n_时价 = 1 Then
        If r_Stock.实际数量 <> 0 Then
          n_当前单价 := Nvl(r_Stock.零售价, Nvl(r_Stock.实际金额 / r_Stock.实际数量, 0));
        Else
          n_当前单价 := Nvl(r_Stock.零售价, n_标准单价);
        End If;
      Elsif n_分批 = 1 Then
        n_当前单价 := n_标准单价;
      End If;
    
      --成本价
      n_当前成本价 := r_Stock.平均成本价;
    
    Else
      --定价不分批
      n_当前数量 := n_总出库数量;
      --售价
      n_当前单价 := n_标准单价;
      --成本价
      n_当前成本价 := r_Stock.平均成本价;
    End If;
  
    n_数量       := n_数量 + n_当前数量;
    n_总出库数量 := n_总出库数量 - n_当前数量;
    n_总金额     := n_总金额 + n_当前数量 * n_当前单价;
    n_总成本     := n_总成本 + n_当前数量 * n_当前成本价;
    If n_总出库数量 = 0 Then
      Exit;
    End If;
  End Loop;

  If n_数量 <> 0 Then
    n_当前单价   := n_总金额 / n_数量;
    n_当前成本价 := n_总成本 / n_数量;
  Else
    --无库存价格直接从收费价目取现价和直接从规格表取成本价
    n_当前单价   := n_标准单价;
    n_当前成本价 := n_成本价;
    n_总出库数量 := 总出库数量_In;
  End If;
  Return n_当前单价 || '|' || n_当前成本价 || '|' || n_总出库数量;

Exception
  When Err_Custom Then
    Return Null;
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_Fun_Getprice;
/


--112195:梁唐彬,2017-07-27,辅助新版病历提供诊断同步的过程
Create Or Replace Procedure Zl_病人诊断记录_首次同步
(
  病人id_In 病人诊断记录.病人id%Type,
  主页id_In 病人诊断记录.主页id%Type,
  记录人_In 病人诊断记录.主页id%Type := Null
) Is
  --功能：首次编辑住院首页之前，如需插入诊断，需先将其他门诊、入院诊断同步到首页中
  n_Count Number;

  v_Temp     Varchar2(255);
  v_人员姓名 人员表.姓名%Type;
Begin
  --当前操作人员
  If 记录人_In Is Null Then
    v_Temp     := Zl_Identity;
    v_Temp     := Substr(v_Temp, Instr(v_Temp, ';') + 1);
    v_Temp     := Substr(v_Temp, Instr(v_Temp, ',') + 1);
    v_人员姓名 := Substr(v_Temp, Instr(v_Temp, ',') + 1);
  Else
    v_人员姓名 := 记录人_In;
  End If;

  Select Count(1) Into n_Count From 病人诊断记录 Where 病人id = 病人id_In And 主页id = 主页id_In And 记录来源 = 3;

  If n_Count = 0 Then
    Insert Into 病人诊断记录
      (ID, 病人id, 主页id, 记录来源, 病历id, 诊断类型, 诊断次序, 疾病id, 诊断id, 证候id, 诊断描述, 入院病情, 出院情况, 是否未治, 是否疑诊, 记录日期, 记录人, 医嘱id, 备注,
       发病时间)
      Select 病人诊断记录_Id.Nextval, a.*
      From (Select Distinct 病人id, 主页id, 3, 病历id, 诊断类型, 诊断次序, 疾病id, 诊断id, 证候id, 诊断描述, 入院病情, 出院情况, 是否未治, 是否疑诊, Sysdate,
                             v_人员姓名, 医嘱id, 备注, 发病时间
             From 病人诊断记录
             Where 病人id = 病人id_In And 主页id = 主页id_In And 记录来源 In (1, 2, 4) And 诊断描述 Is Not Null And
                   诊断类型 In (1, 2, 3, 5, 6, 7, 10, 11, 12, 13, 21) And 取消时间 Is Null) A;
  End If;

Exception
  When Others Then
    Zl_Errorcenter(Sqlcode, Sqlerrm);
End Zl_病人诊断记录_首次同步;
/


--113570:李业庆,2017-08-28,传参调整(从传入收费价目id改为传入药品id)，在过程内部增加检查是否调价
Create Or Replace Procedure Zl_药品收发记录_Adjust
(
  药品id_In   In Number, --药品ID,为0时检查所有预调价
  调价方式_In In Number := 0 --0-检查售价和成本价预调价,1-只检查售价预调价,2-只检查成本价预调价
) As
  Classid          Number(18); --入出类别
  v_Billno         药品收发记录.No%Type; --调价单号
  Adjustdate       Date; --调价时间
  n_批次           Number(18);
  n_现价           收费价目.现价%Type;
  n_原价           收费价目.原价%Type;
  n_序号           Number(8);
  n_原价id         收费价目.原价id%Type;
  n_零售金额       药品库存.实际金额%Type;
  n_收发id         药品收发记录.Id%Type;
  n_流通金额小数   Number;
  n_Stockid        药品收发记录.库房id%Type;
  n_入出类别id     药品收发记录.入出类别id%Type;
  n_入出系数       药品收发记录.入出系数%Type;
  n_价格id         收费价目.Id%Type;
  n_无库存调价模式 Number(1) := 0;
  --定价售价，时价售价预调价记录
  --价格类型：0-定价售价,1-时价售价
  Cursor c_Priceadjust Is
    Select 0 As 价格类型, p.Id As 价格id, p.原价id, p.执行日期, p.原价, p.现价, i.药品id, s.库房id As 库房id, s.批次 As 批次, s.上次批号 As 批号, s.效期,
           s.上次产地 As 产地, s.上次供应商id As 供应商id, s.实际数量, s.上次扣率 As 扣率, s.实际金额, s.实际差价, Nvl(s.零售价, 0) As 零售价, s.平均成本价,
           s.Rowid As 库存记录, s.灭菌效期, s.批准文号, s.上次生产日期 As 生产日期, p.调价汇总号
    From 收费价目 P, 药品规格 I, 收费项目目录 A, 药品库存 S
    Where i.药品id = a.Id And p.收费细目id = i.药品id And i.药品id = s.药品id(+) And s.性质(+) = 1 And Nvl(a.是否变价, 0) = 0 And
          Sysdate Between p.执行日期 And Nvl(p.终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And Nvl(p.变动原因, 0) = 0 And
          p.收费细目id = Decode(药品id_In, 0, p.收费细目id, 药品id_In) And 调价方式_In In (0, 1)
    Union All
    Select 价格类型, p.Id As 价格id, p.原价id, p.执行日期, p.原价, p.现价, i.药品id, p.库房id As 库房id, s.批次 As 批次, s.上次批号 As 批号, s.效期,
           s.上次产地 As 产地, s.上次供应商id As 供应商id, s.实际数量, s.上次扣率 As 扣率, s.实际金额, s.实际差价, Nvl(s.零售价, 0) As 零售价, s.平均成本价,
           s.Rowid As 库存记录, s.灭菌效期, s.批准文号, s.上次生产日期 As 生产日期, p.调价汇总号
    From 药品价格记录 P, 药品规格 I, 收费项目目录 A, 药品库存 S
    Where i.药品id = a.Id And p.药品id = i.药品id And p.库房id = s.库房id(+) And p.药品id = s.药品id(+) And p.批次 = s.批次(+) And
          s.性质(+) = 1 And Sysdate Between p.执行日期 And Nvl(p.终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And
          Nvl(p.记录状态, 0) = 0 And p.药品id = Decode(药品id_In, 0, p.药品id, 药品id_In) And 价格类型 = 1 And 调价方式_In In (0, 1)
    Order By 价格类型, 药品id, 批次, 库房id;

  r_Priceadjust c_Priceadjust%RowType;

  --成本价预调价记录
  Cursor c_Costadjust Is
    Select 价格类型, p.Id As 价格id, p.原价id, p.执行日期, p.原价, p.现价, i.药品id, p.库房id As 库房id, s.批次 As 批次, s.上次批号 As 批号, s.效期,
           s.上次产地 As 产地, s.上次供应商id As 供应商id, s.实际数量, s.上次扣率 As 扣率, s.实际金额, s.实际差价, Nvl(s.零售价, 0) As 零售价, s.平均成本价,
           s.Rowid As 库存记录, s.灭菌效期, s.批准文号, s.上次生产日期 As 生产日期, p.调价汇总号
    From 药品价格记录 P, 药品规格 I, 收费项目目录 A, 药品库存 S
    Where i.药品id = a.Id And p.药品id = i.药品id And p.库房id = s.库房id(+) And p.药品id = s.药品id(+) And p.批次 = s.批次(+) And
          s.性质(+) = 1 And Sysdate Between p.执行日期 And Nvl(p.终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And
          Nvl(p.记录状态, 0) = 0 And p.药品id = Decode(药品id_In, 0, p.药品id, 药品id_In) And 价格类型 = 2 And 调价方式_In In (0, 2)
    Order By 药品id, 批次, 库房id;

  r_Costadjust c_Costadjust%RowType;

  --当前生效的价格，用于无库存调价
  Cursor c_Nostockadjust
  (
    Drugid_In 药品价格记录.药品id%Type,
    Type_In   药品价格记录.价格类型%Type
  ) Is
    Select a.价格类型, a.Id As 价格id, a.原价, a.现价, a.药品id, a.库房id, a.批次, a.供药单位id, a.批号, a.效期, a.产地
    From 药品价格记录 A
    Where Sysdate Between a.执行日期 And Nvl(a.终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And a.记录状态 = 1 And
          a.药品id = Drugid_In And a.价格类型 = Type_In And a.库房id Is Not Null
    Order By a.库房id, a.药品id, a.批次;

  r_Nostockadjust c_Nostockadjust%RowType;
Begin
  --取流通业务精度位数
  --类别:1-药品 2-卫材
  --内容：2-零售价 4-金额
  --单位：药品:1-售价 5-金额单位
  Select 精度 Into n_流通金额小数 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5;

  --取入出类别ID
  Select 类别id Into Classid From 药品单据性质 Where 单据 = 13;

  Adjustdate := Sysdate;

  --售价调价处理
  If 调价方式_In = 0 Or 调价方式_In = 1 Then
  
    n_序号 := 0;
  
    --取调价NO取
    Select Nextno(147) Into v_Billno From Dual;
  
    For r_Priceadjust In c_Priceadjust Loop
      If r_Priceadjust.库房id Is Not Null Then
        --有库房id正常调价
        --要有库存才产生调价盈亏记录
        If r_Priceadjust.库存记录 Is Not Null And r_Priceadjust.原价 <> r_Priceadjust.现价 Then
          n_序号 := n_序号 + 1;
        
          Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        
          n_原价 := r_Priceadjust.原价;
        
          --时价调价，如果原价和当前库存不一致，则以当前库存为准
          If r_Priceadjust.价格类型 = 1 And r_Priceadjust.原价 <> r_Priceadjust.零售价 And r_Priceadjust.库存记录 Is Not Null Then
            n_原价 := r_Priceadjust.零售价;
          End If;
        
          n_零售金额 := Round(r_Priceadjust.现价 * r_Priceadjust.实际数量, n_流通金额小数) - Round(n_原价 * r_Priceadjust.实际数量, n_流通金额小数);
        
          n_价格id := r_Priceadjust.价格id;
          If r_Priceadjust.价格类型 = 1 Then
            Select ID
            Into n_价格id
            From 收费价目
            Where Sysdate Between 执行日期 And Nvl(终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And 收费细目id = r_Priceadjust.药品id;
          End If;
        
          --产生调价影响记录
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 扣率, 零售金额, 差价, 摘要, 填制人,
             填制日期, 库房id, 入出系数, 价格id, 审核人, 审核日期, 单量, 频次, 供药单位id)
          Values
            (n_收发id, 1, 13, v_Billno, n_序号, Classid, r_Priceadjust.药品id, r_Priceadjust.批次, r_Priceadjust.批号,
             r_Priceadjust.效期, r_Priceadjust.产地, 1, r_Priceadjust.实际数量, 0, n_原价, 0, r_Priceadjust.现价, r_Priceadjust.扣率,
             n_零售金额, n_零售金额, '药品调价', Zl_Username, Adjustdate, r_Priceadjust.库房id, 1, n_价格id, Zl_Username, Adjustdate,
             r_Priceadjust.实际金额, r_Priceadjust.实际差价, r_Priceadjust.供应商id);
        
          --更新药品库存，无库存不执行
          If r_Priceadjust.库存记录 Is Not Null Then
            Zl_药品库存_Update(n_收发id, 2, 0);
          End If;
        End If;
      
        --更新原价格信息
        If r_Priceadjust.价格类型 = 1 Then
          Update 药品价格记录 Set 记录状态 = 2 Where ID = r_Priceadjust.原价id;
        End If;
      
        --时价调价更新价格表中的信息
        If r_Priceadjust.价格类型 = 1 Then
          --更新当前价格信息
          If r_Priceadjust.库存记录 Is Not Null And r_Priceadjust.原价 <> r_Priceadjust.现价 Then
            Update 药品价格记录
            Set 批号 = r_Priceadjust.批号, 效期 = r_Priceadjust.效期, 产地 = r_Priceadjust.产地, 灭菌效期 = r_Priceadjust.灭菌效期,
                供药单位id = r_Costadjust.供应商id, 原价 = n_原价, 收发id = n_收发id, 记录状态 = 1
            Where ID = r_Priceadjust.价格id;
          Else
            --无库存时只更新记录状态，收发id
            Update 药品价格记录 Set 收发id = n_收发id, 记录状态 = 1 Where ID = r_Priceadjust.价格id;
          End If;
        End If;
      Else
        --无库存调价模式，价格表中该药品所有生效的价格都要按无库存调价时的价格调整
      
        If r_Priceadjust.价格类型 = 1 Then
          --更新原价格信息
          Update 药品价格记录 Set 记录状态 = 2 Where ID = r_Priceadjust.原价id;
        
          --更新现价格状态
          Update 药品价格记录 Set 记录状态 = 1 Where ID = r_Priceadjust.价格id;
        End If;
      
        n_无库存调价模式 := 0;
        For r_Nostockadjust In c_Nostockadjust(r_Priceadjust.药品id, 1) Loop
          If r_Priceadjust.现价 <> r_Nostockadjust.现价 Then
            Zl_药品价格记录_Stop(1, r_Nostockadjust.库房id, r_Nostockadjust.药品id, r_Nostockadjust.批次,
                           Adjustdate - 2 / 24 / 60 / 60);
            Zl_药品价格记录_Insert(1, 1, r_Nostockadjust.库房id, r_Nostockadjust.药品id, r_Nostockadjust.批次, Null,
                             r_Priceadjust.现价, Adjustdate - 1 / 24 / 60 / 60, '药品调价', Zl_Username, r_Priceadjust.调价汇总号,
                             r_Nostockadjust.供药单位id, r_Nostockadjust.批号, r_Nostockadjust.效期, r_Nostockadjust.产地);
            n_无库存调价模式 := 1;
          End If;
        End Loop;
        If n_无库存调价模式 = 1 Then
          Zl_药品收发记录_Adjust(r_Priceadjust.药品id, 1);
        End If;
      End If;
    
      --更新规格价格
      If r_Priceadjust.现价 <> r_Priceadjust.原价 Then
        Update 药品规格
        Set 上次售价 = r_Priceadjust.现价
        Where 药品id = r_Priceadjust.药品id And 上次售价 <> r_Priceadjust.现价;
      End If;
    
      If r_Priceadjust.价格类型 = 0 Then
        n_价格id := r_Priceadjust.价格id;
      Else
        Begin
          Select ID
          Into n_价格id
          From 收费价目
          Where Sysdate Between 执行日期 And Nvl(终止日期, To_Date('3000-1-1', 'YYYY-MM-DD')) And Nvl(变动原因, 0) = 0 And
                收费细目id = r_Priceadjust.药品id;
        Exception
          When Others Then
            n_价格id := 0;
        End;
      End If;
    
      If n_价格id > 0 Then
        Update 收费价目 Set 变动原因 = 1 Where Nvl(变动原因, 0) = 0 And ID = n_价格id;
      End If;
    End Loop;
  End If;

  --成本价调价处理
  If 调价方式_In = 0 Or 调价方式_In = 2 Then
  
    n_序号    := 0;
    n_Stockid := 0;
  
    Select b.Id, b.系数
    Into n_入出类别id, n_入出系数
    From 药品单据性质 A, 药品入出类别 B
    Where a.类别id = b.Id And a.单据 = 5 And Rownum < 2;
  
    v_Billno := Nextno(25, n_Stockid);
  
    For r_Costadjust In c_Costadjust Loop
      If r_Costadjust.库房id Is Not Null And r_Costadjust.原价 <> r_Costadjust.现价 Then
        --有库房id正常调价
        --要有库存才产生调价盈亏记录
        If r_Costadjust.库存记录 Is Not Null Then
          n_序号 := n_序号 + 1;
        
          --产生库存差价调整单
          Select 药品收发记录_Id.Nextval Into n_收发id From Dual;
        
          --如果原价和当前库存不一致，则以当前库存为准
          n_原价 := r_Costadjust.原价;
          If r_Costadjust.原价 <> r_Costadjust.平均成本价 And r_Costadjust.库存记录 Is Not Null Then
            n_原价 := r_Costadjust.平均成本价;
          End If;
        
          n_零售金额 := Round(n_原价 * r_Costadjust.实际数量, n_流通金额小数) - Round(r_Costadjust.现价 * r_Costadjust.实际数量, n_流通金额小数);
        
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 实际数量, 零售价, 成本价, 差价, 摘要, 填制人,
             填制日期, 审核人, 审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率, 灭菌效期)
          Values
            (n_收发id, 1, 5, v_Billno, n_序号, r_Costadjust.库房id, n_入出类别id, r_Costadjust.供应商id, n_入出系数, r_Costadjust.药品id,
             r_Costadjust.批次, r_Costadjust.产地, r_Costadjust.批号, r_Costadjust.效期, r_Costadjust.实际数量, 0, r_Costadjust.实际金额,
             r_Costadjust.实际差价, n_零售金额, '成本价调价', Zl_Username, Adjustdate, Zl_Username, Adjustdate, r_Costadjust.生产日期,
             r_Costadjust.批准文号, r_Costadjust.现价, 1, n_原价, r_Costadjust.灭菌效期);
        
          Zl_药品库存_Update(n_收发id, 2, 0);
        
          --更新当前价格信息
          Update 药品价格记录
          Set 批号 = r_Costadjust.批号, 效期 = r_Costadjust.效期, 产地 = r_Costadjust.产地, 灭菌效期 = r_Costadjust.灭菌效期,
              供药单位id = r_Costadjust.供应商id, 原价 = n_原价, 收发id = n_收发id, 记录状态 = 1
          Where ID = r_Costadjust.价格id;
        
        Else
          --无库存时只更新记录状态
          Update 药品价格记录 Set 记录状态 = 1 Where ID = r_Costadjust.价格id;
        End If;
      
        --更新原价格信息
        Update 药品价格记录 Set 记录状态 = 2 Where ID = r_Costadjust.原价id;
      Else
        --无库存调价模式，价格表中该药品所有生效的价格都要按无库存调价时的价格调整
      
        --更新原价格信息
        Update 药品价格记录 Set 记录状态 = 2 Where ID = r_Costadjust.原价id;
      
        --更新现价格状态
        Update 药品价格记录 Set 记录状态 = 1 Where ID = r_Costadjust.价格id;
      
        n_无库存调价模式 := 0;
        For r_Nostockadjust In c_Nostockadjust(r_Costadjust.药品id, 2) Loop
          If r_Costadjust.现价 <> r_Nostockadjust.现价 Then
            Zl_药品价格记录_Stop(2, r_Nostockadjust.库房id, r_Nostockadjust.药品id, r_Nostockadjust.批次,
                           Adjustdate - 2 / 24 / 60 / 60);
            Zl_药品价格记录_Insert(1, 2, r_Nostockadjust.库房id, r_Nostockadjust.药品id, r_Nostockadjust.批次, Null,
                             r_Costadjust.现价, Adjustdate - 1 / 24 / 60 / 60, '成本价调价', Zl_Username, r_Costadjust.调价汇总号,
                             r_Nostockadjust.供药单位id, r_Nostockadjust.批号, r_Nostockadjust.效期, r_Nostockadjust.产地);
            n_无库存调价模式 := 1;
          End If;
        End Loop;
        If n_无库存调价模式 = 1 Then
          Zl_药品收发记录_Adjust(r_Costadjust.药品id, 2);
        End If;
      End If;
    
      --更新规格价格
      If r_Costadjust.原价 <> r_Costadjust.现价 Then
        Update 药品规格
        Set 成本价 = r_Costadjust.现价
        Where 药品id = r_Costadjust.药品id And 成本价 <> r_Costadjust.现价;
      End If;
    End Loop;
  End If;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品收发记录_Adjust;
/


--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl_药品价格记录_Insert
(
  调价类型_In   In 药品价格记录.调价类型%Type,
  价格类型_In   In 药品价格记录.价格类型%Type,
  库房id_In     In 药品价格记录.库房id%Type,
  药品id_In     In 药品价格记录.药品id%Type,
  批次_In       In 药品价格记录.批次%Type := Null,
  原价_In       In 药品价格记录.原价%Type := Null,
  现价_In       In 药品价格记录.现价%Type := Null,
  执行日期_In   In 药品价格记录.执行日期%Type := Null,
  调价说明_In   In 药品价格记录.调价说明%Type := Null,
  调价人_In     In 药品价格记录.调价人%Type := Null,
  调价汇总号_In In 药品价格记录.调价汇总号%Type := Null,
  供药单位id_In In 药品价格记录.供药单位id%Type := Null,
  批号_In       In 药品价格记录.批号%Type := Null,
  效期_In       In 药品价格记录.效期%Type := Null,
  产地_In       In 药品价格记录.产地%Type := Null,
  灭菌效期_In   In 药品价格记录.灭菌效期%Type := Null,
  发票号_In     In 药品价格记录.发票号%Type := Null,
  发票日期_In   In 药品价格记录.发票日期%Type := Null,
  发票金额_In   In 药品价格记录.发票金额%Type := Null,
  应付款变动_In In 药品价格记录.应付款变动%Type := 0,
  记录状态_In   In 药品价格记录.记录状态%Type := 0
) As
  n_Id             药品价格记录.Id%Type;
  n_原价id         药品价格记录.原价id%Type;
  n_原价           药品价格记录.原价%Type;
  n_有预调价       Number(1) := 0;
  d_预调价执行日期 药品价格记录.执行日期%Type;
  n_预调价现价     药品价格记录.现价%Type;
  n_预调价价格id   药品价格记录.Id%Type;
  n_预调价原价id   药品价格记录.原价id%Type;
  d_终止日期       药品价格记录.终止日期%Type;
Begin
  d_终止日期 := To_Date('3000-01-01', 'yyyy-mm-dd');

  Begin
    Select ID, 现价
    Into n_原价id, n_原价
    From 药品价格记录
    Where 价格类型 = 价格类型_In And Nvl(库房id, 0) = Nvl(库房id_In, 0) And 药品id = 药品id_In And 批次 = 批次_In And 记录状态 = 1;
  Exception
    When Others Then
      n_原价id := Null;
      n_原价   := Null;
  End;

  If n_原价 Is Null Then
    If 价格类型_In = 1 Then
      Begin
        Select 零售价
        Into n_原价
        From 药品库存
        Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And 批次 = 批次_In;
      Exception
        When Others Then
          n_原价 := Null;
      End;
    Else
      Begin
        Select 平均成本价
        Into n_原价
        From 药品库存
        Where 性质 = 1 And 库房id = 库房id_In And 药品id = 药品id_In And 批次 = 批次_In;
      Exception
        When Others Then
          n_原价 := Null;
      End;
    End If;
  End If;

  If n_原价 Is Null Then
    n_原价 := 原价_In;
  End If;

  --检查是否有预调价记录
  Begin
    Select 1, ID, 原价id, 执行日期, 现价
    Into n_有预调价, n_预调价价格id, n_预调价原价id, d_预调价执行日期, n_预调价现价
    From 药品价格记录
    Where 价格类型 = 价格类型_In And Nvl(库房id, 0) = Nvl(库房id_In, 0) And 药品id = 药品id_In And 批次 = 批次_In And 执行日期 > Sysdate And
          记录状态 = 0;
  Exception
    When Others Then
      n_有预调价 := 0;
  End;

  --如果存在预调价，那么新价格加入到预调价之前
  If n_有预调价 = 1 Then
    d_终止日期 := d_预调价执行日期 - 1 / 24 / 60 / 60;
    n_原价id   := n_预调价原价id;
  End If;

  Select 药品价格记录_Id.Nextval Into n_Id From Dual;

  Insert Into 药品价格记录
    (ID, 原价id, 价格类型, 药品id, 库房id, 批次, 原价, 现价, 执行日期, 终止日期, 记录状态, 调价类型, 调价说明, 调价人, 调价汇总号, 供药单位id, 批号, 效期, 产地, 灭菌效期, 发票号,
     发票日期, 发票金额, 应付款变动)
  Values
    (n_Id, n_原价id, 价格类型_In, 药品id_In, 库房id_In, 批次_In, n_原价, 现价_In, 执行日期_In, d_终止日期, 记录状态_In, 调价类型_In, 调价说明_In, 调价人_In,
     调价汇总号_In, 供药单位id_In, 批号_In, 效期_In, 产地_In, 灭菌效期_In, 发票号_In, 发票日期_In, 发票金额_In, 应付款变动_In);

  --如果存在预调价，更新预调价数据：原价id,原价...
  If n_有预调价 = 1 Then
    Update 药品价格记录
    Set 原价id = n_Id, 原价 = 现价_In, 供药单位id = 供药单位id_In, 批号 = 批号_In, 效期 = 效期_In, 产地 = 产地_In, 灭菌效期 = 灭菌效期_In
    Where ID = n_预调价价格id;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品价格记录_Insert;
/

--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl_药品价格记录_Stop
(
  价格类型_In In 药品价格记录.价格类型%Type,
  库房id_In   In 药品价格记录.库房id%Type,
  药品id_In   In 药品价格记录.药品id%Type,
  批次_In     In 药品价格记录.批次%Type,
  终止日期_In In 药品价格记录.终止日期%Type,
  立即终止_In Number := 0
) Is
Begin
  Update 药品价格记录
  Set 终止日期 = 终止日期_In, 记录状态 = Decode(立即终止_In, 0, 记录状态, 2)
  Where 价格类型 = 价格类型_In And Nvl(库房id, 0) = Nvl(库房id_In, 0) And 药品id = 药品id_In And 批次 = 批次_In And 记录状态 = 1;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品价格记录_Stop;
/


--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl_药品未执行价格_Delete
(
  收费细目id_In In 收费价目.收费细目id%Type,
  删除方式_In   In Number := 0 --0-所有;1-售价;2-成本价
) Is
  v_售价调价汇总号   收费价目.调价汇总号%Type;
  v_时价调价汇总号   药品价格记录.调价汇总号%Type;
  v_成本价调价汇总号 药品价格记录.调价汇总号%Type;
  v_调价汇总号       收费价目.调价汇总号%Type;
  n_价目count        Number;
  n_时价count        Number;
  n_成本count        Number;
Begin
  n_成本count := 0;
  n_时价count := 0;
  n_价目count := 0;

  --删除方式_In：0-所有;1-售价;2-成本价;    
  If 删除方式_In = 0 Or 删除方式_In = 1 Then
    --获取调价汇总号
    Begin
      Select 调价汇总号 Into v_售价调价汇总号 From 收费价目 Where 收费细目id = 收费细目id_In And 执行日期 > Sysdate;
    Exception
      When Others Then
        v_售价调价汇总号 := Null;
    End;
  
    Update 收费价目
    Set 终止日期 = To_Date('3000-01-01', 'yyyy-MM-dd')
    Where 收费细目id = 收费细目id_In And (Sysdate Between 执行日期 And 终止日期 Or Sysdate >= 执行日期 And 终止日期 Is Null);
  
    --删除所有未执行售价调价记录
    Delete From 收费价目 Where 收费细目id = 收费细目id_In And 执行日期 > Sysdate;
  End If;

  --获取时价预调价的调价汇总号
  If 删除方式_In = 0 Or 删除方式_In = 1 Then
    Begin
      Select 调价汇总号
      Into v_时价调价汇总号
      From 药品价格记录
      Where 价格类型 = 1 And 记录状态 = 0 And 药品id = 收费细目id_In;
    Exception
      When Others Then
        v_时价调价汇总号 := Null;
    End;
  End If;

  --获取成本价预调价的调价汇总号
  If 删除方式_In = 0 Or 删除方式_In = 2 Then
    Begin
      Select 调价汇总号
      Into v_成本价调价汇总号
      From 药品价格记录
      Where 价格类型 = 2 And 记录状态 = 0 And 药品id = 收费细目id_In;
    Exception
      When Others Then
        v_成本价调价汇总号 := Null;
    End;
  End If;

  --删除时价，成本价预调价记录，更新原价格终止日期
  If 删除方式_In = 1 Then
    Update 药品价格记录
    Set 终止日期 = To_Date('3000-01-01', 'yyyy-MM-dd')
    Where 价格类型 = 1 And 记录状态 = 1 And 药品id = 收费细目id_In;
    Delete From 药品价格记录 Where 价格类型 = 1 And 记录状态 = 0 And 药品id = 收费细目id_In;
  Elsif 删除方式_In = 2 Then
    Update 药品价格记录
    Set 终止日期 = To_Date('3000-01-01', 'yyyy-MM-dd')
    Where 价格类型 = 2 And 记录状态 = 1 And 药品id = 收费细目id_In;
    Delete From 药品价格记录 Where 价格类型 = 2 And 记录状态 = 0 And 药品id = 收费细目id_In;
  Else
    Update 药品价格记录
    Set 终止日期 = To_Date('3000-01-01', 'yyyy-MM-dd')
    Where 记录状态 = 1 And 药品id = 收费细目id_In;
    Delete From 药品价格记录 Where 记录状态 = 0 And 药品id = 收费细目id_In;
  End If;

  --检查定价调价表是否还存在调价记录
  If v_售价调价汇总号 Is Not Null Then
    Select Count(调价汇总号) Into n_价目count From 收费价目 Where 调价汇总号 = v_售价调价汇总号;
  End If;
  --检查价格时价是否还存在调价记录
  If v_时价调价汇总号 Is Not Null Then
    Select Count(调价汇总号) Into n_时价count From 药品价格记录 Where 价格类型 = 1 And 调价汇总号 = v_时价调价汇总号;
  End If;
  --检查价格表是否还存在成本价调价记录
  If v_成本价调价汇总号 Is Not Null Then
    Select Count(调价汇总号) Into n_成本count From 药品价格记录 Where 价格类型 = 2 And 调价汇总号 = v_成本价调价汇总号;
  End If;

  --更新调价汇总表
  If n_价目count = 0 And n_时价count = 0 And n_成本count = 0 Then
    --已不存在所有预调价，则删除调价汇总表
    Delete From 调价汇总记录
    Where 调价号 = v_售价调价汇总号 Or 调价号 = v_时价调价汇总号 Or 调价号 = v_成本价调价汇总号;
  Else
    --存在售价预调价，但不存在成本价预调价
    If (n_价目count <> 0 Or n_时价count <> 0) And n_成本count = 0 Then
      Update 调价汇总记录 Set 类型 = 1 Where 调价号 = v_成本价调价汇总号;
    End If;
  
    --存在成本价预调价，但不存在售价预调价
    If n_价目count = 0 And n_时价count = 0 And n_成本count <> 0 Then
      Update 调价汇总记录 Set 类型 = 2 Where 调价号 = v_售价调价汇总号 Or 调价号 = v_时价调价汇总号;
    End If;
  End If;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品未执行价格_Delete;
/

--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl_药品库存_Update
(
  Id_In       In 药品收发记录.Id%Type,
  业务类型_In In Number := 0,
  入出类型_In In Number := 0,
  操作类型_In In Number := 0
) Is
  --功能：
  --      根据业务类型处理库存表，处理业务药品所有业务和卫材发料业务，卫材内部流通业务不处理
  --id_in  需要处理收发记录单
  --业务类型_in  业务类型，0-新增、1-删除、2-审核、3-冲销
  --入出类型_in  0-入库，1-出库
  --操作类型：根据业务情况确定
  ----外购入库中，表示财务审核：  0-不是财务审核，1-财务审核，目前只有外购入库有财务审核
  ----申领，移库中冲销流程：0-正常冲销流程，1-申请，审核冲销流程

  n_可用数量 药品库存.实际数量%Type;
  n_实际数量 药品库存.实际数量%Type;
  n_零售金额 药品库存.实际金额%Type;
  n_差价     药品库存.实际差价%Type;
  n_时价     Number(1);
  n_成本价   药品收发记录.成本价%Type;
  n_零售价   药品库存.零售价%Type;

  n_库存数量     药品库存.实际数量%Type;
  n_库存平均价   药品库存.平均成本价%Type;
  n_库存售价     药品库存.零售价%Type;
  n_总数量       药品收发记录.实际数量%Type;
  n_总成本价     药品收发记录.成本价%Type;
  n_总售价       药品收发记录.零售价%Type;
  n_库房分批     药品规格.药库分批%Type;
  n_申请冲销     Number(1);
  n_更新库存     Number(1) := 0;
  v_现价         药品收发记录.零售价%Type;
  v_执行新价格   Number(1) := 0;
  n_有库存       Number(1) := 0;
  v_审核日期     药品收发记录.审核日期%Type;
  n_价格更新     Number(1) := 0;
  n_新增时价售价 Number(1) := 0;
  n_新增成本价   Number(1) := 0;
  --业务明细数据，把库存数据更新需要的数据都列出来
  Cursor c_Detail Is
    Select a.Id, a.记录状态, a.单据, a.No, a.序号, a.库房id, a.供药单位id, a.入出类别id, a.对方部门id, a.入出系数, Nvl(a.发药方式, 0) As 发药方式, a.药品id,
           Nvl(a.批次, 0) 批次, a.产地, a.批号, a.生产日期, a.效期, a.付数, Nvl(a.填写数量, 0) As 填写数量, a.实际数量, a.成本价, a.成本金额, a.扣率, a.零售价,
           Nvl(a.零售金额, 0) As 零售金额, Nvl(a.差价, 0) As 差价, a.配药人, a.配药日期, a.审核人, a.审核日期, a.灭菌日期, a.灭菌效期, a.批准文号, a.商品条码,
           a.内部条码, Nvl(b.是否变价, 0) As 是否变价, a.单量, a.频次, a.摘要, Nvl(a.费用id, 0) As 费用id
    From 药品收发记录 A, 收费项目目录 B
    Where a.药品id = b.Id And a.Id = Id_In;

  r_Detail c_Detail%RowType;
Begin
  For r_Detail In c_Detail Loop
    n_实际数量 := r_Detail.入出系数 * r_Detail.实际数量 * Nvl(r_Detail.付数, 1);
    If n_实际数量 Is Null Then
      n_实际数量 := 0;
    End If;
    n_可用数量 := 0;
    n_零售价   := r_Detail.零售价;
    If r_Detail.单据 = 12 Then
      n_成本价 := r_Detail.单量;
    Else
      n_成本价 := r_Detail.成本价;
    End If;
    n_零售金额 := r_Detail.入出系数 * r_Detail.零售金额;
    n_差价     := r_Detail.入出系数 * r_Detail.差价;
  
    --先取库存和单据的数量和成本价
    Begin
      Select Nvl(实际数量, 0), Nvl(平均成本价, 0), Decode(Nvl(零售价, 0), 0, Decode(Nvl(实际数量, 0), 0, n_零售价, 实际金额 / 实际数量), 零售价)
      Into n_库存数量, n_库存平均价, n_库存售价
      From 药品库存
      Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
    Exception
      When Others Then
        n_库存数量   := 0;
        n_库存平均价 := 0;
        n_库存售价   := 0;
    End;
  
    --时价药品都需要更新库存表零售价字段
    If r_Detail.是否变价 = 1 Then
      n_时价 := 1;
    Else
      n_时价 := 0;
    End If;
  
    --特殊业务处理库存
    --包含业务--5-差价调整；13-调价变动
    --单据5，13都是业务类型_in，2-审核、入出类型_in  0-入库
    If r_Detail.单据 = 5 Or r_Detail.单据 = 13 Then
      --这种类型的单据收发记录成本价字段不是保存的真正成本价而是存储的其他数据
      If r_Detail.单据 = 5 Then
        If r_Detail.填写数量 <> 0 Then
          n_零售价 := Nvl(r_Detail.零售价, 0) / r_Detail.填写数量;
        Else
          n_零售价 := 0;
        End If;
        --审核
        If r_Detail.记录状态 = 1 Then
          --差价调整发药方式=0；主动调价、退货、发药产生的调价修正发药方式=1
          n_成本价 := r_Detail.单量;
        Else
          --冲销 还原原始成本价
          Begin
            --成本价=(金额-差价)/数量
            n_成本价 := (Nvl(r_Detail.零售价, 0) - Nvl(r_Detail.成本价, 0)) / r_Detail.填写数量;
          Exception
            When Others Then
              Select 成本价 Into n_成本价 From 药品规格 Where 药品id = r_Detail.药品id;
          End;
        End If;
      Else
        n_成本价 := Nvl(r_Detail.单量, 0) - Nvl(r_Detail.频次, 0);
      End If;
    
      If r_Detail.单据 = 5 Then
        --单据=5 的成本价修正记录 平均成本价不需要重算，因为保存了最新价格的
        If r_Detail.摘要 = '外购退库差价误差自动修正' Or r_Detail.摘要 = '财务审核价格变动修正' Then
          --这一步肯定是外购退库，外购退库只更新成本价,且肯定有库存
          Update 药品库存
          Set 实际差价 = Nvl(实际差价, 0) + n_差价
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        Else
          Update 药品库存
          Set 平均成本价 = n_成本价, 上次采购价 = n_成本价, 实际金额 = Nvl(实际金额, 0) + n_零售金额, 实际差价 = Nvl(实际差价, 0) + n_差价
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
          If Sql%NotFound Then
            Insert Into 药品库存
              (库房id, 药品id, 批次, 性质, 实际差价, 上次批号, 效期, 上次产地, 上次供应商id, 上次生产日期, 批准文号, 实际金额, 上次采购价, 平均成本价)
            Values
              (r_Detail.库房id, r_Detail.药品id, r_Detail.批次, 1, n_差价, r_Detail.批号, r_Detail.效期, r_Detail.产地,
               r_Detail.供药单位id, r_Detail.生产日期, r_Detail.批准文号, n_零售金额, n_成本价, n_成本价);
          
            Insert Into 药品入库信息
              (药品id, 库房id, 批次, 入库日期)
              Select r_Detail.药品id, r_Detail.库房id, r_Detail.批次, r_Detail.审核日期
              From Dual
              Where Not Exists (Select 1
                     From 药品入库信息
                     Where 药品id = r_Detail.药品id And 库房id = r_Detail.库房id And 批次 = r_Detail.批次);
          End If;
        
          --调整药品批号对照表中的价格
          If r_Detail.摘要 = '成本价调价' Then
            Update 药品批号对照 Set 成本价 = n_成本价 Where 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
          End If;
        End If;
      Elsif r_Detail.单据 = 13 Then
        --单据=13 的售价修正记录 同步更新的金额和差价，所以不需要重算平均成本价
        If r_Detail.费用id = 0 Then
          Update 药品库存
          Set 零售价 = Decode(n_时价, 1, n_零售价, Null), 实际金额 = Nvl(实际金额, 0) + n_零售金额, 实际差价 = Nvl(实际差价, 0) + n_差价
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        Else
          Update 药品库存
          Set 实际金额 = Nvl(实际金额, 0) + n_零售金额, 实际差价 = Nvl(实际差价, 0) + n_差价
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        End If;
      
        If Sql%RowCount = 0 Then
          Insert Into 药品库存
            (库房id, 药品id, 批次, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 零售价)
          Values
            (r_Detail.库房id, r_Detail.药品id, r_Detail.批次, 1, 0, 0, n_零售金额, n_零售金额, Decode(n_时价, 1, n_零售价, Null));
        
          Insert Into 药品入库信息
            (药品id, 库房id, 批次, 入库日期)
            Select r_Detail.药品id, r_Detail.库房id, r_Detail.批次, r_Detail.审核日期
            From Dual
            Where Not Exists (Select 1
                   From 药品入库信息
                   Where 药品id = r_Detail.药品id And 库房id = r_Detail.库房id And 批次 = r_Detail.批次);
        End If;
      
        --调整药品批号对照表中的价格
        If r_Detail.摘要 = '药品调价' Then
          Update 药品批号对照 Set 售价 = n_零售价 Where 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        End If;
      End If;
    Else
      --一般业务处理库存
      --包含业务--1-外购入库；2-自制入库；3-协药入库；4-其他入库；6-库房移出；
      --7-部门领用；8-收费处方发药；9-记帐单处方发药；10-记帐表处方发药；11-其他出库；
      --12-盘点；14-药品盘点记录单
      --21-材料其他出库；24-收费处方发料；25-记帐单处方发料；26-记帐表处方发料
      If 业务类型_In = 0 Or 业务类型_In = 1 Then
        --新增，删除
        If r_Detail.单据 = 8 Or r_Detail.单据 = 9 Or r_Detail.单据 = 10 Or r_Detail.单据 = 21 Or r_Detail.单据 = 24 Or
           r_Detail.单据 = 25 Or r_Detail.单据 = 26 Or r_Detail.单据 = 7 Or r_Detail.单据 = 11 Or
           ((r_Detail.单据 = 2 Or r_Detail.单据 = 3 Or r_Detail.单据 = 12) And r_Detail.入出系数 = -1) Or
           (r_Detail.单据 = 1 And r_Detail.发药方式 = 1) Or (r_Detail.单据 = 6 And r_Detail.入出系数 = -1 And r_Detail.记录状态 = 1) Or
           (r_Detail.单据 = 6 And Mod(r_Detail.记录状态, 3) = 2 And r_Detail.入出系数 = 1) Then
          --需要在新增/删除单据时减少/增加可用数量的单据类型
          ----1.发药/发料单据(收费处方，记账单，记账表)
          ----2.普通出库（领用、其他出库、移库中出的那笔(r_Detail.单据 = 6 And r_Detail.入出系数 = -1 And r_Detail.记录状态 = 1)、盘点单中盘亏那笔）
          ----3.退库单（r_Detail.单据 = 1 And r_Detail.发药方式 = 1）
          ----4.移库申请冲销单（原入库那笔的冲销记录，r_Detail.单据 = 6 And Mod(r_Detail.记录状态, 3) = 2 And r_Detail.入出系数 = 1）
        
          --新增，删除单据时，因为没有审核所以只处理数量不处理金额和差价
        
          If 业务类型_In = 0 Then
            --新增时正常处理可用数量
            n_可用数量 := n_实际数量;
          Else
            --删除时按相反数计算可用数量
            n_可用数量 := -1 * n_实际数量;
          End If;
        
          n_实际数量 := 0;
          n_零售金额 := 0;
          n_差价     := 0;
        
          --处理库存
          Update 药品库存
          Set 可用数量 = 可用数量 + n_可用数量
          Where 药品id = r_Detail.药品id And 库房id = r_Detail.库房id And Nvl(批次, 0) = r_Detail.批次 And 性质 = 1;
        
          n_更新库存 := 1;
        End If;
      Elsif 业务类型_In = 2 Then
        --审核
        --10.35开始，理论上所有的出库类单据在审核时都不再处理可用数量
        If r_Detail.单据 = 8 Or r_Detail.单据 = 9 Or r_Detail.单据 = 10 Or r_Detail.单据 = 21 Or r_Detail.单据 = 24 Or
           r_Detail.单据 = 25 Or r_Detail.单据 = 26 Or r_Detail.单据 = 7 Or r_Detail.单据 = 11 Or
           ((r_Detail.单据 = 2 Or r_Detail.单据 = 3 Or r_Detail.单据 = 12) And r_Detail.入出系数 = -1) Or
           (r_Detail.单据 = 1 And r_Detail.发药方式 = 1) Or (r_Detail.单据 = 6 And r_Detail.入出系数 = -1 And r_Detail.记录状态 = 1) Or
           (r_Detail.单据 = 6 And Mod(r_Detail.记录状态, 3) = 2 And r_Detail.入出系数 = 1) Then
          n_可用数量 := 0;
        Else
          n_可用数量 := n_实际数量;
        End If;
      
        --处理库存
        If 入出类型_In = 0 Then
          --入库审核
          Update 药品库存
          Set 可用数量 = Nvl(可用数量, 0) + n_可用数量, 实际数量 = Nvl(实际数量, 0) + n_实际数量, 实际金额 = Nvl(实际金额, 0) + n_零售金额,
              实际差价 = Nvl(实际差价, 0) + n_差价, 上次供应商id = r_Detail.供药单位id,
              上次采购价 = Decode(r_Detail.单据, 1, Decode(r_Detail.发药方式, 1, 上次采购价, n_成本价), n_成本价),
              上次批号 = Nvl(r_Detail.批号, 上次批号), 上次生产日期 = Nvl(r_Detail.生产日期, 上次生产日期), 上次产地 = Nvl(r_Detail.产地, 上次产地),
              灭菌效期 = Nvl(r_Detail.灭菌效期, 灭菌效期), 效期 = Nvl(r_Detail.效期, 效期), 批准文号 = Nvl(r_Detail.批准文号, 批准文号),
              上次扣率 = Decode(r_Detail.单据, 12, 上次扣率, r_Detail.扣率), 商品条码 = Nvl(r_Detail.商品条码, 商品条码),
              内部条码 = Nvl(r_Detail.内部条码, 内部条码)
          Where 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And 性质 = 1;
        Else
          --出库审核，只需要下数量和金额
          Update 药品库存
          Set 可用数量 = Nvl(可用数量, 0) + n_可用数量, 实际数量 = Nvl(实际数量, 0) + n_实际数量, 实际金额 = Nvl(实际金额, 0) + n_零售金额,
              实际差价 = Nvl(实际差价, 0) + n_差价, 平均成本价 = Decode(平均成本价, Null, n_成本价, 平均成本价),
              上次采购价 = Decode(上次采购价, Null, n_成本价, 上次采购价)
          Where 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And 性质 = 1;
        End If;
      
        n_更新库存 := 1;
      Elsif 业务类型_In = 3 Then
        --冲销
        If r_Detail.单据 = 8 Or r_Detail.单据 = 9 Or r_Detail.单据 = 10 Or r_Detail.单据 = 21 Or r_Detail.单据 = 24 Or
           r_Detail.单据 = 25 Or r_Detail.单据 = 26 Then
          --发药/发料单退药/退料时同时又产生了未发单据，所以就不处理可用数量
          n_可用数量 := 0;
        Elsif r_Detail.单据 = 6 And Mod(r_Detail.记录状态, 3) = 2 And r_Detail.入出系数 = 1 Then
          --药库单的冲销单据，要判断是否需要申请
          n_申请冲销 := 操作类型_In;
          If n_申请冲销 = 0 Then
            --不需要申请的在冲销时处理可用数量
            n_可用数量 := n_实际数量;
          Else
            --需要申请的，已经在申请时处理了可用数量
            n_可用数量 := 0;
          End If;
        Else
          n_可用数量 := n_实际数量;
        End If;
      
        Begin
          Select 1
          Into n_有库存
          From 药品库存
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        Exception
          When Others Then
            n_有库存 := 0;
        End;
      
        --冲销时如果原来无库存，由于需要重新插入数据，则需要取最新价格(时价售价和成本价)，因为后面可能还要触发调价修正
        If n_有库存 = 0 Then
          --取原始单据的审核时间
          Select a.审核日期
          Into v_审核日期
          From 药品收发记录 A, 药品收发记录 B
          Where b.Id = r_Detail.Id And a.单据 = b.单据 And a.No = b.No And a.库房id = b.库房id And a.药品id + 0 = b.药品id And
                a.序号 = b.序号 And (a.记录状态 = 1 Or Mod(a.记录状态, 3) = 0);
        
          --成本价取最新价格
          Begin
            Select 1, 现价
            Into v_执行新价格, v_现价
            From 药品价格记录
            Where 价格类型 = 2 And 库房id + 0 = r_Detail.库房id And 药品id + 0 = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And
                  记录状态 = 1
            Order By 执行日期 Desc;
          Exception
            When Others Then
              v_执行新价格 := 0;
            
              --可能是无库存调价，那么不需要库房id和批次判断，只需要用药品id
              Begin
                Select 1, 新成本价
                Into v_执行新价格, v_现价
                From 成本价调价信息
                Where 库房id Is Null And Nvl(批次, 0) = 0 And 药品id + 0 = r_Detail.药品id And 执行日期 > v_审核日期 And Rownum = 1
                Order By 执行日期 Desc;
              Exception
                When Others Then
                  v_执行新价格 := 0;
              End;
          End;
        
          If v_执行新价格 = 1 Then
            n_成本价 := v_现价;
          End If;
        
          --时价售价取最新价格
          If r_Detail.是否变价 = 1 Then
            --从价格表取
            Begin
              Select 1, 现价
              Into v_执行新价格, v_现价
              From 药品价格记录
              Where 价格类型 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And
                    记录状态 = 1;
            Exception
              When Others Then
                v_现价       := 0;
                v_执行新价格 := 0;
            End;
          
            If v_现价 = 0 Then
              v_执行新价格 := 0;
            End If;
          
            If v_执行新价格 = 1 Then
              n_零售价 := v_现价;
            End If;
          End If;
        End If;
      
        --处理库存
        If 入出类型_In = 0 Then
          --出库单据冲销需要将入库库房数据都更新
          Update 药品库存
          Set 可用数量 = Nvl(可用数量, 0) + n_可用数量, 实际数量 = Nvl(实际数量, 0) + n_实际数量, 实际金额 = Nvl(实际金额, 0) + n_零售金额,
              实际差价 = Nvl(实际差价, 0) + n_差价, 上次供应商id = r_Detail.供药单位id,
              上次采购价 = Decode(r_Detail.单据, 1, Decode(r_Detail.发药方式, 1, 上次采购价, n_成本价), 上次采购价),
              上次批号 = Nvl(r_Detail.批号, 上次批号), 上次生产日期 = Nvl(r_Detail.生产日期, 上次生产日期), 上次产地 = Nvl(r_Detail.产地, 上次产地),
              灭菌效期 = Nvl(r_Detail.灭菌效期, 灭菌效期), 效期 = Nvl(r_Detail.效期, 效期), 批准文号 = Nvl(r_Detail.批准文号, 批准文号),
              上次扣率 = Decode(r_Detail.单据, 12, 上次扣率, r_Detail.扣率), 商品条码 = Nvl(r_Detail.商品条码, 商品条码),
              内部条码 = Nvl(r_Detail.内部条码, 内部条码)
          Where 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And 性质 = 1;
        Else
          --入库单据冲销只需要下数量和金额
          Update 药品库存
          Set 可用数量 = Nvl(可用数量, 0) + n_可用数量, 实际数量 = Nvl(实际数量, 0) + n_实际数量, 实际金额 = Nvl(实际金额, 0) + n_零售金额,
              实际差价 = Nvl(实际差价, 0) + n_差价
          Where 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And 性质 = 1;
        End If;
      
        n_更新库存 := 1;
      End If;
    
      --新增/删除/审核/冲销业务时，库存表未找到数据则需要产生库存表所有信息
      If Sql%RowCount = 0 And n_更新库存 = 1 Then
      
        --35.70盘点审核时价格可能是历史价格，如果是新增入库则需要用最新价格保存，后面会触发调价修正
        If 业务类型_In = 2 And r_Detail.单据 = 12 And r_Detail.入出系数 = 1 Then
          --判断是否有库存
          Begin
            Select 1
            Into n_有库存
            From 药品库存
            Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
          Exception
            When Others Then
              n_有库存 := 0;
          End;
        
          If n_有库存 = 0 Then
            --取原始单据的审核时间
            Select a.审核日期
            Into v_审核日期
            From 药品收发记录 A, 药品收发记录 B
            Where b.Id = r_Detail.Id And a.单据 = b.单据 And a.No = b.No And a.库房id = b.库房id And a.药品id + 0 = b.药品id And
                  a.序号 = b.序号 And (a.记录状态 = 1 Or Mod(a.记录状态, 3) = 0);
          
            --成本价取最新价格
            Begin
              Select 1, 现价
              Into v_执行新价格, v_现价
              From 药品价格记录
              Where 价格类型 = 2 And 库房id + 0 = r_Detail.库房id And 药品id + 0 = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And
                    记录状态 = 1
              Order By 执行日期 Desc;
            Exception
              When Others Then
                v_执行新价格 := 0;
              
                --可能是无库存调价，那么不需要库房id和批次判断，只需要用药品id
                Begin
                  Select 1, 新成本价
                  Into v_执行新价格, v_现价
                  From 成本价调价信息
                  Where 库房id Is Null And Nvl(批次, 0) = 0 And 药品id + 0 = r_Detail.药品id And 执行日期 > v_审核日期 And Rownum = 1
                  Order By 执行日期 Desc;
                Exception
                  When Others Then
                    v_执行新价格 := 0;
                End;
            End;
          
            If v_执行新价格 = 1 Then
              n_成本价 := v_现价;
            End If;
          
            --时价售价取最新价格
            If r_Detail.是否变价 = 1 Then
              --从价格表取
              Begin
                Select 1, 现价
                Into v_执行新价格, v_现价
                From 药品价格记录
                Where 价格类型 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And
                      记录状态 = 1;
              Exception
                When Others Then
                  v_现价       := 0;
                  v_执行新价格 := 0;
              End;
            
              If v_现价 = 0 Then
                v_执行新价格 := 0;
              End If;
            
              If v_执行新价格 = 1 Then
                n_零售价 := v_现价;
              End If;
            End If;
          End If;
        End If;
        --以上处理盘点审核时价格取值
      
        --新增库存
        Insert Into 药品库存
          (库房id, 药品id, 批次, 效期, 性质, 可用数量, 实际数量, 实际金额, 实际差价, 上次供应商id, 上次采购价, 上次批号, 上次生产日期, 上次产地, 灭菌效期, 批准文号, 零售价, 上次扣率,
           商品条码, 内部条码, 平均成本价)
        Values
          (r_Detail.库房id, r_Detail.药品id, r_Detail.批次, r_Detail.效期, 1, n_可用数量, n_实际数量, n_零售金额, n_差价, r_Detail.供药单位id,
           n_成本价, r_Detail.批号, r_Detail.生产日期, r_Detail.产地, r_Detail.灭菌效期, r_Detail.批准文号, Decode(n_时价, 1, n_零售价, Null),
           r_Detail.扣率, r_Detail.商品条码, r_Detail.内部条码, n_成本价);
      
        Insert Into 药品入库信息
          (药品id, 库房id, 批次, 入库日期)
          Select r_Detail.药品id, r_Detail.库房id, r_Detail.批次, r_Detail.审核日期
          From Dual
          Where Not Exists (Select 1
                 From 药品入库信息
                 Where 药品id = r_Detail.药品id And 库房id = r_Detail.库房id And 批次 = r_Detail.批次);
      
        --入库业务审核时新增或更新价格
        If 业务类型_In = 2 And 入出类型_In = 0 Then
          n_价格更新 := 0;
          If r_Detail.是否变价 = 1 Then
            Begin
              Select 0
              Into n_价格更新
              From 药品价格记录
              Where 价格类型 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And 批次 = r_Detail.批次 And Rownum < 2;
            Exception
              When Others Then
                n_价格更新 := 1;
            End;
          
            n_新增时价售价 := 0;
          
            If n_价格更新 = 1 Then
              --产生新的价格，时价售价
              Zl_药品价格记录_Insert(0, 1, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, 0, n_零售价, Sysdate, '入库新增批次价格',
                               Zl_Username, Null, r_Detail.供药单位id, r_Detail.批号, r_Detail.效期, r_Detail.产地, Null, Null,
                               Null, Null, Null, 1);
              n_新增时价售价 := 1;
            End If;
          End If;
        
          n_价格更新 := 0;
          Begin
            Select 0
            Into n_价格更新
            From 药品价格记录
            Where 价格类型 = 2 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And 批次 = r_Detail.批次 And Rownum < 2;
          Exception
            When Others Then
              n_价格更新 := 1;
          End;
        
          n_新增成本价 := 0;
          If n_价格更新 = 1 Then
            --产生新的价格，成本价售价
            Zl_药品价格记录_Insert(0, 2, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, 0, n_成本价, Sysdate, '入库新增批次价格',
                             Zl_Username, Null, r_Detail.供药单位id, r_Detail.批号, r_Detail.效期, r_Detail.产地, Null, Null, Null,
                             Null, Null, 1);
            n_新增成本价 := 1;
          End If;
        End If;
      
      End If;
    
      --重算平均成本价，入库审核需要重算平均成本价和零售价，注意只限于不分批药品，分批药品不用重算（确保和之前库存的数据一致）
      --10.35.70盘点入库类型不重算价格，已改为单据中价格是历史价格，在盘点审核后做调价修正
      If 入出类型_In = 0 And 业务类型_In = 2 And r_Detail.批次 = 0 And r_Detail.单据 <> 12 Then
      
        --按总金额/总数量方式计算平均成本价而不用（金额-差价）/数量是为了数据的准确性
        n_总数量 := (n_库存数量 + n_实际数量);
        If n_总数量 <> 0 Then
          n_总成本价 := (n_库存数量 * n_库存平均价 + n_实际数量 * n_成本价) / n_总数量;
        
          If n_总成本价 < 0 Then
            n_总成本价 := n_成本价;
          End If;
        
          Update 药品库存
          Set 平均成本价 = n_总成本价
          Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
        
          --价格表更新，成本价
          If n_新增成本价 = 0 Then
            Zl_药品价格记录_Stop(2, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, Sysdate, 1);
            Zl_药品价格记录_Insert(2, 2, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, Null, n_总成本价, Sysdate + 1 / 24 / 60 / 60,
                             '入库价格重算', Zl_Username, Null, r_Detail.供药单位id, r_Detail.批号, r_Detail.效期, r_Detail.产地, Null,
                             Null, Null, Null, Null, 1);
          End If;
        
          --更新时价零售价
          If n_时价 = 1 Then
            n_总售价 := (n_库存数量 * n_库存售价 + n_实际数量 * n_零售价) / n_总数量;
            Update 药品库存
            Set 零售价 = n_总售价
            Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And
                  Nvl(实际数量, 0) <> 0;
            If Sql%NotFound Then
              n_总售价 := n_零售价;
              Update 药品库存
              Set 零售价 = n_总售价
              Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次;
            End If;
          
            --价格表更新，时价售价
            If n_新增时价售价 = 0 Then
              Zl_药品价格记录_Stop(1, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, Sysdate, 1);
              Zl_药品价格记录_Insert(2, 1, r_Detail.库房id, r_Detail.药品id, r_Detail.批次, Null, n_总售价, Sysdate + 1 / 24 / 60 / 60,
                               '入库价格重算', Zl_Username, Null, r_Detail.供药单位id, r_Detail.批号, r_Detail.效期, r_Detail.产地, Null,
                               Null, Null, Null, Null, 1);
            End If;
          End If;
        End If;
      End If;
    End If;
  
    --删除多余的库存数据，外购入库财务审核为了确保库存不变产生修正数据必须保证不删除库存
    If Not (r_Detail.单据 = 1 And 操作类型_In = 1) Then
      Delete From 药品库存
      Where 性质 = 1 And 库房id = r_Detail.库房id And 药品id = r_Detail.药品id And Nvl(批次, 0) = r_Detail.批次 And Nvl(可用数量, 0) = 0 And
            Nvl(实际数量, 0) = 0 And Nvl(实际金额, 0) = 0 And Nvl(实际差价, 0) = 0;
    End If;
  End Loop;
Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品库存_Update;
/


--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl_药品收发记录_调价修正(收发id_In In 药品收发记录.Id%Type) Is
  v_单据         药品收发记录.单据%Type;
  v_入出类别id   药品收发记录.入出类别id%Type;
  v_售价精度     Number;
  v_金额精度     Number;
  v_收发id       药品收发记录.Id%Type;
  v_原价         药品收发记录.零售价%Type;
  v_现价         药品收发记录.零售价%Type;
  v_是否变价     收费项目目录.是否变价%Type;
  v_价格id       收费价目.Id%Type;
  v_库房id       药品收发记录.库房id%Type;
  v_药品id       药品收发记录.药品id%Type;
  v_批次         药品收发记录.批次%Type;
  v_实际数量     药品收发记录.实际数量%Type;
  v_修正金额     药品收发记录.零售金额%Type;
  v_入出系数     药品收发记录.入出系数%Type;
  v_执行修正     Number;
  v_填制日期     药品收发记录.填制日期%Type;
  v_审核日期     药品收发记录.审核日期%Type;
  d_调价时间     收费价目.执行日期%Type;
  v_零售金额     药品收发记录.零售金额%Type;
  v_差价         药品收发记录.差价%Type;
  v_No           药品收发记录.No%Type;
  n_记录状态     药品收发记录.记录状态%Type;
  n_当前数量     药品库存.实际数量%Type;
  n_是否门诊记录 Number;
  n_价目表售价   药品收发记录.零售价%Type;
  d_盘点时间     药品收发记录.填制日期%Type;
Begin
  ----售价：
  --定价和时价分批：判断在此期间有无主动调价，有则修正，无则不修正
  --时价不分批：直接比较价格
  --要修正时：判断当前价格与冲销价格是否不同，不同则处理否则不处理
  --          时价无库存不修正，因为取不出价格

  ----成本价：
  --有调价记录则修正，无调价检查库存与冲销价格是否不同，不同则修正，否则不修正

  --取原始单据的审核时间
  Select a.审核日期, a.填制日期
  Into v_审核日期, v_填制日期
  From 药品收发记录 A, 药品收发记录 B
  Where b.Id = 收发id_In And a.单据 = b.单据 And a.No = b.No And a.库房id = b.库房id And a.药品id + 0 = b.药品id And a.序号 = b.序号 And
        (a.记录状态 = 1 Or Mod(a.记录状态, 3) = 0);

  --1、售价调价处理
  --提取单据信息，原价，现价及药价属性
  Select a.单据, a.记录状态, a.库房id, a.药品id, Nvl(a.批次, 0) As 批次, a.入出系数 * Nvl(a.实际数量, 0) * Nvl(a.付数, 1) As 实际数量,
         Nvl(a.零售价, 0) 原价, b.现价, Nvl(c.是否变价, 0) 是否变价, b.Id As 价格id, b.执行日期,
         To_Date(a.频次, 'yyyy-mm-dd hh24:mi:ss') As 盘点时间
  Into v_单据, n_记录状态, v_库房id, v_药品id, v_批次, v_实际数量, v_原价, v_现价, v_是否变价, v_价格id, d_调价时间, d_盘点时间
  
  From 药品收发记录 A, 收费价目 B, 收费项目目录 C
  Where a.药品id = b.收费细目id And a.药品id = c.Id And
        (Sysdate Between b.执行日期 And b.终止日期 Or Sysdate >= b.执行日期 And b.终止日期 Is Null) And a.Id = 收发id_In;

  v_执行修正 := 0;
  If v_是否变价 = 0 Then
    --定价，单据原始时间在最新调价时间后，需要执行调价修正
    If v_单据 = 8 Or v_单据 = 9 Or v_单据 = 10 Then
      --发药单看填制日期
      If v_填制日期 < d_调价时间 Then
        v_执行修正 := 1;
      End If;
    Elsif v_单据 = 12 Then
      If d_盘点时间 < d_调价时间 Then
        v_执行修正 := 1;
      End If;
    Elsif v_审核日期 < d_调价时间 Then
      --其他流通单据看审核时间
      v_执行修正 := 1;
    End If;
  Else
    --时价主要比较新老价格，这里不判断
    v_执行修正 := 1;
  End If;

  If v_执行修正 = 1 Then
    If v_是否变价 = 1 Then
      --从价格表取
      Begin
        Select 现价
        Into v_现价
        From 药品价格记录
        Where 价格类型 = 1 And 库房id = v_库房id And 药品id = v_药品id And Nvl(批次, 0) = v_批次 And 记录状态 = 1;
      Exception
        When Others Then
          v_现价     := 0;
          v_执行修正 := 0;
      End;
    
      If v_现价 = 0 Then
        v_执行修正 := 0;
      End If;
    End If;
  
    If v_执行修正 = 1 Then
      Select 精度 Into v_金额精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5 And 性质 = 0;
    
      --比较原价和现价，不同则处理
      If Round(v_原价, 4) <> Round(v_现价, 4) Then
        v_修正金额 := Round(v_现价 * v_实际数量, v_金额精度) - Round(v_原价 * v_实际数量, v_金额精度);
      
        If v_修正金额 <> 0 Then
          Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
          Select Nextno(147) Into v_No From Dual;
        
          Select 类别id Into v_入出类别id From 药品单据性质 Where 单据 = 13;
        
          --产生调价修正记录
          Insert Into 药品收发记录
            (ID, 记录状态, 单据, NO, 序号, 入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, 填写数量, 实际数量, 成本价, 成本金额, 零售价, 扣率, 零售金额, 差价, 摘要, 填制人,
             填制日期, 库房id, 入出系数, 价格id, 审核人, 审核日期, 费用id)
            Select v_收发id, 1, 13, v_No, 序号, v_入出类别id, 药品id, 批次, 批号, 效期, 产地, 付数, v_实际数量, 0, v_原价, 0, v_现价, 扣率, v_修正金额,
                   v_修正金额, '自动修正调价变动', 审核人, 审核日期, 库房id, 1, v_价格id, 审核人, 审核日期, 收发id_In
            From 药品收发记录
            Where ID = 收发id_In;
        
          --更新药品库存
          Zl_药品库存_Update(v_收发id, 2, 0);
        End If;
      End If;
    End If;
  End If;

  --2、成本价调价，发药单退药再发和冲销业务，盘盈入库审核才修正
  v_执行修正 := 0;
  If (v_单据 In (8, 9, 10) And Mod(n_记录状态, 3) = 1 And n_记录状态 <> 1) Or Mod(n_记录状态, 3) = 2 Or (v_单据 = 12 And n_记录状态 = 1) Then
    Select 库房id, 药品id, Nvl(批次, 0) 批次, 入出系数 * Nvl(实际数量, 0) * Nvl(付数, 1) As 实际数量, 入出系数 * 零售金额, 入出系数 * 差价,
           Decode(单据, 12, 单量, 成本价) As 原价
    Into v_库房id, v_药品id, v_批次, v_实际数量, v_零售金额, v_差价, v_原价
    From 药品收发记录
    Where ID = 收发id_In;
  
    --冲销时取当前库存成本价
    Begin
      Select 1, 平均成本价, Nvl(实际数量, 0) As 实际数量
      Into v_执行修正, v_现价, n_当前数量
      From 药品库存
      Where 库房id = v_库房id And 药品id = v_药品id And Nvl(批次, 0) = v_批次 And 性质 = 1;
    Exception
      When Others Then
        v_现价     := 0;
        v_执行修正 := 0;
    End;
  
    --无库存判断
    If v_实际数量 = n_当前数量 Then
      --表示冲销或退药回去前无库存
      Begin
        Select 1, 现价
        Into v_执行修正, v_现价
        From (Select 现价
               From 药品价格记录
               Where 价格类型 = 2 And 库房id = v_库房id And 药品id = v_药品id And 批次 = v_批次 And 执行日期 > v_审核日期
               Order By 执行日期 Desc)
        Where Rownum < 2;
      Exception
        When Others Then
          v_现价     := 0;
          v_执行修正 := 0;
      End;
    
      If v_现价 = 0 Then
        --可能调价时无库存，查询有无无库存调价的情况
        Begin
          Select 1, 现价
          Into v_执行修正, v_现价
          From (Select 新成本价 As 现价
                 From 成本价调价信息
                 Where 库房id Is Null And 药品id = v_药品id And Nvl(批次, 0) = 0 And 执行日期 > v_审核日期
                 Order By 执行日期 Desc)
          Where Rownum < 2;
        Exception
          When Others Then
            v_现价     := 0;
            v_执行修正 := 0;
        End;
      End If;
    End If;
  
    If v_执行修正 = 1 And Round(v_现价, 4) <> Round(v_原价, 4) Then
      Select 精度 Into v_金额精度 From 药品卫材精度 Where 类别 = 1 And 内容 = 4 And 单位 = 5 And 性质 = 0;
    
      v_修正金额 := Round(v_原价 * v_实际数量, v_金额精度) - Round(v_现价 * v_实际数量, v_金额精度);
    
      If v_修正金额 <> 0 Then
        Select 药品收发记录_Id.Nextval Into v_收发id From Dual;
        Select b.Id, b.系数
        Into v_入出类别id, v_入出系数
        From 药品单据性质 A, 药品入出类别 B
        Where a.类别id = b.Id And a.单据 = 5 And Rownum < 2;
      
        v_No := Nextno(25, v_库房id);
      
        --产生库存差价调整单
        Insert Into 药品收发记录
          (ID, 记录状态, 单据, NO, 序号, 库房id, 入出类别id, 供药单位id, 入出系数, 药品id, 批次, 产地, 批号, 效期, 填写数量, 零售价, 成本价, 差价, 摘要, 填制人, 填制日期,
           审核人, 审核日期, 生产日期, 批准文号, 单量, 发药方式, 扣率, 灭菌效期, 费用id)
          Select v_收发id, 1, 5, v_No, 1, 库房id, v_入出类别id, 供药单位id, v_入出系数, 药品id, 批次, 产地, 批号, 效期, v_实际数量, v_零售金额, v_差价,
                 v_修正金额, '自动修正调价变动', 审核人, 审核日期, 审核人, 审核日期, 生产日期, 批准文号, v_现价, 1, 成本价, 灭菌效期, 收发id_In
          From 药品收发记录
          Where ID = 收发id_In;
      
        --更新库存
        Zl_药品库存_Update(v_收发id, 2, 0);
      
      End If;
    End If;
  End If;

Exception
  When Others Then
    zl_ErrorCenter(SQLCode, SQLErrM);
End Zl_药品收发记录_调价修正;
/


--113570:李业庆,2017-08-28,新增药品价格表处理
Create Or Replace Procedure Zl1_Autoadjustall As
Begin
  If f_Is_Primary_Node = 0 Then
    Return;
  End If;
  Zl_药品收发记录_Adjust(0);
End;
/

